mapper.select:
select 

from t_back_feed_type
where 1 = 1

    and feed_type_name = #{feed_type_name,jdbcType=VARCHAR}


    and create_date = #{create_date,jdbcType=TIMESTAMP}


    and is_valid = #{is_valid,jdbcType=INTEGER}

mapper.update:
update t_back_feed_type
set feed_type_name = #{feed_type_name,jdbcType=VARCHAR},
  create_date = #{create_date,jdbcType=TIMESTAMP},
  is_valid = #{is_valid,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}
