/*Table structure for table `t_cust` */

DROP TABLE IF EXISTS `t_cust`;

CREATE TABLE `t_cust` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT '主键id',
  `user_name` varchar(20) NOT NULL COMMENT '系统用户',
  `nick_name` varchar(20) DEFAULT NULL COMMENT '用户昵称',
  `salt` char(50) DEFAULT NULL COMMENT '加密key（一次加密）',
  `password` char(50) NOT NULL COMMENT '密码（二次加密）',
  `level_id` int(11) DEFAULT NULL COMMENT '等级id',
  `create_date` datetime DEFAULT NULL COMMENT '创建时间',
  `last_login_date` datetime DEFAULT NULL COMMENT '最近的登录日期',
  `is_valid` int(1) NOT NULL COMMENT '是否有效，0,有效；1，无效2，删除',
  `head_img_url` varchar(100) DEFAULT NULL COMMENT '头像地址',
  `wei_xin` varchar(50) DEFAULT NULL COMMENT '微信号',
  `qq` varchar(50) DEFAULT NULL COMMENT 'qq号',
  `sina_wei_bo` varchar(50) DEFAULT NULL COMMENT '新浪微博号',
  `is_wei_xin` int(1) DEFAULT '0' COMMENT '0，未绑定；1，绑定',
  `is_qq` int(1) DEFAULT '0' COMMENT '0，未绑定；1，绑定',
  `is_sina_wei_bo` int(1) DEFAULT '0' COMMENT '0，未绑定；1，绑定',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=9 DEFAULT CHARSET=utf8 COMMENT='前台会员表';

