/*Table structure for table `t_data` */

DROP TABLE IF EXISTS `t_data`;

CREATE TABLE `t_data` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT '主键id',
  `category_id` int(11) unsigned NOT NULL COMMENT '数据源分类id',
  `title` varchar(20) NOT NULL COMMENT '书名(标题）',
  `author` varchar(20) DEFAULT NULL COMMENT '作者',
  `cover` varchar(20) DEFAULT NULL COMMENT '封面',
  `capture_date` datetime DEFAULT NULL COMMENT '抓取日期',
  `publisher` varchar(20) DEFAULT NULL COMMENT '出版社',
  `sub_title` varchar(20) DEFAULT NULL COMMENT '副标题',
  `original_titile` varchar(20) DEFAULT NULL COMMENT '原作者',
  `translator` varchar(20) DEFAULT NULL COMMENT '译者',
  `publish_year` year(4) DEFAULT NULL COMMENT '出版年',
  `page_size` int(11) unsigned DEFAULT NULL COMMENT '页数',
  `price` double DEFAULT NULL COMMENT '定价',
  `bind` varchar(20) DEFAULT NULL COMMENT '装帧',
  `book_category` varchar(20) DEFAULT NULL COMMENT '数据类别',
  `isbn` varchar(50) DEFAULT NULL COMMENT '出版编号',
  `content_brief` varchar(50) DEFAULT NULL COMMENT '内容简介',
  `author_brief` varchar(50) DEFAULT NULL COMMENT '作者简介',
  `score_number` varchar(20) DEFAULT NULL COMMENT '评分及人数(评论人数）',
  `score_detail` varchar(20) DEFAULT NULL COMMENT '评分细节',
  `where_buy` varchar(20) DEFAULT NULL COMMENT '从何处购买',
  `douban_label` varchar(20) DEFAULT NULL COMMENT '豆瓣标签',
  `reading_number` int(11) unsigned DEFAULT NULL COMMENT '在读人数',
  `readed_numer` int(11) unsigned DEFAULT NULL COMMENT '读过人数',
  `want_reading_number` int(11) unsigned DEFAULT NULL COMMENT '想读人数',
  `subordinate_series` varchar(20) DEFAULT NULL COMMENT '所属丛书',
  `view_count` int(11) unsigned DEFAULT NULL COMMENT '浏览次数',
  `dianzan_count` int(11) unsigned DEFAULT NULL COMMENT '点赞数量',
  `status` int(2) unsigned DEFAULT NULL COMMENT '状态',
  `vedio_image_url` varchar(20) DEFAULT NULL COMMENT '视频/图片地址',
  `content` text COMMENT '内容',
  `is_valid` int(2) unsigned DEFAULT NULL COMMENT '0,有效；1，无效；2，删除',
  PRIMARY KEY (`id`)
