/*Table structure for table `t_info` */

DROP TABLE IF EXISTS `t_info`;

CREATE TABLE `t_info` (
  `info_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '资讯主键',
  `author_id` int(11) DEFAULT NULL COMMENT '作者id',
  `author_type` varchar(20) DEFAULT NULL COMMENT '作者类型',
  `author_name` varchar(30) DEFAULT NULL COMMENT '作者姓名',
  `tag_id` int(11) DEFAULT NULL COMMENT '标签ID',
  `has_banner` varchar(2) DEFAULT NULL COMMENT '是否有banner图',
  `has_image` varchar(2) DEFAULT NULL COMMENT '是否含有图片',
  `has_video` varchar(2) DEFAULT NULL COMMENT '是否含有视频',
  `has_flash` varchar(2) DEFAULT NULL COMMENT '是否含有flash',
  `search_word` varchar(50) DEFAULT NULL COMMENT '搜索关键词',
  `search_tag` varchar(50) DEFAULT NULL COMMENT '搜索标签',
  `origin` varchar(10) DEFAULT NULL COMMENT '来源分类',
  `origin_url` varchar(100) DEFAULT NULL COMMENT '来源地址',
  `origin_name` varchar(50) DEFAULT '未知' COMMENT '来源名称',
  `stage` varchar(10) DEFAULT NULL COMMENT '当前审核阶段',
  `status` varchar(10) DEFAULT NULL COMMENT '状态,（1101-1草稿，1101-2，保存待确认，1102-1待审核阶段，1102-2，一阶段审核未通过，1102-3，二阶段待审核，1102-4，二阶段审核未通过，1102-5二阶段审核通过，1102-6,待发布，1103-1，发布，1103-2，发布撤回）',
  `is_valid` varchar(2) NOT NULL DEFAULT '0' COMMENT '是否有效，0-有效，1-无效，2-删除',
  `create_time` timestamp NULL DEFAULT NULL COMMENT '创建时间',
  `update_time` timestamp NULL DEFAULT NULL COMMENT '最后一次更新时间',
  `publish_time` timestamp NULL DEFAULT NULL COMMENT '发布时间',
  `rollback_time` timestamp NULL DEFAULT NULL COMMENT '撤回时间',
  `resv1` varchar(20) DEFAULT '0' COMMENT '是否推荐0-未推荐,1-已推荐',
  `resv2` varchar(20) DEFAULT NULL COMMENT '1-只是首页，2-只是模块，3-既有首页也有模块，4表示删除掉图片了',
  `resv3` varchar(20) DEFAULT NULL,
  `resv4` varchar(20) DEFAULT NULL,
  `ban_id` int(11) DEFAULT NULL COMMENT 'ban_id',
  `check_id` int(11) DEFAULT NULL COMMENT 'check_id',
  `comment_id` int(11) DEFAULT NULL COMMENT 'comment_id',
  `content_id` int(11) DEFAULT NULL COMMENT 'content_id',
  `count_id` int(11) DEFAULT NULL COMMENT 'count_id',
  `view_id` int(11) DEFAULT NULL COMMENT 'view_id',
  PRIMARY KEY (`info_id`)
) ENGINE=InnoDB AUTO_INCREMENT=71 DEFAULT CHARSET=utf8 COMMENT='资讯主表';

