/*Table structure for table `t_info_check` */

DROP TABLE IF EXISTS `t_info_check`;

CREATE TABLE `t_info_check` (
  `check_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '审核表主键',
  `info_id` int(11) NOT NULL COMMENT '资讯表ID',
  `user_id` int(11) DEFAULT NULL COMMENT '审核人ID',
  `user_name` varchar(20) DEFAULT NULL COMMENT '审核人姓名',
  `opinion` varchar(100) DEFAULT NULL COMMENT '审核意见',
  `is_agree` varchar(2) DEFAULT NULL COMMENT '是否同意',
  `stage` varchar(10) DEFAULT NULL COMMENT '当前审核阶段',
  `check_time` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '审核时间',
  `is_valid` varchar(2) NOT NULL DEFAULT '0',
  `resv1` varchar(20) DEFAULT NULL,
  `resv2` varchar(20) DEFAULT NULL,
  `resv3` varchar(20) DEFAULT NULL,
  `resv4` varchar(20) DEFAULT NULL,
  PRIMARY KEY (`check_id`)
