/*Table structure for table `t_info_comment` */

DROP TABLE IF EXISTS `t_info_comment`;

CREATE TABLE `t_info_comment` (
  `comment_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '评论主键',
  `info_id` int(11) NOT NULL COMMENT '资讯表主键',
  `parent_comment_id` int(11) DEFAULT NULL COMMENT '父comment的ID',
  `author_id` int(11) DEFAULT NULL COMMENT '发表评论的用户ID',
