/*Table structure for table `t_news_info` */

DROP TABLE IF EXISTS `t_news_info`;

CREATE TABLE `t_news_info` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT '主键id',
  `title` varchar(100) NOT NULL COMMENT '标题',
  `subtitle` varchar(100) DEFAULT NULL COMMENT '副标题',
  `releaseTime` varchar(50) DEFAULT NULL COMMENT '发表时间',
  `author` varchar(100) NOT NULL COMMENT '作者',
  `newsType` varchar(100) DEFAULT NULL COMMENT '新闻类别',
  `content` text COMMENT '内容',
  `attachments` varchar(100) DEFAULT NULL COMMENT '附件，相关图片/视频/附件',
  `viewCount` varchar(11) NOT NULL COMMENT '浏览次数',
  `newsOriginal` varchar(100) DEFAULT NULL COMMENT '新闻来源',
  `crawlerOriginal` varchar(100) DEFAULT NULL COMMENT '抓取来源',
  `crawlerTime` varchar(50) DEFAULT NULL COMMENT '抓取日期',
  `indexNum` varchar(50) DEFAULT NULL COMMENT '索引号',
  `themeType` varchar(50) DEFAULT NULL COMMENT '主题分类',
  `publishOrgan` varchar(50) DEFAULT NULL COMMENT '发布机关',
  `publishDate` varchar(50) DEFAULT NULL COMMENT '发布日期',
  `name` varchar(50) DEFAULT NULL COMMENT '名称',
  `themeWord` varchar(100) DEFAULT NULL COMMENT '主题词',
  `docNum` varchar(100) DEFAULT NULL COMMENT '文号',
  `fromUnit` varchar(50) DEFAULT NULL COMMENT '来文单位',
  `approvalFileName` varchar(100) DEFAULT NULL COMMENT '审批文件名称',
  `approvalDocNum` varchar(50) DEFAULT NULL COMMENT '审批文号',
  `approvalTime` varchar(50) DEFAULT NULL COMMENT '时间',
  `crawlerUrl` varchar(100) DEFAULT NULL COMMENT '抓取URL',
  `is_valid` varchar(50) DEFAULT NULL COMMENT '是否有效',
  `resv1` varchar(50) DEFAULT NULL COMMENT '保留字段1',
  `resv2` varchar(50) DEFAULT NULL COMMENT '保留字段2',
  `resv3` varchar(50) DEFAULT NULL COMMENT '保留字段3',
  `resv4` varchar(50) DEFAULT NULL COMMENT '保留字段4',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=147 DEFAULT CHARSET=utf8 COMMENT='抓取新闻数据内容存放表';

/*Data for the table `t_news_info` */

insert  into `t_news_info`(`id`,`title`,`subtitle`,`releaseTime`,`author`,`newsType`,`content`,`attachments`,`viewCount`,`newsOriginal`,`crawlerOriginal`,`crawlerTime`,`indexNum`,`themeType`,`publishOrgan`,`publishDate`,`name`,`themeWord`,`docNum`,`fromUnit`,`approvalFileName`,`approvalDocNum`,`approvalTime`,`crawlerUrl`,`is_valid`,`resv1`,`resv2`,`resv3`,`resv4`) values (1,'国家新闻出版广电总局 - 行业信息 - 广东展团3000余种图书亮相','广东展团3000余种图书亮相','2015年9月28日','王坤宁  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 本届书博会广东展团由南方出版传媒股份有限公司具体承办，省内21家出版单位参展，参展图书3021种。其中南方出版传媒8家出版单位携1100多种图书参展，新书品种约占50%。 <br> </p> \n<p style=\"text-indent:2em;\"> 广东展团在本次书博会上设置了精品展示区，通过主题出版项目、国家和省部级获奖项目、国家出版基金项目、市场精品图书等板块集中展示品牌形象和优秀出版成果。展示的精品包括纪念中国人民抗日战争暨世界反法西斯战争胜利70周年主题图书，如广东经济出版社的《血火铸长城——写给新一代人看的抗战史》、暨南大学出版社的《丰碑永铸——华侨华人与抗日战争图片集》；弘扬主旋律、弘扬社会主义核心价值观、弘扬中国优秀传统文化的主题图书，如广东人民出版社的《历史视野下的精神家园》、广东教育出版社的《中国特色社会主义研究论丛》；习近平总书记系列重要讲话精神和治国理政的重要图书，如花城出版社的《继续写好中国特色社会主义这篇大文章》、广东海燕电子音像出版社的《追梦在路上——我的梦·中国梦》；获得国家级奖项的图书，如获中华优秀出版物奖的广东经济出版社“中国南海文化研究丛书”、获得茅盾文学奖的花城出版社《这边风景》，以及入选总局向全国青少年推荐的百种优秀图书、新世纪出版社的《晚安的吻在路上》；获得国家出版基金项目资助的图书，如广东教育出版社的《黄埔军校史料汇编》、广东科技出版社的“美丽中国丛书”等。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：王坤宁 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年9月28日 </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 广东展团3000余种图书亮相','2015-12-16 17:36:41','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265703.shtml',NULL,NULL,NULL,NULL,NULL),(2,'国家新闻出版广电总局 - 行业信息 - 两岸同步出版抗战图书','两岸同步出版抗战图书','2015年10月8日','章红雨  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 9月30日，由台海出版社与台湾华品文创出版公司联合出版的繁体字版“台湾抗日丛书”在台湾诚品信义店举行首发式，以纪念抗日战争胜利暨台湾光复70周年。 <br> </p> \n<p style=\"text-indent:2em;\"> 据台湾华品文创出版公司总经理王承惠介绍，“台湾抗日丛书”包括《台湾抗日人物集之一》《台湾抗日人物集之二》《台湾抗日人物集之三》《台湾抗日团体》《台湾共产党抗日史实》《台湾光复》6册，从不同的角度展示了抗日战争中台湾人民浴血杀敌的历史事实，体现了台湾人民反抗外来侵略的不屈精神和在空前惨烈的八年抗战岁月中台湾人民作出的不可磨灭的贡献。 </p> \n<p style=\"text-indent:2em;\"> 台海出版社副总编辑刘向鸿说，书中许多史料、人物是第一次对外披露，具有极高的文献价值和史学价值。台海出版社将于10月12日在北京举行该丛书简体字版首发式。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：章红雨 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月8日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 两岸同步出版抗战图书','2015-12-16 17:36:42','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265966.shtml',NULL,NULL,NULL,NULL,NULL),(3,'国家新闻出版广电总局 - 行业信息 - 2015刊博会：谋划期刊产业新常态','2015刊博会：谋划期刊产业新常态','2015年9月15日','刘蓓蓓  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 新常态、新融合、新发展，是2015中国（武汉）期刊交易博览会的主题，也是本届刊博会各项活动力图展现的时代风采，将于9月18日至20日在武汉国际展览中心举办的本届刊博会，在活动策划上务实求新、精彩纷呈，力求带来一场期刊文化的盛宴。 <br> </p> \n<p style=\"text-indent:2em;\"> 据了解，本届刊博会将策划组织各类活动117场，比上届刊博会增加50%。其中，各类论坛活动16个、仪式类活动13个、专业类活动17个、大众类活动55个、评比颁奖发布类活动7个。既有主题多样的高端论坛、峰会、研讨会、交流会，又有主题突出的中文期刊200年回顾、现代汽车文化展演、“互联网+”、动漫文化以及凸显中华优秀传统文化诸多元素的展示活动；既有代表现代媒体前沿的学术交流与探讨，又有知名作家见面会、讲座等多种社会大众喜闻乐见的互动性活动。 </p> \n<p style=\"text-indent:2em;\"> 本届刊博会推出12大类主题馆展区，其中新推出中央国家机关期刊荟萃馆、中国军事期刊馆、青少年期刊馆、女性期刊馆等。各类精心设计的活动多元化呈现、多角度展示、分众化服务，媒介的功能愈加凸显；在中国期刊媒体国际创新发展论坛、世界期刊版权研讨会和中国最美期刊遴选等六大已有品牌活动的基础上，中国期刊走出去高峰论坛、期刊融合发展论坛等20余项新活动亦将在人们的期待中相继开锣；业界期待的“全国百强报刊”、“全国年度期刊发行百强”、“中国最美期刊”、“全国数字期刊影响力百强”等都将在本届刊博会上发布。 </p> \n<p style=\"text-indent:2em;\"> 此外，本届刊博会主论坛以及其他各个论坛力图专业化、国际化、高端化。创新发展论坛、国际数字出版论坛、“一带一路”国际期刊出版论坛、华中国际版权论坛、中国广告东湖论坛、低碳经济论坛等诸多论坛，紧紧围绕新常态下期刊产业发展主题，专门设置了数字时代的广告与营销、数字媒体的跨平台传播、媒体未来的更多想象等多个平行分论坛。论坛活动共邀请到来自英、美、日、德、法、俄、韩等40余个国家和地区行业标杆企业的50余位负责人担任演讲嘉宾。 </p> \n<p style=\"text-indent:2em;\"> 本届刊博会吸引了全球40多个国家和地区的业界精英和社会名流。韩国期刊协会会长金基源，英国创新国际媒体咨询集团首席设计顾问、MP媒体投资集团总经理杰弗里（Jeffrey P. Sprafkin），资深广告人克里斯·贝克（Chris Baker）等100余名国际期刊界精英将出席论坛活动。刘醒龙、余熙、仓央嘉措、余秀华等国内文化名人和知名作家亦将参加刊博会，与广大读者一起开展各类活动。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：刘蓓蓓 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年9月15日 </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 2015刊博会：谋划期刊产业新常态','2015-12-16 17:36:42','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/264866.shtml',NULL,NULL,NULL,NULL,NULL),(4,'国家新闻出版广电总局 - 行业信息 - “做焦裕禄式的县委书记丛书”出版','“做焦裕禄式的县委书记丛书”出版','2015年9月17日','朱侠  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 由中共中央党校组织编写的“做焦裕禄式的县委书记丛书”近日由人民出版社出版发行。9月15日，该书出版座谈会在中央党校召开，来自基层的县委书记代表结合实践，畅谈学习习近平总书记重要讲话精神，并引发参加座谈会的中央党校2015年第四期县委书记研修班200多名学员的共鸣。 <br> </p> \n<p style=\"text-indent:2em;\"> 据了解，该丛书是新中国成立以来第一套专门为县级领导干部量身打造的学习读物。丛书紧紧围绕习近平总书记提出的做焦裕禄式的县委书记这一主题，从不同视角、不同层面进行了全面、系统、生动的阐释。丛书共8种，分别是《县委书记的楷模：焦裕禄 谷文昌》、《全国优秀县委书记风采录》（上、下）、《县委书记谈怎样做“四有”县委书记》（上、下）、《改革发展看县市》、《全面依法治国中的县域治理》、《建设强富美的新农村》、《县委书记的领导能力》、《中国古代贤明县令》。 </p> \n<p style=\"text-indent:2em;\"> 丛书内容既有专家学者撰写的从多方面提升县委书记素质和能力的专题著作，又有县委书记们的体会文章，还讲述了陶渊明、狄仁杰、包拯、王安石、海瑞、郑板桥等古代贤明官吏的事迹，帮助读者吸取中国古代的治理智慧和经验。 </p> \n<p style=\"text-indent:2em;\"> 与会者表示，丛书的出版，对于县委书记和其他县级领导干部深入学习贯彻习近平总书记重要讲话精神，认真践行“四有”和做“四个人”的要求，做焦裕禄式的好干部，具有重要的启示和价值。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：朱侠 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年9月17日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - “做焦裕禄式的县委书记丛书”出版','2015-12-16 17:36:43','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265005.shtml',NULL,NULL,NULL,NULL,NULL),(5,'国家新闻出版广电总局 - 行业信息 - “农家书屋让出版人大受鼓舞”','“农家书屋让出版人大受鼓舞” ——社长总编辑“深入生活，扎根人民”走进陕西农家书屋活动纪行','2015年9月25日','左志红  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> “灞桥秋柳黄绿间，月河流觞意缠绵。汉阴古县有文气，‘三沈’传文又启贤。总局率队访陕南，农家书屋经验传。八亿农民爱读书，中国之梦早日圆。”9月16日，在结束了对陕西农家书屋为期两天的调研后，人民文学出版社总编辑刘国辉有感而发，即兴写下了这首诗。 </p> \n<p style=\"text-indent:2em;\"> 这次调研是国家新闻出版广电总局组织的“深入生活，扎根人民”走进农家书屋活动的第二站，总局印刷发行司司长王岩镔、巡视员谭汶带队，率7家在京出版社和西安交通大学出版社的社长总编辑们，来到陕西省渭南市华县和安康市汉阴县，走访了5家农家书屋。 </p> \n<p style=\"text-indent:2em;\"> <strong> 看书屋：“感到非常震撼” </strong> </p> \n<p style=\"text-indent:2em;\"> 9月15日，调研组一行来到渭河南岸的华县。在杏林镇三溪村农家书屋和赤水镇步背后村、辛庄村农家书屋，墙上都挂着一个专家咨询导师公示牌，上面有8个林业、农艺、教育、医学等方面的专家的电话。社长总编辑们对此很好奇，华县文体局工会主席李变丽解释说：“农民朋友在读书时有问题可以给专家打电话，他们会给予解答。这项服务很受欢迎，专家们的电话常常被打爆。” </p> \n<p style=\"text-indent:2em;\"> 9月16日，从一条条隧道中穿过秦岭，调研组一行来到汉阴县城关镇，两名退休老干部义务担任农家书屋管理员的事迹让社长总编辑们很是敬佩。月河村农家书屋的管理员沈继惠退休前是宁陕县县长，2011年，当他得知老家建成农家书屋但急缺管理员的消息后，便主动回村做管理员。4年来，在沈继惠的努力下，书屋借阅量达2600人次。五一村农家书屋的管理员李传文2006年从县委党校退休后，将自家祖屋改建成传文书屋，2007年书屋被纳入农家书屋工程建设。8年来，传文农家书屋借阅量已有近万人次。 </p> \n<p style=\"text-indent:2em;\"> 走访过这5家农家书屋后，出身农村家庭的人民出版社副总编辑陈鹏鸣深有感触：“看到农家书屋发挥了这么大的作用，影响了这么多人，我感到建设农家书屋真的非常有必要。知识改变命运，这一点我有切身体会。当年我读书时，在农村找书读非常困难。现在有了农家书屋，像我这样当年想读书却没书读的孩子，就有书可读了。” </p> \n<p style=\"text-indent:2em;\"> “感到非常震撼。”中国医药科技出版社社长兼总编辑吴少祯是第一次走进农家书屋，“农家书屋很大程度上解决了农民种植养殖和医疗等方面的问题，提高了8亿农民的文化素养，今后我们要多策划农民需要的好书。”中国少年儿童新闻出版总社副总经理赵恒峰也用“很震撼”来形容这几天的感受：“这几个农家书屋一个比一个大，一个比一个阅读活动开展得好，我们作为出版人大受鼓舞。” </p> \n<p style=\"text-indent:2em;\"> 《人民日报》政文部文化新闻采访室主编张贺说：“近期有些媒体对农家书屋的报道有些偏颇，这是因为他们对具体情况不了解。《人民日报》作为党报，有责任真实、准确、客观地报道。” </p> \n<p style=\"text-indent:2em;\"> <strong> 谈感受：“要给农民做好书” </strong> </p> \n<p style=\"text-indent:2em;\"> “农家书屋目前处于提档升级阶段，主要任务之一是抓内容建设。希望社长总编辑们通过这次调研，策划出版更多农民朋友看得懂、用得上的好书，真正将‘三严三实’精神落到实处。”王岩镔表示。 </p> \n<p style=\"text-indent:2em;\"> 对此，社长总编辑们在调研之后的座谈会上积极回应。中信出版社总编辑洪勇刚说：“通过调研，我发现农村出版物市场大有可为。农家书屋最多的读者是中小学生，今后我社会针对农村儿童加强选题策划。”赵恒峰也表示，目前全国农村有几千万留守儿童，中少总社将积极为他们策划选题，并加大在农村地区开展阅读推广活动的力度。 </p> \n<p style=\"text-indent:2em;\"> “农家书屋不仅要让农民有书读，还要有好书给农民看。种植养殖的书有时间和地域方面的限制，但文学书没有。”基于此，刘国辉提议几家出版社联合出版一套高质量的“农家文库”，专供农家书屋。 </p> \n<p style=\"text-indent:2em;\"> 陈鹏鸣说：“今年我们在怎么使图书更接地气上做了一些尝试，如出版插图本，用手机扫书上的二维码看视频等。今后我们将努力使图书更接地气，以适应农民读者的需求。” </p> \n<p style=\"text-indent:2em;\"> 在华县农家书屋看到自己社的7本书，这让北京科学技术出版社党委副书记李金莉很欣喜。她说：“我发现农民读者的结构发生了变化，老人、妇女、儿童多，今后我社将为这些群体有针对性地出书。比如，可以给老人出图多、字大的书，给学龄前儿童开发图画书。” </p> \n<p style=\"text-indent:2em;\"> 除了在策划选题方面有所思考，社长总编辑们也纷纷表态，将以实际行动支持农家书屋。洪勇刚表示，中信出版社将向月河村农家书屋和传文农家书屋捐赠中信图书的2个书架，并向汉阴县图书馆捐赠1000册图书。中国地图出版集团发行公司总经理池涛说，将把集团目前的营销活动融入到农家书屋项目，链接到乡间学校，集团还将向月河村农家书屋和传文农家书屋赠送2个示范书架，并拟与传文农家书屋联合实施一个学生读书楼众筹项目。吴少祯也表示，中国医药科技出版社将大力支持农家书屋工程建设。 </p> \n<p style=\"text-indent:2em;\"> <strong> 提建议：“农家书屋需要现代乡贤” </strong> </p> \n<p style=\"text-indent:2em;\"> 对于农家书屋的未来发展，参加调研的各界人士也纷纷提出了自己的建议。 </p> \n<p style=\"text-indent:2em;\"> “全民阅读不要忘了8亿农民的阅读。我到老乡家发现农民的住房和家电都很现代化，但没有几本书。由此可见农村出版物市场大有可为，希望社长总编辑们重视农村市场。”谭汶说。 </p> \n<p style=\"text-indent:2em;\"> 刘国辉将沈继惠和李传文两位书屋管理员看作“现代社会的乡贤”。他认为：“‘耕读传家’是农村的传统，乡贤在农村传统文脉的传承中起着不可替代的作用。农家书屋需要现代乡贤，应该号召党员干部在退休之后参与农家书屋的建设管理，为文化、文明、子孙贡献他们的一份力量。”洪勇刚建议，推动全民阅读不是一个部门一个系统的事，而是全社会的事。工青妇等社会团体都有阅读工程，可以整合这些团体的力量建设农家书屋。 </p> \n<p style=\"text-indent:2em;\"> 汉阴县县委副书记、县长周永鑫全程陪同调研组在汉阴县走访农家书屋，他在座谈会上表示，将农家书屋建设作为提升汉阴文化软实力的一项战略来抓。他希望上级主管部门适当预算一定的书屋管理运行经费，并积极支持陕南移民搬迁新型社区建立农家书屋。华县图书馆馆长王翠侠希望国家给予管理员一定报酬，选拔有文化、有能力的人担任管理员，使农家书屋发挥更大的作用。 </p> \n<p style=\"text-indent:2em;\"> 沈继惠也根据自己担任管理员的体会建议，党委政府每年给公务员和教师下达一定数量的阅读任务，并纳入年终考核，这样以干部带群众，以党风带民风，把全民阅读和书香社会建设落到实处，形成社会风尚。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：左志红 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年9月25日 </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - “农家书屋让出版人大受鼓舞”','2015-12-16 17:36:43','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265608.shtml',NULL,NULL,NULL,NULL,NULL),(6,'国家新闻出版广电总局 - 行业信息 - 2015邮政发行报刊百强公布','2015邮政发行报刊百强公布','2015年9月21日','范燕莹  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 9月18日，中国邮政集团公司公布了“2015年中国邮政发行报刊百强排行榜”。《人民日报》、《参考消息》、《新华每日电讯》、《扬子晚报》、《环球时报》位居报纸类前五名，《读者》、《特别关注》、《求是》、《知音漫客》、《特别文摘》位居期刊类前五名。 <br> </p> \n<p style=\"text-indent:2em;\"> 鉴于报纸和期刊的不同特点，本次评选分别排定“报纸50强”和“期刊50强”，两项合计100强。“报纸50强”既有各级党报和行业报刊，也有广受市民欢迎的消费类报纸。“期刊50强”覆盖面较广，大众消费类期刊占据了榜单的主要位置。 </p> \n<p style=\"text-indent:2em;\"> 据了解，中国邮政发行报刊百强排行榜是中国邮政集团公司为了提升知名邮发品牌报刊的影响力推出的榜单，自2013年开始，每年评选一次。榜单评定时，综合考虑报刊的期发量和流转额两个关键指标，按照综合得分情况进行排名。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：范燕莹 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年9月21日 </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 2015邮政发行报刊百强公布','2015-12-16 17:36:42','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265175.shtml',NULL,NULL,NULL,NULL,NULL),(7,'国家新闻出版广电总局 - 行业信息 - 第25届书博会山西省主展区：展山西出版实力 秀三晋多彩  文化','第25届书博会山西省主展区：展山西出版实力 秀三晋多彩 文化','2015年9月24日','刘蓓蓓  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 山西作为第25届全国图书交易博览会的主办省，依托本省丰富的文化资源和出版资源，历时数月精心打造山西主展区，将在凉爽的9月为全国出版界同仁和广大人民群众奉献一场精美的文化盛宴。 <br> </p> \n<p style=\"text-indent:2em;\"> <strong> 亮点一：重点图书彰显出版实力 </strong> </p> \n<p style=\"text-indent:2em;\"> 本届书博会山西省主展区将展示千余种晋版新书、近万种精品图书，尽显山西出版的实力和风采。 </p> \n<p style=\"text-indent:2em;\"> 在图书综合展示区，彰显地方文化特色是重要主题。被誉为山西省建设文化强省标志性工程和山西当代“四库全书”的“山西文华”丛书第一批图书将集中在山西省展区亮相。“山西文华”丛书分为“著述编”“史料编”“图录编”，此次展示的共计有《傅山全书》《陈廷敬集》《晋政辑要（清光绪版）》等20余种。之外，《陈廷敬传》《一代廉吏于成龙》《画说廉政》《血铸山河》等“三大文化”丛书，将全面阐释山西源远流长的法治文化、博大精深的廉政文化和光耀千秋的红色文化。 </p> \n<p style=\"text-indent:2em;\"> 今年是中国人民抗日战争暨世界反法西斯战争胜利70周年，抗战主题图书也是山西省主展区展示的重点。由山西省青年作家创作的《中国战场之共赴国难》，首度用史诗笔法呈现了红军抗日先锋军东征山西，实现中国抗日民族统一战线形成的历史。《绥远1936》通过一幅幅珍贵照片和杨红林的笔触，再现了被称为“中国的罗伯特·卡帕”的失踪战地摄影师方大曾的传奇一生和绥远抗战。 </p> \n<p style=\"text-indent:2em;\"> 此外，获奖图书展示板块将集中展示近年来获得国家级奖项的晋版图书，如在连续三届中宣部“五个一工程”奖评选中分别入选的《流动的花朵》《乍放的玫瑰》《少年的荣耀》，获得中国出版政府奖、中华优秀出版物奖的30余种图书。重点项目展示板块将集中展示入选国家“十二五”规划、国家出版基金的图书，如《近代名家散佚学术著作丛刊》《历代散佚碑帖珍本考释》等。地方文化展示板块将集中展示《三晋石刻大全》《山西故事丛书》等晋商类、山西旅游类图书。 </p> \n<p style=\"text-indent:2em;\"> 在出版社展示区，山西省8家出版社将分别展示最新出版的图书和特色图书，在人文社科、教育、少儿、中医、武术、文学、财经、古籍、电子音像出版物等方面为广大读者贡献优质丰富的精神食粮。 </p> \n<p style=\"text-indent:2em;\"> <strong> 亮点二：本土作家作品展示地域魅力 </strong> </p> \n<p style=\"text-indent:2em;\"> 本届书博会山西省主展区特设山西作家作品展。山西作家在中国现当代文学史上占据重要位置，从赵树理、山药蛋派到“晋军崛起”，再到第五代作家享誉全国文坛，山西作家一直潜心创作，精品涌现。山西作家作品展主要展示山西作家的创作风貌，重点展示《三晋百部长篇小说文库》和《山药蛋派经典文库》等。 </p> \n<p style=\"text-indent:2em;\"> 《三晋百部长篇小说文库》对赵树理以来的山西长篇小说经典力作进行了大规模、系统化的梳理出版，全面、客观、真实地反映了山西长篇小说创作轨迹。首批12册图书推出赵树理、马烽、成一、刘江、高冈、柯云路、哲夫、吕新、葛水平等山西老中青作家的代表作品。《山药蛋派经典文库》共12册，有诸如《吕梁英雄传》《三里湾》《李有才板话》《汾水长流》及赵树理、马烽、胡正、西戎、李束为、孙谦小说散文集等，这些图书对传承文学经典，引领全民阅读有重要意义。 </p> \n<p style=\"text-indent:2em;\"> 此外，山西新一代作家葛水平、李骏虎、孙频等的新作，也将在书博会期间集中展示，显示本土年轻作家的创作实力。 </p> \n<p style=\"text-indent:2em;\"> <strong> 亮点三：多元转型展区生动有趣 </strong> </p> \n<p style=\"text-indent:2em;\"> 多元转型展区将集中展示山西出版业在多元业务拓展、数字出版、网络出版等领域进行的探索和取得的成就。 </p> \n<p style=\"text-indent:2em;\"> “晋韵华彩”系列展示以山西地方特色的文化产品为内容，包括晋作石末砚、曲沃青铜器、平定砂器与刻花瓷、黎城黎侯虎、平遥推光漆器、晋作家具等，向观众展示丰富、生动、高雅的地方文化产品。《五台山文化遗产数字库》运用三维信息采集等现代化信息技术手段对五台山的建筑、雕像、壁画及相关历史文献信息进行全面系统的采集、处理和存储，使观众不到五台山亦可领略五台山的绝美风景。尔慈视觉卡将国际最先进的AR增强现实技术与儿童启蒙学习卡相结合，让学习变得立体丰富，生动有趣。 </p> \n<p style=\"text-indent:2em;\"> <strong> 亮点四：法治文明成果展图文并茂 </strong> </p> \n<p style=\"text-indent:2em;\"> 为了深入宣传依法治国和弘扬山西源远流长的法治文化，山西省主展区特设法治文明建设历程出版成果展。这部分以展板展示和图书展示为主，全方位展示中国法治进程及山西法治发展历程，与山西展区其他亮点交相辉映。 </p> \n<p style=\"text-indent:2em;\"> 展览分为四个部分，一是简要介绍我国法治进程的重大决策，二是重点展示我国法治建设的成果，三是展示我国法治历程典型案例，四是介绍我国法治人物。这四个部分图文并茂，深入浅出，对参观者，尤其是青少年具有很好的法治教育意义。 </p> \n<p> <br> </p> \n<p> 作者：刘蓓蓓 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年9月24日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 第25届书博会山西省主展区：展山西出版实力 秀三晋多彩 文化','2015-12-16 17:36:43','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265488.shtml',NULL,NULL,NULL,NULL,NULL),(8,'国家新闻出版广电总局 - 行业信息 - 社会效益重要性被提到新高度','社会效益重要性被提到新高度','2015年9月18日','孙海悦  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> “过去我们一直坚持社会效益和经济效益相统一，这次中央更加突出了将国有文化企业的社会效益放在首位，将社会效益的重要性提高到了一个新高度。”国务院参事、中国出版传媒股份有限公司副总经理樊希安近日接受记者采访时难掩兴奋。 <br> </p> \n<p style=\"text-indent:2em;\"> 中共中央办公厅、国务院办公厅近日印发的《关于推动国有文化企业把社会效益放在首位、实现社会效益和经济效益相统一的指导意见》（以下简称《指导意见》），引起了新闻出版广播影视界人士的广泛关注和热烈回应。 </p> \n<p style=\"text-indent:2em;\"> <strong> 标准具体 提供改革遵循 </strong> </p> \n<p style=\"text-indent:2em;\"> “强调文化产品的社会效益，就是强调其精神属性，强调其要给人以向上向善向美的精神力量。”在中国电影家协会副主席、中国电影文学学会会长王兴东看来，《指导意见》强调了充分认识文化产品宗旨和任务的重要性，“文化产品不是商业宣传品，也不是一般商品，而是作用于人的灵魂的精神产品。” </p> \n<p style=\"text-indent:2em;\"> 过去业界多次反映，对国有文化企业的考核应主要放在文化价值、社会影响等方面，应以社会效益为先。此次《指导意见》明确，当两个效益、两种价值发生矛盾时，经济效益服从社会效益、市场价值服从社会价值，越是深化改革、创新发展，越要把社会效益放在首位。对此，樊希安说，《指导意见》把对国有文化企业的考核评价标准具体化了，明确提出充分考虑不同类型国有文化企业的功能作用、明确社会效益指标考核权重应占50%以上，从而从根本上解决了导向问题。 </p> \n<p style=\"text-indent:2em;\"> “《指导意见》指明了下一步文化产业发展的方向，为文化体制改革提供了遵循，将对下一步繁荣文化事业、发展文化产业起到极大的推动作用。”重庆日报报业集团总裁向泽映道出了业界人士的共同心声。 </p> \n<p style=\"text-indent:2em;\"> <strong> 坚守责任 助力文化繁荣 </strong> </p> \n<p style=\"text-indent:2em;\"> 中国出版协会副理事长、韬奋基金会理事长聂震宁9月15日刚刚参加了《新闻出版广播影视从业人员职业道德自律公约》签约仪式。在他看来，党的十八大以来，广大新闻出版从业人员生产了大量优秀出版物，努力实现社会效益和经济效益相统一，使得行业的整体水平不断提高，行业的总体规模不断扩大，为中国特色社会主义文化事业的繁荣发展作出了很大贡献。 </p> \n<p style=\"text-indent:2em;\"> 继2013年12月发布中国出版业单体出版社首部系统化企业社会责任报告后，社会科学文献出版社不久前发布了第二部企业社会责任报告。该社社长谢寿光表示，该社始终把社会效益作为立社之本，不拼经济规模，拼的是发展质量和社会影响力。 </p> \n<p style=\"text-indent:2em;\"> 少儿出版是一项“树人工程”。浙江少年儿童出版社社长汪忠认为，少儿出版人应以弘扬社会主义核心价值观为己任，坚持把社会效益放在首位，努力实现社会效益与经济效益相统一。 </p> \n<p style=\"text-indent:2em;\"> 随着市场化进程加快和行业内外竞争日益激烈，行业失序、道德失范等不良现象时有发生。对此，聂震宁认为，面对前所未有的挑战和复杂局面，新闻出版广播影视业发挥舆论导向作用、服务改革发展大局的任务更重了，为人民群众提供丰富的精神食粮的任务也更重了。在新形势、新任务和新要求下，广大从业人员应提升正能量、创造新业绩。 </p> \n<p style=\"text-indent:2em;\"> <strong> 加强引导 确保意见落地 </strong> </p> \n<p style=\"text-indent:2em;\"> 对于如何切实落实《指导意见》，业界人士纷纷建言献策。谢寿光表示，要实现将社会效益放在首位，无论在政府还是企业层面，必须解决激励机制问题，如各类政府评奖和基金评审，都应对社会效益大的企业与项目有所扶持与倾斜，防止“劣币驱逐良币”。 </p> \n<p style=\"text-indent:2em;\"> “电影最重要的不是看票房，而是要看打动了多少人的心房。”为了确保社会效益第一，王兴东建议，国家应进一步出台细化措施，完善引导激励机制，加强原创和现实题材创作，努力创作生产更多传播当代中国价值观念、体现中华文化精神、弘扬中华优秀传统文化、反映中国人民奋斗追求的优秀文化产品。 </p> \n<p style=\"text-indent:2em;\"> “唯票房、唯收视率、唯发行量、唯点击率，这些现象背离了文化改革发展的原则和方向。”向泽映认为，在市场经济条件下，新业态、新技术迅猛发展，给传统媒体带来冲击和影响，要把社会效益放在首位、实现社会效益和经济效益相统一，宏观层面需要政府对国有文化企业的改革发展加以规划，提供相应的政策扶持和保障，如在投资、转移支付、政府采购等方面给予引导和鼓励；在企业内部管理的微观层面，要建立健全把社会效益放在首位的体制机制，加大对社会效益的考核权重，使企业更好地集中人力、物力，创造更多体现社会价值的优秀文化产品。 </p> \n<p style=\"text-indent:2em;\"> 向泽映表示，希望政府在各方面扶持推动传统媒体和新兴媒体融合发展，尤其是加大对纸媒在内容生产方面的扶持力度，使传统媒体在坚持社会效益首位的原则下进一步做大做强。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：孙海悦 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年9月18日 </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 社会效益重要性被提到新高度','2015-12-16 17:36:43','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265113.shtml',NULL,NULL,NULL,NULL,NULL),(9,'国家新闻出版广电总局 - 行业信息 - 全国党刊融合发展高层研讨会将在汉召开','全国党刊融合发展高层研讨会将在汉召开','2015年9月17日','刘蓓蓓  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 2015全国党刊融合发展高层研讨会将于9月18日在中国（武汉）期刊交易博览会上举办。研讨会由2015刊博会组委会、中国新闻出版传媒集团有限公司、中国新闻出版广电报社联合主办。 <br> </p> \n<p style=\"text-indent:2em;\"> 论坛主题为“互联网+党刊：丰富党刊发展新格局”，将设“融合的技术手段和传播方式”、“融合人才管理和激励机制”、“融合发展资金投入与使用”、“党刊集团（社）体制机制如何创新”等几个分议题。《紫光阁》杂志社、《党建》杂志社、《当代贵州》杂志社、辽宁党刊集团等党刊、数字期刊网站负责人将在会上分享经验、交流做法、碰撞热点。 </p> \n<p style=\"text-indent:2em;\"> 会上还将宣读“全国党刊数字联盟”倡议书。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：刘蓓蓓 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年9月17日 </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 全国党刊融合发展高层研讨会将在汉召开','2015-12-16 17:36:43','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265006.shtml',NULL,NULL,NULL,NULL,NULL),(10,'国家新闻出版广电总局 - 行业信息 - 新世界出版社与日本侨报社签署版权输出协议','新世界出版社与日本侨报社签署版权输出协议','2015年9月23日','章红雨  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 9月22日，新世界出版社与日本侨报社在中国外文局举行版权输出协议签署仪式，双方决定就新世界出版社出版的22本讲述中国故事的优秀图书日文版版权进行合作。 <br> </p> \n<p style=\"text-indent:2em;\"> 这22本图书主要包括《铁证：日军侵华罪证自录》《铁证：王艾甫抗日藏品精选》《对话：中国模式》《我从新疆来》《历史的轨迹：中国共产党为什么能？》《中国共产党如何应对挑战？》《图说中国梦》等，涉及中国政治、文化、历史等方面。 </p> \n<p style=\"text-indent:2em;\"> 日本侨报社总编辑段跃中表示，日本侨报社一次性买入22本讲述中国故事的图书版权，希望用好书促进中日两国相互理解，让日本民众了解真实的中国、真实的历史。 </p> \n<p style=\"text-indent:2em;\"> 日本侨报社成立于1996年4月1日，以报道活跃在日本的华人学者、企业家、文化人以及为中国发展作出贡献的日本人为主，同时出版大量关于中国社会、经济、文化和中日交流的书籍。自2000年起，日本侨报社在每年7月7日或8月15日都会推出一两本反战图书，15年间从未间断过，其中包括《我认识的鬼子兵》《日本新历史教科书批判》《一个人的抗战》《鬼子又来了》《尊严：走过半个世纪的花冈事件》等，在日本社会引起强烈反响。 </p> \n<p style=\"text-indent:2em;\"> 中国外文局副局长陆彩荣，新世界出版社社长王君校、总编辑张海鸥出席签约仪式。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：章红雨 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年9月23日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 新世界出版社与日本侨报社签署版权输出协议','2015-12-16 17:36:43','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265372.shtml',NULL,NULL,NULL,NULL,NULL),(11,'国家新闻出版广电总局 - 行业信息 - 上海人民社打造“上海抗日战争史丛书”','上海人民社打造“上海抗日战争史丛书”','2015年9月22日','金鑫  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 9月18日，上海人民出版社与上海市地方史志学会、文史资料研究会、中共党史学会共同举办了“上海抗日战争史丛书”出版座谈会暨上海抗日战争史研究学术研讨会。 <br> </p> \n<p style=\"text-indent:2em;\"> 上海人民出版社出版的“上海抗日战争史丛书”包括《上海抗日战争史通论》、《一·二八淞沪抗战》、《八一三淞沪抗战》、《抗战时期的上海经济》、《上海抗日救亡运动》等。丛书运用大量翔实的历史资料，并参考近年来的研究成果，对上海的抗日战争作了全面系统的记叙，展现抗战史研究的新成果。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：金鑫 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年9月22日 </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 上海人民社打造“上海抗日战争史丛书”','2015-12-16 17:36:43','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265258.shtml',NULL,NULL,NULL,NULL,NULL),(12,'国家新闻出版广电总局 - 行业信息 - 中国报刊发行高峰论坛共探“互联网+发行”新路','中国报刊发行高峰论坛共探“互联网+发行”新路','2015年9月21日','范燕莹  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 9月18日，在2015中国（武汉）期刊交易博览会上，举行了以“融合、创新、发展”为主题的中国报刊发行高峰论坛。来自国内外报刊出版发行业的嘉宾共聚一堂，深入交流了“互联网+报刊发行”的新模式。 <br> </p> \n<p style=\"text-indent:2em;\"> 中国外文局副局长王刚毅在致辞中说，中国报刊正处在转型升级的关键阶段，当前发展的基础是好的，总体架构是平稳的，缺少的只是融合发展的勇气和创新发展的动力。希望报刊出版发行单位顺应“互联网+”的时代脚步，营造良好的融合发展、转型创新氛围，最大限度地激发市场主体的创造活力。 </p> \n<p style=\"text-indent:2em;\"> 论坛上，中国新闻出版研究院党委书记、副院长黄晓新，中国国家地理杂志社社长李栓科，美国EBSCO公司大中华区副总裁公丕俭，南方人物周刊杂志社主编徐列，上海第一财经传媒有限公司首席执行官周建工，台湾点阅串流科技公司董事长陈国华，加拿大PRESS READER旅游服务部销售总监季虹，分别就互联网思维、品牌价值、销售平台等问题进行了发言与交流。 </p> \n<p style=\"text-indent:2em;\"> 本次论坛由中国国际出版集团与中国邮政集团共同举办。中国国际图书贸易集团有限公司、中国邮政集团报刊发行局联合承办。中国国际图书贸易集团发布了“2015年度中文期刊最受海外读者欢迎50强”和“2015年度中文期刊最受海外机构欢迎50强”榜单。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：范燕莹 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年9月21日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 中国报刊发行高峰论坛共探“互联网+发行”新路','2015-12-16 17:36:43','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265178.shtml',NULL,NULL,NULL,NULL,NULL),(13,'国家新闻出版广电总局 - 行业信息 - 青岛出版集团与澳大利亚出版企业达成合作协议','青岛出版集团与澳大利亚出版企业达成合作协议','2015年9月22日','范燕莹  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 青岛出版集团与澳大利亚威尔顿国际集团合作公司签约仪式日前在澳大利亚悉尼举行。合作公司的落地发展，将进一步扩展青岛出版集团所属的城市传媒与澳大利亚出版商的版权合作范围和方式，未来将有许多合作项目落地。中宣部副部长孙志军、中国驻悉尼总领事李华新及悉尼副市长郭耀文出席签约仪式，澳大利亚前总理霍克发贺信表示祝贺。 <br> </p> \n<p style=\"text-indent:2em;\"> 孙志军首先感谢威尔顿国际出版集团为推动中澳间的文化交流所作出的贡献。在看到双方合作出版的《中国——新长征》后，他表示，让《中国——新长征》这样的图书更多一点，让像威尔顿这样的朋友更多一点，让读这些书的人更多一点。希望这次与青岛出版集团的合作，就像春天播种，好好耕耘，一定会有好的收获。 </p> \n<p style=\"text-indent:2em;\"> 李华新对双方合资建立澳大利亚公司表示祝贺，他说，通过出版《新丝路》这样的书籍，向澳大利亚和世界各国人民介绍中国的“一带一路”战略，是非常有意义的事情。 </p> \n<p style=\"text-indent:2em;\"> 青岛出版集团总编辑李亚川说，未来合作公司将充分整合资源，将优秀的中华文化传递到澳大利亚和世界，同时新公司也将共同策划开发适合中国市场的项目和内容。 </p> \n<p style=\"text-indent:2em;\"> 作为澳方代表，威尔顿国际出版集团董事长魏凯文和首席执行官魏华德在致辞中说，通过外国人在新丝绸之路的感受，把在新丝绸之路上发生的故事讲给西方人听，推动国与国之间的合作，是这个项目的意义所在。与中国青岛出版集团合资创办澳大利亚实体公司，对威尔顿国际出版集团的发展而言是历史性的一步。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：范燕莹 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年9月22日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 青岛出版集团与澳大利亚出版企业达成合作协议','2015-12-16 17:36:43','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265259.shtml',NULL,NULL,NULL,NULL,NULL),(14,'国家新闻出版广电总局 - 行业信息 - 我国拟实施志书出版资助工程 解决民族地区和贫困地区  地方志工作难题','我国拟实施志书出版资助工程 解决民族地区和贫困地区 地方志工作难题','2015年9月17日','刘奕湛 陈哨  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 9月14日，据中国地方志指导小组透露，我国拟在民族地区和贫困地区实施志书出版资助工程，着力解决这些地区地方志工作的实际困难。 <br> </p> \n<p style=\"text-indent:2em;\"> 目前，全国地方志工作开展很不平衡，特别是一些民族地区和贫困地区在经费保障与人员保障上存在较大困难，实施这一工程就是要着力解决这些地区地方志工作的实际困难。《全国地方志事业发展规划纲要（2015—2020年）》明确提出，到2020年，要全面完成第二轮修志规划任务，省、市、县三级地方志书全部出版，同时提出要加大对民族地区、贫困地区地方志工作支持力度。 </p> \n<p style=\"text-indent:2em;\"> 中国社会科学院副院长、中国地方志指导小组常务副组长李培林指出，要明确各级政府在地方志工作中的主体责任，把依法治志的要求制度化、规范化，充分发挥地方志在服务中心工作中的作用。 </p> \n<p style=\"text-indent:2em;\"> 据介绍，中国志书精品工程、中国年鉴精品工程、中国名镇志文化工程、中国名村志文化工程、全国信息方志与数字方志建设工程等多个工程也将予以实施。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：刘奕湛 陈哨 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年9月17日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 我国拟实施志书出版资助工程 解决民族地区和贫困地区 地方志工作难题','2015-12-16 17:36:43','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265003.shtml',NULL,NULL,NULL,NULL,NULL),(15,'国家新闻出版广电总局 - 行业信息 - 书店成黄金周热门“景点”','书店成黄金周热门“景点”','2015年10月8日','王坤宁 樊国安 张福财 汤广花  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> “今年国庆长假，北京图书市场销售与去年持平，整体情况还不错。”10月6日，记者在北京中关村图书大厦采访时，恰好碰上正在店里了解假日图书销售情况的北京发行集团副总经理郑岩。 <br> </p> \n<p style=\"text-indent:2em;\"> 郑岩告诉记者，北京图书大厦和其他几个书店在国庆长假人气都很旺，尤其是少儿读物销售区，每天都坐满了家长和孩子。 </p> \n<p style=\"text-indent:2em;\"> 郑岩所言，也是今年国庆长假全国各大书店的一个缩影。记者在采访中了解到，国庆期间，各大书店都在营销上推出了一些创新主题文化活动，让广大读者度过了一个读好书、买好书的书香四溢的假期。 </p> \n<p style=\"text-indent:2em;\"> 作为天津市文化地标的天津图书大厦，结合13周年店庆开展了以“书香、惠民、感恩、同行”为主题的优秀图书推荐活动，其中包括文学、社科、少儿、生活等多个门类约2000余种新书、畅销书。同时，该大厦打造的电子商务平台天添网，为读者提供多种购书方式，满足读者的不同需求。 </p> \n<p style=\"text-indent:2em;\"> 在北京，北京市新华连锁公司除了推出“纪念中国人民抗日战争暨世界反法西斯战争胜利70周年优秀出版物展示展销”，还举办了“遇见好书——百社万种图书优惠大联展”，集中展示哲学社科、史地文化、文学、少儿、生活保健等类别图书，购买展销图书消费满100元的读者可获赠50元购书券，消费满50元的读者可获赠20元购书券。 </p> \n<p style=\"text-indent:2em;\"> 在福州，为了庆祝中国人民抗日战争暨世界反法西斯战争胜利70周年，越洋图书城特意开辟了两个专架，专门把与抗战相关的书集中在一起。同时，为了激发市民看书购书的欲望，书城还推出了买赠促销、读者见面会等一系列活动，让读者的国庆假期过得更充实。 </p> \n<p style=\"text-indent:2em;\"> 记者在采访中了解到，许多书店并没有因为长假而变得冷清，相反，人气很旺。在北京，无论在西单的北京图书大厦，还是在中关村图书大厦、亚运村图书大厦，看书购书者络绎不绝。刘慈欣的《三体》系列图书销售一直在榜首，文学类的图书紧随其后。当然，如《现代汉语词典》《新华字典》等工具书，始终是假日里人们的必选。 </p> \n<p style=\"text-indent:2em;\"> 在武汉，记者在采访中了解到，部分市民因为交通拥堵、景区人多等因素，放弃外出旅游，选择在书店读书充电度过假期。10月5日上午，记者刚进入光谷书城，一个手里抱着几本书的先生边排队等待边埋怨：“怎么连书店也这么挤！”光谷书城企划部主管吕品田告诉记者，黄金周以来，书城每天的营业额超过10万元。“这几天每天到这里来买书的人比平时多了好几倍，人流量每小时近500人。”吕品田说，国庆期间逛书店的读者主要有三类人群：一是工薪族利用休闲时间逛书店；二是学生放假后，到书店购买一些学习和课外用书；三是一些没有上班的外来人员到书店购书“充电”。 </p> \n<p style=\"text-indent:2em;\"> 在位于武汉楚河汉街的文华书城内，宽敞明亮的大厅内人流如梭，很多读者以家庭为单位前来购书。一名导购人员说，黄金周期间，书城在推出系列优惠购书活动的同时，还组织策划了一些主题活动以增加人气。 </p> \n<p style=\"text-indent:2em;\"> 一些特色书店在国庆长假也收获不小。记者在位于北京金源时代购物中心的字里行间书店看到，20元一杯咖啡或饮品的消费区坐满了看书的读者，也有不少读者在图书陈列区站着看书。“国庆期间的销售额和平时周末相比还是有所增加的。”该店一名工作人员说。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：王坤宁 樊国安 张福财 汤广花 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月8日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 书店成黄金周热门“景点”','2015-12-16 17:36:44','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265968.shtml',NULL,NULL,NULL,NULL,NULL),(16,'国家新闻出版广电总局 - 行业信息 - 中央编译出版社：做三个维度的“摆渡者”','中央编译出版社：做三个维度的“摆渡者”','2015年9月24日','范燕莹  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> “前不久，《中国共产党十八届中央委员会第四次全体会议文件》以7种外语翻译出版，《中国抗日战争史简明读本》英文版出版发行，都是我社在理论外宣、翻译服务方面推出的重要著作。‘十三五’的一些重点选题我们已经在开始着手准备。”中央编译出版社总编辑刘明清介绍说。 <br> </p> \n<p style=\"text-indent:2em;\"> 从中央编译出版社申报的“十三五”国家重点图书出版规划项目选题来看，依然紧密结合该社出版优势和出版特色，围绕理论外宣、社科学术、翻译服务等几大重点出版业务板块展开。其中《国内外马克思主义文献书目大系》力求较为全面、系统地揭示马克思主义的国内外出版情况，完整地反映了马克思主义自产生以来相关文献的出版全貌。《当代中国重要政治文献多语种文库》将1978年以来全国党代会重要文件、全国两会重要文件分7个语种进行出版，作为向国际社会系统介绍我国改革开放以来政治发展进步的文献资料。具体包括： </p> \n<p style=\"text-indent:2em;\"> 一是文献系列，二是政经术语系列。《趋势：首届世界互联网大会全纪录》全景展现历届世界互联网大会的成果。《国外毛泽东研究译丛》是以毛泽东研究为主题的国际学术论文汇编。《西班牙语文学译丛》选择了拉美文学和西班牙文学中最有代表性的7部文学作品，包括《诗与歌》《人工呼吸》《伊达之路》《残酷的故事》《祖国的儿女们》《人子》《留住黑夜》，将为中国读者深入了解西班牙语文学提供重要版本。《国家命运》探讨、宣讲关系到国家前途命运的重大热点问题，主题契合当前中国时局，聚焦全面深化改革热点和前沿，对党政干部、社会各界了解我国未来的发展会有极为重要的参考价值，也是中国国情教育重点读本。“协商民主实践丛书”根据党的十八大精神，结合不同省份、地区的实际，从实践案例出发，系统梳理和反映各省市协商民主实践探索。 </p> \n<p style=\"text-indent:2em;\"> “随着学术界、理论界关注重点可能产生的一些变化，在选题方面，我们也会随之有增补或调整，但这一出版计划本身是非常重要的。”刘明清表示。在他看来，出版的短期行为，造成了出版的有些图书根本立不住，经不起时间和市场的考验。因此，与国家政治、经济同步的出版业五年规划，尤其是对于出版社长期的可持续发展意义重大。 </p> \n<p style=\"text-indent:2em;\"> 目前，中央编译出版社作为隶属于中央编译局的一家专业化的社科出版机构，主要翻译介绍世界政治、经济、哲学和文化等社科方面的经典著作和前沿作品。建社20多年来，一直坚守自己的学术定位，以“让中国了解世界，让世界了解中国”为立社使命，做“思想文化的摆渡者”。刘明清介绍，“摆渡者”是讲三个维度的“摆渡”，“东西方思想文化的摆渡”“古今思想文化的摆渡”和“马克思主义和中国文化的摆渡”，这仍将是中央编译出版社坚守的出版品牌特色。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：范燕莹 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年9月24日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 中央编译出版社：做三个维度的“摆渡者”','2015-12-16 17:36:43','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265486.shtml',NULL,NULL,NULL,NULL,NULL),(17,'国家新闻出版广电总局 - 行业信息 - 7名新闻出版广电系统工作者荣获“全国中青年德艺双馨  文艺工作者”称号','7名新闻出版广电系统工作者荣获“全国中青年德艺双馨 文艺工作者”称号','2015年9月17日','李明远  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 9月15日下午，由中宣部、人力资源和社会保障部、中国文联共同主办的第四届全国中青年德艺双馨文艺工作者表彰大会在京举行。54位受到表彰的“全国中青年德艺双馨文艺工作者”中，有吉林电视台副台长王俊杰、八一电影制片厂演员剧团团长刘之冰、八一电影制片厂导演陈力、新华社摄影部体育新闻室副主任费茂华、中央电视台新闻中心新闻播音部副主任康辉、《文艺报》总编辑梁鸿鹰、中国广播艺术团一级演员戴志诚等7名新闻出版广电系统工作者。 <br> </p> \n<p style=\"text-indent:2em;\"> 这次接受表彰的文艺工作者，主要来自戏剧、电影、电视、音乐、舞蹈、美术、摄影、书法、曲艺、杂技、民间文艺以及文艺评论等艺术门类。虽然来自不同行业，强烈的社会责任感和过硬的艺术功底，是他们的共同特点。他们用自己的言行展示了文艺工作者的人格魅力，在文艺界和群众中获得了良好口碑。 </p> \n<p style=\"text-indent:2em;\"> 康辉代表受表彰者宣读倡议书，倡议广大文艺工作者自觉践行社会主义核心价值观，大力弘扬中国精神、凝聚中国力量；努力坚持以人民为中心的创作导向，始终与人民同忧患共欢乐、同呼吸共命运；积极开拓文艺创作新天地，努力创作有筋骨、有道德、有温度的优秀作品；切实履行人类灵魂工程师的神圣职责，矢志追求崇德尚艺、德艺双馨的崇高精神。 </p> \n<p style=\"text-indent:2em;\"> “全国中青年德艺双馨文艺工作者”评选表彰活动始于2004年，当年授予20位艺术家“全国中青年德艺双馨文艺工作者”荣誉称号，此后分别在2007年、2011年举办第二、第三届评选，先后有140位艺术家获得该荣誉称号。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：李明远 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年9月17日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 7名新闻出版广电系统工作者荣获“全国中青年德艺双馨 文艺工作者”称号','2015-12-16 17:36:43','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265007.shtml',NULL,NULL,NULL,NULL,NULL),(18,'国家新闻出版广电总局 - 行业信息 - 三卷本《山西文明史》出版','三卷本《山西文明史》出版','2015年9月29日','王坤宁  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 9月25日，研究山西区域历史文化、反映山西区域文明发展总貌的著作《山西文明史》（三卷）首发式在山西太原举行。中国出版协会常务副理事长、中国图书评论学会会长邬书林出席首发式，并对该书的出版表示祝贺。 <br> </p> \n<p style=\"text-indent:2em;\"> 《山西文明史》历时5年编撰而成，共8编，约150万字，时间跨度从远古的180万年前到1912年，由商务印书馆出版，是目前研究山西历史文明较为权威的一部著作。 </p> \n<p style=\"text-indent:2em;\"> 邬书林指出，山西省有着悠久的文明发展史，曾经是尧、舜、禹建都的地方，是中华民族重要的文明发祥地之一。古老的文明、辉煌的历史，为出版工作提供了丰富的文化资源。他希望出版界在深入挖掘这些文化精髓的基础上，用中华优秀传统文化激励当代中国人，进一步弘扬民族精神，为实现中国梦贡献文化人的力量。 </p> \n<p style=\"text-indent:2em;\"> 该书以时代发展为经，空间发展为纬，将山西文明发展演进分为三个时期，史前到秦汉是山西区域文明的初创、成熟和扩散时期；魏晋南北朝经隋唐到辽宋金元是山西区域文明丰富发展的重要时期，并达到最高峰；明清两朝是山西区域文明出现显著不同的发展时期，出现了新的发展因素。全书把山西区域文明历史作为中华文明历史的一个重要组成部分，对历史上山西的地理环境、经济发展、社会进步、政治变革、文化流动、社会心态等进行了全面梳理和系统思考，对山西区域文明在中华文明起源和发展过程中的作用和贡献进行了初步探讨。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：王坤宁 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年9月29日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 三卷本《山西文明史》出版','2015-12-16 17:36:44','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265844.shtml',NULL,NULL,NULL,NULL,NULL),(19,'国家新闻出版广电总局 - 行业信息 - 第十一届海图会达成两岸图书版贸314项','第十一届海图会达成两岸图书版贸314项','2015年9月15日','张福财  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 9月14日，为期3天的第十一届海峡两岸图书交易会在厦门圆满落幕。展会期间，两岸300余家图书馆400余名图书采购人员到场订货，现场订购、销售图书总码洋4160万元，达成两岸图书版权贸易314项。两岸图书订购和版权贸易总额均比往届大幅度提升。 <br> </p> \n<p style=\"text-indent:2em;\"> 海图会自2005年创办以来，已成为两岸出版界沟通交流的重要平台、成果展示的重要窗口以及产业发展的重要品牌。第十二届海峡两岸图书交易会将于2016年在台湾举办。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：张福财 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年9月15日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 第十一届海图会达成两岸图书版贸314项','2015-12-16 17:36:44','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/264867.shtml',NULL,NULL,NULL,NULL,NULL),(20,'国家新闻出版广电总局 - 行业信息 - 山西出版传媒集团：24场活动凸显三晋风采','山西出版传媒集团：24场活动凸显三晋风采','2015年9月28日','李彦  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 山西籍专家学者、作家齐聚展台，是山西展区的一大特色。在书博会上，山西出版传媒集团推出一个巨大展板，让山西籍著名作家赵树理、马烽、西戎等悉数“登场”，将其作品获奖情况一一列出，进场的参展商、读者都会驻足观看。 <br> </p> \n<p style=\"text-indent:2em;\"> 9月25日，书博会一开幕，山西展区的系列活动令人应接不暇。其中，最重要的活动当数山西出版集团出版的“山西文华”丛书首发式，第一期第一批项目包含27种共100余册图书。山西出版传媒集团相关部门负责人康宏说：“今年山西出版集团的展场上，名家特别多。国际趋势观察家多丽丝·奈斯比特的新作《唤醒迷茫的青春》签售现场非常火爆。”当日下午，由三晋出版社主办的一场雕版印刷术演示活动，也吸引了众多雕版印刷爱好者。 </p> \n<p style=\"text-indent:2em;\"> 展会现场，山西省作家郭天印现场签售由山西教育出版社出版的《狄仁杰传》；山西特意策划的名人论晋活动，梁衡、韩石山、李骏虎3位山西籍名作家悉数到场；央视一套《看见》节目主编王开岭与中学生畅谈阅读体验；希望出版社举办的“希望名家大咖秀”活动……来参加这些活动的读者和观众都十分踊跃。记者发现，就连山西展台上的小书签都成了读者的抢手货。 </p> \n<p style=\"text-indent:2em;\"> 康宏在接受记者采访时说，3天展期，山西共组织了24场活动，吸引了上千人参与。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：李彦 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年9月28日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 山西出版传媒集团：24场活动凸显三晋风采','2015-12-16 17:36:45','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265704.shtml',NULL,NULL,NULL,NULL,NULL),(21,'国家新闻出版广电总局 - 行业信息 - 中国人力资源和社会保障出版集团：挖掘内容资源 推动  融合发展','中国人力资源和社会保障出版集团：挖掘内容资源 推动 融合发展','2015年9月24日','邹韧  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 今年，正值中国人力资源和社会保障出版集团有限公司实施“2014-2016年选题规划”的中间年，据集团总经理兼党委书记张梦欣介绍，出版集团每3年制定一次选题规划，目前正在执行的就是“2014-2016年选题规划”。“十三五”期间，出版集团将在现有职业教育、职业培训、人事人才、劳动保障、安全生产五大专业板块基础上，加大专业内容资源的挖掘，积极运用大数据、云计算、移动互联网、物联网等新技术，加强内容数据库和用户数据库的建设，针对不同读者群体定制开发出版产品，推动传统出版和新兴出版融合发展。 <br> </p> \n<p style=\"text-indent:2em;\"> 为了顺应在经济发展新常态下，产业结构迈向中高端水平、对劳动者职业技能提升的新要求和加快发展现代职业教育的新举措，出版集团及时开发了相关内容的选题，并在产品内容上、传播方式上进行创新，致力于技术技能型人才培养，以及劳动者就业能力和创业能力的提高。同时围绕就业、社会保障、劳动关系、收入分配、职业安全等主题，开发高端、原创、学术性产品和政策解读性通俗理论读物。为了能更好地服务于公务员和专业技术人才队伍建设，出版集团按照“深化干部人事制度改革，建立集聚人才体制机制”的新要求，大力开发人事人才领域的相关选题，以及针对不同行业、不同岗位特点，深入开发安全生产类选题产品。 </p> \n<p style=\"text-indent:2em;\"> 除此之外，张梦欣表示，在“十三五”期间出版集团在加强版权保护体系建设、打造版权交易产业链方面也有一些新的举措。由于传统出版企业比较注重专有出版权，对内容版权关注得不多，对版权内容管理方面研究得不深，因此版权资产多处于沉淀状态。网络新技术的应用为我国版权保护带来了新挑战，提出了新要求，加强版权管理和版权保护，也是传统出版与新兴出版融合发展的迫切需要。 </p> \n<p style=\"text-indent:2em;\"> 据了解，出版集团通常是以自主开发教材居多，教材编写多为委托编写模式进行，著作权大多归属于出版社。为了实现版权资产最大化，出版集团对现有版权资源进行了全面梳理，并对版权权属类型进行审查，区分出自我版权资源、需续签版权资源和需买断版权资源，争取获得全部版权，为实现数字出版转型提供版权资源基础。同时，出版集团还积极开展有关版权贸易的调查研究，争取在“十三五”时期实现版权输出方面的新突破。除此之外，为了切实提高版权保护水平，出版集团将进一步完善版权保护制度建设，提高全员版权意识，努力培养版权专业人才，坚决打击侵权盗版行为。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：邹韧 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年9月24日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 中国人力资源和社会保障出版集团：挖掘内容资源 推动 融合发展','2015-12-16 17:36:46','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265485.shtml',NULL,NULL,NULL,NULL,NULL),(22,'国家新闻出版广电总局 - 行业信息 - 苏州大学新媒体研究院成立 柳斌杰等出席挂牌仪式','苏州大学新媒体研究院成立 柳斌杰等出席挂牌仪式','2015年9月22日','章红雨  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 9月17日上午，“互联网+”时代新媒体战略研讨会暨苏州大学新媒体研究院挂牌仪式在江苏苏州举行。全国人大教科文卫委员会主任委员柳斌杰，副主任委员刘振起、李长才，中宣部原常务副部长龚心瀚出席。 <br> </p> \n<p style=\"text-indent:2em;\"> 柳斌杰对苏州大学新媒体研究院的成立表示祝贺。他指出，建立形态多样、业态多种的新型主流媒体，建立有影响力、创造力、竞争力的出版传媒集团，是新时期的重大任务和历史使命。互联网新技术的兴起，对发展新型主流媒体具有推动作用，让新型主流媒体成为传播主流传统文化的载体是当务之急。苏州大学新媒体研究院的成立，对我国教育改革、文化建设，促进媒体融合具有重要意义。柳斌杰希望苏州大学新媒体研究院将产、学、研融为一体，调动各方面研究力量，探索出“互联网+”时代新媒体发展规律，探索出提升文化传播力的有效途径，探索出具有中国特色的新闻传播教育经验，培养适应时代发展的新闻传播人才，引领我国新闻传播业健康发展。 </p> \n<p style=\"text-indent:2em;\"> 据了解，苏州大学新媒体研究院的核心业务是致力于新媒体研究与地域文化产业发展，将以新媒体与技术、新媒体与实践、传统媒体变革与新媒体融合、新媒体传播研究、社交媒体研究、大数据挖掘与云计算、互联网用户行为分析等为研究领域，下设新闻出版数据库实验室、新闻出版与舆情研究实验室、智慧出版与图书市场情报实验室、大数据与云出版新媒体形态实验室，着重培养既具有扎实新媒体理论与实践基础，又具有敏锐创新意识和积极创造精神的复合型人才。苏州大学新媒体研究院首任院长胡守文表示，新媒体研究院将紧密结合教学与实践，为学生提供实习和项目基地。 </p> \n<p style=\"text-indent:2em;\"> 仪式上，中国出版协会、安徽省出版工作者协会、时代出版传媒股份有限公司与苏州天梯卓越传媒有限公司新媒体平台“出版头条”签署了四方战略合作协议。中国出版协会常务副理事长兼秘书长刘建国表示，为行业和会员单位服务是中国版协的宗旨。四方战略合作协议的签署，将为会员单位与新媒体进一步融合发展搭建平台、创造机遇。 </p> \n<p style=\"text-indent:2em;\"> 中宣部出版局巡视员刘建生，总局数字出版司司长张毅君以及江苏、安徽有关单位负责人参加挂牌仪式。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：章红雨 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年9月22日 </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 苏州大学新媒体研究院成立 柳斌杰等出席挂牌仪式','2015-12-16 17:36:46','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265260.shtml',NULL,NULL,NULL,NULL,NULL),(23,'国家新闻出版广电总局 - 行业信息 - 首个民间筹资古籍整理出版基金设立','首个民间筹资古籍整理出版基金设立','2015年9月30日','章红雨  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 9月29日，宋云彬旧藏书画捐拍仪式在中华书局举行。这标志着国内民间筹资首个古籍整理出版基金设立，该基金将在全国范围内奖励优秀古籍整理者和古籍出版编辑。 <br> </p> \n<p style=\"text-indent:2em;\"> 据了解，宋云彬早年从事教科书编纂，晚年全力投入“二十四史”点校、编辑工作，未能完成个人《史记集注》计划。宋云彬所藏书画今得以保存82件。为实现宋云彬未尽的事业，其后人将其毕生所收藏的书画作品拍卖，拍卖所得全部款项交由中华书局用以设立“宋云彬古籍整理出版基金”。 </p> \n<p style=\"text-indent:2em;\"> 宋云彬的后人宋京其在捐拍仪式上表示，表彰古籍整理和出版工作中的突出贡献者，同时希望通过这一举动，使得握有先人藏品的后人，能够让藏品发挥最大作用。 </p> \n<p style=\"text-indent:2em;\"> 中华书局总经理徐俊表示，设立“宋云彬古籍整理出版基金”的目的在于团结古籍整理出版人才、聚合人力和智力资源，弘扬像宋云彬一样倾心投入古籍整理出版业之精神，推动出版更多的古籍整理精品力作，为社会主义核心价值观的培育和践行提供重要的文献支持和精神保障。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：章红雨 &nbsp;来源：新华网 &nbsp;发布时间：2015年9月30日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','新华网  ','国家新闻出版广电总局 - 行业信息 - 首个民间筹资古籍整理出版基金设立','2015-12-16 17:36:46','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265900.shtml',NULL,NULL,NULL,NULL,NULL),(24,'国家新闻出版广电总局 - 行业信息 - 全国党刊年会聚焦“互联网+”','全国党刊年会聚焦“互联网+”','2015年9月30日','范燕莹  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 互联网+党刊高峰论坛暨第六届全国党刊年会近日在京召开。百余名党刊工作者通过专题讲座、高峰论坛、分组讨论的形式，探讨党刊如何有效运用“互联网+”的手段，加快传统媒体和新媒体融合发展。中共中央党校副校长赵长茂，北京市委常委、宣传部长李伟出席开幕式并讲话。 <br> </p> \n<p style=\"text-indent:2em;\"> 赵长茂说，党刊的地位、功能与时代特色决定了其作为大众传媒的传播效果。党刊应该主动适应互联网发展的新趋势，逐步建立起PC互联网、移动互联网和党刊三位一体的新模式。李伟表示，如今党刊面临与新兴媒体相互竞争、融合发展的挑战，如何把党刊办出特色、办出水平和影响力，如何推动纸质党刊与新兴媒体融合发展，让党刊的主流队伍向网上进军、内容优势在网上发挥，是“互联网+”形势下党刊转型发展的迫切任务，希望本届年会能在这方面取得前瞻性、突破性成果。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：范燕莹 &nbsp;来源：新华网 &nbsp;发布时间：2015年9月30日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','新华网  ','国家新闻出版广电总局 - 行业信息 - 全国党刊年会聚焦“互联网+”','2015-12-16 17:36:47','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265899.shtml',NULL,NULL,NULL,NULL,NULL),(25,'国家新闻出版广电总局 - 行业信息 - 全国教育出版社社长总编年会举办','全国教育出版社社长总编年会举办','2015年9月23日','冯文礼  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> “努力提升中国知识生产和传播的水平，既是知识界和出版界承担的责任，也是自身发展的需要，做好这件事也是对世界文明的贡献”。9月21日，全国教育出版社社长总编辑年会在山东烟台举办，中国出版协会常务副理事长邬书林在会上作了题为《关注国际知识生产与传播的趋势，努力提升我国知识生产传播水平》的专题报告。 <br> </p> \n<p style=\"text-indent:2em;\"> 邬书林在报告中史今结合，用大量的案例分析了当前国际国内出版业发展的趋势和特点。他说，信息技术的革命性进步，特别是互联网（尤其是移动互联网）的广泛使用，已经对出版业的载体形式、内容生产和传播方式产生巨大影响和深刻变化。要总结好出版历史发展经验，准确把握出版的本质功能，找准知识生产与传播在经济社会、科技创新中的地位和作用，深化对出版功能的认识，努力提高出版水平，从而强化、完善和提升出版的社会功能。 </p> \n<p style=\"text-indent:2em;\"> 在谈到用好机遇、提升出版水平时，邬书林说，经济社会快速发展和巨大的经济规模是发展中国出版的坚实基础，科技创新成果的显著增加是发展中国专业出版的不竭内容来源，科教兴国的基本国策为中国出版长远发展提供良好环境，宏大的研发队伍是发展中国专业出版的生力军，相对健全的传播体系是发展中国出版的重要力量，与世界先进水平的差距为发展中国专业出版提供了空间。他建议，要抓住信息技术革命性进步的历史机遇，紧紧围绕经济社会发展和科技创新提升中国专业出版水平，选择优势产业和技术领域作为专业出版的突破口，围绕经济发展的优势领域做好相关信息技术服务，同时要关注基础研究领域的学科进展，这也是专业出版的一个重点。 </p> \n<p style=\"text-indent:2em;\"> 此次年会由中国出版协会教育图书工作委员会主办。教育图书工委主任、人民教育出版社副总编辑魏运华，教育图书工委副主任、山东教育出版社社长刘东杰等来自全国34家教育出版社的社长总编辑参加会议。会上，代表们不仅学习了《新闻出版广播影视从业人员职业道德自律公约》，还围绕教育出版未来发展的着力点、融合的新路径等话题，介绍了经验探索，进行了热烈研讨。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：冯文礼 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年9月23日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 全国教育出版社社长总编年会举办','2015-12-16 17:36:48','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265373.shtml',NULL,NULL,NULL,NULL,NULL),(26,'国家新闻出版广电总局 - 行业信息 - 新华书店协会倡导“新华新空间”营销','新华书店协会倡导“新华新空间”营销','2015年9月29日','范燕莹  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 9月25日，中国新华书店协会在书博会现场举办“新华新空间展示厅——新华书店发展方向”发布会，介绍协会将在全国新华书店卖场推动和引导的空间设想与营销新理念。中国出版协会常务副理事长、中国图书评论学会会长邬书林出席发布会并讲话。 <br> </p> \n<p style=\"text-indent:2em;\"> 邬书林高度赞赏新华书店协会的这项工作。他说，70多年来，新华书店为中国共产党的壮大、新中国的发展，贡献了不可磨灭的文化力量。在新的历史进程中，我们要适应新形势的发展，把新华书店工作做得更好，为实现中华民族伟大复兴的中国梦贡献新华人的新力量。邬书林强调，开拓新空间需要新思路、新产品、新技术。在新技术快速进步、数字阅读快速发展的今天，实体书店和纸质图书仍然有广阔的发展空间。希望新华书店以此为契机，深入研究变化了的阅读市场和阅读习惯，用新思路、新技术、新产品使传统业务不断地拓展新空间。 </p> \n<p style=\"text-indent:2em;\"> 据介绍，为充分体现新华书店空间设计与规划的浓郁文化特色，中国新华书店协会在此次书博会上对新华书店的“新华新空间”进行创意展示，把“新华新空间”的设计理念同“新华定制，一地一品”的营销战略有机结合，实现阅读与文化结合、发行与上下游融合、出版产业与社会相关产业有机融合的创意设想。目前，全国新华书店正在掀起新一轮的店堂改造热潮，并将通过“新华新空间”的展示和倡导，使新华书店成为每位读者愿意进、乐意留的文化体验中心。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：范燕莹 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年9月29日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 新华书店协会倡导“新华新空间”营销','2015-12-16 17:36:51','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265843.shtml',NULL,NULL,NULL,NULL,NULL),(27,'国家新闻出版广电总局 - 行业信息 - 刊博会APP上线发布','刊博会APP上线发布','2015年9月16日','刘蓓蓓  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 9月15日，由南京扫扫看科技有限公司为2015中国（武汉）期刊交易博览会量身定制的APP正式上线。 <br> </p> \n<p style=\"text-indent:2em;\"> 刊博会APP分为参展单位、参展申请、展馆导航、活动发布、刊博会新闻、业界资讯六大板块，不仅具备展馆定位导航、数字化会刊服务、重要活动实时推送等多种功能，更增设了“微阅读”与“合作意向提交”模块，对参展单位及其出版物进行立体化展示与推广，为参展单位之间搭建沟通与合作的线上桥梁。 </p> \n<p style=\"text-indent:2em;\"> 据了解，刊博会连续举办两届以来，一直在展会服务数字化、信息化方面不断寻求突破。2015刊博会除上线APP外，还将实现刊博会品牌活动“中国最美期刊”的数字化展示。“中国最美期刊”数字化运用互联网工具“扫扫看-微阅读制作平台”，将传统期刊内容进行数字整合，生成二维码，并通过微信、QQ等移动端进行线上传播。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：刘蓓蓓 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年9月16日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 刊博会APP上线发布','2015-12-16 17:36:52','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/264946.shtml',NULL,NULL,NULL,NULL,NULL),(28,'国家新闻出版广电总局 - 行业信息 - 中国图书评论学会发布8月“中国好书”','中国图书评论学会发布8月“中国好书”','2015年9月30日','杨雅莲  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 中国图书评论学会日前发布8月“中国好书”，共有10本书入选。 <br> </p> \n<p style=\"text-indent:2em;\"> 上榜图书分别为：《西方那一块土：钱乘旦讲西方文化通论》（北京大学出版社）、《国史讲话·春秋》（上海人民出版社）、《故宫营造》（中华书局）、《抗战烽火中的中国大学》（北京大学出版社）、《蘑菇圈》（长江文艺出版社）、《民国文学十五讲》（山西人民出版社）、《童书非童书：给陪伴孩子看书的父母》（社会科学文献出版社）、《黑白男女》（上海文艺出版社）、《图解时间》（科学出版社）、《致成长中的你：十五封青春书简》（长江文艺出版社）。 </p> \n<p> <br> </p> \n<p> 作者：杨雅莲 &nbsp;来源：新华网 &nbsp;发布时间：2015年9月30日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','新华网  ','国家新闻出版广电总局 - 行业信息 - 中国图书评论学会发布8月“中国好书”','2015-12-16 17:36:54','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265898.shtml',NULL,NULL,NULL,NULL,NULL),(29,'国家新闻出版广电总局 - 行业信息 - 全国党刊融合发展研讨会倡议成立党刊数字联盟','全国党刊融合发展研讨会倡议成立党刊数字联盟','2015年9月21日','汤广花  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 9月18日，2015全国党刊融合发展高层研讨会在刊博会上举行。来自全国各地的与会代表共同宣读了“党刊数字联盟”倡议书，大家表示，未来将以先进的传播方式和手段引导社会舆论，为党和国家工作大局服务，弘扬主旋律，传播正能量。 <br> </p> \n<p style=\"text-indent:2em;\"> 研讨会由2015刊博会组委会、中国新闻出版传媒集团有限公司、中国新闻出版广电报社联合主办。中国期刊协会会长石峰等出席会议。 </p> \n<p style=\"text-indent:2em;\"> 会上，多家中央及地方党刊代表就各自的经验进行了交流，并对党刊未来发展提出建议。紫光阁杂志社社长闪伟强在主题演讲中说，党刊要做好内容、找准定位，同时插上互联网的翅膀，如此必能在激烈的媒体竞争中占有一席之地。党建杂志社副总编辑徐遥分享了过去一年里杂志社的“互联网+”做法，她认为，党刊在媒体融合过程中，要扬长避短，经营好自己的内容优势。当代贵州杂志社副总编辑王健明建议打造中国党刊数据中心及全国党建出版云平台。辽宁党刊集团副总编辑杨喜祥认为，党刊在媒体融合方面才刚刚起步，媒体融合的关键是人才，打造一支优秀的人才队伍，有助于实现传统党刊与新媒体的深入融合。 </p> \n<p style=\"text-indent:2em;\"> 中宣部出版局巡视员刘建生、湖北省直机关工委副书记项水伦、湖北省新闻出版广电局副局长邵明义、中国新闻出版广电报社副总编辑冯文礼与会。中国编辑学会会长郝振省主持研讨会。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：汤广花 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年9月21日 </p> \n<p style=\"text-indent:0em;\"> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 全国党刊融合发展研讨会倡议成立党刊数字联盟','2015-12-16 17:36:54','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265179.shtml',NULL,NULL,NULL,NULL,NULL),(30,'国家新闻出版广电总局 - 行业信息 - “十大中国著作权人”年度评选启动','“十大中国著作权人”年度评选启动','2015年9月29日','方圆  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 由中国版权保护中心主办的2015“CPCC十大中国著作权人”年度评选活动9月25日在京启动。活动即日起开始报名，12月31日结束，2016年2月举行颁奖典礼。 <br> </p> \n<p style=\"text-indent:2em;\"> 今年活动的主题为“尊重原创之传统再造”，旨在进一步揭示原创的重要性、丰富性和时代性，引导人们从中国传统文化中捕捉灵感、挖掘价值，将传统文化与现代信息技术和互联网应用巧妙结合起来，讲好中国故事，向世界传播中国文化价值。 </p> \n<p style=\"text-indent:2em;\"> 历经7年打造的“CPCC十大中国著作权人”年度评选活动已成为品牌，先后有70个版权行业的先锋企业和个人获得了该荣誉。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：方圆 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年9月29日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - “十大中国著作权人”年度评选启动','2015-12-16 17:36:54','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265845.shtml',NULL,NULL,NULL,NULL,NULL),(31,'国家新闻出版广电总局 - 行业信息 - 《新闻出版广播影视从业人员职业道德自律公约》在京发布 50家行业社团签署自律公约','《新闻出版广播影视从业人员职业道德自律公约》在京发布 50家行业社团签署自律公约','2015年9月16日','李明远  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 9月15日上午，《新闻出版广播影视从业人员职业道德自律公约》在京发布。《公约》以践行社会主义核心价值观、追求职业理想、遵守宪法法律法规、倡导弘扬行业良好风尚为统领，推进从业人员从“提倡”和“不为”两个方面进行职业道德自律。中国新闻文化促进会会长李东东、中国电影表演艺术学会会长葛优、中国电影导演协会会长李少红等50家行业社团的代表在发布会现场签署了《公约》。 </p> \n<p style=\"text-indent:2em;\"> 50家行业社团代表从业人员郑重承诺：维护党的领导和国家利益，不发表或传播损害党和国家形象的言论；秉持真实客观公正原则，不搞有偿新闻和虚假新闻；传递正能量，不在网络及其他媒介上制作或传播有害信息；追求健康向上的文化品位，不使用低俗粗俗媚俗的语言、文字和图像；确保制作服务质量，不提供粗制滥造的出版物、视听作品和技术服务；对社会公众负责，不制作、代言和传播虚假广告；崇尚契约精神，不做出影响行业诚信和秩序的违约行为；积极自主创新，不抄袭剽窃他人创意及成果；开展健康的媒介与文艺批评，不贬损他人名誉及作品；树立良好职业形象，不涉“黄赌毒”和违反公序良俗的行为。 </p> \n<p style=\"text-indent:2em;\"> 《公约》由中国广播电影电视社会组织联合会和中国出版协会发起制定。《公约》的适用主体涵盖新闻出版广播影视机关、企事业单位和社团从业人员。 </p> \n<p style=\"text-indent:2em;\"> 为确保《公约》具有实际约束力，《公约》提出，签约社团应将相关内容纳入社团章程实施管理，签约社团所属的会员单位也应将相关内容纳入聘用合同、劳动合同及与合作方签订的业务合同。联合签署《公约》的社团组织，欢迎行业其他机构和人员加入《公约》，并欢迎社会各界对《公约》的实施进行监督。 </p> \n<p style=\"text-indent:2em;\"> 中国广播电影电视社会组织联合会分党组书记、会长张海涛在致辞中指出，制定《公约》，是学习贯彻习近平总书记系列重要讲话精神、落实中央“三严三实”要求的具体行动，是加强新闻出版广播影视行业职业道德建设、党风廉政建设的重要举措。落实《公约》，需要从业人员个人、所在单位、行业组织通力合作，行业社团及企事业单位要做好学习推介、贯彻执行、教育培训、引导带动、监督检查等多项工作。 </p> \n<p style=\"text-indent:2em;\"> 中国出版协会常务副理事长邬书林在致辞时表示，在全党全国深入贯彻党中央提出的“四个全面”战略布局，践行社会主义核心价值观，提升国家文化软实力的形势下，《公约》的签署十分必要。希望全国新闻出版从业人员自觉身体力行《公约》，在工作中牢固树立政治意识、大局意识、责任意识。坚定理想信念，遵纪守法，履行行规，坚持操守，正己化人，同时要树立精品意识，努力提高出版质量，为人民奉献更多的精品力作。 </p> \n<p style=\"text-indent:2em;\"> 韬奋基金会理事长聂震宁、中国国家话剧院演员唐国强、中央电视台主持人贺红梅先后代表行业从业人员发言。 </p> \n<p style=\"text-indent:2em;\"> 《公约》签署仪式暨发布会由中国广播电影电视社会组织联合会分党组成员、副会长张丕民主持。 </p> \n<p> <br> </p> \n<p> 作者：李明远 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年9月16日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 《新闻出版广播影视从业人员职业道德自律公约》在京发布 50家行业社团签署自律公约','2015-12-16 17:36:55','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/264947.shtml',NULL,NULL,NULL,NULL,NULL),(32,'国家新闻出版广电总局 - 行业信息 - 中国出版集团积极当好“出版信使”','中国出版集团积极当好“出版信使”','2015年9月28日','王坤宁 李婧璇  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 记者从中国出版集团公司获悉，应海外华侨和国际出版友好人士之邀，在习近平主席首次对美国进行国事访问并出席联合国成立70周年系列峰会期间，中国出版集团“读懂中国”沙画英文形象片，于美国当地时间9月22日-29日，以每天70次、每隔20分钟一次的播放频率再次登上了纽约时代广场大屏幕。该片在今年5月美国书展期间，首次在纽约时代广场亮相。 <br> </p> \n<p style=\"text-indent:2em;\"> “读懂中国”形象片以构思新颖的沙画形式创作，由著名沙画艺术家苏大宝创作。形象片以一个从来没有去过中国的美国青年的视角依次展开，他坐在纽约曼哈顿街头的长椅上，如痴如醉地拿起一本本书，来“阅读中国”“读懂中国”。片子中出现了自由女神、曼哈顿街头等美国的标志性文化元素，同时还沁入了颇多中国文化元素，如用“书”垒砌的长城和天坛等。这些视觉符号凸显了中美文化交流的多彩与丰富，也表明了图书作为重要的文化载体对促进两国关系的重要作用。 </p> \n<p style=\"text-indent:2em;\"> 据了解，“读懂中国”形象片引来不少美国民众的关注。律师Kaul表示，他非常喜欢中国和中国图书，尤其是哲学和小说等，他相信经过长时间的努力，中美两国的关系将越来越好。一位驻美国的中资企业负责人认为，形象片让更多美国人关注中国和中国文化，这也是习近平主席出访美国带来的“礼物”。 </p> \n<p style=\"text-indent:2em;\"> 据介绍，“读懂中国”形象片是一个持续性的品牌推广项目，被列入中国出版集团的海外系列宣传计划，明年将在海外的重点场合持续播放，为进一步促进中外文化交流当好“出版信使”。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：王坤宁 李婧璇 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年9月28日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 中国出版集团积极当好“出版信使”','2015-12-16 17:36:57','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265705.shtml',NULL,NULL,NULL,NULL,NULL),(33,'国家新闻出版广电总局 - 行业信息 - 人民出版社：积极实施“1+1+N”创新发展战略','人民出版社：积极实施“1+1+N”创新发展战略','2015年9月24日','李彦  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 人民出版社将由“十二五”期间的制度创新、体制探索转向体制机制全面创新。人民出版社社长黄书元告诉记者：“2016-2020年是人民出版社的战略转型期、体制机制创新期，也是提质增效、稳健发展的时期。到2020年，建成国内领先、具有较大国际影响力、与人民出版社肩负的特殊使命相适应的出版强社。” <br> </p> \n<p style=\"text-indent:2em;\"> “十二五”期间，人民出版社立足传统出版行业信息化平台建设，构建了中国共产党思想理论资源数据库、中国理论网等，积极采用新的传媒技术和手段，提高马克思主义理论传播能力。“十三五”期间将大力推进媒体融合，切实做好数字出版。全力打造以新技术为支撑、以新媒体为载体，旨在推动全民阅读活动的“读书会”平台。 </p> \n<p style=\"text-indent:2em;\"> 黄书元表示，人民出版社作为公益性事业单位，始终不忘提升社会效益。按照中办、国办印发的《关于推动国有文化企业把社会效益放在首位、实现社会效益和经济效益相统一的指导意见》的要求，人民出版社在“十三五”期间，将继续“提供精神产品，传播思想信息，担负文化传承使命”“始终坚持把社会效益放在首位、实现社会效益和经济效益相统一”。 </p> \n<p style=\"text-indent:2em;\"> 记者了解到，“十三五”期间，人民出版社将进一步加大公益性出版的力度，丰富主题出版的内容、形式和品种。政治性公益性主题出版项目将在“十二五”的基础上有大幅提升和增长，占“十三五”期间出版物品种数量的80%以上。其中，包括“政治制度与国家治理研究丛书”、《大国追梦——中华民族的复兴之路》、“延安精神研究丛书”、《当代中国改革开放史》等政治性主题出版重点项目，《马克思恩格斯全集》（第二版）、《列宁全集》（第二版增订版）等马列类主题出版重点项目，以及《清史》《中国丝绸之路通史》等重大学术出版项目等。 </p> \n<p style=\"text-indent:2em;\"> 此外，进一步深化体制机制改革也是该社“十三五”发展规划期间的一个重要任务。根据规划，人民出版社在“十二五”的基础上，将进一步深化体制机制改革，以机制创新为动力，以基层创业为重点，根据已实施分社制改革试点的实际情况，分社将由试点时期的2-3个，逐步扩大至10-15个；形成公益性主题出版中心、高水平学术出版中心、全覆盖大众出版中心，一体两翼、双向融合的格局。 在业务方面，要由以政治性、综合性出版为主更多转向以政治性、公益性主题出版为主。围绕党的精神、政府政策，多维开发、立体开发、成套开发学者怎么评、专家怎么谈、百姓怎么看、基层怎么干系列选题，强调以学术化大众化为主题的宣传服务。 </p> \n<p style=\"text-indent:2em;\"> 黄书元透露，以创新驱动为引擎，积极实施“1+1+N”创新发展战略，围绕中心，服务大局，继续当好党和人民出版事业的排头兵，将是人民出版社“十三五”发展规划期间的工作重点，也将是工作亮点。“一个宗旨、一份担当、万千谋划”，这就是人民出版社“1+1+N”战略创新模式。 </p> \n<p style=\"text-indent:2em;\"> 具体而言，在“十三五”期间，人民出版社将在公益性事业出版单位制度下，积极探索创设“内部模拟法人制度”，简化和下放一系列业务审批事项，简政放权，充分激发和释放各部门的潜力和活力，让创业创新活力迸发，让优秀人才脱颖而出。人民出版社在编辑部门形成编印发统一协调、整体营销的经营模式，进而探索出公益性事业出版单位的新型管理模式，为该社进一步全面深化改革做试点，也将为我国公益性事业出版单位体制机制创新继续先试先行。 </p> \n<p style=\"text-indent:2em;\"> 黄书元表示，人民出版社在“十三五”期间将立足新的定位，肩负使命，顺应潮流，开拓业务，双向融合，内融多媒体，外融互联网；积极顺应“互联网+”发展趋势，依托丰富的思想理论资源和作者读者资源，探索传统出版与现代出版融合的发展模式，扩大与提升宣传工作的实效，从而掌握舆论主动权，提升主流媒体的传播力、公信力、影响力和舆论引导力。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：李彦 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年9月24日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 人民出版社：积极实施“1+1+N”创新发展战略','2015-12-16 17:36:57','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265487.shtml',NULL,NULL,NULL,NULL,NULL),(34,'国家新闻出版广电总局 - 行业信息 - 刊博会主论坛聚焦融合时代变革与发展','刊博会主论坛聚焦融合时代变革与发展','2015年9月21日','李雪昆  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 9月18日至19日，在刊博会主题论坛——2015中国期刊媒体国际创新发展论坛暨医药卫生新媒体融合发展论坛上，业界人士共论媒体融合时代期刊业的变革与发展。 <br> </p> \n<p style=\"text-indent:2em;\"> 论坛上，中国期刊协会会长石峰作了“媒体融合呼唤内容价值的回归”主题报告。他指出，媒体融合是一项系统工程，需要利益相关方协同共进、利益共享，才可能持续发展。他建议将媒体融合作为一项国家工程进行立项或设立国家专项基金；建议在一体化发展理念指导下，调整利益格局，由政府部门协调确定渠道、运行、内容生产三方的利益比例；建议国家加大对互联网环境下内容产品的著作权保护，制定更加严格的措施。同时，大力倡导在互联网上有偿使用、浏览有著作权产品的舆论环境和社会氛围；建议有关政府部门对编辑出版人员的资质内涵进行调整，引导传统媒体人成为现代媒体人。 </p> \n<p style=\"text-indent:2em;\"> 论坛期间，来自国际期刊界、数字传播技术界以及国内期刊数字化实践取得较好成果的期刊社代表与业界同行分享了各自媒体融合发展中的经验体会。 </p> \n<p style=\"text-indent:2em;\"> 论坛由中国期刊协会及其医药卫生期刊分会主办。中宣部出版局巡视员刘建生，国家新闻出版广电总局新闻报刊司司长李军，湖北省委宣传部副部长、湖北广播电视台台长王茂亮在论坛上作主题报告。中国期刊协会常务副会长兼秘书长余昌祥主持会议。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：李雪昆 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年9月21日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 刊博会主论坛聚焦融合时代变革与发展','2015-12-16 17:36:58','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265180.shtml',NULL,NULL,NULL,NULL,NULL),(35,'国家新闻出版广电总局 - 行业信息 - 上海交通大学出版社：创新固本 提质增效','上海交通大学出版社：创新固本 提质增效','2015年9月24日','李彦  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"line-height:1.75em;text-indent:2em;\"> 上海交通大学出版社社长韩建民向记者透露，“十三五”期间，该社将在集团化建设方面迈出实质性步伐，率先建成国家级高水平的学术出版基地，率先建成国际版权合作的重镇，率先建成“人心思上、互相关爱、收入上乘”的员工之家。 <br> </p> \n<p style=\"line-height:1.75em;text-indent:2em;\"> “十二五”期间，上海交通大学出版社以主题出版和精品出版为抓手，引领出版社沿着“专业规模化、产品经营化、业态数字化、平台国际化”的路径转型发展，相继策划出版了一系列服务党和国家战略需求的优秀出版物，如《平易近人》、“东京审判出版工程”“大飞机出版工程”“犹太人在上海出版工程”等。 </p> \n<p style=\"line-height:1.75em;text-indent:2em;\"> 2009年以来，该社累计已有19个项目获得“国家出版基金”资助，26个项目入选“十二五”国家重点图书出版规划。韩建民表示，从出版选题重点项目上看，他们比较圆满地完成了“十二五”规划。这些成绩的取得，得益于有关部门的指导和学校的大力支持；得益于出版社坚持体制机制改革、推进“三大转型”，以及创新出版理念；更得益于全社员工团结拼搏，营造共谋事业发展的良好氛围。 </p> \n<p style=\"line-height:1.75em;text-indent:2em;\"> 按照该社“十三五”规划，除继续做大做强重点图书项目外，在学术出版项目上加大力度，凭借其固有优势，将“东京审判出版工程”继续向纵深发展。此外，“大飞机出版工程”“犹太人在上海出版工程”“光物理出版工程”“能源与环境出版工程”“核物理出版工程”“软化医学出版工程”等一系列重点图书规划都将在“十三五”期间完成。 </p> \n<p style=\"line-height:1.75em;text-indent:2em;\"> 在韩建民看来，与先进出版社相比，上海交通大学出版社在一些方面仍有较大差距。比如，在转型发展方面，在新的业态上还没有找到新的赢利模式；单本书的利润率还有待提高；营销的瓶颈还没有突破；市场化的真正有效运营还没有做到。此外，在资本运作、股权激励探索方面才刚刚起步。 </p> \n<p style=\"line-height:1.75em;text-indent:2em;\"> 在“十二五”规划取得成绩的基础上，“十三五”期间，上海交通大学出版社将在专业化、国际化和数字化方面有新的规划，在重点选题规划上、重点选题项目上进行新探索，尤其在集团化建设、股份制改革、体制机制等方面进行新的探索。在人才队伍建设方面，通过加强培训等多种方式，让年轻员工尽快成长，成为出版社的核心竞争力。在企业文化上，进一步打造“讲正气、讲人气、讲书气”的企业氛围，形成“互相关爱、收入上乘、人心思上”的企业文化。 </p> \n<p style=\"line-height:1.75em;text-indent:2em;\"> 韩建民告诉记者，“十二五”期间该社完成了咖啡厅（作者中心）的改造；“十三五”期间，该社将重点打造读者中心，把书店进行改造，打造一个以阅读体验为中心、以读者为中心的工程建设。同时，拥有上海交通大学48种学术期刊的交大期刊群的建设，也是出版社“十三五”期间的重点工程之一。此外，在数字出版、数字营销方面准备加大力度，使出版社在业态转型上、国际化上、企业文化上有一个新的提升。 </p> \n<p style=\"line-height:1.75em;text-indent:2em;\"> “‘十三五’期间，出版社将在多元化经营访谈方面进行探索，但做好主业是最根本的，因为我们是内容出版商。”韩建民说。 </p> \n<p> <br> </p> \n<p style=\"line-height:1.75em;text-indent:0em;\"> 作者：李彦 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年9月24日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 上海交通大学出版社：创新固本 提质增效','2015-12-16 17:37:05','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265483.shtml',NULL,NULL,NULL,NULL,NULL),(36,'国家新闻出版广电总局 - 行业信息 - 主题出版显实力 经典阅读再升温','主题出版显实力 经典阅读再升温 ——从CIP数据看2015书博会新书亮点','2015年9月24日','杜一诗  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 今年是中国人民抗日战争暨世界反法西斯战争胜利70周年，纵览半年以来我国出版的新书，纪念抗战胜利主题出版成果显著。这类图书共有840余种，接近去年同期的8倍，可称是中国出版界为纪念中国人民抗日战争暨世界反法西斯战争胜利70周年献上的一份厚礼。 </p> \n<p style=\"text-indent:2em;\"> 其中，抗战史图书约有740余种，占大多数。此类图书史料丰富翔实，研究细致深入，通过档案资料、当事人回忆、历史图片等，客观真实地再现了中国人民的抗战历程，记录了日军在中国犯下的种种罪行。如多家出版社共同策划出版的《中国抗日战争全景录》，分别反映了各省市抗战的全貌，既有很高的学术性、可读性，又有很鲜明的现实意义，是一套弘扬爱国主义和民族精神的珍贵图书；中国文史出版社出版的“抗战：1000‘草根’刻骨铭心的记忆”丛书，通过抗战亲历者的回忆性文章，从“草根”角度讲述了普通百姓的个人抗战史，是宝贵的抗战史料；厦门大学出版社出版的《日本记者镜头中的侵华战争》，收录了作者从世界各地收集而来的500多张日军侵略的战场照片，有许多是首次在国内图书中披露。 </p> \n<p style=\"text-indent:2em;\"> 为抗战胜利70周年庆，多家出版社都策划出版了主题文艺图书。中国人民大学出版社的《抗战家书：我们先辈的抗战记忆》，通过一封封家书及其背后一个个真实感人的故事，为我们描绘出那时候军民的状态。南京师范大学出版社的《以笔为枪：重读抗战诗篇》，选录了包括解放区、大后方、沦陷区等在内的100多位爱国诗人的代表作品，对每篇入选作品作精彩的评价，并配发了相关老照片，烘托了诗歌作品所独有的意境。众多以抗战为主题的当代艺术作品也纷纷结集出版。如山西人民出版社《中国梦 太行魂：纪念抗战胜利70周年摄影作品集》、人民音乐出版社《纪念中国人民抗日战争暨世界反法西斯战争胜利70周年交响管乐作品精选》、安徽美术出版社《丹青诗墨：纪念抗战70周年全国中青年书画作品邀请展作品集》等，不胜枚举，仅作挂一漏万的介绍。 </p> \n<p style=\"text-indent:2em;\"> <strong> 国家战略形成出版新热点 </strong> </p> \n<p style=\"text-indent:2em;\"> “一带一路”战略构想是以习近平同志为总书记的党中央主动应对全球形势深刻变化、统筹国内国际两个大局作出的重大战略决策，它对推进我国新一轮对外开放和沿线国家共同发展意义重大。统计期内，有近50本研究“一带一路”的专著得到出版，各方学者从不同角度深入研究，建言献策。如社会科学文献出版社出版的丛书“中国社会科学院‘一带一路’研究系列”，从东盟自贸区、亚洲一体化、中美战略比较等方面为中国“一带一路”的推进提供了政策性参考建议；人民出版社出版的国际问题专家王义桅教授所著《“一带一路”：机遇与挑战》，首次从国际关系角度解读“一带一路”战略；上海交通大学出版社的《改变世界经济地理的“一带一路”》，由林毅夫、葛剑熊、胡鞍钢等国内十几位知名学者和专家共同编撰，从国家战略、经济、文化、历史、外交等角度，全面解读“一带一路”战略及其所带来的影响。 </p> \n<p style=\"text-indent:2em;\"> 我国经济发展进入新常态，把握和认识这一时期的发展阶段特征对深化改革开放，全面建成小康社会具有重大意义。围绕新常态，总结各领域各地区发展中新实践新经验新进展，是今年新书出版一个重要主题。如内蒙古人民出版社的《新常态下鄂尔多斯市转型发展的思路研究》，对新常态下鄂尔多斯市转型发展的思路进行深入研究，阐释实现转型发展的基础和条件，分析实现转型发展的新增长点和重点任务；经济科学出版社的《经济新常态下民营经济发展环境研究》深入研究了我国经济发展进入新常态背景下，如何更好调动和激发民营经济的活力和创造力；中国金融出版社的《新常态：深化改革与金融探索》介绍了全球治理与对外开放、产业发展与绿色金融、稳增长与调结构、深化改革与机构转型；西南财经大学出版社的《经济新常态下我国产业结构转型升级的就业效应研究》，研究产业结构与就业结构的协同关系，为政府制定积极就业政策提供借鉴。 </p> \n<p style=\"text-indent:2em;\"> <strong> 中国梦主题出版时时翻新 </strong> </p> \n<p style=\"text-indent:2em;\"> 党的十八大以来，中国梦成为当下中国社会一个热门的主题词，各行各业争相提中国梦，每个人也谈论着自己的中国梦。中国梦主题出版一直是市场热点，关于中国梦的图书层出不穷，从理论研究、实践总结到思想教育、文艺作品，内容丰富，包含广阔，是一个时时翻新的出版选题。 </p> \n<p style=\"text-indent:2em;\"> 在理论研究方面，不乏研究深入的厚重之作。如中国社会科学出版社出版的《全面建成小康社会与中国梦构建研究》，紧紧围绕中国特色社会主义道路和全面建成小康社会、实现中国梦的战略目标以及它们之间的辩证关系展开讨论，认为走什么样的路，依靠什么力量，是实现中国梦的关键；中共中央党校出版社出版的《中国梦理论与实践研究》阐释了中国梦提出的时代背景、基本内涵、本质要求和实践路径，回答了为什么提出中国梦、什么是中国梦、怎样实现中国梦的问题，论述了中国梦的理论价值和实践意义。 </p> \n<p style=\"text-indent:2em;\"> 中国梦主题教育图书，注重与榜样人物相结合，用身边的生动事迹鼓励人们立足岗位，用实际行动践行中国梦。如中国文史出版社《中国梦 劳动美：安顺历年劳模风采掠影》，通过对新中国成立后所有安顺籍劳模感人事迹的全新解读，为人们树立一个个传递正能量的榜样；党建读物出版社的《我的中国梦：百名优秀人才践行社会主义核心价值观风采录》收录了百名专家学者培育和践行社会主义核心价值体系、在各自岗位上干出不平凡成绩、为实现中华民族伟大复兴中国梦而奋斗的事迹。 </p> \n<p style=\"text-indent:2em;\"> 中国梦主题出版中，文学艺术类图书占据了相当比例。文艺工作者们集合在中国梦的宏大主题下，用多种艺术形式，通过不同艺术视角，抒发心中的激情和感慨，具有较高的艺术性和思想性。如中国文联出版社的《中华情·中国梦美术书法作品集》，汇集内地及港、澳、台书法绘画作品600余幅，围绕中华民族繁荣富强的主旋律创作，具有鲜明的时代特色、独特的文化视角和深厚的人文关怀；中国铁道出版社的《中国梦 天路情 劳动美：青藏铁路纪实摄影展作品集》，收录了青藏铁路建设时期与建成后的摄影作品约200幅，全面展现了青藏铁路建设的纪实场景和建成后的概貌；新疆美术摄影出版社的《喀什噶尔的中国梦》，以纪实文学的形式真实记录了党中央为支持新疆民族地区发展，将喀什批准为国家级经济特区以来喀什的发展变化。 </p> \n<p style=\"text-indent:2em;\"> <strong> 文学出版经典流行各有市场 </strong> </p> \n<p style=\"text-indent:2em;\"> 近几年来，文学出版一直是市场关注热点，严肃文学和流行阅读各有市场。今年，四年一度的茅盾文学奖再次颁奖，使得一批传统名家名作再度回到市场和读者的视线中，这对于推动全民阅读，倡导深度阅读将起到良好促进作用。在新书市场上，可以看到今年茅盾文学奖获奖作家的部分作品，如《王蒙精选集》（北京燕山出版社）、《苏童精选集》（北京燕山出版社）、格非《雨季的感觉》（长江少年儿童出版社）、金宇澄《繁花》（新疆人民出版社）、李佩甫《李氏家族》（河南文艺出版社）等。此外，人民文学出版社畅销多年的《中华散文珍藏版》，收录了中国现当代多位名家散文，今年增加了一批新作家后陆续向读者推出。浙江文艺出版社出版的丛书《名家散文典藏》也将推出新作，当代名家梁晓声、鲍尔吉·原野等俱在其中。 </p> \n<p style=\"text-indent:2em;\"> 类型文学方面，也是一派红红火火、百花齐放的景象。科幻、推理、青春、职场、都市、侠义、言情等类型小说都各自占据一席之地，满足了不同读者的要求。近年来大热的日本推理小说家东野圭吾的作品在今年持续升温，半年间就有近20本作品被介绍到国内或再次出版：其2014年新作《虚无的十字架》，最近由湖南文艺出版社出版；新星出版社的《东野圭吾的最后致意》是东野圭吾唯一一部自传体散文集，是读者了解这位作家最好、最直接的方式。最新雨果奖得主刘慈欣身为中国科幻文学领军人物，其作品一直深受读者喜爱，新书市场上也不乏他的小说身影：重庆出版社最近出版了其获奖作品《三体》典藏版，或将引起新一轮《三体》阅读潮；其他代表作《时间移民》《白垩纪往事》《中国太阳》等也在近期出版。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：杜一诗 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年9月24日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 主题出版显实力 经典阅读再升温','2015-12-16 17:37:05','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265489.shtml',NULL,NULL,NULL,NULL,NULL),(37,'国家新闻出版广电总局 - 行业信息 - 书博会遇上中秋节：火爆升级嗨翻全场','书博会遇上中秋节：火爆升级嗨翻全场','2015年9月29日','邹韧 刘蓓蓓  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 9月27日是第25届全国图书交易博览会的最后一天，虽然当天正赶上中秋节，但来逛书博会的市民一点也不比前两天少，大家都希望在这个中国传统假日里有不一样的收获。 <br> </p> \n<p style=\"text-indent:2em;\"> <strong> 人气旺 样书销售一空 </strong> </p> \n<p style=\"text-indent:2em;\"> 9月27日，记者看到不少展位空空如也，畅销书更是早已脱销。还有一些图书本来不打算卖，但参展人员看到读者爱不释手的样子，还是破例出售了。很多出版社负责发行的人员都说，没想到山西的阅读气氛这么浓厚，市民这么喜欢纸质图书。 </p> \n<p style=\"text-indent:2em;\"> 9月27日下午3点半撤展前，南方出版传媒股份有限公司展区的书绝大部分已经被读者买走，或者被新华书店、图书馆馆配人员整体买走。公司出版部总监萧宿荣告诉记者，他觉得太原读者很热爱阅读。而为了站好最后一班岗，公司所属各社都安排了骨干业务人员坚守岗位。 </p> \n<p style=\"text-indent:2em;\"> 最后一天，除了台湾馆和山西主展区人气非常旺外，少儿展区也是人头攒动。少儿图书在书博会上非常受欢迎，尤其是有搞活动的少儿图书，基本上都是出版社带来多少就被买走多少。安徽少年儿童出版社带来了今年出版的几千码洋的新书，在最后一天全都售罄。山东教育出版社的“不一样的数学故事”系列180册也销售一空。 </p> \n<p style=\"text-indent:2em;\"> <strong> 签售火 作家到哪儿读者跟哪儿 </strong> </p> \n<p style=\"text-indent:2em;\"> “太棒啦，我签到了28本书。”9月25日，在书博会“红沙发”活动现场，一名排了几个小时队终于让中国当代科幻小说作家刘慈欣签上名的学生，一边跳着一边兴奋地向还在排队的同学们炫耀。而一名还在排队的同学则充满羡慕和嫉妒地说：“你小子，真行，太厉害了，匀我们几本吧。”他一听，抱着签好的书撒腿就跑。而与刘慈欣一起做访谈的物理学家李淼，也被粉丝们堵个正着。很多学生干脆请李淼在他们的衣服上签名。 </p> \n<p style=\"text-indent:2em;\"> 记者在书博会上看到，无论是哪个展台，只要有作者签名售书，一定会被热情的读者围个水泄不通。像台湾漫画家蔡志忠，一天有好几个签售会，他到哪儿读者就追到哪儿，而等他签名的队伍总是早早排得像长龙一般。刘先生是土生土长的太原人，现在做通讯行业，9月26日他告诉记者：“我从1989年就开始看蔡志忠的漫画，每一本都买，不过我最喜欢的还是《庄子说》《禅说》《菜根谭》等哲学系列，我当时看完觉得太棒了，真的是受益匪浅。”据他介绍，在太原很少见到作者签售活动，当他听说书博会上蔡志忠来签售，就带着10岁的儿子赶到现场。 </p> \n<p style=\"text-indent:2em;\"> <strong> 亲子热 全家逛展会其乐融融 </strong> </p> \n<p style=\"text-indent:2em;\"> “这是我妈妈，这是我老婆，这是我儿子，这不正好赶上中秋节放假，我们全家就一起来逛逛。”9月26日上午，一位从事煤炭行业的陈先生接受记者采访时说道，他觉得来书博会逛逛很有意义，不仅能让孩子多看点书，也能长点知识，还能全家在一起，一举多得。他刚刚看了青年作家李骏虎的《中国战场之共赴国难》，觉得写得很客观，与以往他看的类似的书不同，他很喜欢。记者看到，他手里还拎着四五本书，他说是给儿子买的，都是孩子们喜欢的儿童读物。 </p> \n<p style=\"text-indent:2em;\"> 尽管是中秋小长假，记者看到很多年轻父母带着孩子来逛展会。9月26日下午，记者在山西展台看到，一进门的右侧台阶上坐着很多孩子，每人都拿着一本书在认真地看着。“小朋友，你看的是什么书？”记者蹲下身小声问一位正在看书的小朋友。“作文。”他简短地回答完，马上又埋头继续看书。他妈妈告诉记者，儿子上六年级，最喜欢看有关作文类的书，刚一来就坐下开始看了。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：邹韧 刘蓓蓓 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年9月29日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 书博会遇上中秋节：火爆升级嗨翻全场','2015-12-16 17:37:05','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265846.shtml',NULL,NULL,NULL,NULL,NULL),(38,'国家新闻出版广电总局 - 行业信息 - 柳斌杰在中国版协践行职业道德自律公约座谈会上指出：把《公约》内容纳入出版业人员考核体系','柳斌杰在中国版协践行职业道德自律公约座谈会上指出：把《公约》内容纳入出版业人员考核体系','2015年9月21日','邹韧  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 9月18日，中国出版协会践行《新闻出版广播影视从业人员职业道德自律公约》座谈会在京举办。中国出版协会在京会员单位代表，分支机构、直属单位主要负责人，韬奋出版奖获得者齐聚一堂，畅谈学习《公约》的体会，提出贯彻落实建议，并作出庄严承诺。全国人大教科文卫委员会主任委员、中国出版协会理事长柳斌杰出席座谈会并讲话。 <br> </p> \n<p style=\"text-indent:2em;\"> 柳斌杰说，新闻出版广播影视行业50家社团签署《公约》，正值全党深入开展“三严三实”专题教育之时，这是适应形势推进行业精神文明建设、提升行业素质的务实之举。《公约》是培育社会主义核心价值观，提升职业道德约束力，规范从业人员行为，引领行业新风尚的重要举措。 </p> \n<p style=\"text-indent:2em;\"> 柳斌杰就出版行业贯彻落实《公约》提出6点意见。一是根据行业实际制定实施细则，将《公约》落实到出版行业中，体现行业特点和针对性。二是兑现承诺，从发现选择文化内容、加工制造、市场推广等多个环节履行出版责任，将承诺落实在行动上。三是加强教育，培育从业人员的职业操守与职业意识，尤其是加强价值观教育，提高从业人员素质。四是规范行为，把《公约》内容充实到《中国出版工作者职业道德准则》中，落实到对从业人员的要求上，纳入对从业人员的培养、教育、考核评价体系中。五是加强监督，落实到行业诚信建设上，完善行业诚信考核体系建设。六是严格要求，落实到出版行业管理上，将《公约》体现在对企业的评价、产品的评比、先进个人评选和衡量整个行业行为的价值考核中，强化《公约》的执行力。 </p> \n<p style=\"text-indent:2em;\"> 中国出版集团副总裁刘伯根、人民出版社总编辑辛广伟、中华书局总经理徐俊、中国教育出版传媒股份有限公司副总经理黄强、人民教育出版社副总编辑魏运华、韬奋出版奖获得者马静，版协分支机构负责人俸培宗、张增顺、王子荣等14位代表在座谈会上发言。大家一致认为，出版工作者肩负着传播知识、传递信息、传承文化的重要文化使命，在深入宣传党和国家的方针政策，培育和践行社会主义核心价值观，提高公民道德素质，丰富工作内容，提高行业公信力，推进社会主义文化强国建设中承担着重要职责。认真贯彻执行好《公约》，有利于出版业更好地履行文化使命。中国出版协会会员单位、分支机构、直属单位的从业人员，应发挥表率作用，凝聚全行业力量，引领行业风尚，带头自觉身体力行《公约》。希望社会各界用《公约》对出版工作者进行监督。 </p> \n<p style=\"text-indent:2em;\"> 座谈会由中国出版协会常务副理事长兼秘书长刘建国主持。国家新闻出版广电总局有关部门负责人就相关情况作了介绍。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：邹韧 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年9月21日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 柳斌杰在中国版协践行职业道德自律公约座谈会上指出：把《公约》内容纳入出版业人员考核体系','2015-12-16 17:37:08','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265181.shtml',NULL,NULL,NULL,NULL,NULL),(39,'国家新闻出版广电总局 - 行业信息 - 财政部下达2015年文化产业专资50亿元','财政部下达2015年文化产业专资50亿元','2015年10月8日','韩洁  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 财政部日前下达2015年度文化产业发展专项资金50亿元，共支持项目850个，项目数较2014年增长6.25%。 <br> </p> \n<p style=\"text-indent:2em;\"> 记者从中央文化企业国有资产监督管理领导小组办公室获悉，为使专项资金分配更加科学、合理、规范，今年重点从三方面作了改进。 </p> \n<p style=\"text-indent:2em;\"> 一是继续优化资金投向。按照党中央、国务院有关要求，今年安排的50亿元资金着重支持推动传统媒体和新兴媒体融合发展、文化创意和设计服务与相关产业融合、推动影视产业发展、推动对外文化贸易发展、巩固文化金融扶持计划、支持特色文化产业发展、扶持实体书店发展等重点领域，做到既统筹全局又突出重点。 </p> \n<p style=\"text-indent:2em;\"> 二是积极创新管理模式。今年建立以项目专家评审分数为主要依据的资金分配标准体系，并在地方转移支付中全面引入因素分配法，探索对部分绩效奖励项目采取整体下达的形式，使分配过程有据可循、有律可依。 </p> \n<p style=\"text-indent:2em;\"> 三是全面引入第三方监督机制。今年首次引入财政部预算评审中心参与合规性审核环节；首次设置专项资金前置性审核环节，由财政部驻各地监察专员办事处发挥就地就近优势，对申报单位独立提出审核意见，通过多个环节的严格把关，专项资金申报质量得到进一步提升。 </p> \n<p style=\"text-indent:2em;\"> 财政部数据显示，截至目前，文化产业发展专项资金已累计安排242亿元，支持项目4100多个。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：韩洁 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月8日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 财政部下达2015年文化产业专资50亿元','2015-12-16 17:37:08','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265969.shtml',NULL,NULL,NULL,NULL,NULL),(40,'国家新闻出版广电总局 - 行业信息 - 九家科技社联合打造百种“三农”书','九家科技社联合打造百种“三农”书','2015年9月30日','范占英  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 近日，在武汉举行的全国地科联图书订货会上，全国9家科技出版社研讨了其联合打造的百种“三农”书的出版细节。 <br> </p> \n<p style=\"text-indent:2em;\"> 据了解，全国地方科技出版联合体中的9家科技社在“三农出版联合平台”的基础上，正在打造首批135种“三农”丛书，目前已经完成书稿的60%，计划2016年面世。这批“三农”丛书名为“新型职业农民书架出版工程”，计划出版“三农”类图书上千种，由中原农民出版社、山西科技出版社、陕西科技出版社、山东科技出版社、湖南科技出版社、湖北科技出版社、江西科技出版社、安徽科技出版社等共同打造，图书出版后9家科技社将在所在地区分别发行全套图书。 </p> \n<p style=\"text-indent:2em;\"> 据中原农民出版社社长刘宏伟介绍，该套丛书是配合国家新闻出版广电总局“十三五”规划研讨，进行“三农”出版规划的具体内容。中国版协科技出版工作委员会常务副主任、秘书长张敬德认为，9家科技社联合出书标志着地方科技出版社联合体由松散联合走向了紧密合作，这种联合将实现资源共享，优势互补，值得进一步探索。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：范占英 &nbsp;来源：新华网 &nbsp;发布时间：2015年9月30日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','新华网  ','国家新闻出版广电总局 - 行业信息 - 九家科技社联合打造百种“三农”书','2015-12-16 17:37:08','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265901.shtml',NULL,NULL,NULL,NULL,NULL),(41,'国家新闻出版广电总局 - 行业信息 - “2015期刊数字影响力100强”评选揭晓','“2015期刊数字影响力100强”评选揭晓','2015年9月21日','范燕莹  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 9月19日下午，由中国（武汉）期刊交易博览会组委会主办、DCCI互联网数据中心协办的“2015期刊数字影响力100强”遴选结果公布。 <br> </p> \n<p style=\"text-indent:2em;\"> 此次百强名单分为大众期刊和学术/科技期刊两类。学术/科技期刊类包括《包装工程》、《北方园艺》、《北京体育大学学报》、《兵工学报》等，大众期刊类包括《21世纪商业评论》、《IT经理世界》、《百科知识》、《半月谈》等入选。 </p> \n<p style=\"text-indent:2em;\"> 据刊博会执委会办公室副主任胡耕介绍，此次遴选活动的举办，旨在借一年一度刊博会的大平台契机，对目前期刊作为整体及独立品牌形象在数字领域的影响力现状，给予总结及鼓励。 </p> \n<p style=\"text-indent:2em;\"> 据悉，本次遴选活动历时3个月，主要依据第三方数据，并由国内知名数据分析机构宏博知微进行独家数据分析挖掘，再经专家组终审，综合得出数字影响力100强名单。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：范燕莹 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年9月21日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - “2015期刊数字影响力100强”评选揭晓','2015-12-16 17:37:09','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265176.shtml',NULL,NULL,NULL,NULL,NULL),(42,'国家新闻出版广电总局 - 行业信息 - 《中国大百科全书》维吾尔文版首发','《中国大百科全书》维吾尔文版首发','2015年9月28日','诸葛瑞金  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 《中国大百科全书》（维吾尔文版）近日在新疆乌鲁木齐首发。 <br> </p> \n<p style=\"text-indent:2em;\"> 《中国大百科全书》（维吾尔文版）以《中国大百科全书》（第二版，简明版）为翻译版本，共10卷，2600万字，内容涉及历史、地理、军事、经济、自然科学、工程技术等12个方面，75个学科知识领域，其内容不仅涵盖了全人类科学文化成果，而且注重对悠久的中国各民族历史文化遗迹和科学成就的传承。它的出版是新疆出版界献给自治区成立60周年的一份厚礼。 </p> \n<p style=\"text-indent:2em;\"> 据了解，2011年11月，中国大百科全书出版社与新疆人民出版社启动翻译出版《中国大百科全书》维吾尔文版和哈萨克文版的工作。其中，维吾尔文版被列入新疆新闻出版“东风工程”民文出版译制项目，获得专项资金468万元。在翻译出版《中国大百科全书》（维吾尔文版）过程中，新疆人民出版总社将此项工作作为重中之重，200多名翻译、主审、编辑、校对等人员经过几年艰苦奋斗，终于在今年9月由新疆人民出版总社、新疆人民出版社出版发行。 </p> \n<p style=\"text-indent:2em;\"> “《中国大百科全书》（维吾尔文版）的出版可以帮助少数民族读者共享科学文化新成果，增强少数民族群众对伟大祖国的认同、对中华民族的认同、对中华文化的认同、对中国共产党的认同、对中国特色社会主义道路的认同。它的出版，对于加强民族团结，维护祖国统一，促进社会稳定，实现新疆社会稳定和长治久安具有重要意义。”新疆人民出版总社原社长阿不都热合曼·艾白说。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：诸葛瑞金 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年9月28日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 《中国大百科全书》维吾尔文版首发','2015-12-16 17:37:09','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265702.shtml',NULL,NULL,NULL,NULL,NULL),(43,'国家新闻出版广电总局 - 行业信息 - 光明日报社发布融媒体成果','光明日报社发布融媒体成果','2015年10月8日','李子木  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 9月29日，光明日报社向社会各界交出一份“年度答卷”。光明日报融媒体成果暨合作签约仪式在京举行。 <br> </p> \n<p style=\"text-indent:2em;\"> 光明日报社总编辑何东平在会上作了题为“打造一流新兴媒体 掌握网络时代话语权”的主题发言，阐述了光明日报社在媒体融合发展工程建设中的理念、思路、战略和实际举措。他表示，媒体融合的积极推进擦亮了“光明”品牌，极大地提升了《光明日报》在教科文卫等领域的舆论引导能力，也通过与市场的接轨，形成了在新媒体领域一定的可持续发展能力。 </p> \n<p style=\"text-indent:2em;\"> 会上，光明日报社副总编辑、融媒体中心主任陆先高从内容建设、平台建设、渠道建设三个方面，全面介绍了一年来光明日报社融媒体的建设成果，包括光明云媒3.0版、光明微站、光明都市传媒、光明校园传媒、光明媒体云等一系列创新产品。 </p> \n<p style=\"text-indent:2em;\"> 在光明云媒3.0版中，所有的新闻资讯均集中在“要闻”一个栏目中，主要精选当天最重要、最受关注的新闻；开设“时评”“思想”“人物”等栏目，突出特色的是时政评论和思想理论文章。光明云媒3.0版同时推出了智能电视版本，向智能电视用户提供新闻服务。此外，光明云媒还探索在车联网领域的信息服务。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：李子木 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月8日 </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 光明日报社发布融媒体成果','2015-12-16 17:37:09','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265967.shtml',NULL,NULL,NULL,NULL,NULL),(44,'国家新闻出版广电总局 - 行业信息 - 《中国抗日战争史简明读本》英文版在法兰克福书展首发','《中国抗日战争史简明读本》英文版在法兰克福书展首发','2015年10月16日','','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 10月14日当地时间下午，中央编译出版社与英国新经典出版社在法兰克福国际书展中国展区活动中心联合举办了《中国抗日战争史简明读本》英文版全球首发式。德国黑森州政府欧洲及国际事务司原司长博喜文博士、法兰克福国际书展日本商务代表镰田博树先生、英国新经典出版社总经理黄永军、中央编译出版社总编辑刘明清，以及来自德国、中国等国家的新闻媒体记者、出版人和读者出席了首发式。 <br> </p> \n<p style=\"text-indent:2em;\"> 本届法兰克福国际书展正值世界各国以各种不同形式纪念世界反法西斯战争胜利70周年的大背景，以二战为内容的主题出版物成为本届书展的一大亮点。刘明清介绍了《中国抗日战争史简明读本》英文版一书的内容和在全球出版发行的特别意义。与会嘉宾认为《中国抗日战争史简明读本》英文版将有助于国际社会全面公正地了解中国人民在世界反法西斯战争中的巨大贡献。本书首发式是法兰克福国际书展中国出版展团的重要活动之一，受到了众多国际出版人士的关注。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 来源：新华悦读 &nbsp;发布时间：2015年10月16日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','新华悦读  ','国家新闻出版广电总局 - 行业信息 - 《中国抗日战争史简明读本》英文版在法兰克福书展首发','2015-12-16 17:37:09','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/266556.shtml',NULL,NULL,NULL,NULL,NULL),(45,'国家新闻出版广电总局 - 行业信息 - 《2014-2015中国出版业发展报告》出版','《2014-2015中国出版业发展报告》出版','2015年10月9日','涂桂林  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 10月8日，由中国书籍出版社出版的《2014-2015中国出版业发展报告》（中国出版蓝皮书）在京举行新书首发式。 <br> </p> \n<p style=\"text-indent:2em;\"> 蓝皮书由中国新闻出版研究院组织编写，内容包括图书出版、期刊出版、报纸出版、数字出版、印刷业、出版物发行业等6部分，从多角度、多层面反映了2014年我国出版业发展状况，分析了2015年我国出版业发展趋势，并提出相应对策与建议。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;line-height:normal;\"> 作者：涂桂林 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月9日 </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 《2014-2015中国出版业发展报告》出版','2015-12-16 17:37:10','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/266017.shtml',NULL,NULL,NULL,NULL,NULL),(46,'国家新闻出版广电总局 - 行业信息 - 中国财经出版社：在专业领域深耕细作','中国财经出版社：在专业领域深耕细作','2015年9月24日','邹韧  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 中国财政经济出版社于1956年成立，作为财政部直属出版单位，在近60年的历史期间，牢牢坚守出版人的使命和责任，用图书来记载历史。近日，该社副社长黄琦接受了记者的采访。他告诉记者，从起步到目前，该社经过不断的努力，2014年的出版码洋已达4亿元，不仅出版了一大批财政、会计、税收、金融等经济领域理论及教材、教辅类图书，而且还积极输出版权，向世界展示中国财经理论与实践发展情况，是全国百家图书出版单位和中国出版政府奖先进出版单位。 <br> </p> \n<p style=\"text-indent:2em;\"> 据黄琦介绍，财经出版社在“十三五”期间将在以下四个方面开拓创新： </p> \n<p style=\"text-indent:2em;\"> 首先是对专业出版领域深耕细作。中国财政经济出版社是国内成立最早并具专业实力的出版机构之一，是宣传和传播经济思想论著、财经理论知识、各类教育资格考试的排头兵，例如会计职业资格类图书、证券期货保险从业资格类图书以及成人教育类图书等。因此，他们会充分利用占得一些先机的优势，对自己的专业出版领域深耕细作。但黄琦也强调，依然要有危机意识，若不居安思危、与时俱进，得到的也终将失去。 </p> \n<p style=\"text-indent:2em;\"> 其次是调整出版目标战略。专业出版领域有其自身的规律，但是随着新技术的发展及时代的不断迈进，人才、资源、渠道的内容和运作方式等也都发生了很大的变化。黄琦告诉记者，在总结过去经验和分析未来形势的基础上，中国财政经济出版社拟用5年左右的时间，努力把财经类图书做大做强。从规模数量型向质量效益型转变，致力于打造3个落脚点：图书出版与前中后期全程营销服务无缝契合，产品经营和品牌经营双管齐下，出好书和出人才并驾齐驱，实现图书内容精细化、图书形式多样化、营销服务立体化。 </p> \n<p style=\"text-indent:2em;\"> 再次是培育一些精品读物、重点读物。中国财政经济出版社在社会上影响比较大的主要是有关财经方针政策、财政理论知识类的图书，这些图书通过独特的思想判断和鲜明的论点论据，能够彰显个性、标新立异。不仅经济效益有所保障，社会效益也脱颖而出，从而实现了两个效益的高度统一，其中不乏获得中国图书奖、国家图书奖、中华优秀出版物奖的图书。“这也是我社精品图书或重点图书的一个基本方向。”黄琦表示。 </p> \n<p style=\"text-indent:2em;\"> 最后是积极开拓版权贸易。黄琦认为不能仅满足于兼收并蓄，更重要的是要开放视野“走出去”，要取长补短“引进来”。本土资源经验的不足与缺乏是客观情况，因此把世界各地优秀的财经类作品“引进来”作为补充，是发展的必需。作为世界知识沟通的桥梁与渠道，让世界分享全人类共通的精神财富是所有出版人责无旁贷的责任和义务。 </p> \n<p style=\"text-indent:0em;\"> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：邹韧 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年9月24日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 中国财经出版社：在专业领域深耕细作','2015-12-16 17:37:10','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265484.shtml',NULL,NULL,NULL,NULL,NULL),(47,'国家新闻出版广电总局 - 行业信息 - “阳光教育”网店销售盗版案终审维持原判','“阳光教育”网店销售盗版案终审维持原判','2015年10月12日','邹韧  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 日前，北京市第一中级人民法院终审裁定，驳回“阳光教育”网店销售盗版光盘案主犯王某的上诉，维持北京市海淀区人民法院的一审判决：主犯王某被判处有期徒刑3年，罚金10万元；其他3名案犯张某某、姜某、王某某分别被判处有期徒刑2年至2年2个月不等，罚金1万至2万元不等。 <br> </p> \n<p style=\"text-indent:2em;\"> 2013年10月，北京市文化市场行政执法总队接到举报，称淘宝网“阳光教育”网店销售大量盗版音像制品。接报后，执法总队立即开展了案件前期调查取证工作，将经“阳光教育”“爱婴乐园99”等淘宝网店售出的《清华幼儿英语语感启蒙》等59张光盘送交有关单位进行鉴定，认定为非法出版物。同年12月23日，公安机关现场起获光盘6203张、电脑机箱3台、刻录机1台及发货单365张，经鉴定，光盘中有5620张系非法出版物。王某、张某某、姜某被公安机关当场抓获，王某某主动投案自首。 </p> \n<p style=\"text-indent:2em;\"> 2015年5月13日，海淀区人民法院作出一审判决，以侵犯著作权罪，判处王某有期徒刑3年；判处张某某有期徒刑2年2个月，缓刑3年；判处姜某有期徒刑2年2个月，缓刑3年；王某某因投案自首，减轻处罚，判处有期徒刑2年，缓刑3年。其后，主犯王某认为量刑过重，提起上诉，北京市第一中级人民法院经二审查明事实和证据与一审相同，证据收集及质证符合法定程序，能够证明认定的事实，予以确认。据此，一中院驳回上诉，维持原判。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：邹韧 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月12日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - “阳光教育”网店销售盗版案终审维持原判','2015-12-16 17:37:11','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/266108.shtml',NULL,NULL,NULL,NULL,NULL),(48,'国家新闻出版广电总局 - 行业信息 - 荆报传媒：全媒体融合快车鸣笛出发','荆报传媒：全媒体融合快车鸣笛出发','2015年9月15日','崔琦  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 今年4月，荆州日报传媒集团全力打造了荆州第一网络视频媒体荆州微视，一上线就对2015年中国龙舟公开赛（荆州站）的赛事进行了网络视频直播。10多个小时的全程网络视频直播，获得50万网友点赞，首开通过网络视频直播国家级赛事的尝试，在全媒体融合的道路上迈出了坚实的一步。 <br> </p> \n<p style=\"text-indent:2em;\"> 在新的媒介环境下，如何促进传统媒体与新媒体的融合？近年来，荆州日报传媒集团进行了一系列的积极探索与实践，融媒体建设上初步建立了以荆州发布为核心的“双微”政务发布矩阵，以《荆州日报》、荆州新闻客户端为核心的新闻传播矩阵，以党报电子阅报栏、“社区帮”、社区报为核心的社区服务矩阵，形成了以《荆州晚报》、特色周刊为核心的深度服务平台等融媒体发展格局。 </p> \n<p style=\"text-indent:2em;\"> <strong> 细化全媒体产品定位 </strong> </p> \n<p style=\"text-indent:2em;\"> 荆州日报社近来针对自身媒体定位以及媒体环境因素，明确提出“精准定位，细分媒体”的战略，主动将内部各媒体定位进行细化，重点推进全媒体建设，抢占新闻制高点，实现了从可读到可视、从静态到动态、从一维到多维的实时传播、多屏传播、立体传播的新媒体发展格局。荆州日报传媒集团党委书记、社长代志武告诉记者，未来报社将以传统媒体为基础，以新媒体云传媒矩阵建设为突破口，推进全媒体采编平台、数字印刷出版中心、大数据中心三大平台建设，力争在3到5年内，将荆州日报传媒集团媒体与经营转型发展提升到一定高度。 </p> \n<p style=\"text-indent:2em;\"> 为推动新媒体与传统媒体的有机融合，荆州日报传媒集团在抓好“格、源、基”3个字上下功夫。“格”，即格局规划、顶层设计。真正发挥党报优势，围绕中心工作、核心观点，变传声筒为主张者，在政治解读、深度报道、观点引导等方面形成传播力量。“源”，即信息源建设。代志武进一步强调说，党报要在新闻源上下功夫，立足全市，形成以报社为主，记者站、编辑部为翼的党报信息传播通道。“基”，即内容建设。要“改头换面”，增强互动性、服务性与生活性，使党报媒体真正成为亲政亲民的媒体，促进党报提档升级。 </p> \n<p style=\"text-indent:2em;\"> <strong> 加强渠道建设促党报升级 </strong> </p> \n<p style=\"text-indent:2em;\"> 在全媒体渠道建设上，代志武介绍，报社以人群为核心进行设计，核心目的是搭建全媒体传播渠道，重点打造城市云传播体系，形成全渠道覆盖。 </p> \n<p style=\"text-indent:2em;\"> 城市云传播体系以政府建设的“智慧荆州”为接口，利用门户平台，为读者提供新闻推送、深度营销服务；实时传播渠道通过移动新闻客户端、网络、微矩阵建立集团24小时新闻发布圈；多屏传播渠道从PC端到移动手机屏、从阅报栏数字传播屏到城市联播网大屏，形成报社可视新闻互动传播新平台；微矩阵传播渠道通过微媒体进行即时传播，增强媒体品牌影响力和传播力；立体传播渠道从纸到屏，从网络到移动端，从固定到户外，从读报到读屏，从单一纸版、网版到多微平台传播，真正形成快速传播、全面传播的立体渠道。 </p> \n<p style=\"text-indent:2em;\"> 在媒体创新与技术融合上，集团根据荆州特点，调整楚网定位，打造云传媒体验平台。同时，加强以荆州微视为品牌的频媒建设。荆州微视是集团全力打造的荆州第一网络视频媒体，视频节目以本土性、生活化为特色，旨在打造时尚化、城市味的流媒体。 </p> \n<p style=\"text-indent:2em;\"> 在采写上，集团启动了流程再造的采编革新，先后出台了《荆州日报采编手册》、《荆州晚报采编工作流程及管理》，目前集团已基本形成了融媒体采编流程。当天的新闻首先是通过简讯的形式在官微平台发布，重要新闻由当天“荆州新闻客户端”下午或者晚上首发，深度新闻、独家新闻纸媒先发，客户端与网站转发。 </p> \n<p style=\"text-indent:2em;\"> 在机构设置上，集团还组建了全媒体中心，由客户端编辑部和技术部两个部门组成，同时成立“双微”平台联动中心，有力地保证新媒体平台运行。集团的新闻业务培训要求全员参训，持续3个月，实现了党报媒体人职业理念、职业思维、职业技能、职业操守的融合。 </p> \n<p style=\"text-indent:2em;\"> <strong> 转型发展初显成效 </strong> </p> \n<p style=\"text-indent:2em;\"> “东方之星”长江客船翻沉事件发生后，作为事发地的党报媒体，荆州日报传媒集团充分发挥融媒体力量，进行全方位播报。代志武告诉记者，在报道工作中，报社根据媒体特点进行整体部署，创新报道方式。纸媒作深度，以长篇通讯进行全面深度报道；新媒体作动态，适时报道事件发展。“这体现了主流媒体的力量，取得了好的传播效果。”据统计，“东方之星”翻沉事故报道中，荆州日报社的官方微博微信矩阵推出的话题阅读量达2000多万。 </p> \n<p style=\"text-indent:2em;\"> 其中，值得一提的是，在“头七”祭奠日，荆州日报传媒集团创新新媒体传播形式，推出了首个数字原创视频作品《七日·祭》。在特定的时间内推出原创性数字作品，取得了很好的传播效果。 </p> \n<p style=\"text-indent:2em;\"> 代志武介绍说，荆州日报传媒集团今年还承担并开发了“荆州大数据中心（文化荆州数字出版工程）”项目，该项目以荆州古城数千年的荆楚人文资源、三国文化资源、荆州新闻库等为基础，运用现代科技技术，进行数据化加工处理，形成数字云文库。目前，该计划已经纳入荆州智慧城市项目总体规划布局中。 </p> \n<p style=\"text-indent:2em;\"> “融媒体发展，没有成功经验可以复制。目前，荆州日报传媒集团转型发展工作仍然处于探索期、草创阶段，从传统报业到全媒体融合运行任重道远。在以后的媒体实践和创新中，我们将借鉴先前经验，结合荆州实际，不断创新、不断开拓，积极加快媒体融合转型的步伐。”对于未来，代志武充满了信心。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：崔琦 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年9月15日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 荆报传媒：全媒体融合快车鸣笛出发','2015-12-16 17:37:10','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/264865.shtml',NULL,NULL,NULL,NULL,NULL),(49,'国家新闻出版广电总局 - 行业信息 - 全国出版社编校人员业务培训班强调编校质量','全国出版社编校人员业务培训班强调编校质量','2015年10月9日','李子木  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 中国出版协会编校工作委员会于近日在京举办了第12期全国出版社编校人员业务培训班。来自全国50余家出版单位的近200名编辑、校对人员参加了培训。 <br> </p> \n<p> 此次培训班开设了出版物质量管理、出版选题策划和拓展、出版的品牌意识、语言文字规范、数字出版基础知识以及校对工作意义和方法等课程，邀请多位业内专家重点围绕提高编校质量进行讲授。 </p> \n<p> <br> </p> \n<p> 作者：李子木 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月9日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 全国出版社编校人员业务培训班强调编校质量','2015-12-16 17:37:11','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/266019.shtml',NULL,NULL,NULL,NULL,NULL),(50,'国家新闻出版广电总局 - 行业信息 - 韩国650种期刊首次亮相刊博会','韩国650种期刊首次亮相刊博会','2015年9月21日','邹韧  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 在2015中国（武汉）期刊交易博览会上，有一个展区很有特色，那就是今年全新亮相的韩国展区。9月19日，记者在韩国展区看到，来自韩国各大期刊社的不同种类的期刊，整齐地摆放在展架上供读者翻阅。 <br> </p> \n<p style=\"text-indent:2em;\"> 韩国期刊协会多媒体宣传室室长南基业告诉记者，韩国共有4400种期刊，这次带来了650种期刊参展，涵盖时尚、女性、时事、教育、旅游、体育、文学、幼儿、科学、制造等多种类别，这些都是在韩国最具代表性的期刊，希望能展示给中国读者，也希望通过这次展示能有机会与更多的业界同行进行切磋、沟通以及合作。 </p> \n<p style=\"text-indent:2em;\"> 记者看到，此次展示的都是韩国的纸质期刊，那么韩国期刊的数字化进程又是怎样的呢？据南基业介绍，目前，韩国期刊都已经进行了数字化转型，一般是采取纸质期刊和电子版期刊同时发行，但为了避免电子版期刊对纸质期刊的冲击，他们通常采取在纸质期刊发行一周后，再上电子版期刊的方式。 </p> \n<p style=\"text-indent:2em;\"> 记者在采访中了解到，韩国大多数电子版期刊由两种形式组成，一种是PDF格式，就是把纸质期刊直接进行数字化，它的内容和形式与纸质期刊一模一样，用户需要下载后才能阅读。另一种是网络版，而网络版又分为网页版和移动终端版两种形式，内容主要由两部分组成，一部分是纸质期刊内容的50%，而另一部分则是多媒体形式的，例如加入视频等形式的内容。为了加强期刊与用户的互动，韩国的很多期刊都会采取在一些文章下面设立用户点评的形式，通过用户的评价来了解他们更需要哪些内容，从而让期刊内容更接近读者。 </p> \n<p style=\"text-indent:2em;\"> 南基业告诉记者，到目前为止，由于韩国政府给予期刊社一定的财政支持，因此对于网络上的个人用户，他们是提供免费服务的，但是明年有可能会针对一些服务采取收费的模式。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：邹韧 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年9月21日 </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 韩国650种期刊首次亮相刊博会','2015-12-16 17:37:11','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265177.shtml',NULL,NULL,NULL,NULL,NULL),(51,'国家新闻出版广电总局 - 行业信息 - 新闻道德委员会将在全国范围建立','新闻道德委员会将在全国范围建立','2015年9月25日','昇平  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 中宣部、中国记协24日召开新闻道德委员会试点工作交流研讨会，要求全国各省（区、市）今年年内都要建立省一级新闻道德委员会。 <br> </p> \n<p style=\"text-indent:2em;\"> 建立新闻道德委员会是新形势下加强新闻职业道德建设、提升新闻队伍整体素质的重要举措。2013年以来，19个省（区、市）和单位试点建立新闻道德委员会，接受社会监督、加强行业自律，取得重要阶段性进展，得到社会各界普遍认可。 </p> \n<p style=\"text-indent:2em;\"> 会议要求，今年年内全国各省（区、市）都要建立省一级新闻道德委员会。要把新闻道德委员会建设作为学习贯彻习近平总书记系列重要讲话、贯彻落实“四个全面”战略布局，作为落实党管媒体原则、自觉践行群众路线，作为促进新闻媒体切实履行社会责任、确保行业健康发展的重要举措，聚焦有偿新闻、虚假报道、低俗之风、不良广告、新闻敲诈等新闻媒体和新闻从业人员中存在的突出问题，扎实做好举报查处、新闻评议、案例通报等工作，切实发挥监督约束作用。要进一步理顺工作机制，配足工作力量，把工作对象向新媒体及其从业人员拓展，探索完善媒体社会责任报告制度等工作手段，不断提高工作科学化规范化水平。 </p> \n<p style=\"text-indent:2em;\"> 中宣部副部长、国务院新闻办主任蒋建国出席会议并讲话。中国记协党组书记翟惠生主持会议。中央网信办、国家新闻出版广电总局、中国记协，各省（区、市）党委宣传部、记协负责同志等参加会议。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：昇平 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年9月25日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 新闻道德委员会将在全国范围建立','2015-12-16 17:37:11','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265609.shtml',NULL,NULL,NULL,NULL,NULL),(52,'国家新闻出版广电总局 - 行业信息 - 第25届全国书博会交易额超30亿码洋','第25届全国书博会交易额超30亿码洋','2015年9月29日','刘蓓蓓  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 9月27日下午6点，随着山西省永济县风陵渡镇张书耕等18名“蔬菜种植户”的离场，第25届全国图书交易博览会太原主会场落下帷幕。据了解，展会期间太原主会场参观总人次达29.1万，出版物交易额达12064万册、30.16亿码洋，现场总销售60.01万册、1500.04万码洋。 <br> </p> \n<p style=\"text-indent:2em;\"> 本届书博会呈现出4个特点：崇尚精品，名人荟萃；注重活动，全民参与；展销一体，效益双赢；全国水平，山西特色。本届书博会展示了山西省文化事业改革发展，尤其是新闻出版事业改革发展的新成绩、新气象，为实现山西省文化强省目标，以及省内外文化交流合作创造了新契机、新平台，进一步推动了全民阅读活动，促进了学习型社会建设。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：刘蓓蓓 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年9月29日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 第25届全国书博会交易额超30亿码洋','2015-12-16 17:37:11','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265847.shtml',NULL,NULL,NULL,NULL,NULL),(53,'国家新闻出版广电总局 - 行业信息 - 50册日本侵华战犯笔供图书发布','50册日本侵华战犯笔供图书发布','2015年9月17日','李明远  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 9月16日上午，《中央档案馆藏日本侵华战犯笔供选编》（第一辑）在中国人民抗日战争纪念馆发布。发布会上，中华书局和中央档案馆向国家图书馆、中国人民抗日战争纪念馆赠书作为永久收藏。 <br> </p> \n<p style=\"text-indent:2em;\"> 《笔供选编》共120册，获国家出版基金资助，由中央档案馆主编，中华书局分两辑出版发行。中央档案馆保存有1109名日本侵华战犯的笔供，《笔供选编》共选收800多名日本侵华战犯的笔供，绝大部分供述为首次公布。该书第一辑共50册，涉及战犯327人，档案超过2.6万页。 </p> \n<p style=\"text-indent:2em;\"> 《笔供选编》以影印出版的方式对日本侵华战犯的笔供作了无删节修改的全面呈现。每个战犯的笔供，包括笔供原文、补充、更正、附言等，以及当时的中文译文。笔供中涉及的罪行包括南京大屠杀，细菌武器制造，虐杀战俘，强征“慰安妇”，奴役、残杀、掠夺平民等违反国际准则和人道主义原则的残虐暴行。日本侵华战犯在笔供中对所犯罪行供认不讳，这是日本军国主义侵略者对中国人民所犯罪行的铁证。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：李明远 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年9月17日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 50册日本侵华战犯笔供图书发布','2015-12-16 17:37:11','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265004.shtml',NULL,NULL,NULL,NULL,NULL),(54,'国家新闻出版广电总局 - 行业信息 - 中美盲文出版交流活动揭幕','中美盲文出版交流活动揭幕','2015年9月21日','','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 中美盲文出版交流活动21日在美国华盛顿州有声读物及盲文图书馆揭幕。中国国务院新闻办公室副主任郭卫民、华盛顿州州务卿金·怀曼和中美盲文出版、教育、图书馆服务机构代表出席剪彩仪式。 <br> </p> \n<p style=\"text-indent:2em;\"> 这次活动以“阅读让生活更精彩”为主题，21日开始、27日结束，是在中国国家主席习近平对美国进行国事访问期间举行的中美人文交流活动之一，由中国国务院新闻办公室主办，中国盲文出版社、美国国家盲文出版社、华盛顿州有声读物及盲文图书馆和中国图书进出口总公司联合承办。 </p> \n<p style=\"text-indent:2em;\"> 华盛顿州有声读物及盲文图书馆位于美国西北部城市西雅图市中心，向特殊需求群体所提供的服务在全美国居于领先地位。就这次交流活动，这家图书馆在网站首页以英文、中文和盲文加以推介。 </p> \n<p style=\"text-indent:2em;\"> 活动以文字、图片、实物和信息无障碍技术方式介绍中美盲文出版交流情况，展示数字时代的盲文出版，讲述中国盲人通过学习和阅读成长、成才、成功的故事，向美国民众展现中美两国在残疾人人权事业发展方面交流合作的积极成果，倡导盲文和阅读对重建盲人信心以及促进盲人教育和就业、丰富盲人文化和生活的重要意义。 </p> \n<p style=\"text-indent:2em;\"> 剪彩仪式上，华盛顿州州务卿怀曼致辞，对华盛顿州有声读物及盲文图书馆与中国盲文出版社成为合作伙伴表示赞许，认为这次交流将是一个新的起点。 </p> \n<p style=\"text-indent:2em;\"> 中国盲文出版社社长张伟说，盲文出版是支撑盲人平等接受教育、无障碍共享文明成果、参与社会生活的基石。这次中美交流旨在促进两国盲文出版机构之间分享服务经验，相互借鉴服务模式；中方将与美方盲人朋友和盲人服务机构研讨数字化服务、阅读推广和志愿服务等话题。 </p> \n<p style=\"text-indent:2em;\"> 对这场活动，怀曼借助网络博客予以发布，引述华盛顿州有声读物及盲文图书馆馆长丹妮尔·米勒所作评价：“这次（交流）展览令人兴奋，既显现盲文在全球范围的重要作用，也显现中国和美国之间在向盲人和视觉障碍群体提供服务方面建立合作伙伴关系的重要意义。” </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 来源：新华网 &nbsp;发布时间：2015年9月21日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','新华网  ','国家新闻出版广电总局 - 行业信息 - 中美盲文出版交流活动揭幕','2015-12-16 17:37:11','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/265374.shtml',NULL,NULL,NULL,NULL,NULL),(55,'国家新闻出版广电总局 - 行业信息 - 中国财政经济出版社：打不完的盗版 伤不起的正版','中国财政经济出版社：打不完的盗版 伤不起的正版','2015年10月12日','邹韧  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 注册会计师（CPA）考试作为“中国最难考的三大执业资格考试”之一，其通过率仅在5%左右，因此含金量非常高，参加考试的人自然很多。中国财政经济出版社就是一家出版CPA考试用书的专业出版社。原本一直把心思都放在出版上的财经社，近些年却被大量盗版该社的教材教辅以及图书所困扰。 <br> </p> \n<p style=\"text-indent:2em;\"> <strong> 网友举报发现重要线索 </strong> </p> \n<p style=\"text-indent:2em;\"> 今年8月中旬，记者跟随财经社打盗维权人员来到广州，想采访该社配合当地公安部门查缴一个“产、经、销”一条龙服务的盗版窝点案件。该社打盗维权人员王华莹告诉记者，今年年初，该社连续接到多位淘宝店主的举报，称广州有人冒充该社给他们发公函和警告函对他们进行敲诈。接报后，该社为了弄清真相，授权广东法制盛邦律师事务所调查此事。后经律师调查，将目标锁定在周某某身上，发现其在广州市天河区有两个存储书籍的仓库，该社赶紧派人到该仓库进行多次核实，确定该仓库为存储盗版图书的窝点。同时，他们还对其在白云区的印刷厂进行了实地查访。据王华莹介绍，周某某可以说是他的一个“网友”，财经社针对普通读者难于辨别图书真伪的难题，专门设了一个QQ号，主要是方便读者即时辨别真伪，同时发布一些该社的打盗信息。而周某某在淘宝、天猫先后开了10多家网上书店，很容易就加入到这个群中，得到了一些出版社打击盗版的情况，这自然成为他的销售风向标。 </p> \n<p style=\"text-indent:2em;\"> 8月19日，财经社将情况向广州市公安局天河分局新塘派出所举报，下午4点，广州市文化执法大队的10余名执法人员和多名公安干警到达周某某库房附近，但因警方无法打开房门，直到当晚7时半左右才进入周某某的住处和库房，现场查获盗版书籍3万多册。其中标识为财经社的书籍约为8000册，包括《经济法》《财务成本管理》《会计》等教材以及配套习题；标识为北大出版社的约为13000册；后经两社鉴定均为盗版书籍。另有盗印的韩文原版图书《秘密花园》1万多册。现场还查获了盗版复印胶片3套，假冒防伪贴码近万个，以及大量快递单和封装完好准备发货的快递等。执法人员将犯罪嫌疑人周某某、杨某某依法进行刑事拘留。 </p> \n<p style=\"text-indent:2em;\"> 截至记者发稿时，该案还在进一步调查中。 </p> \n<p style=\"text-indent:2em;\"> <strong> 图书一直被盗版，还越来越真 </strong> </p> \n<p style=\"text-indent:2em;\"> 9月中旬，记者又采访了财经社副社长黄琦。他告诉记者，从上世纪90年代初到现在，财经社的图书一直都存在被盗版现象，虽然他们也采取了一些防范措施，但由于利益的驱动，盗版现象依然屡禁不止，而且愈演愈烈。被盗版的主要是以各类资格考试的教材和教辅为主，其中最突出的是全国会计类考试用书。 </p> \n<p style=\"text-indent:2em;\"> 据黄琦介绍，目前该社被盗版图书主要有3种形式：低仿、高仿和超高仿。低仿图书的特点是总体印刷质量低劣，通常用纸较差，墨色不匀，套印不准，图像模糊不清，文字和标点符号差错较多，病句连篇，因此容易辨别。高仿图书的特点是，总体印刷质量较好，与正版差别不大，用纸很好，墨色较匀，套印较准，图像清楚，成品方正，装订规整，而且文字差错较少，较难辨别。随着技术的进步，现在又出现了超高仿图书，这类图书与正版几乎没有差异，有些盗版图书的纸张甚至还超过正版，而且封面及内文印刷、装订规范整洁，极难辨别真假。 </p> \n<p style=\"text-indent:2em;\"> <strong> 尝试各种打盗维权方式 </strong> </p> \n<p style=\"text-indent:2em;\"> 据介绍，面对大量盗版不断冲击正版图书现象，财经社近年做了大量防止和打击盗版的工作，出版社的重点图书在出厂前全部会贴防伪标，而且防伪标从设计到印刷再到贴书各流程及岗位统统分离。出版社也一再要求严格履行图书印刷及发行手续，并在印刷、库存、运输过程中加强管理。 </p> \n<p style=\"text-indent:2em;\"> 2011年，为了加大打击盗版的力度，财经社设置了专职打盗维权人员和举报电话，还加入了“京版十五社反盗版联盟”，同时积极配合政府执法部门的各种执法行动，并实施举报奖励制度，对提供有关盗版信息的读者、协助出版社打击盗版的个人，以及相关协助单位给予奖励。“通过近两三年持续打击盗版，现在低仿盗版图书基本上已经退出了省会城市，但二、三线城市的书店和图书市场依然还有很多。”黄琦说。 </p> \n<p style=\"text-indent:2em;\"> 针对这种情况，“财经社今后打击盗版的重点将集中在3个方面：首先是网店；其次是全国各地的会计培训学校，原因是现在他们销售和使用的该社图书正版率低；最后是制作盗版图书的上游盗版商和印刷厂。”黄琦说。 </p> \n<p style=\"text-indent:2em;\"> <strong> 势单力薄成出版社打盗难点 </strong> </p> \n<p style=\"text-indent:2em;\"> 其实，近两年财经社配合执法部门查办了许多件金额较大、性质比较严重的案件。例如去年查办的“4·10”石家庄盗版库房案、“5·12”石家庄盗版库房案、“5·27”郑州盗版库房案、“9·12”广州盗版库房案、“11·17”巩义黑印厂案、“3·26”成都天府广场盗版图书案和6月~8月的广州系列网店案等，这些案件查获的盗版书少则几万册，多的达到二三十万册。 </p> \n<p style=\"text-indent:2em;\"> 黄琦坦言，目前该社在打击盗版的过程中确实碰到了很多问题，但主要问题还是势单力薄。尽管也在与相关部门合作，但由于利益主体不同，所涉及的利益范围有宽有窄，打击盗版的参与度也有深有浅。现在盗版行为严重冲击了图书产业的发展，黄琦认为，出版社打击盗版不只是维护自身的合法权益，更重要的是要承担起一份社会责任。尽管出版社在打击盗版的行动中取得了一定的成效，但还远远不够，因为打击盗版非一日之功，杜绝盗版非一己之力，为了维护合法权益，财经社将继续加强与相关执法部门的合作，与同行紧密合作，希望可以为我国知识产权保护贡献自己微薄的力量。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：邹韧 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月12日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 中国财政经济出版社：打不完的盗版 伤不起的正版','2015-12-16 17:37:13','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/266109.shtml',NULL,NULL,NULL,NULL,NULL),(56,'国家新闻出版广电总局 - 行业信息 - 我们从英国出版业学到什么','我们从英国出版业学到什么 ——江西出版集团首批赴英出版编辑人才培训班考思录','2015年10月19日','左志红  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 在世界十大学府之一——有着浓厚历史文化底蕴的英国牛津大学，聆听出版传媒业知名学者、资深高管授课；到英国出版科技集团、约翰·威立国际出版公司英国子公司等享誉全球的出版企业考察，与国外同行“零距离”交流；去剑桥大学、牛津博物馆、英国国家美术馆、大英博物馆、伦敦书店街参观，接受厚重的艺术熏陶…… <br> </p> \n<p style=\"text-indent:2em;\"> 对江西出版集团“数字出版时代编辑出版管理与实践高级培训班”的20名学员来说，2015年的夏天是美好又难忘的。8月16日-30日，为期15天的培训对他们来说，不仅是出版从业水平的一次全面提升，也是职业生涯中的一次宝贵阅历。 </p> \n<p style=\"text-indent:2em;\"> 这样大手笔的培训，在江西出版史上是第一次，甚至在全国出版界也是罕见的。集团董事长赵东亮对赴英培训班寄予厚望，他曾以法国旅欧支部为喻，希望这支队伍“当起集团远征海外先遣队的责任”，站在更高的纬度上来观察世界出版业发展走向，为集团的转型升级收集情报与线路图。 </p> \n<p style=\"text-indent:2em;\"> 英国归来，学员们整理出14万字的课堂笔记并各自做了学习总结，最核心的就是从英国出版业可以学到什么。记者从他们的文字中撷取精华，再加以采访，汇集为以下答案。 </p> \n<p style=\"text-indent:2em;\"> <strong> “版权经营是出版最高境界” </strong> </p> \n<p style=\"text-indent:2em;\"> “版权经营是出版最高境界，这是董事长这几年深入研究出版业后提出并一直倡导的理念。在这次学习当中，我们对这一理念有了更深入的理解。”参加此次培训的中文天地出版传媒股份有限公司出版产业部主任刘凯军告诉记者。 </p> \n<p style=\"text-indent:2em;\"> 作为世界第二大版权输出国，英国出版界高度重视版权工作，对版权的保护和开展国际版权贸易有一套完备的体系和成功的做法。他们从选题策划开始就着眼于国际市场，图书的设计通常采取“一书多版”的方式。剑桥大学出版社有1.5万种图书已不再出版纸质版图书了，但出版社继续经营这些书的版权，每年仍有较大收益；约翰·威立国际出版公司有一支40多人的版权经营团队，每年活跃在世界各地，创造巨大的收益；斯特林出版中心等一些大型出版社将纸书版权卖向全世界、卖给其他产业链，对版权经营的重视超过了纸质书。赴英培训中看到和听到的这些版权经营的理论与案例，给学员们留下了深刻的印象。 </p> \n<p style=\"text-indent:2em;\"> “开眼看世界”的江西出版人，也在培训和考察中对中英图书版权工作进行了对比，进而思考中国出版业如何才能汲取经验，以便做得更好。 </p> \n<p style=\"text-indent:2em;\"> 思考一：希望能和作者签长期版权 </p> \n<p style=\"text-indent:2em;\"> “我们和英国的版权政策和数字版权经营的环境有很大不同，一定程度上制约了我们的数字出版发展。”考察归来，江西人民出版社副社长余晖对中英版权问题进行了对比。 </p> \n<p style=\"text-indent:2em;\"> 英国数字版权管理专家克莱尔·佩因特在课堂上讲道，欧洲的版权签约一般是终身制，并还可延续至作者故后的70年。欧洲出版产业的有序发展，源于公平健全的法律法规以及相关各方公平规范的合作机制。英国出版社签下作者的版权很多是长期版权，且纸质图书版权很少和数字版权分开授予。而我国出版单位与作者目前签的大多是5-8年的版权，且很多作者并不授予出版单位数字版权。 </p> \n<p style=\"text-indent:2em;\"> 英国伦敦城市大学教授、北京印刷学院兼职教授卫克安就在课堂上提出疑问：“中国大型出版集团有收购各类企业的雄厚财力，为什么不拿这些资金去购买作者的世界长期版权？”这值得中国出版业从业者深思。 </p> \n<p style=\"text-indent:2em;\"> 英国作者会把版权长期授权给出版商，这是西方出版的传统，电子书出现后数字版权也延续了这个传统，而中国没有。对比之下，余晖认为，中国这种过短的版权期以及图书版权和数字版权分离的现状对出版社是极为不利的，既不利于出版社做图书推广，也不利于发展数字出版。由此，她提出，是否可以呼吁出版行业协会让国家出台规定版权期不能低于多少年等对出版商有利的政策。 </p> \n<p style=\"text-indent:2em;\"> 思考二：为作者成立专门的运营部 </p> \n<p style=\"text-indent:2em;\"> 在约翰·威立国际出版公司英国子公司参观时，令二十一世纪出版社集团总编辑王军印象最深刻的是其成功经营作家的经验。在自出版发达的英国，作者可以跳过出版社，在网上直接和读者交流，出版社不得不面临作者流失的压力。而约翰·威立国际出版公司感知到这一市场变化，成立了专门的作者运营部。 </p> \n<p style=\"text-indent:2em;\"> 作者运营部主要负责以下工作：第一，为书稿提供四种语言的编辑工作；第二，帮助作者为全球化的读者寻求联系，建立全球化市场，以提升作者知名度；第三，最大限度地帮助作者在相关领域提升作品的影响力，他们专门为作者设计了一套操作工具，提高作者作品的能见度，同时还为作者设计了自我推广的工具箱；第四，帮助作者进行调查评估，给他们反馈统计数据，例如提供在社交媒体上作者相关文章被引用的次数，或者是在新闻报道中被提到的次数。总之，作者运营部的目标是为作者创造价值，从而获得作者有价值的版权。由于措施到位，该公司云集了国内外一大批一流的专家、作家和学者。 </p> \n<p style=\"text-indent:2em;\"> “受新技术影响，读者的消费习惯变化了，市场变化了，出版环境变化了，威立就设置了相应的部门，而我国出版社业务部门的设置十几年一贯制，几乎没有变化，这应当引起我们的反思。”王军说。 </p> \n<p style=\"text-indent:2em;\"> <strong> “我们的数字出版模式必须从头做起” </strong> </p> \n<p style=\"text-indent:2em;\"> “这次组织大家去英国，学习主题就是国外出版业数字化转型的经验和模式，希望这次高层次培训能为集团战略目标的推进提供人才与智力的支撑。”培训班出行前，赵东亮对学员们此次培训提出了明确目标。在培训和考察过程中，学员们也积极寻求数字化转型的猛药良方。 </p> \n<p style=\"text-indent:2em;\"> 记者从学员们的课堂笔记中可以窥见英国数字出版业的发展状况：2013年英国出版的图书有18.4万种，其中有三分之一是电子书。电子书的销售近年来呈现快速增长的趋势，2011年占图书总量的8%，2012年增长到12%，2013年是15%，2014年是17%。 </p> \n<p style=\"text-indent:2em;\"> 爱思唯尔集团作为全球最大的科学与医药信息出版商，每年在网上销售期刊收益达到30亿美元；培生集团买入与教育数字化密切相关的商业英语学习软件公司环球西文和教育服务商美国在线；约翰·威立国际出版公司的数字出版收入占比已达到55%；塔森出版集团从1980年开始就有经营艺术品版权及数据库的尝试。“老师在课堂上讲到的这些案例，让我们充分领略到了数字出版的魅力。”江西教育出版社副社长熊侃说。 </p> \n<p style=\"text-indent:2em;\"> 学员们对英国数字出版生产流程和商业模式的考察，也引发了他们关于中国数字出版业该如何发展的思考。 </p> \n<p style=\"text-indent:2em;\"> 思考一：出版流程要从“接力赛”变为“足球赛” </p> \n<p style=\"text-indent:2em;\"> “传统的出版流程就像接力赛，是线性的，一棒一棒地交接；而数字出版的流程就像是足球赛，所有的队员都同时在场上，大家分工合作。”英国数字版权专家大卫·艾特沃尔在课堂上非常形象地将两种不同的出版流程做了对比。大卫说过的这句话，出现在多位学员的学习总结中。 </p> \n<p style=\"text-indent:2em;\"> 江西人民出版社副社长桂梅对此深有感悟：“一直以来，我们的工作流程都是先纸质出版编、校、排，然后再稍作数字化的改造甚至不改造就生成了电子书。而数字时代的编辑过程是一个纸质出版和数字出版同时进行的复合过程，理想的模式是当编辑过程完成了，纸质书和数字书就都同时生成了。” </p> \n<p style=\"text-indent:2em;\"> 刘凯军在比较后指出了我国数字出版存在的问题：“一些出版社在生产源头——排版软件的格式，就不符合数字出版的传播格式，需要进行复杂的再次转换；接下来是对内容元数据的处理，目前，我们的编辑基本上没有掌握这些新技术；再接下来，产品是为谁生产的，需求在哪里，我们很少考虑这个问题。” </p> \n<p style=\"text-indent:2em;\"> 刘凯军认为，在许多环节上我们的工作是缺位的，整个数字编辑出版流程没有形成完整的链条，产品的数量与质量跟不上，难以激发市场需求，怎能形成赢利模式，“一言以蔽之，现在的数字出版模式必须从头做起”。桂梅则建议，出版社需要装备一套实用高效的自动化编撰系统，需要颠覆编辑的传统工作习惯和思维方式，需要编辑具备全新的编辑技能。 </p> \n<p style=\"text-indent:2em;\"> 思考二：要帮助“搜索型消费者”找到内容 </p> \n<p style=\"text-indent:2em;\"> “搜索型消费者”——二十一世纪出版社集团图文设计部主任彭蕾在总结中提出这个概念。她解释道，在传统出版时代，读者通常通过书店、图书馆或者各类评论等中介媒介来寻找他们需要的书。而现在，更多的人选择直接上网去搜索。也就是说，现在的出版商所要竞争的不仅是怎样提供优质的载体，更重要的是怎样提供可被搜索到的、可见度高的内容。 </p> \n<p style=\"text-indent:2em;\"> 如牛津布鲁克斯大学国际出版中心主任安格斯·菲利普在课堂上所说：“要帮助消费者找到你的内容，应花时间在元数据的建立和开发上，增加你的产品或内容的可见度。”数字出版的一个重要环节，就是编辑对内容做碎片化处理，做元数据开发和主题词的编制、搜索引擎优化等，让读者能在大海一样的网络信息中找到它。而编辑最好让读者在三次以内就能搜索到相关内容，否则大部分读者会放弃继续寻找。 </p> \n<p style=\"text-indent:2em;\"> 这也引发了学员们关于数字出版相关技术标准的思考。学员们的课堂笔记中提到，英国出版社在书稿文件格式上使用的是目前世界上最新的、使用最广泛的（可扩展标记语言）文件格式，该格式是唯一为全世界的出版行业青睐的标准数字出版格式，具有清晰的数据结构和跨媒介发布的巨大优势。它能保留完整精确的数据信息，便于消费者在网络上通过关键词搜索到该书，也便于生成可标记语言的数字产品。而我国出版社正在使用的方正格式的技术，已落后于数字出版时代。 </p> \n<p style=\"text-indent:2em;\"> 思考三：我们的竞争对手早已不限于同行 </p> \n<p style=\"text-indent:2em;\"> “老师们在培训中反复提示，出版社的竞争对手已不再是业内同行，最后击败出版社的很可能是新兴行业，要引起业内人士重视。我想，在中国，击败我们的很可能是移动、联通。”红星电子音像出版社社长刘永东告诉记者。 </p> \n<p style=\"text-indent:2em;\"> 刘永东认为，以前出版商拥有垄断的出版权，现在由于数字出版的门槛很低，大型搜索、社交平台，如谷歌、脸书等也涉足了出版行业。这些公司都有各自不同的出版商业模式，索尼公司目前也出售电子书，亚马逊也越过出版商直接和作者签合同。作者也跳出来分一杯羹，自助出版也越来越盛行。今天，出版商有由内容提供商向服务提供商转化的趋势。他认为，所有这些现象和趋势，都应引起业内的注意，值得大家去思考和探索对策。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：左志红 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月19日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 我们从英国出版业学到什么','2015-12-16 17:37:14','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/266687.shtml',NULL,NULL,NULL,NULL,NULL),(57,'国家新闻出版广电总局 - 行业信息 - 中国主题图书法兰克福书展受欢迎','中国主题图书法兰克福书展受欢迎','2015年10月15日','朱音  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 当地时间10月14日，中国外文局携旗下多家出版社亮相第67届法兰克福国际书展。 <br> </p> \n<p style=\"text-indent:2em;\"> 中国外文局共有478种图书参展，其中2014年10月以后出版的新书396种，外文图书约占参展图书总量的92%，英文（含对照）图书305种，其他外语文种（含德、法、西、阿等16个文种）图书137种。新书中，中国主题类图书成为市场热点，《习近平谈治国理政》《中国道路与中国梦》《中共十八大：中国梦与世界》和“解读中国特色社会主义主题图书”等受到海外同业和读者热捧。《抗美援朝战争史》英文版首发仪式暨国际合作出版签约仪式也将举办。该书是一部具有鲜明特色的、全景式反映抗美援朝战争的鸿篇巨制，美国学术出版社对其鲜明的中国观点及学术价值表示出浓厚兴趣。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：朱音 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月15日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 中国主题图书法兰克福书展受欢迎','2015-12-16 17:37:18','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/266389.shtml',NULL,NULL,NULL,NULL,NULL),(58,'国家新闻出版广电总局 - 行业信息 - 海外华文传媒合作组织发布《首尔宣言》','海外华文传媒合作组织发布《首尔宣言》','2015年10月14日','','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 海外华文传媒合作组织第10届年会开幕大会10月13日在韩国首尔举行。 <br> </p> \n<p style=\"text-indent:2em;\"> 本届年会共有来自20多个国家和地区的近百名海外华文媒体代表出席，以“全球华媒齐聚韩国 促东北亚经济一体化”为主题，探讨在中国“一带一路”建设对接韩国“欧亚合作倡议”大背景下，海外华媒的作为与前景。 </p> \n<p style=\"text-indent:2em;\"> 韩国总统朴槿惠向大会发来贺信。韩国国务总理黄教安以视频讲话形式发表致词。朴槿惠在贺信中表示，近年来在“汉风”和“韩流”的影响下，韩中两国迎来文化交流新时期，两国国民的心也越来越近。她呼吁海外华文传媒组织各成员“同心同德，为韩国与中国，以及全亚洲发展积聚力量”。 </p> \n<p style=\"text-indent:2em;\"> 中国驻韩大使邱国洪发来贺信，希望广大海外华文传媒把握历史机遇，进一步加强与所在国主流媒体的联系交流，更加自觉地弘扬中华文化。 </p> \n<p style=\"text-indent:2em;\"> 会上，合作组织成员共同发布《首尔宣言》。宣言指出，随着中国综合实力不断增强，海外华文媒体不仅承担着传播中华文化的重任，更肩负着传播中国立场、塑造中国大国形象的历史使命。海外华文传媒合作组织成员要共同致力于建设一个公正、平衡、合理的国际话语体系，推动世界多元化和谐发展。在报道中国事务上，一方面要向海外华侨华人展示真实客观的中国社会，另一方面，也要成为各国主流社会了解中国的权威媒介，减少当地受众由于文化隔膜等因素对中国产生的误解和偏见。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 来源：新华网 &nbsp;发布时间：2015年10月14日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','新华网  ','国家新闻出版广电总局 - 行业信息 - 海外华文传媒合作组织发布《首尔宣言》','2015-12-16 17:37:18','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/266387.shtml',NULL,NULL,NULL,NULL,NULL),(59,'国家新闻出版广电总局 - 行业信息 - 实施“一票否决” 让造假者血本无归','治理虚假失实报道，媒体人建议 实施“一票否决” 让造假者血本无归','2015年10月13日','李雪昆 梁晓龙  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 国家新闻出版广电总局9月28日公开通报了《扬子晚报》等15家媒体刊登虚假失实报道的查办情况。这已是自2014年中宣部等九部门深入开展打击新闻敲诈和假新闻专项行动以来，总局第七次公开通报违法违规案件的查处情况。针对这一行业“顽疾”，业界如何看待？近日，记者就此进行了采访。 </p> \n<p style=\"text-indent:2em;\"> 在采访中，一些业界同仁表示，要加强制度建设，强化落实管理，从思想上让采编人员提高防范意识，全面规范新闻记者采编行为，加强稿件审核把关。 </p> \n<p style=\"text-indent:2em;\"> <strong> 虚假报道背后是浮躁作怪 </strong> </p> \n<p style=\"text-indent:2em;\"> <span style=\"font-family:楷体, 楷体_gb2312, simkai;\"> 多有一个疑问、多设一道关口，或许虚假新闻也不会出现 </span> </p> \n<p style=\"text-indent:2em;\"> “总局通报15家媒体刊登虚假失实报道的查处情况，反映出当前一些新闻记者作风浮躁、职业道德失范等问题。媒体刊登虚假失实报道影响了新闻媒体的公信力和美誉度。”采访中，温州日报报业集团社长方立明强调，新闻媒体作为社会传播机构，必须把增强新闻职业操守和职业道德建设作为一项重要工作常抓不懈。 </p> \n<p style=\"text-indent:2em;\"> “《长沙股民170万四倍融资全仓一股 两跌停赔光跳楼》《裸女跳河，救人者遭冷遇》《退休机务副总落马 南航三大重要部门全部沦陷》等，这些新闻的标题虽然很‘扎眼’，但却是假的。”光明日报社文化产业研究中心副主任张玉玲表示，仔细看这15家媒体的报道，有的是为了吸引眼球，故意“放大”信息；有的对网络上的信息不经核实，以讹传讹；还有的是为了“抢发”，萝卜快了不洗泥，不加甄别或凭想象编写。她认为，表面上看这些似乎都是“技术”性错误，但从根本上反映的却是一些新闻记者作风的浮躁和部分新闻单位管理的漏洞。“如果记者稍微有一点责任心，打个电话核实一下，或许就能杜绝一条虚假新闻；如果整个单位的管理规范，审核程序严谨，能多有一个疑问、多设一道关口，或许虚假新闻也不会出现。” </p> \n<p style=\"text-indent:2em;\"> 新华社记者陈聪也认为，从此次查处的各媒体情况看，大多与新媒体消息源有关。其实，网络的便捷的确为新闻消息源的丰富提供了便利，但事物都具有两面性，如果仅仅注重所谓的便利而忽视了其真实性、放松了对内容的核实，那么，这样的便利无疑是危险且无益的。 </p> \n<p style=\"text-indent:2em;\"> <strong> 以公信力作为立身之本 </strong> </p> \n<p style=\"text-indent:2em;\"> <span style=\"font-family:楷体, 楷体_gb2312, simkai;\"> 让新闻工作者不至于“流汗又流泪”，真正工作得有尊严、有担当 </span> </p> \n<p style=\"text-indent:2em;\"> “以新闻为业，以真实为基，从选择与新闻相关专业那时起，从做这份职业的那天起，真实一直是时刻萦绕在眼前的。”在中国国际广播电台新闻中心采编部记者杨琼看来，坚守这份职业要“勤”，不仅是眼勤、耳勤，更要嘴勤、腿勤，每个人在采访中都勤快一点，换来的是整个媒体公信力的提升，而这份对职业的坚守与负责不仅是为了自己，更是为了无数获知新闻的受众。 </p> \n<p style=\"text-indent:2em;\"> 方立明认为，在新的媒体时代，要不断强化马克思主义新闻观教育，做到真学、真懂、真信、真用，从而夯实新闻工作者的思想和理论基础，提升新闻职业综合素养，使马克思主义新闻观成为每个新闻工作者的立身之本、从业之基、成长之魂。而在完善服务保障方面，当前新闻媒体在市场化浪潮冲击下逐利趋向日益强烈，采编人员的待遇、收入结构、社会地位都发生了明显变化，特别是在当前媒体经营收入不断下滑的情况下，更加使得媒体和从业人员的驱利行为日趋严重。“只有通过合理的绩效评价体系，确保稳定的收入和较高的社会地位，才能让新闻工作者不至于‘流汗又流泪’，真正工作得有尊严、有担当。”方立明补充说。 </p> \n<p style=\"text-indent:2em;\"> 而张玉玲更是强调，主管部门通报违法违规案件，是外部的监督和处理，只能治标。而只有把真实内化为每个新闻记者的自觉行为，才是杜绝虚假新闻的治本之策。因此，对新闻记者的教育培训应贯穿职业生涯全过程，既有新闻记者自我的修身和完善，也有新闻单位的正确引导和激励。不管是传统媒体还是新媒体，都要以自己的公信力作为立身之本，在规章制度和评价机制上要立起“警界线”。 </p> \n<p style=\"text-indent:2em;\"> <strong> 强化“关卡”主体责任 </strong> </p> \n<p style=\"text-indent:2em;\"> <span style=\"font-family:楷体, 楷体_gb2312, simkai;\"> 无论传统媒体还是新媒体，都要严格执行“三审”，减少假新闻存活几率 </span> </p> \n<p style=\"text-indent:2em;\"> 在打击假新闻的时候，应该追根溯源，进而寻求对策。 </p> \n<p style=\"text-indent:2em;\"> 《黑龙江日报》全媒体编辑出版中心副主任王巍强调，治假还要铲在根上。一方面，要从源头上保真。在自媒体时代，人人都是“发声筒”，那些习惯从“网曝”中寻找新闻的媒体“一不小心”便会沦为假新闻的帮凶。本次通报的假新闻“李玉刚在台北出家”便是首发于微博，多家媒体未经核实转发。媒体规范记者采访行为并非空话、套话，记者必须实地采访、多方求证，对网传信息特别是自媒体信息要反复核实。 </p> \n<p style=\"text-indent:2em;\"> 另一方面，要从流程上保真。王巍认为，在媒体融合阶段，各媒体都在做大做强自己的新媒体产品，“两微一端+网站”已成为标配。然而，传统媒体的出版流程在新媒体上体现度并不高，很多新兴媒体片面追求时效，往往减少甚至省去“三审”，这就为假新闻提供了温床。因此，严格执行“三审”可以较大限度地减少假新闻的存活几率，强化现有流程中各“关卡”负责人的主体责任，力保对稿件客观性的判断，避免稿件基本新闻事实出现偏差。 </p> \n<p style=\"text-indent:2em;\"> 当然，要杜绝虚假新闻和失实报道，必须坚持自律和他律相结合的原则。在方立明看来，严格自律、严守他律，二者的统一结合才能达到最佳教育效果，才能日趋高尚，向上向善。“学界、业界及媒体管理单位，要健全、完善有效的监督机制，强化监管力度，以刚性的规矩增强新闻工作者的戒惧意识和底线意识。” </p> \n<p style=\"text-indent:2em;\"> 对此，王巍也表示认同。他认为，斩断假新闻上升的通途要靠制度上的保证，首先要实施“一票否决制”，提高“造假成本”，实施无上限处罚，对触犯法律者不护短，让造假者血本无归，望而生畏；其次，建立“新闻监审制”，由经验丰富的资深媒体人担任，对记者稿件进行逐一“查验”，对问题稿件及时纠正，记录在案进而惩戒追责；此外，强化“岗位培训制”，将流于形式的媒体自律和行业准则真正落到实处，加强日常培训，天天讲，时时讲，让警钟不绝于耳。如此合力，方能铲除假新闻“毒根”。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：李雪昆 梁晓龙 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月13日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 实施“一票否决” 让造假者血本无归','2015-12-16 17:37:19','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/266211.shtml',NULL,NULL,NULL,NULL,NULL),(60,'国家新闻出版广电总局 - 行业信息 - 华文联盟电子商务平台READONE上线 中国书刊走出去  有了全球性平台','华文联盟电子商务平台READONE上线 中国书刊走出去 有了全球性平台','2015年10月15日','朱侠  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 德国法兰克福时间10月14日下午，由中国国际图书贸易集团有限公司开发的面向全球读者销售华文书刊的跨境电商平台——华文联盟电子商务平台READONE，在第67届法兰克福国际书展上上线试运营。 <br> </p> \n<p style=\"text-indent:2em;\"> 该平台此前已在英、法两国进行了运行测试，效果良好。这次在法兰克福书展上线试运营后，该平台将面向20余家受邀商户定向开放。2016年4月，该平台将在伦敦书展上面向全球开放注册与招商工作。READONE将聚合世界各地经营华文书刊的书店和经销商，为其提供包括数据、供应链和技术在内的完整解决方案，帮助海外商户开辟中国出版物网上贸易新通道，为海外读者提供智能化的购书体验。 </p> \n<p style=\"text-indent:2em;\"> 国图集团公司副总经理夏战生介绍，随着出版发行业的转型升级和信息技术的飞速发展，海外以销售中国出版物为主的华文书店经营普遍遇到困难。为帮助海外商户开辟中国出版物网上贸易新通道，向世界输送更多优质中国书刊，打造中国文化走出去的新力量，国图集团公司利用大数据重构国际化线上销售的商业模式，历时3年，打造了华文联盟电子商务平台READONE。 </p> \n<p style=\"text-indent:2em;\"> 国图集团公司已与世界各地数据商、中盘商广泛合作，建立了高效的READONE出版物数据中心（PDC），目前已拥有超过200万种华文书刊数据、1300万种外文书刊数据，所有数据向平台商户免费开放。平台帮助商户开展预订业务，让商户销售的商品不再局限于自有库存，同时帮助商户扩大全球市场空间，使每个个体商户不再局限于本地销售。另外，READONE也支持商户多店铺运营，目前正在积极与包括亚马逊在内的多家第三方平台建立互通，未来将可以实现登录READONE一个账号，同步管理多个网店。 </p> \n<p style=\"text-indent:2em;\"> READONE和美国亚马逊等电商平台有何不同？夏战生表示：“亚马逊的图书针对的是大众市场，而READONE瞄准的是华文书刊这个小众市场，因为中国图书在海外是小众市场。” </p> \n<p style=\"text-indent:2em;\"> 针对商户关心的物流问题，夏战生说，书刊运输和库存成本一直是制约中国书刊走出去的重要因素，READONE改善了供应链环境，后端对接了支持全球发货的供应商，为海外商户提供了到户发货与集散发货两种方式，帮助商户最大程度降低物流成本和库存压力，提高了书刊的有效周转，为中国书刊海外销售规模的扩大创造了有利条件。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：朱侠 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月15日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 华文联盟电子商务平台READONE上线 中国书刊走出去 有了全球性平台','2015-12-16 17:37:20','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/266386.shtml',NULL,NULL,NULL,NULL,NULL),(61,'国家新闻出版广电总局 - 行业信息 - 北京17家重点网站承诺杜绝新闻“标题党”','北京17家重点网站承诺杜绝新闻“标题党”','2015年10月16日','柳杰  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 10月13日，北京市网信办、首都互联网协会组织北京地区17家重点网站召开“网络新闻报道标题规范及要求”评议会。此次评议会针对目前网络新闻报道中片面追求所谓“精彩”的“标题党”乱象提出了批评，并明确了网络新闻报道中新闻标题的使用规范和要求。 <br> </p> \n<p style=\"text-indent:2em;\"> 会上，新浪网、搜狐网、网易网、凤凰网等网站有关负责人分别就互联网标题乱象治理、网站采取的措施等进行了交流。 </p> \n<p style=\"text-indent:2em;\"> 网易网相关负责人在发言中说，网易针对自身在新闻标题上存在的问题进行认真梳理和反思，在不断完善新闻编发机制的同时，对编辑实行以内容为导向的新型考核制度。搜狐网相关负责人表示，将继续完善内部“新闻采编规范手册”，继续坚持“真实、简洁、精彩、独到、守法”的新闻理念，杜绝篡改标题现象。新浪网客户端负责人发言说，新浪网正组织编辑加强业务能力学习培训，提高新闻报道水平。凤凰网相关负责人表示，坚决支持治理标题乱象，并将恪守以事实为依据，不以偏概全，不恶意篡改标题行为准则，共同杜绝“标题党”。 </p> \n<p style=\"text-indent:2em;\"> 首都互联网协会名誉会长闵大洪指出，各网站在登载相关信息需更改标题时，必须做到准确表达原意，不得曲解主题、断章取义、以偏概全。各网站要充分认识到，改后的标题不改变文章原意既是对原作者的尊重，也是法理的基础性要求。 </p> \n<p style=\"text-indent:2em;\"> 中国传媒大学教授詹新惠认为，网站编辑在制作新闻标题时，要充分、仔细、系统考虑每个关键语句的上下文语境。当选取原文某一句话作为新闻标题时，应考虑是否可以充分表达文章原意，是否改变了原文的主要观点等。 </p> \n<p style=\"text-indent:2em;\"> 会议强调，杜绝“标题党”现象，各网站要建立严格的新闻规范，制定严谨的操作流程，并加强对一线编辑人员的培训和管理。会上，各网站负责人做出承诺，将不断加强对从业人员的培训和管理，同时把责任落实到人，杜绝“标题党”现象的发生。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：柳杰 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月16日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 北京17家重点网站承诺杜绝新闻“标题党”','2015-12-16 17:37:21','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/266558.shtml',NULL,NULL,NULL,NULL,NULL),(62,'国家新闻出版广电总局 - 行业信息 - 南京新华书店：打造多元业态文化大平台','南京新华书店：打造多元业态文化大平台','2015年10月9日','魏芳  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 9月28日，南京新华书店新街口店迎来自己华丽升级一周年的日子。回望这一年，南京新华书店有限责任公司总经理陈建国在接受记者采访时说，一年间，新装亮相的新街口店让南京新华书店从经营业态到赢利模式都发生了翻天覆地的变化，实现了从一个体量较大的传统书店转型成以全民阅读为引领、多元文化业态为特色的文化平台。 <br> </p> \n<p style=\"text-indent:2em;\"> <strong> 发挥优势搭建文化平台 </strong> </p> \n<p style=\"text-indent:2em;\"> 南京新华书店有限责任公司市场运营部主任谢光锋告诉记者，新街口店借助升级改造，完成了45个多元业态招商项目，其中，有11个项目为首次引进实体书店。这些项目都具有创新性强、体验度高、互动性强的特点，希望强化实体书店的体验感，与网络书店错位竞争，用创意方式来盘活文化空间。 </p> \n<p style=\"text-indent:2em;\"> 谢光锋坦言，过去书店办活动无非就是配合出版社的签售、发布之类的，由于场地有限，每次活动还要临时腾出地方，现在新街口店装修之后，不再把书装得满满当当，每层都有专门举办活动的场地，甚至已经形成南京的文化品牌。据他介绍，每周五晚7点新华书店“书香南京晚七点”开讲，这个活动邀请人文历史方面的专家学者，说南京的历史文化故事，吸引了不少外地来南京上学的学生以及青少年参加，在今年抗战胜利纪念日前后，还邀请了南京大屠杀纪念馆馆长朱成山等与抗战相关的文化学者讲座。每周日上午10点在同一场地“凤凰文化早市”开市，针对中老年人关注的养生保健方面的知识开展活动。“一早一晚的讲座已经成为南京新华书店的文化品牌，嘉宾也乐于与读者交流分享，通常分文不收，我们会在讲座周围摆放相关图书，与图书销售联动。”谢光锋说。 </p> \n<p style=\"text-indent:2em;\"> <strong> 展示图书不如推送内容 </strong> </p> \n<p style=\"text-indent:2em;\"> 在陈建国看来，网络时代，实体书店要学习如何向读者推送内容，改变书店过去单向、被动的卖书者姿态，从读者的阅读需求出发，从线上和线下为读者推送适合他们的图书。 </p> \n<p style=\"text-indent:2em;\"> 谢光锋介绍，2014年南京新华书店创造了日发货1200单、综合让利20.7%的纪录。同时，着重在线上线下互动推广上发力，继教育书店上线后，又扩容少儿书店、外文书店、古旧书店上线，累计实现销售831万元，增幅达51%，保持了强劲的发展势头。南京新华书店还积极利用“互联网+”的思路，利用公司微信公众号等自媒体及“南京发布”“龙虎网”等新媒体广泛“吸粉”，取得良好成效。与“腾讯微生活”合作，推出微信会员卡平台，电子会员购书增长迅猛。除此之外，在线下还与中国联通签订战略合作协议，将新街口旗舰店打造成更具消费体验感的O2O书店，为广大读者提供更好的服务体验，与亚马逊（中国）签订Kindle电子书江苏总代理协议，销售电子书2800台，打开了新的市场通道。 </p> \n<p style=\"text-indent:2em;\"> <strong> 脑洞大开活动拉近用户 </strong> </p> \n<p style=\"text-indent:2em;\"> 去年7月，南京新华书店与团市委联合举办汉字听写公益挑战大赛，大赛根据小学生课表要求出题，以大百科全书出版社出版的《汉字应用规范字典》为参考，全市有数百家小学报名参赛。今年10月有“我爱南京”中学生英语达人大赛，刚刚结束的少年中国说演讲比赛等，不仅用活动带动图书销售，更重要的是让阅读、进书店成为青少年一项主动追求的时尚。 </p> \n<p style=\"text-indent:2em;\"> 新装修的新街口店还将在六层设置微电影厅，每周六举办“微影大义”微电影分享沙龙，为一些民间读书会提供活动支持，开展“大学生读剧月”活动，给南京大学生话剧社团提供展示的舞台，除此之外还有以中小学生为服务对象的“书魔方”、以大中专院校为主体的“凤凰部落”阅读活动。 </p> \n<p style=\"text-indent:2em;\"> 谢光锋说，对南京新华书店来说，今后平台的思路还会继续拓宽，服务的内容也要继续延伸。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：魏芳 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月9日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 南京新华书店：打造多元业态文化大平台','2015-12-16 17:37:22','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/266018.shtml',NULL,NULL,NULL,NULL,NULL),(63,'国家新闻出版广电总局 - 行业信息 - 用更多点击量吸引更多广告','中国新闻代表团与美国同行共话转型后的新媒体如何赢利 用更多点击量吸引更多广告','2015年10月13日','易博文 张业亮  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 传统媒体如何应对互联网发展带来的冲击，新媒体怎样实现赢利？美国媒体是怎么做的，有没有一些经验值得借鉴？作为中美记者交流项目，中国新闻代表团一行6人近日访问美国，先后参访了《赫芬顿邮报》、美国全国广播公司、美国公共广播电台、《华盛顿邮报》等美国主流媒体，与美国同行交流了这一共同关心的话题。 </p> \n<p style=\"text-indent:2em;\"> 近年来，互联网尤其是移动互联网对传统媒体冲击很大，转型后的新媒体如何赢利，美国媒体也在艰难探索中。 </p> \n<p style=\"text-indent:2em;\"> 《华盛顿邮报》是以时政报道著称的纸媒，在大部分报纸瘦身图存的情况下，由于美国最大的电子商务网店亚马逊的收购，这几年逆势增长，投资增加，发行增加，记者由500名增加到600名。2013年，该报成立新媒体部，发展很快。今年8月，网站点击量平均每天达到5450万次，与7月相比增长16%。尽管如此，《华盛顿邮报》记者帕特克坦言，死守原有的“发行+广告”的模式，纸媒的前途将黯淡无光。他表示，《华盛顿邮报》的网站还未实现赢利，不引入外部互联网公司的力量，只是依靠纸媒自身建设网站，开设社交媒体账号，也于事无补。 </p> \n<p style=\"text-indent:2em;\"> 美国全国广播公司也在3年前设立了新媒体部，共有10余名编辑。3年来，这个广播公司仍旧是传统媒体赢利支持新媒体的发展。美国全国广播公司新媒体部制片人瑞贝卡表示，如何利用搜索引擎抢占头条消息，不仅是美国传统媒体竞争的重点，也是传统媒体与新媒体之间的竞争重点。传统媒体记者要学会写“吸引人的”新闻标题，让报道更易在网络上被搜索到。 </p> \n<p style=\"text-indent:2em;\"> 《赫芬顿邮报》是在线媒体，走的是免费阅读新闻内容的路子，并发展出一种“新闻视频+广告”的赢利模式。读者在网站上看到一条吸引眼球的文字新闻后，如果想要了解现场的情况，打开一个与此相关的新闻视频就可以了。但是，观看这段视频新闻之前，会有一段时长30秒的强制广告。这样的广告模式受到商家欢迎，也为《赫芬顿邮报》带来不错的广告收益，获得了丰厚的利润。 </p> \n<p style=\"text-indent:2em;\"> 而大多数美国媒体走的则是收费阅读的路子。美国全国广播公司开发出一种叫做“闪回”的产品，新媒体部的人员把珍贵的历史音视频资料剪辑制作为在线播出的产品，用户有需要，就必须实名注册后付费点播，但不得下载。参观时，一名中国代表团记者当即提出想搜一下尼克松总统访华的视频报道，新媒体部工作人员先搜了一下年份，再搜了一下类别，1分钟之内就调出了想找的视频。 </p> \n<p style=\"text-indent:2em;\"> 不管是哪一种赢利方法，有一点都值得一提：尽量扩大互联网、移动互联网的覆盖面。拥有了足够多的受众，就会带来更多的点击量，因而将吸引更多的广告投放。 </p> \n<p style=\"text-indent:2em;\"> 中美记者交流项目始于2010年，由中国记协、美国东西方中心和香港明天更好基金会联合举办，每年一届，迄今已举办六届。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：易博文 张业亮 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月13日 </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 用更多点击量吸引更多广告','2015-12-16 17:37:22','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/266209.shtml',NULL,NULL,NULL,NULL,NULL),(64,'国家新闻出版广电总局 - 行业信息 - 《习近平关于协调推进“四个全面”战略布局论述摘编》出版发行','《习近平关于协调推进“四个全面”战略布局论述摘编》出版发行','2015年10月13日','','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 由中共中央文献研究室编辑的《习近平关于协调推进“四个全面”战略布局论述摘编》一书，近日由中央文献出版社出版，在全国发行。 <br> </p> \n<p style=\"text-indent:2em;\"> 党的十八大以来，以习近平同志为总书记的党中央从坚持和发展中国特色社会主义全局出发，提出并形成了全面建成小康社会、全面深化改革、全面依法治国、全面从严治党的战略布局，确立了新形势下党和国家工作的战略目标和战略举措，为实现“两个一百年”奋斗目标、实现中华民族伟大复兴的中国梦提供了理论指导和实践指南。习近平同志指出，“四个全面”战略布局是中国在新的历史条件下治国理政方略。这个战略布局，既有战略目标，也有战略举措，每一个“全面”都具有重大战略意义。全面建成小康社会是我们的战略目标，我们所有奋斗都要聚焦于这个目标。全面深化改革、全面依法治国、全面从严治党是三大战略举措，对实现全面建成小康社会战略目标一个都不能缺，要努力做到“四个全面”相辅相成、相互促进、相得益彰。“四个全面”战略布局是从我国发展现实需要中得出来的，从人民群众的热切期待中得出来的，也是为推动解决我们面临的突出矛盾和问题提出来的。习近平同志强调，要教育引导各级领导干部自觉用“四个全面”战略布局统一思想、引领工作。协调推进“四个全面”战略布局，要求全党同志以与时俱进、奋发有为的精神状态，不断推进实践创新和理论创新，继续书写马克思主义中国化、时代化新篇章。认真学习这些重要论述，对于深入理解“四个全面”战略布局的重大意义、系统把握“四个全面”战略布局的科学内涵和总体要求、把“四个全面”战略布局落到实处，具有十分重要的指导意义。 </p> \n<p style=\"text-indent:2em;\"> 《习近平关于协调推进“四个全面”战略布局论述摘编》，共分6个专题，收入287段论述，摘自习近平同志2012年11月15日至2015年9月3日期间的讲话、报告、批示、指示等110多篇重要文献。其中部分论述是第一次公开发表。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 来源：新华网 &nbsp;发布时间：2015年10月13日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','新华网  ','国家新闻出版广电总局 - 行业信息 - 《习近平关于协调推进“四个全面”战略布局论述摘编》出版发行','2015-12-16 17:37:22','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/266309.shtml',NULL,NULL,NULL,NULL,NULL),(65,'国家新闻出版广电总局 - 行业信息 - 十八届五中全会文件将加大网络发行力度','十八届五中全会文件将加大网络发行力度','2015年10月15日','朱侠  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 10月13日至14日，由人民出版社主办、党建读物出版社和外文出版社协办的《第四批全国干部学习培训教材》《习近平谈治国理政》发行表彰会暨党的十八届五中全会重要文件发行工作会议在京召开。会议对来自全国的优秀发行单位分别进行了表彰和颁奖，并对即将召开的十八届五中全会文件及相关读物的出版发行和宣传工作进行部署。 <br> </p> \n<p style=\"text-indent:2em;\"> 记者从会上了解到，截至目前，《习近平总书记系列重要讲话读本》总计已发行1500多万册、十八届四中全会文件及相关读物发行540多万册、《第四批全国干部学习培训教材》发行近130万套、《习近平谈治国理政》多语种整体累计发行522万余册、《邓小平文集》发行8万多套、《法治热点面对面——理论热点面对面·2015》发行347万册。 </p> \n<p style=\"text-indent:2em;\"> 党的十八届五中全会将于10月26日至29日在北京召开。对此，人民出版社社长黄书元说，当前及今后一段时间，发行好、宣传好十八届五中全会公报、文件、辅导读本、文件汇编是人民出版社的首要政治任务。他表示：“我们一定要高度重视，科学谋划，周密安排，总结借鉴以往发行全会文件及相关读物的好经验、好做法，创新发行理念，拓宽发行渠道，加大数字、网络发行力度，力争本次全会文件及相关读物发行工作取得更大的突破，为全国各族干部群众学习十八届五中全会文件精神及时提供读本。” </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：朱侠 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月15日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 十八届五中全会文件将加大网络发行力度','2015-12-16 17:37:23','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/266388.shtml',NULL,NULL,NULL,NULL,NULL),(66,'国家新闻出版广电总局 - 行业信息 - 人民日报社新媒体中心成立','人民日报社新媒体中心成立','2015年10月9日','吴重生  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 10月8日下午，人民日报客户端三期上线暨人民日报社新媒体中心成立仪式在人民网演播大厅举行，人民日报社社长杨振武出席仪式并讲话。 <br> </p> \n<p style=\"text-indent:2em;\"> 据悉，此次新上线的客户端三期，在优化内容呈现的同时，把提供政务服务和生活服务作为一大亮点，使《人民日报》向移动互联网的入口和平台迈出了关键性一步。 </p> \n<p style=\"text-indent:2em;\"> 杨振武说，多年来，人民日报社热情拥抱互联网，积极运用互联网，已基本建成覆盖桌面互联网、移动互联网、社交媒体、电子屏等各类终端的现代传播体系，由一份报纸发展成为全媒体形态的“人民媒体方阵”，用户总计已扩展到3亿。 </p> \n<p style=\"text-indent:2em;\"> 杨振武表示，以人民日报社新媒体中心成立为标志，人民日报社将在全力支持办好人民网的同时，切实加快各类新媒体发展，努力形成以人民日报客户端为核心的移动传播新格局。在内容生产上，牢牢把握正确导向，着力创新传播方式；在产品体验上，始终坚持技术驱动，不断优化产品形态；在用户拓展上，充分调动各类资源，努力实现用户量级的新跨越；在运行机制上，积极探索适应融合发展的新机制，提升《人民日报》新媒体的整体影响力。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：吴重生 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月9日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 人民日报社新媒体中心成立','2015-12-16 17:37:23','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/266020.shtml',NULL,NULL,NULL,NULL,NULL),(67,'国家新闻出版广电总局 - 行业信息 - 专家纵论国学出版传承与创新','专家纵论国学出版传承与创新','2015年10月19日','范燕莹  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 首届孔学堂·国学图书博览会期间，博览会系列论坛之“国学出版的现状与未来高端对话”邀请两岸三地的业界专家、学者纵论国学出版的传承与创新。 <br> </p> \n<p style=\"text-indent:2em;\"> 中国出版集团党组书记、副总裁王涛，台北故宫博物院原院长周功鑫，中国新闻出版传媒集团总经理李忠，中国出版协会古籍工作委员会副主任兼秘书长姜小青围绕国学出版、阅读普及等议题发表主题演讲。 </p> \n<p style=\"text-indent:2em;\"> 王涛认为，当下的国学、传统文化的出版拥有非常好的外部环境和难得的发展机遇，但国学出版的人才队伍存在青黄不接的问题。另外，重复出版严重。他提出，国学出版既要着眼于当下，又要面向未来。周功鑫认为，国学出版应与突飞猛进的科技发展相衔接，呈现文化的延展与广泛应用，让青少年在移动平台也能接触和学习到国学。在李忠看来，“酒香更怕巷子深”，国学出版还需要大力推广。他建议业界在中小学多举办经典诵读活动，同时针对不同领域和人群开展推广活动，再通过全媒体方式在全国范围放大传播。从“有意思”到“有意义”，当国学的阅读推广与文化传播变得越来越有趣，国学出版的意义就会不断延展放大，出版人的敬业和努力也会得到应有的价值反馈。面对未来的古籍出版，姜小青提出，坚持专业发展的基本路径，坚持数字化的发展方向。 </p> \n<p style=\"text-indent:2em;\"> 在高端对话环节，两岸三地出版人、专家学者就“国学出版的传承与创新、固守与开拓”的话题进行了探讨。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：范燕莹 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月19日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 专家纵论国学出版传承与创新','2015-12-16 17:37:25','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/266688.shtml',NULL,NULL,NULL,NULL,NULL),(68,'国家新闻出版广电总局 - 行业信息 - “犹太难民与上海”出版工程全球首发','“犹太难民与上海”出版工程全球首发','2015年10月19日','刘蓓蓓  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 在刚刚落幕的法兰克福国际书展上，上海交通大学出版社举行了中国代表团重要活动——“犹太难民与上海”出版工程全球首发式，以纪念世界反法西斯战争胜利70周年。中国出版协会常务副理事长邬书林出席首发式并讲话。 <br> </p> \n<p style=\"text-indent:2em;\"> 据介绍，上海交通大学出版社联合上海社会科学院上海犹太研究中心开展了“犹太难民与上海”资料征集、采访回顾及图书编撰工作，这套丛书是首批成果。丛书对当年的亲历者进行了采访，其中耳熟能详的人物有美国前财政部长布卢门撒尔、犹太医生罗生特，还包括当年上海媒体上刊登的相关报道，也有一些现在犹太人主办的“黄包车”网站上网友的回忆文章，另有一卷是犹太人的生活绘本。丛书70%以上的内容是首次面世，对于犹太难民及国际关系研究具有重要意义。 </p> \n<p style=\"text-indent:2em;\"> 邬书林说，“犹太难民与上海”出版工程具有重要的现实意义。这套丛书生动记录了犹太难民与中国人民融洽相处的点点滴滴，体现了上海市民的友善与宽容。他希望上海交通大学出版社能够出版更多好书，讲好中国故事，使世界各国人民在互相理解的基础上共同创造美好的生活。 </p> \n<p style=\"text-indent:2em;\"> 中国驻法兰克福总领事梁建全说，“犹太难民与上海”出版工程真实反映了法西斯给中国人民和世界各国人民带来的创伤与苦难，向世界展示了中国人民对和平的向往和救人于危难的高尚民族品格。 </p> \n<p style=\"text-indent:2em;\"> 荷兰博睿出版社全球副总裁皮特·科伯格表示，博睿愿意与上海交大出版社、上海犹太研究中心等联合打造“犹太难民与上海”出版工程，包括英文、希伯来文等文种的出版工作，向全球读者全面介绍这段历史。 </p> \n<p style=\"text-indent:2em;\"> 中外出版界人士和当地读者近百人参加了活动。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：刘蓓蓓 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月19日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - “犹太难民与上海”出版工程全球首发','2015-12-16 17:37:25','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/266685.shtml',NULL,NULL,NULL,NULL,NULL),(69,'国家新闻出版广电总局 - 行业信息 - 社会效益放首位 双效统一得实惠','《关于推动国有文化企业把社会效益放在首位、实现社会效益和经济效益相统一的指导意见》印发以来，业界对于两个效益相统一的经验交流不断升温 社会效益放首位 双效统一得实惠','2015年10月13日','李雪昆 梁晓龙  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 正如《关于推动国有文化企业把社会效益放在首位、实现社会效益和经济效益相统一的指导意见》中所述，在一批国有文化企业迅速成长，有力促进文化产业发展和文化市场繁荣的同时，我们也应看到，还有一些国有文化企业改革没有到位，两个效益相统一的问题还没有很好地解决，两个效益相统一的环境条件需要进一步优化。因此，国有文化企业两个效益相统一的整体提升还将有一段路要走，这一过程中不但需要业界理清思路，更需要沉下心来进行科学的谋划。日前，记者采访了行业内在这方面有突出表现的文化企业，在分享他们各自经验、体会的同时，也为文化企业谋划下一步发展提供更多参考。 </p> \n<p style=\"text-indent:2em;\"> <strong> 谈效果：良好社会效益打开新市场 </strong> </p> \n<p style=\"text-indent:2em;\"> 国有文化企业是发展文化产业、建设社会主义先进文化的重要力量，因此，在推动两个效益相统一中更应走在前列。对此，采访中，各方均表示，实践证明唯有把社会效益放在首位，才能让社会效益、经济效益协调发展，也才能在发展中打造文化创新主体，提升文化软实力。 </p> \n<p style=\"text-indent:2em;\"> “集团各精品项目取得良好社会效益的同时，更为我们打开了新的市场。”黑龙江出版集团董事长李久军说，集团成立5年来有80余种图书获国家级奖励、入选国家级重点项目，其中不乏双效俱佳的优秀产品。比如围绕弘扬张丽莉英雄事迹推出了《爱铸师魂》学习读本、与俄罗斯出版企业合作出版了《和平丰碑》大型画册等。 </p> \n<p style=\"text-indent:2em;\"> 中南出版传媒集团股份有限公司董事长龚曙光谈道，坚持两个效益相统一应是文化企业必须遵循的铁律。他介绍，该集团不断强化思想认识、优化制度保障、细化考核体系、完善评价体系，将两个效益相统一在绩效考核中进行实化、细化，进而起到激励和引导作用。也正是在这样的认识和实践下，中南传媒连续5年保持了20%以上的高速增长。 </p> \n<p style=\"text-indent:2em;\"> 《国家命运》《中国往事》《推拿》《雪豹》《听风者》等许多影视作品大家都不陌生，他们均出自全国文化企业30强之一的华策影视。采访中，华策影视品牌宣传总监王颖说，引导媒体和观众对有思想价值、有艺术追求、有鲜明人物和故事的精品力作的需求，看似市场效益存在风险，而事实却不是这样。单集制作费过百万元的《中国往事》就是很好的例证，而正是社会效益放首位、两个效益双丰收的制作原则奠定了该公司生存发展的良好基础。 </p> \n<p style=\"text-indent:2em;\"> <strong> 聊体会：双效统一让企业永保生命力 </strong> </p> \n<p style=\"text-indent:2em;\"> 其实，社会效益与经济效益相统一的发展一直伴随着文化产业发展，但多年来，有些企业成功了，有些企业却失败了。对此，龚曙光在谈及体会时表示，文化企业有时也会感到困惑，比如转型投入的问题。投入发展数字出版，当然是出版转型和赢得未来的关键所在，但短期内看不到收益，带有风投的性质，投了影响当前，不投影响未来。 </p> \n<p style=\"text-indent:2em;\"> “虽然社会效益看似无数据可循，没有固定的考核标准，而经济效益则可以直接体现在指标上，简单明确，但文化企业如果过于追求经济效益，则会偏离航线、迷失方向。”李久军说。 </p> \n<p style=\"text-indent:2em;\"> “做有价值的事，挣有价值的钱，这两者并不矛盾。有些企业没有成功，或者是没有很好地进行战略规划，或者是没有持之以恒地执行和贯彻。如果把我们的事业当成公益，肯定做不长久，商业化是必须坚持的一个定义，但我们在创造经济效益的同时无疑要把创造社会效益放在首位。因为坚持以百姓为中心的创作导向，是企业永保生命力的法宝，坚持全球化发展策略，传播中华优秀文化，是企业广阔的未来。”王颖这样认为。 </p> \n<p style=\"text-indent:2em;\"> <strong> 话经验：抓双效提升国际运营能力 </strong> </p> \n<p style=\"text-indent:2em;\"> 把社会效益放在首位、实现两个效益相统一无疑是新形势下提升文化软实力、参与国际文化竞争、维护国家文化安全的必然选择。谈及国有文化企业在推动中华文化走出去的过程中如何实现两个效益相统一时，龚曙光认为，一方面要加快海外并购股权合作的步伐，打造全球专业类出版航母，培育国际产品群；另一方面，要推进全球数字化出版平台，全方位实施走出去发展战略，全面提升“全流程、多介质”的国际化运营能力。 </p> \n<p style=\"text-indent:2em;\"> 王颖则认为，纵观全球市场，国际一流的文化企业可以给业界很多很好的借鉴，比如迪斯尼、华纳，他们的唐老鸭、米老鼠、《玩具总动员》、《海底总动员》、《狮子王》等优秀作品都体现了真善美，才得以行销世界。可以说，两个效益相统一是打造全球一流文化企业的基石，是优秀文艺作品创作和文化企业经营中必须遵守的内在规律。这其中，建立系统的项目考核机制十分必要，针对不同题材作品实施不同的考核。如重大题材类、主旋律剧方面，不仅以收视率利润率考核，还要综合观众口碑和播出平台，并以获奖情况作为考核奖励标准。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：李雪昆 梁晓龙 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月13日 </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 社会效益放首位 双效统一得实惠','2015-12-16 17:37:28','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/266210.shtml',NULL,NULL,NULL,NULL,NULL),(70,'国家新闻出版广电总局 - 行业信息 - 《习近平用典》亮相法兰克福书展','《习近平用典》亮相法兰克福书展','2015年10月15日','冯雪珺 管克江  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 10月14日，法兰克福书展开幕。走进北京图书展台，如同进入北京四合院内的书房。正中一名身着汉服的女子正在表演茶艺，身旁的沉香炉散发着幽香。由人民日报出版社出版的《习近平用典》一书，在这充满中华古韵的氛围中亮相书展。 <br> </p> \n<p style=\"text-indent:2em;\"> 这本书从习近平数百篇讲话和文章中，遴选出使用频率高、影响深远、最能体现其治国理政理念的典故135则，并对这些古典名句的释义和习近平引述所赋予的现实意义加以解读。其古朴素雅的装帧设计，与展台淡雅的风格相互辉映，引得不少参观者驻足翻阅。 </p> \n<p style=\"text-indent:2em;\"> 伦敦城市大学名誉教授、中国文化对外翻译与传播研究中心高级顾问卫克安非常期待尽快读到这本书的英文版。“我熟读过《习近平谈治国理政》，习近平主席的讲话引人入胜。他常常以通俗易懂的语言，加以中国古代哲人的智慧名句，来表达一些深刻的道理，让年轻人也能够非常容易理解。” </p> \n<p style=\"text-indent:2em;\"> 谈到习近平引用过的“功崇惟志，业广惟勤”，卫克安说：“习近平正是这句话的笃行者。机会不会无端落在谁的头上，而是需要为之付出十分的努力。他自己正是一步一步脚踏实地走过来的。”卫克安表示，他每次去中国，都会为中国年轻人丰富的知识而惊叹。“那些普通的年轻人，都能和你随口聊历史、文化和艺术，而且都能表达出自己的见解。”他相信《习近平用典》通过诠释中国古典智慧的古今意义，将给中国年轻人带来更多的智慧。 </p> \n<p style=\"text-indent:2em;\"> 扎赫萝是一名有中国血统的德国中学老师，她对《习近平用典》非常感兴趣。“纵使经过数千年历史沉浮，中国传统文化的根源从未断过。”她表示，典故是中国古代哲学智慧的结晶。“若在讲话中引用典故，一句话还未说完，对方就能够明白你的深意。这正得益于中国人深厚的文化底蕴。”她认为，《习近平用典》借习近平的讲话给古文以现代意义，结合当代中国的发展情况，体现出了深刻的治国智慧。“对古文进行现代解读十分重要。中国的古代文明，正是通过不断被赋予新的意义，才能够流传至今，经久不衰。” </p> \n<p style=\"text-indent:2em;\"> 在德国专售中国语言和文化类图书的奥利佛图书网创办人奥利佛·埃文斯，第一时间把《习近平用典》放到了自己的网站上展示。奥利佛出生在北威州，到中国留学过一段时间后便喜欢上了中国文化。目前，他的图书网经营1000多种中国图书。他告诉本报记者：“《习近平用典》通过广泛阐释典故背景，分析习近平讲话的深刻意义，将历史典故同当代社会有机结合起来，是一本理解当代中国的重要书籍。” </p> \n<p style=\"text-indent:2em;\"> 截至目前，《习近平用典》在中国大陆发行量已突破100万册，繁体中文版已由香港新青年出版社出版，韩文版已与韩国GANA出版社签署出版协议，英文、日文、葡文等版权输出事宜正在洽谈中。 </p> \n<p style=\"text-indent:2em;\"> 荷兰博睿出版社全球副总裁皮特·科伯格翻阅《习近平用典》后向本报记者表示，他很高兴看到越来越多的中国图书走向国际市场。他说，国际图书走入中国和中国图书走向世界的数量都在不断增多，这是真正意义上的双赢。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：冯雪珺 管克江 &nbsp;来源：人民日报 &nbsp;发布时间：2015年10月15日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','人民日报  ','国家新闻出版广电总局 - 行业信息 - 《习近平用典》亮相法兰克福书展','2015-12-16 17:37:28','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/266390.shtml',NULL,NULL,NULL,NULL,NULL),(71,'国家新闻出版广电总局 - 行业信息 - 绿色印刷材料（上海）交易中心揭牌','绿色印刷材料（上海）交易中心揭牌','2015年10月19日','金鑫  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 10月16日，绿色印刷材料（上海）交易中心揭牌暨沪版教材绿色印刷材料服务平台上线。这是上海金山绿色印刷创意示范园区在推进印刷产业绿色、创意转型过程中的又一创举，旨在通过对绿色印刷源头的控制，实现对教材绿色印刷的全程监管。 <br> </p> \n<p style=\"text-indent:2em;\"> 据介绍，交易中心以服务沪版教材绿色印刷材料采购为基础，将利用“互联网+”技术，线上交易、线下展示、质检咨询多渠道商业模式相结合，为绿色印刷的采购提供安全可靠、便捷高效、低价优质的服务。同时，力争在“十三五”期间，对接国际市场的标准和要求，未来引导国际市场的订单导入。目前，第一期面向2016年沪版春季教材印制的21家绿色印刷材料供应商的246款产品，以及26家承担沪版春季教材印制的印刷企业已全部完成网上注册，更多产品和企业也将在交易中心上线交易。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：金鑫 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月19日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 绿色印刷材料（上海）交易中心揭牌','2015-12-16 17:37:29','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/266686.shtml',NULL,NULL,NULL,NULL,NULL),(72,'国家新闻出版广电总局 - 行业信息 - “领导干部学国学全国行”走进贵州','“领导干部学国学全国行”走进贵州','2015年10月19日','范燕莹  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 10月17日，作为首届孔学堂·国学图书博览会的主体活动之一，“领导干部学国学全国行·走进贵州贵阳”启动仪式在贵阳孔学堂阳明大讲堂举行，与会嘉宾围绕“领导干部如何读经典、学国学”的话题，与现场读者分享、互动。 <br> </p> \n<p style=\"text-indent:2em;\"> “领导干部学国学全国行”走进贵阳，是该活动启动以来的第二站，旨在在领导干部队伍中广泛传播普及中国优秀传统文化。 </p> \n<p style=\"text-indent:2em;\"> 在“领导干部学国学高端对话”中，中央社会主义学院党组书记、第一副院长叶小文表示，国学就是中国文化。领导干部学国学有两层意义：一是修身修德才能为官，国学中蕴含着经世致用的大道理；二是在决战决胜阶段，领导干部不能“以其昏昏使人昭昭”，需要提升为官为政的能力。 </p> \n<p style=\"text-indent:2em;\"> “领导干部学国学全国行”活动发起人、中央党校教授王杰认为，各级领导干部应该把学习国学的精髓作为一种人生的必修课，真正让国学的精髓能够在每个领导干部心中生根、开花、结果。这也是他倡导、推动“领导干部学国学全国行”的一个重要原因。中央党校文史部主任、教授冯鹏志所理解的“国学热”是现代化建设的各项成果在文化层面的集中体现，是一种与时俱进的、健康积极的文化热潮，要站在时代的角度，结合践行“三严三实”、推进“四个全面”来理解。 </p> \n<p style=\"text-indent:2em;\"> 中共中央党校出版社一直致力于为领导干部出好书，促进领导干部读书学习。为此，中共中央党校出版社做了两件事情：一是建立中国领导干部读书研究会，二是建立中国领导读书网。对于领导干部如何学国学，中共中央党校出版社社长兼总编辑田国良提出，一是一定要读精品和经典，二是要与西方经典、马克思主义著作融会贯通地读，三是要联系时代联系实践地读。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：范燕莹 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月19日 </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - “领导干部学国学全国行”走进贵州','2015-12-16 17:37:30','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/266689.shtml',NULL,NULL,NULL,NULL,NULL),(73,'国家新闻出版广电总局 - 行业信息 - 《中国抗日战争史简明读本》英文版出版座谈会召开','《中国抗日战争史简明读本》英文版出版座谈会召开','2015年10月22日','杨雅莲  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 10月21日上午，《中国抗日战争史简明读本》英文版出版座谈会在中共中央编译局召开。中央编译局局长贾高建出席座谈会。 <br> </p> \n<p style=\"text-indent:2em;\"> 《中国抗日战争史简明读本》英文版，由中央编译局根据人民出版社出版的《中国抗日战争史简明读本》翻译，中央编译出版社出版发行。中央编译局副局长魏海生在座谈会上表示，《中国抗日战争史简明读本》英文版的出版发行，为中国人民抗日战争暨世界反法西斯战争胜利70周年纪念活动的成功举办，为更好地对外宣传中国抗日战争历史提供了有力的学术支撑和思想舆论支持。 </p> \n<p style=\"text-indent:2em;\"> 与会嘉宾认为，该书的出版发行，为世界各国专家学者研究中国抗日战争提供了文本依据，将会极大地促进并帮助国际社会对中国抗日战争历史的理解和研究。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：杨雅莲 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月22日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 《中国抗日战争史简明读本》英文版出版座谈会召开','2015-12-16 17:37:30','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/267017.shtml',NULL,NULL,NULL,NULL,NULL),(74,'国家新闻出版广电总局 - 行业信息 - 阅文集团联袂上海世纪文睿：从线上体验到线下出版','阅文集团联袂上海世纪文睿：从线上体验到线下出版','2015年10月16日','李婧璇  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 日前，阅文集团与上海世纪文睿举行战略合作仪式。双方将就数字阅读内容形态创新、线下出版模式探索、全新运营推广等领域深入合作，建立一个良性的优秀作家孵化机制，创作与分享更多优质内容，以此共同推动数字阅读产业的发展繁荣。针对此次双方联袂合作，记者进行了采访。 <br> </p> \n<p style=\"text-indent:2em;\"> <strong> 联合游戏、出版、文学强势资源—— </strong> </p> \n<p style=\"text-indent:2em;\"> <strong> 发掘优秀作者和作品 </strong> </p> \n<p style=\"text-indent:2em;\"> 对于此次战略合作，上海世纪文睿副总经理、副总编辑林岚在接受记者采访时表示，这是配合腾讯今年重点游戏《上古世纪》的推出，而进行的以《上古世纪》的时代背景、框架为基础的全球征文，并在此基础上进行的冠亚军作品的线下出版推广项目，“不仅包括《上古世纪》IP内容的深化合作，进行征文优秀作品的实体图书出版与推广，还将联手对获奖作家进行全方位的包装。” </p> \n<p style=\"text-indent:2em;\"> 据了解，此前阅文集团旗下起点中文网、创世中文网联合上海世纪文睿与《上古世纪》，共同发布了《上古世纪》全球征文大赛。《上古世纪》为2015年腾讯力推的大作，基于半沙盒化高自由度设定，玩家可以在浩瀚的无缝世界中“解禁想象，逃离无趣”。阅文集团高级副总裁、总编辑林庭锋表示，和《上古世纪》合作展开征文活动，除了看中游戏不断提升的人气以外，最大的原因还是游戏的品质及其与文学的契合度。而与世纪文睿的战略合作，是希望联合游戏、出版、文学三方的强势资源，发掘出更多的优秀作者和作品。 </p> \n<p style=\"text-indent:2em;\"> 对于此次战略合作，林岚充满期待：“它可以说是传统出版主动介入线上游戏的一个项目，它不是一个单纯的版权授权出版，我们的深度合作体现在从源头开始的介入，即在征文还未开始之前就进行了共同磋商，对征文的形式、内容、取向及评选标准等都做了共同的商定，以确保最后的作品能够符合出版的要求。同时，还规划了整个项目的时间节点、各方参与的工作、以及数字版和纸质版图书的上线与出版的配合等。运营推广则贯穿整个项目的始终。” </p> \n<p style=\"text-indent:2em;\"> 林岚认为，此次合作意味着数字化阅读已从线上体验延伸到线下出版，开启了阅读的全新想象。 </p> \n<p style=\"text-indent:2em;\"> <strong> 线上征文 线下运营—— </strong> </p> \n<p style=\"text-indent:2em;\"> <strong> 创新作家孵化机制 </strong> </p> \n<p style=\"text-indent:2em;\"> 值得注意的是，阅文集团与世纪文睿合作，对于网络文学产业而言，也是个非常重要的信号。在以往的网络文学发展过程中，如何系统地挖掘和培养明星作家成为制约内容发展的一大问题。通过线上征文和线下运营合作，正是为了吸引人才，一方面能直接挖掘有潜力的新人，另一方面也能系统化明星作家的运作模式。 </p> \n<p style=\"text-indent:2em;\"> 据林岚介绍，本次长篇征文的冠亚军获得者李达和E伯爵的作品《第十三个骑士》和《猩红帆》已正式列入出版计划，“从目前开始，我们就将在线上对这两位获奖者及其作品进行宣传。腾讯将通过游戏官网、旗下的各种文学网站进行得奖公布，而后还有电子书的先期跟进；等到实体书出版后我们还会召开正式的新闻发布会，从线上到线下进行同步推广。”同时，这两部作品的影视版权也已在洽谈中。林岚表示，未来世纪文睿还将和腾讯密切合作，随着游戏发布的推进来做更多的互动、乃至植入。 </p> \n<p style=\"text-indent:2em;\"> 此次战略合作，阅文集团将以重点IP为切入，联动各大合作伙伴，从游戏、文学、动漫等方面进行系列整合，深度挖掘IP价值，并以此与线下合作公司建立全新的作家孵化机制。对此，林岚表示，网络作家多半是自生自灭型，能生存下来并进入“大神”级别的多数基于网民的喜好与选择，这种机制很可能将一些小众但有特色的写作者淹没掉；而传统作家的诞生则存在更多的偶然以及基于编辑的喜好与眼光，这种机制有利于发掘出有独特写作风格的作者，但是也有可能错过众多大众喜好的类型作者。“我们正在建立的作家孵化机制，将结合这两者的优点，也就是说通过不同的项目（例如《上古世纪》的征文），先从众多写作者中选拔出一批优秀的写手（选拔的过程兼具网民的点击量选择和专业评委的选择），然后再从中选择出有写作潜力的作者进行进一步的培养，包括写作的指导、其他项目的定向参与等。” </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：李婧璇 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月16日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 阅文集团联袂上海世纪文睿：从线上体验到线下出版','2015-12-16 17:37:32','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/266557.shtml',NULL,NULL,NULL,NULL,NULL),(75,'国家新闻出版广电总局 - 行业信息 - 业界共议音乐数字化生态 “捞鱼”须先“养鱼”','业界共议音乐数字化生态 “捞鱼”须先“养鱼”','2015年10月20日','孙海悦  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> “现在有很多公司在‘捞鱼’，把原有存量和版权内容都捞在自己名下，但是‘养鱼’的人越来越少，终归有一天大家会发现没鱼可捞了。”在近日召开的2015音乐数字化生态发展研讨会上，中国音像与数字出版协会音乐产业促进工作委员会会长汪京京道出了不少业界人士的担忧。由中国文联、中国传媒大学、中国文艺评论基地联合主办的此次研讨会，汇集了各相关领域专家学者，共同为互联网语境下音乐数字化生态健康发展把脉。 <br> </p> \n<p style=\"text-indent:2em;\"> <strong> 现状：数字音乐崛起 </strong> </p> \n<p style=\"text-indent:2em;\"> 《2014中国音乐产业发展报告》显示，2013年，中国音乐产业市场总规模达2716.56亿元，其中实体唱片市场规模仅为6.5亿元，整个唱片市场销售量呈下滑趋势。“相较之下，数字音乐市场扮演了‘救市’的角色：市场规模达440.7亿元，用户数量达4.53亿人以上。”中国音像与数字出版协会副理事长、原创文化管理集团副总裁臧彦彬分析说。在他看来，传统唱片业建立了一套完整而有效的商业模式，而互联网对音乐产业的冲击是全方位的：音乐改变了传统音乐产业以唱片为介质的销售模式，数字音乐的崛起使传统唱片业的销售模式趋于消亡。 </p> \n<p style=\"text-indent:2em;\"> 调查统计，我国手机音乐用户仅有3.5%的付费用户。业内人士透露，2014年音乐版权方的收入仅为1.05亿美元，而整个产业里有1.8万音乐人，分摊下来可谓杯水车薪。对于音乐网站来讲，每千首音乐消费，流媒体网站需要负担版权成本2.5元、带宽成本2元，而广告收益仅为1.5元，净亏损3元。 </p> \n<p style=\"text-indent:2em;\"> 作为文化产业的重要组成部分，音乐产业的著作权保护始终为业界高度关注。“完善的著作权保护机制，有赖于立法、行政管理、从业者等各方面的共同努力。”中国音乐著作权协会副总干事刘平认为，数字化技术的高度发展导致权利人对其作品网络传播的控制和主张权利的能力更加弱化。为此，音著协经过多年维权努力和沟通磋商，开拓了通过音乐作品网络海量使用者与著作权集体管理组织达成一揽子授权付酬的主渠道合作模式，来解决网络音乐海量使用者所面临的海量音乐使用授权的难题。 </p> \n<p style=\"text-indent:2em;\"> <strong> 趋势：开启音乐创新模式 </strong> </p> \n<p style=\"text-indent:2em;\"> 在“互联网+”时代，数字音乐最大的特点就是变化，“变化成为所有创业者的理想，也对传统产业形成挑战。”中国音像与数字出版协会常务副理事长兼秘书长王炬说。 </p> \n<p style=\"text-indent:2em;\"> 对于数字音乐的发展趋势，王炬给出了自己的判断。第一大趋势在于数字音乐的资本化，以金融资本为主导的数字音乐开创了全新局面，音乐产业已经全面进入多元化发展时代。第二大趋势是数字音乐巨头时代来临。新的数字音乐局面正在形成，海洋音乐、阿里音乐和腾讯音乐3个资本规模大、版权数量多、在线音乐传播渠道顺畅的大型音乐集团已经成型，并有望继续扩张和兼并。独立音乐人的崛起是数字音乐发展的第三大趋势。独立音乐人自主发展，在我国原创音乐最不景气的时候独树一帜，逐渐形成气候。“金融资本完成并购还只是互联网音乐竞争‘万里长征’的第一步，因为音乐产业的强弱并不在于音乐企业的规模，也不在于拥有版权的多少，互联网音乐的未来在于原创力，在于谁能够开启音乐创新模式。” </p> \n<p style=\"text-indent:2em;\"> 汪京京提出了“音乐+”的概念：“其实互联网是音乐要‘+’的一个渠道而已，因为音乐的关联性最强，它是文化产业的基础性组成部分。”他表示，传统音乐企业与音乐互联网公司应融合发展，政府、行业协会等各方面应共同促进音乐产业有序发展。 </p> \n<p style=\"text-indent:2em;\"> <strong> 建言：建立新的音乐生态 </strong> </p> \n<p style=\"text-indent:2em;\"> 对互联网“爱恨交加”，是太平洋影音公司总经理刘钦隆等很多业界人士的真切感受。如臧彦彬所言，音乐从实体渠道转向互联网的这10年，发展速度远远落后电影、电视等其他娱乐产业。数字音乐想要建立新的音乐生态，就要改变创作人无法养活自己的恶性循环，创作才能成为音乐产业发展的根基。刘钦隆认为，今天的唱片市场必须以数字发行平台为基础，整合资源，建立一个新的以内容为核心、以网络为渠道、以技术为辅助的商业生态系统。 </p> \n<p style=\"text-indent:2em;\"> 怎样能将音乐资源效益最大化？臧彦彬给出的建议是，从挖掘用户的付费潜力着手：在用户付费习惯没有形成的情况下，给用户提供“免费+增值服务”的形式；而会员只是付费的初级阶段，想获得可持续大规模的收入则须从“粉丝经济”下功夫，深度挖掘粉丝的商业价值。在内容方面，应以歌手为核心选择优质的数字音乐进行包装，并通过运营打通和粉丝的互动；在产品方面，应提供便捷的社交网络分享和多种支付渠道。 </p> \n<p style=\"text-indent:2em;\"> 如何突破数字音乐产业发展的瓶颈？中国唱片总公司副总经理侯钧认为，互联网平台的优势在于对海量原创音乐资源的聚拢，传统唱片公司的优势则是对优质作品的分析、判断、整合、制作；以资本为纽带使二者深度融合，通过并购重组打造新型音乐公司，可以使之兼具互联网的强大市场能力和优质内容的整合制作能力。“无论何种赢利模式，只有在法律层面对版权加以保护，才是音乐产业蓬勃发展的坚实基础。” </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：孙海悦 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月20日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 业界共议音乐数字化生态 “捞鱼”须先“养鱼”','2015-12-16 17:37:32','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/266796.shtml',NULL,NULL,NULL,NULL,NULL),(76,'国家新闻出版广电总局 - 行业信息 - 《习近平总书记在文艺工作座谈会上的重要讲话学习  读本》出版 中宣部要求认真组织学习','《习近平总书记在文艺工作座谈会上的重要讲话学习 读本》出版 中宣部要求认真组织学习','2015年10月18日','','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 为推动深入学习贯彻习近平总书记在文艺工作座谈会上的重要讲话精神，中央宣传部组织编写了《习近平总书记在文艺工作座谈会上的重要讲话学习读本》。该书由学习出版社出版发行。 </p> \n<p style=\"text-indent:2em;\"> 《读本》收录了习近平总书记在文艺工作座谈会上的重要讲话，分五个专题全面阐述了习近平总书记在文艺工作座谈会上的重要讲话的重大意义、科学内涵、精神实质和实践要求，阐述了讲话提出的一系列新思想、新观点、新要求。《读本》内容丰富、观点鲜明，语言生动、深入浅出，为广大党员、干部和文艺工作者学习讲话精神提供了重要参考。 </p> \n<p style=\"text-indent:2em;\"> 中宣部在《通知》中要求，各地区各部门要在组织党员干部认真学习习近平总书记重要讲话的同时，组织好《读本》的学习，并同学习贯彻党的十八大和十八届三中、四中全会精神结合起来，同学习贯彻习近平总书记系列重要讲话精神结合起来，坚持学用结合，把讲话精神贯穿融入到文艺实践之中，推动多出精品、多出人才，开创社会主义文艺繁荣发展的新局面。各级党委宣传部门要统筹组织好《读本》的学习宣传工作，各级文化、新闻出版广电、文联、作协要组织开展多种形式的学习研讨，各级党委讲师团和宣传思想文化培训单位要把《读本》纳入培训教学内容，作为重要辅助材料。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 来源：新华网 &nbsp;发布时间：2015年10月18日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','新华网  ','国家新闻出版广电总局 - 行业信息 - 《习近平总书记在文艺工作座谈会上的重要讲话学习 读本》出版 中宣部要求认真组织学习','2015-12-16 17:37:32','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/266691.shtml',NULL,NULL,NULL,NULL,NULL),(77,'国家新闻出版广电总局 - 行业信息 - 《习近平谈治国理政》韩文版在首尔首发','《习近平谈治国理政》韩文版在首尔首发','2015年10月22日','','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 《习近平谈治国理政》韩文版首发式10月21日在韩国首尔举行。《习近平谈治国理政》韩文版由中国外文出版社与韩国未来恩出版集团合作翻译出版。 <br> </p> \n<p style=\"text-indent:2em;\"> 《习近平谈治国理政》由外文出版社以中、英、法、俄、阿、西、葡、德、日等多语种于2014年9月向全球发行。目前，该书已经发行到100多个国家和地区，全球发行520多万册。 </p> \n<p style=\"text-indent:2em;\"> 《习近平谈治国理政》韩文版是第一个中方与外国出版机构合作翻译出版的版本，这不仅顺应了韩国读者朋友的期待，也为本书与国外出版机构合作翻译出版积累了经验。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 来源：新华网 &nbsp;发布时间：2015年10月22日 </p> \n<p> </p>',NULL,'','新华网  ','国家新闻出版广电总局 - 行业信息 - 《习近平谈治国理政》韩文版在首尔首发','2015-12-16 17:37:33','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/267019.shtml',NULL,NULL,NULL,NULL,NULL),(78,'国家新闻出版广电总局 - 行业信息 - 多彩贵州又添国学雅韵','多彩贵州又添国学雅韵 ——首届孔学堂·国学图书博览会侧记','2015年10月19日','范燕莹  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 3天时间，近6万人次参观、购书，图书销售码洋500万元左右，20万册图书基本售罄。孔学堂国学书店3天有1.1万人次参观、购书，销售图书6000余册，码洋20万余元……10月16日-18日，首届孔学堂·国学图书博览会在贵阳孔学堂举办。220余家出版社携2万多种国学类图书参展，如此规模、如此范围、如此集中展示，是国内第一次。此次博览会也成为多彩贵州以对中华优秀文化创造性转化、创新性发展，推动中华优秀传统文化走进民间，融入社会为目的的专业博览会。 <br> </p> \n<p style=\"text-indent:2em;\"> <strong> 两万多种国学图书集中亮相 </strong> </p> \n<p style=\"text-indent:2em;\"> 在展区，记者看到，中华书局、岳麓书社等知名古籍图书出版机构悉数亮相。据组委会介绍，此次博览会以简易古朴特装方式布展，2万余种国学类图书包括国学童书、国学数字出版物、国内外国学期刊、贵州非物质文化出版物成果等。 </p> \n<p style=\"text-indent:2em;\"> 于2014年8月挂牌的孔学堂书局，特别挑选了53本书籍参展。其中不仅有“孔学堂文库”2015年推出的新书《比较视域中的儒学研究——姚新中学术论集》，还有“大众儒学书系”的22本儒学经典。 </p> \n<p style=\"text-indent:2em;\"> 山东省作为孔孟之乡，也是儒学出版大省。此次博览会，山东出版集团11家参展单位带了《孔子家语通解》《论语诠解》《澄衷蒙学堂字课图说》等一大批出版物。山东省新闻出版广电局出版管理处副处长蒯海军说，希望这批优秀出版物能够受到大众的喜爱，更好地将齐鲁文化、齐鲁风韵在贵州乃至全国推广。 </p> \n<p style=\"text-indent:2em;\"> <strong> 文化名家轮番登场 </strong> </p> \n<p style=\"text-indent:2em;\"> “我简直不敢相信一群10岁左右的小孩子能演出这么好看的片子，我和儿子在这里已经看了两集了。”在孔学堂六艺学宫国学精品电视节目推广区，《国学少年》系列剧格外吸引眼球，一位带着孩子前来参观的父亲笑着说。《国学少年》出品人、总策划刘洪云说，相比于照本宣科的教育方式，这种新的方式更能达到寓教于乐的效果。 </p> \n<p style=\"text-indent:2em;\"> 在“我们的传统节日——儿童长卷国画创作”现场，记者看到来自贵阳星星美术学校的22名孩子围绕“我们的传统节日”这一主题进行现场创作。画卷中的人物都是以古代的形象展现，主办方希望孩子们通过画国画了解传统文化。 </p> \n<p style=\"text-indent:2em;\"> 除了特色突出的文化活动，在为期3天的博览会上，聆听文化名家讲座可谓逛展会的一项必要内容。《党建》杂志社总编辑刘汉俊带来“孔子思想之光”讲座；著名民俗画、连环画家罗希贤为读者剖析连环画的艺术价值和中国古典文化的意义；《百家讲坛》主讲人董平围绕“儒道二家的道、论”主题，与贵阳市广大市民“谈天说道”……或关于史学，或关于中医，或关于哲学智慧，文化名家为读者带来内容丰富、精彩多元的国学讲座，读者纷纷感慨“耳福不浅”。 </p> \n<p style=\"text-indent:2em;\"> <strong> 文化惠民使读者得实惠 </strong> </p> \n<p style=\"text-indent:2em;\"> 记者在孔学堂的国学书店看到，不少读者拿着一张《图书认购意向登记表》，上面书名和价格列得非常清楚。“这张表相当于200元的购书券，确实很实惠。我选了八九本书，只需要自己再贴一点钱就能把这些书全部带回家。”一位在收银台前结账的退休教师如是说。据了解，贵州省财政拨出100万元专款，用于省文明办、省教育厅、省工会、省妇联、团省委等系统干部职工购买优秀国学图书。 </p> \n<p style=\"text-indent:2em;\"> 博览会开幕当天，浙江出版联合集团、浙江人民出版社向贵州省文朝荣式好支书和先进基层党组织等捐赠1000册《之江新语》。“浙江和贵州从历史上到现在一直有着千丝万缕的联系，向贵州赠送习近平总书记的著作，是希望贵州在发展中能够借鉴一些浙江的经验，因为书中既有总书记的执政思考，也有浙江经济发展的脉络痕迹。”《之江新语》责任编辑、浙江人民出版社副社长虞文军说。 </p> \n<p style=\"text-indent:2em;\"> 此外，首届孔学堂·国学图书博览会图书集中捐赠活动中，共有60家单位向贵州省文史馆、孔学堂国学图书馆、修文县王阳明纪念馆、中国文化书院贵阳国学大讲堂4家单位捐赠图书4114册。中国文化书院贵阳国学大讲堂创办人李宽定说，举办国学图书博览会可以推动更多的贵州人前来读书、买书，各单位捐赠的书籍也将推动贵州人学习并传承国学文化，他坚信爽爽的贵阳终有一天会变成书香贵阳。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：范燕莹 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月19日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 多彩贵州又添国学雅韵','2015-12-16 17:37:34','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/266690.shtml',NULL,NULL,NULL,NULL,NULL),(79,'国家新闻出版广电总局 - 行业信息 - 《习近平的国家治理现代化思想》出版座谈会举行','《习近平的国家治理现代化思想》出版座谈会举行','2015年10月21日','王坤宁  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 国家智库报告之《习近平的国家治理现代化思想》《创新纪检监察体制，遏制“一把手”腐败》等图书日前由中国社会科学出版社出版。10月20日，由中国社会科学出版社和光明日报智库研究与发布中心举办的《习近平的国家治理现代化思想》出版座谈会在京举行，来自中国社会科学院、中国人民大学等单位的10多名专家从国家治理的特征、遏制“一把手”腐败、法治社会、经济转型等角度，对习近平的治国理政思想进行了研讨。 <br> </p> \n<p style=\"text-indent:2em;\"> 座谈会上，大家一致认为，在中国空前接近于中华民族伟大复兴的当下，急需自己的新概念、新范畴、新表述来将自己的经验理论化，并引领时代的话语权，而“国家治理体系与治理能力的现代化”就是这样一种概念。《习近平的国家治理现代化思想》一书的作者、中国人民大学教授杨光斌认为，习近平在阐述治国理政时，是从中华文明基体论出发，以改革为方法，以公正价值为导向，努力将制度体系与治理能力相适应、使以民主集中制为内核的中国模式成为更有竞争力的政治思想体系。 </p> \n<p style=\"text-indent:2em;\"> 据了解，最近中国社会科学出版社与院内外的智库联系，开辟和推出了国家智库报告和国家智库丛书两大出版系列。已经出版了《中国政府信息公开第三方评估报告》《中国海外投资国家风险评级报告》《中国地方立法实践分析》等报告15种，社会反响良好。近期还将推出《中国对外贸易的一般均衡建设与模拟》《中国人文社会科学期刊评价报告》《“十三五”时期的中国金融体系改革建议》等智库报告系列丛书。 </p> \n<p> <br> </p> \n<p> 作者：王坤宁 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月21日 </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 《习近平的国家治理现代化思想》出版座谈会举行','2015-12-16 17:37:36','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/267018.shtml',NULL,NULL,NULL,NULL,NULL),(80,'国家新闻出版广电总局 - 行业信息 - 习近平《在文艺工作座谈会上的讲话》单行本正式出版','习近平《在文艺工作座谈会上的讲话》单行本正式出版','2015年10月20日','','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 中共中央总书记、国家主席、中央军委主席习近平《在文艺工作座谈会上的讲话》单行本已由人民出版社正式出版，即日起在全国新华书店发行。 <br> </p> \n<p> <br> </p> \n<p> 来源：新华网 &nbsp;发布时间：2015年10月20日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','新华网  ','国家新闻出版广电总局 - 行业信息 - 习近平《在文艺工作座谈会上的讲话》单行本正式出版','2015-12-16 17:37:36','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/266798.shtml',NULL,NULL,NULL,NULL,NULL),(81,'国家新闻出版广电总局 - 行业信息 - 《中共中央关于繁荣发展社会主义文艺的意见》全文公布','《中共中央关于繁荣发展社会主义文艺的意见》全文公布','2015年10月20日','','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 中共中央日前出台文件，指导社会主义文艺繁荣发展。10月19日，新华社授权全文播发《中共中央关于繁荣发展社会主义文艺的意见》。 <br> </p> \n<p style=\"text-indent:2em;\"> 《意见》分为六部分25条，包括：做好文艺工作的重大意义和指导思想；坚持以人民为中心的创作导向；让中国精神成为社会主义文艺的灵魂；创作无愧于时代的优秀作品；建设德艺双馨的文艺队伍；加强和改进党对文艺工作的领导。 </p> \n<p style=\"text-indent:2em;\"> 其中，《意见》指出，要坚持重心下移，把各种文艺惠民措施纳入公共文化服务体系建设规划，推行菜单式服务，以实效为标准，提升质量和水平。创新形式、持续开展“文化进万家”、“送欢乐下基层”、“心连心”、文化艺术志愿服务、农村电影放映、全民阅读等活动。组织实施基层群众文化建设工程，发挥农家书屋、社区书屋效用，落实乡镇文化站职能，在编制总量内健全社区文化中心专兼职岗位，落实国家规定的工资待遇政策。 </p> \n<p style=\"text-indent:2em;\"> 《意见》强调，评价文艺作品，要以最广大人民的根本利益为出发点和落脚点，坚持把社会效益放在首位，努力实现社会效益和经济效益、社会价值和市场价值相统一，绝不让文艺成为市场的奴隶。建立健全反映文艺作品质量的综合评价体系，完善影视剧、文艺演出、美术和文艺类出版物等创作生产出版的立项、采购、评审标准，完善文艺作品推介传播等环节的评估标准，把票房收入、收视率、收听率、点击率、发行量等量化指标，与专家评价和群众认可统一起来，推动文艺健康发展。 </p> \n<p style=\"text-indent:2em;\"> 《意见》提出，不断丰富拓展中国梦的表现内容，既讲好国家民族宏大故事，又讲好百姓身边日常故事，用生动的艺术形象和叙事体现中国梦的丰富内涵；文学、艺术、电影、出版等方面的基金、资金，要重点支持传递向上向善价值观的青少年文艺创作和推广；要唱响爱国主义主旋律，引导人民树立和坚持正确的历史观、民族观、国家观、文化观，不断增强中国特色社会主义道路自信、理论自信、制度自信；要传承和弘扬中华优秀传统文化，做好古籍整理、经典出版、义理阐释、社会普及工作，加强对中华诗词、音乐舞蹈、书法绘画、曲艺杂技和历史文化纪录片、动画片、出版物等的扶持。 </p> \n<p style=\"text-indent:2em;\"> 《意见》强调，要牢固树立精品意识，推出更多思想精深、艺术精湛、制作精良，体现时代文化成就、代表国家文化形象的文艺精品。实施网络文艺精品创作和传播计划，充分发挥新媒体的独特优势，把握传播规律，加强重点文艺网站建设，善于运用微博、微信、移动客户端等载体，促进优秀作品多渠道传输、多平台展示、多终端推送。要加强文艺阵地建设。进一步加强领导、加强规划、加大投入，充分发挥报纸、期刊、电台、电视台、网络媒体、图书音像电子出版物的积极作用，建好用好剧场、电影院、文化馆（站）、群艺馆、美术馆、工人文化宫、文化广场、基层综合性文化服务中心等各类文艺阵地。因地制宜、因时制宜，采用群众喜闻乐见的方式，举办各种展映展播展演展览和品读鉴赏传唱活动，让优秀文艺作品走进基层群众特别是广大青少年。 </p> \n<p style=\"text-indent:2em;\"> 《意见》明确提出，要加强统筹指导，完善协调机制，把实施丝绸之路文化项目、丝绸之路影视桥、丝路书香等项目纳入国家“一带一路”战略，制定文化交流合作专项计划。实施中国当代作品翻译工程，遴选具有代表性的中国当代文艺作品，进行多语种翻译、出版、播映、展示。充分利用多种对外交流渠道和活动平台，把文艺走出去纳入人文交流机制，向世界推介我国优秀文艺作品。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月20日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 《中共中央关于繁荣发展社会主义文艺的意见》全文公布','2015-12-16 17:37:36','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/266797.shtml',NULL,NULL,NULL,NULL,NULL),(82,'国家新闻出版广电总局 - 行业信息 - 2015年北京台湖国际教育图书展构筑中外教育图书信息  交流平台','2015年北京台湖国际教育图书展构筑中外教育图书信息 交流平台','2015年10月26日','李婧璇 王坤宁  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 10月22日至23日，由北京市图书进出口有限公司主办的“2015年北京台湖国际教育图书展”在北京台湖出版物会展贸易中心园区开展。本次书展汇集了来自英、美、法等国的48家出版集团、380余家出版社的5万余种外文原版图书，重点展出万余种与国外教学同步的最新原版教材教辅，吸引了全国30余个省（区、市）的200余家国际学校、教育培训集团、公立学校到现场采购订货。 <br> </p> \n<p style=\"text-indent:2em;\"> <strong> 品种丰富 品类齐全 </strong> </p> \n<p style=\"text-indent:2em;\"> 走进本次教育图书展主会场，位于北京台湖出版物会展贸易中心园区内的北京国际图书城，记者看到整个展场共分为两大展区，国际出版集团教育图书展区和北京市图书进出口有限公司自主备货订采区。据北京发行集团相关负责人介绍，两大展区共集中展出万余种与国外教学同步的最新原版教材教辅和读物，内容涵盖从幼儿园到高中的各个教学阶段。其中，国际出版集团教育图书展区邀请到牛津出版社、哈珀科林斯出版集团等18家世界知名出版集团共同参展；北京市图书进出口有限公司自主备货订采区则精心陈列有牛津出版社和剑桥出版社出版的国际考试教材、系列读物等精品图书。 </p> \n<p style=\"text-indent:2em;\"> 在国际出版集团教育图书展区内，由18家世界知名出版集团开设的展销专区各具特色，采购客户可以现场享受讲解、预订等“一条龙”优质服务。据记者了解，其间展出品种最多的是英国牛津出版社，其参展的教育类新书样本达千余种。而在北京市图书进出口有限公司的自主备货订采区内，由企鹅出版社、兰登书屋等国际知名大众图书出版集团出版的进口外文原版图书副本量也是备货充足，及时满足采购客户现场挑选、现场采购、快速发货的购书需求。 </p> \n<p style=\"text-indent:2em;\"> <strong> 精彩互动 轮番上阵 </strong> </p> \n<p style=\"text-indent:2em;\"> 一年一度的“北京台湖国际教育图书展”为国外出版社、国内教育教学培训机构、大书城、专业书店以及外语学习爱好者进行面对面交流提供了难得的契机，构筑起中外教育图书的信息交流平台。在本次特别开设的互动交流专区内，由主办方精心策划推出的多场原版教材教辅主题教学讲座和新品图书推介活动可谓“你方唱罢我登场”。 </p> \n<p style=\"text-indent:2em;\"> 书展开幕当日上午，来自国内首家小学托福考试试点——树人英语学校的师生们在互动交流专区现场进行了模拟课堂展示。他们现场所使用的演示教材就是由美国霍顿米弗林出版社出版的《星舰英语》。同时，为满足广大外语从教者与原版教材出版者间需求反馈与信息交流的需求，主办方还特别推出了原版教材推介和教研培训系列活动，如举办“美国核心课程在ELL课堂中的应用”专题讲座、“英文阅读能力测评和管理系统”推介活动、“牛津数字化学习资源介绍”讲座等。 </p> \n<p style=\"text-indent:2em;\"> 来自北京朝阳外国语学校的田佳凝、王潇、石雨鑫就是跟着老师一起来现场观摩学习的，“老师说借着这次机会带我们一起到这里锻炼一下，跟参展的外国人进行沟通交流，顺便看看有没有自己喜欢的原版图书。” </p> \n<p style=\"text-indent:2em;\"> <strong> 对焦专业 提升服务 </strong> </p> \n<p style=\"text-indent:2em;\"> 随着出国留学潮不断向低龄化延伸，进口外文原版教材教辅和读物的市场需求也在持续升温。由北京市图书进出口有限公司于2012年倾力打造的“北京台湖国际教育图书展”历经3年的发展，已成效显著。参展单位从最初的几家出版教育集团发展到今年的18家世界知名教育出版集团；参展品种也从最初的原版教材增加到覆盖幼儿园到高中各个年龄段的教材教辅和原版读物；更以“品种全、质量高、到货快”的服务特色得到众多国际知名出版集团和采购客户的认可和好评，成为业内外公认的国际教育图书专业品牌展会。 </p> \n<p style=\"text-indent:2em;\"> 来自瑞思培训学校的课程设计人员琳达·周和自己的同事分散在展会现场内各自翻阅现场图书，“我们待会儿还要碰头讨论，来为我们学校的课程设计寻找好书以及可以借鉴参考的素材。”虽然是第一次来，但给琳达·周留下了不错的印象，“新书种类多，品种全。”而来自学而思教育集团旗下乐加乐英语的教研课程设计专员丁清楠则对兰登书屋出版的《初学者蓝宝书》和《初学者红宝书》进行了研究，“这些故事非常适合学龄前儿童阅读，以孩子的视角来观察世界，将浅显的道理蕴于简单的故事中，寓教于乐。”谈到参展的感受，她表示：“随着图书品种的逐步丰富，供我们选择的图书范围也更广了，能挑选出的好书也更多了。” </p> \n<p style=\"text-indent:2em;\"> 据北京发行集团相关负责人介绍，针对此次“2015年北京台湖国际教育图书展”，主办方特别推出定向推荐、现场订购、出单结算的“一站式”接待交易模式，并开展全程“一对一”的陪同服务，同时以优惠的价格为客户提供图书现采和订购业务。此外书展期间，广大读者、外语爱好者到场购买参展外文原版图书可享受九折优惠。 </p> \n<p style=\"text-indent:2em;\"> 据记者了解，“2015年北京台湖国际教育图书展”以丰富的品种、充分的交流和优质的服务，吸引了京西国际学校、顺义国际学校、北京加拿大国际学校等知名国际学校，以及瑞思培训学校、新东方教育集团、学而思教育集团等大型培训机构到场采购订货。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：李婧璇 王坤宁 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月26日 </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 2015年北京台湖国际教育图书展构筑中外教育图书信息 交流平台','2015-12-16 17:37:37','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/267141.shtml',NULL,NULL,NULL,NULL,NULL),(83,'国家新闻出版广电总局 - 行业信息 - 青岛城市传媒启动百社千校书香童年活动','青岛城市传媒启动百社千校书香童年活动','2015年10月27日','范燕莹  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 青岛城市传媒股份有限公司近日在崂山区实验小学启动“百社千校书香童年——书香满校园”阅读活动，为该校学生捐赠价值2万元的优秀少儿读物1000册。 <br> </p> \n<p style=\"text-indent:2em;\"> 此次活动捐赠的图书包括“曹文轩系列”“常新港系列”“图说儿童百科全书系列”“儿童心理成长励志系列”以及《少年读史记》《有故事的汉子》等100多种优秀读物。活动中，该校向全校学生发出了“积极参加‘书香满校园’阅读活动，爱读书、读好书”的倡议。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：范燕莹 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月27日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 青岛城市传媒启动百社千校书香童年活动','2015-12-16 17:37:37','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/267231.shtml',NULL,NULL,NULL,NULL,NULL),(84,'国家新闻出版广电总局 - 行业信息 - 湖北首家少儿类出版集团成立','湖北首家少儿类出版集团成立','2015年10月12日','汤广花 姚磊  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 10月9日，湖北省首家少儿类出版集团——长江少年儿童出版社（集团）有限公司正式挂牌，开始公司化运作。其旗下有海豚传媒、爱立方、上海安柏、北京智慧树等数家公司。 <br> </p> \n<p style=\"text-indent:2em;\"> 据了解，长江少儿出版集团核心业务包括少儿图书出版、数字出版、学前教育、青少年成长体验等。目前，该集团年出版图书约3000种，年销售码洋过15亿元，在全国出版单位中市场占有率排名前十。 </p> \n<p style=\"text-indent:2em;\"> 长江少儿出版集团总经理李兵表示：“未来我们将以少儿文化创意内容为核心，向幼教、动漫、网游等新兴产业发展，推动集团由少儿出版产品供应商向幼儿教育全程服务商、少儿文化娱乐服务商模式转型。” </p> \n<p style=\"text-indent:2em;\"> 据李兵介绍，近年来，集团采取“一体两翼”的发展模式，以优秀少儿文化内容为核心，以幼儿教育全程服务、少儿文化娱乐服务为两大发展方向，构建开拓“互联网+”平台，融合线上线下资源，贯穿出版单位与书店、幼儿园、学校、教育机构等多类终端之间的链条，打造新型出版通路。其在全国率先建立的“课程+玩教具+幼教云平台”三位一体的幼教全程服务体系，深受广大幼儿园教师和家长的欢迎。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：汤广花 姚磊 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月12日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 湖北首家少儿类出版集团成立','2015-12-16 17:37:38','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/266107.shtml',NULL,NULL,NULL,NULL,NULL),(85,'国家新闻出版广电总局 - 行业信息 - 中青国际法兰克福书展订货码洋同比增15%','中青国际法兰克福书展订货码洋同比增15%','2015年10月26日','刘蓓蓓  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 在近日落幕的2015年法兰克福国际书展上，中青国际与来自欧洲、北美、亚洲的出版社和分销商达成合印包销和分销意向80余个，总订货码洋比去年增长15%。 <br> </p> \n<p style=\"text-indent:2em;\"> 据介绍，此次中青国际共带来了包括《如画四季——中国古典绘画中的最美花卉》《中国风俗画稀世珍品——乾陵南巡图》《中国风俗画稀世珍品——姑苏繁华图》《百鸟画谱——中国最美的工笔画》《中国时尚设计》等全新产品在内的120余个文化艺术和创意设计类图书与选题，并进行了20余次展位会谈。 </p> \n<p style=\"text-indent:2em;\"> 书展开幕当天下午，中青国际在展台举行了大型艺术画册《如画四季——中国古典绘画中的最美花卉》英文版、中文版、法文版全球首发仪式。来自20余个国家和地区的出版社和分销商代表表达了希望参加其他语言版本合印包销或分销的意向。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：刘蓓蓓 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月26日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 中青国际法兰克福书展订货码洋同比增15%','2015-12-16 17:37:38','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/267140.shtml',NULL,NULL,NULL,NULL,NULL),(86,'国家新闻出版广电总局 - 行业信息 - 浙江新华人：人生因书更精彩','浙江新华人：人生因书更精彩','2015年10月26日','章红雨  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> <span style=\"text-indent:2em;\"> “如果说考虑收入，我不会选择在新华书店工作。选择新华书店，是因为我喜欢它的文化氛围，在这里我有存在感。”说这话的是杭州市新华书店有限公司庆春路购书中心业务组的年轻主管赵仲睿。 </span> </p> \n<p style=\"text-indent:2em;\"> 8年前，学建筑专业的赵仲睿抱着试试看的心理走进庆春路购书中心做营业员。一路走下来，让赵仲睿没有想到的是，自己竟然如此喜欢在新华书店工作，乃至“掉在里面出不来了”，其中的理由之一便是新华书店的领导特别重视年轻人的想法。 </p> \n<p style=\"text-indent:2em;\"> 让员工在企业中勇于实现自我价值，是浙江省新华书店集团有限公司倡导“六有”的结果。何为“六有”？浙江出版联合集团党委委员、浙江省新华书店集团有限公司董事长王忠义在接受记者采访时说，“六有”就是让员工有信心、有激情、有干劲、有尊严、有体面、有奔头，而这正是浙江新华的愿景。 </p> \n<p style=\"text-indent:2em;\"> 在王忠义看来，企业有愿景，员工才有奔头。国有企业员工如果对企业没有信心，怎么会有激情、有干劲？工作中如果没有尊严、没有体面、没有奔头，怎么能激发出员工的信心、激情和干劲呢？ </p> \n<p style=\"text-indent:2em;\"> 如今，“六有”平台造就出的痴迷于新华书店工作的员工在浙江新华人中不在少数。记者在浙江省10个市、区、县新华书店采访中发现，林懂义、李伟明、王凯、张国良、杨红英、杨明、王晶、张红梅、范进剑等浙江新华人，有着同样的情结。 </p> \n<p style=\"text-indent:2em;\"> 在杭州市萧山区新华书店，总经理杨岳江提起萧山书城经理王凯时满脸爱意，因为正是手下有王凯等多位能干的“大将”，萧山区新华书店年销售码洋才会过亿，可以和浙江省一个中等规模城市新华书店的年销售额相媲美。 </p> \n<p style=\"text-indent:2em;\"> 在丽水市景宁畲族自治县新华书店，经理李伟明告诉记者：“如果想把事情干好，首先要在社会上被其他人认可。”正是在这一朴素理念下，景宁这个经济欠发达县的人均购书量连续8年名列全省县级新华书店前四名。这其中新华书店的作用功不可没。 </p> \n<p style=\"text-indent:2em;\"> 事实上，上述两个区县级新华书店案例，仅是浙江全省新华书店基层店的代表。“六有”所带来的“蝴蝶效应”中，最为强劲的是，浙江新华人品尝到连年取得的丰硕果实：2013年，浙江省新华书店销售码洋为106亿元，首次突破百亿大关。2014年，浙江新华销售码洋再上一个台阶，成功升至118亿元。2015年虽然还未到年终，但是实现销售码洋135亿元的目标已经在望。 </p> \n<p style=\"text-indent:2em;\"> 记者为此算了一笔账：在土地面积并不大、人口并不多的浙江省，浙江新华销售码洋能够连续3年超过百亿元，这意味着全省5000多名新华人，每人每年为企业创造销售额200余万元。 </p> \n<p style=\"text-indent:2em;\"> 在实体书店不断被唱衰的今天，是什么力量让浙江新华人干劲十足？采访中记者发现，除浙江新华建立“六有”平台外，另一个关键因素是，近年来浙江新华的企业文化建设追求——“求真务实、创新协同”的理念。这个颇具浙江新华特色的“八字方针”，正潜移默化地融入每个浙江新华人的血液中。 </p> \n<p style=\"text-indent:2em;\"> 生于上世纪60年代的金华市新华书店购书中心经理杨红英对记者说：“每当我走进店堂，就有一种使命感，一股无形的力量支撑着我投入到每一天每一项工作中。” </p> \n<p style=\"text-indent:2em;\"> 生于80年代的赵仲睿则说：“创新协同是我作为新华人身体里流淌着的血液，哪里需要就输送到哪里。” </p> \n<p style=\"text-indent:2em;\"> 两代新华人，两种表达方式，从中折射出的共识却十分一致，那就是他们都有浓浓的新华情结。 </p> \n<p style=\"text-indent:2em;\"> 值得关注的是，浙江新华人的情结已经蔓延到新华书店外，每年都有一批又一批有志于图书发行的高素质年轻人，走进浙江新华书店这个大家庭中。 </p> \n<p style=\"text-indent:2em;\"> 记者了解到，在浙江省新华书店下沙物流基地，这个拥有14万平方米的大型现代书业物流中心，正有一批海外留学归来的年轻学子，在此为实现自己的理想努力拼搏着。 </p> \n<p style=\"text-indent:2em;\"> 记者还了解到，在浙江省新华书店图书信息员队伍中，不少计算机专业毕业的年轻人不愿意到薪酬更高的企业就职，原因是浙江新华倡导技术领先，具有自主知识产权的ERP系统（企业资源计划），让他们的所学始终有用武之地。 </p> \n<p style=\"text-indent:2em;\"> “从19岁到37岁，一路走来，在浙江新华这个大家庭中，我切切实实领悟并且体会到：改变世界不仅仅有‘王江民（江民杀毒软件创始人）、求伯君（金山软件创始人）’那样一种方式。”温州市新华书店信息中心主任林懂义动情地对记者说。 </p> \n<p style=\"text-indent:2em;\"> “30年坚守，不是为了得到多少荣誉，得到多少人的认同，而是因为我选择了这份事业。”杨红英说出了5000多名浙江新华人的共同心声。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：章红雨 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月26日 </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 浙江新华人：人生因书更精彩','2015-12-16 17:37:39','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/267142.shtml',NULL,NULL,NULL,NULL,NULL),(87,'国家新闻出版广电总局 - 行业信息 - 《光明日报》“寻找最美”系列公益活动取得良好成效','《光明日报》“寻找最美”系列公益活动取得良好成效','2015年10月27日','史竞男  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> “寻找最美乡村教师”“寻找最美导游”“寻找最美养路工”“寻找最美邮递员”……《光明日报》“寻找最美”大型公益活动连续开展5年来，掀起了“寻找最美”热潮，传递了正能量，生动诠释了社会主义核心价值观，受到社会各界广泛赞誉。 <br> </p> \n<p style=\"text-indent:2em;\"> 据了解，“寻找最美”系列活动是培育和宣传社会主义核心价值观的生动实践，也是中央主流媒体敢于担当社会责任的鲜活实例。《光明日报》坚持用文化的滋养和催化，推出系列“寻找最美”报道，以德树人、以文化人，深入发掘、讲好故事，用感人的事迹和清新的文风报道“最美”，以“最美”系列弘扬和引领社会主义核心价值观。 </p> \n<p style=\"text-indent:2em;\"> 2011年，《光明日报》率先发起“寻找最美乡村教师”大型公益活动。至2015年，“寻找最美乡村教师”大型公益活动更名为“寻找最美教师”，活动将推选范围由乡村教师拓宽至全社会的教育工作者。活动开展5年来，共评选出54个“最美（乡村）教师”（个人、团体）和103个“特别关注（乡村）教师”（个人、团体）。 </p> \n<p style=\"text-indent:2em;\"> 此外，《光明日报》与国家旅游局合作开展了“寻找最美导游”活动，与民政部区划地名司联合主办了“寻找最美地名”活动。 </p> \n<p style=\"text-indent:2em;\"> 专家指出，“寻找最美”系列公益活动，为时代点燃了一盏光明之灯，向全社会传递了人间的大爱，充分体现了中央媒体高度的社会责任感，是在用实际行动践行社会主义核心价值观。 </p> \n<p style=\"text-indent:2em;\"> 有读者表示，“最美”系列犹如一面镜子，其故事蕴藏着砥砺人心、烛照时代的精神力量，使得社会主义核心价值观更有感染力、穿透力，也让社会主义核心价值观更好地走进人们的心灵。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：史竞男 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月27日 </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 《光明日报》“寻找最美”系列公益活动取得良好成效','2015-12-16 17:37:41','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/267232.shtml',NULL,NULL,NULL,NULL,NULL),(88,'国家新闻出版广电总局 - 行业信息 - 中国图书评论学会公布9月“中国好书”','中国图书评论学会公布9月“中国好书”','2015年11月4日','','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 中国图书评论学会日前公布了今年9月“中国好书”，共有7本图书入选。 <br> </p> \n<p style=\"text-indent:2em;\"> 上榜图书分别为：《改变世界经济地理的“一带一路”》（上海交通大学出版社）、《高思在云：中国兴起与全球秩序重组》（中国人民大学出版社）、《晚明大变局》（中华书局）、《隐忧与曲谏：〈清明上河图〉解码录》（北京大学出版社）、《玩具思维：改变未来行业的新思维》（机械工业出版社）、《昆虫漫话》（新星出版社）、《森林里的小火车》（二十一世纪出版社）。 </p> \n<p> <br> </p> \n<p> 来源：中国新闻出版广电报 &nbsp;发布时间：2015年11月4日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 中国图书评论学会公布9月“中国好书”','2015-12-16 17:37:41','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/267791.shtml',NULL,NULL,NULL,NULL,NULL),(89,'国家新闻出版广电总局 - 行业信息 - 北京出版集团展出精品图书200余种','北京出版集团展出精品图书200余种','2015年11月2日','王坤宁  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> “我们的图书挺受欢迎，开幕半天就销售了1000余元。”10月29日，《中国新闻出版广电报》记者在文博会唯一一家出版集团展区——北京出版集团的展位上听到工作人员这样说。 <br> </p> \n<p style=\"text-indent:2em;\"> 现场集中展出了北京出版集团出版的原创精品图书、最新出版的优秀图书和近年获得国家级大奖及市场畅销的各类精品图书共200余种，包括《大家诗苑》《藏北十二年》等原创新品以及《平凡的世界》《穆斯林的葬礼》等市场畅销书。另外，还有为纪念抗日战争胜利70周年出版的“北平抗战实录丛书”，还特别展示了北教传媒出版的先锋“互联网+”概念图书等。 </p> \n<p> <br> </p> \n<p> 作者：王坤宁 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年11月2日 </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 北京出版集团展出精品图书200余种','2015-12-16 17:37:44','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/267601.shtml',NULL,NULL,NULL,NULL,NULL),(90,'国家新闻出版广电总局 - 行业信息 - 2015中国上海国际童书展11月开幕','2015中国上海国际童书展11月开幕','2015年10月27日','金鑫  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 2015中国上海国际童书展（CCBF）将于11月13日至15日在上海世博展览馆举办。上海市新闻出版局近日在沪召开新闻发布会，介绍了本届展会的筹备情况。 <br> </p> \n<p style=\"text-indent:2em;\"> 据悉，2015中国上海国际童书展将强化“国际性、发现推介新人新作、促进少儿阅读和亲子阅读”三大功能。目前，已有300余家国内外童书出版和文化创意机构，1000余位国内外童书作家、插画家和出版专业人士确定参展，将带来5万余种童书、100余场阅读活动。英国沃克出版社、瑞典邦尼集团将以超大规模的展位面积独立参展。此外，一批外国出版机构将派出观察员观摩考察童书展，为今后参展做准备。童书展由专业展区和公众展区两部分构成。专业展区第三日对大众读者开放，公众展区3天均面向大众读者开放。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：金鑫 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月27日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 2015中国上海国际童书展11月开幕','2015-12-16 17:37:45','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/267233.shtml',NULL,NULL,NULL,NULL,NULL),(91,'国家新闻出版广电总局 - 行业信息 - 中国书店办“世界民间藏中国敦煌文献精品展”','中国书店办“世界民间藏中国敦煌文献精品展”','2015年10月30日','王坤宁  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 10月28日，中国书店在雁翅楼店举办了“世界民间藏中国敦煌文献精品展”，展出了来自各地的民间藏珍贵敦煌经卷十余卷，多为入选《世界民间藏中国敦煌文献》第一辑、第二辑和第三辑的珍贵敦煌经卷。 <br> </p> \n<p style=\"text-indent:2em;\"> 记者在现场看到，展品中包含了1.4米隋写本《大般涅槃经》敦煌经卷三卷，世界范围内已知的只有7件，其中1件为中国书店所藏。同时展出的还有极具特色的《佛名经》、缝缋装《大悲启请》及同仁堂乐家的藏品《佛说无量寿宗要经》等。 </p> \n<p style=\"text-indent:2em;\"> 展览开幕当天，还举行了“世界民间藏中国敦煌文献”座谈会。会议透露，《世界民间藏中国敦煌文献》将以藏家为单位，陆续推出。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：王坤宁 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月30日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 中国书店办“世界民间藏中国敦煌文献精品展”','2015-12-16 17:37:46','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/267458.shtml',NULL,NULL,NULL,NULL,NULL),(92,'国家新闻出版广电总局 - 行业信息 - 网络文艺将集体亮相第十届北京文博会','网络文艺将集体亮相第十届北京文博会','2015年10月29日','王坤宁 李婧璇  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 近日了解到，第十届北京文博会新闻出版广电展馆将首次设立网络文艺主题展区，阅文集团、北青网等网络文艺领军企业将携优秀作品与广大读者见面，现场还将举办多场签约仪式。 <br> </p> \n<p style=\"text-indent:2em;\"> 在网络文艺主题展区，阅文集团、腾讯·大燕网、北青网、海洋音乐、天籁K歌、蓝海传媒、华韵尚德等企业将集体亮相，向社会集中宣传推介一批热门网络作品，举办网络作家见面会等丰富多彩的互动活动。 </p> \n<p style=\"text-indent:2em;\"> 与此同时，本届文博会首次增设精品图书展卖区。雨枫书馆和字里行间两家特色书店将现场销售几万种精品图书，并提供较大折扣优惠。在这里还能看到载道文化选用国内外最珍稀的藏本，使用原生态的纸张，通过高科技手段印刷，呈现藏本最真实的风貌。 </p> \n<p style=\"text-indent:2em;\"> 由北京市新闻出版广电局主办的新闻出版广电展馆展览面积约2200平方米。除新增设的网络文艺主题展区和精品图书展卖区，还包括新闻出版、广播影视、走出去、版权、精品创作等传统板块。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：王坤宁 李婧璇 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月29日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 网络文艺将集体亮相第十届北京文博会','2015-12-16 17:37:46','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/267383.shtml',NULL,NULL,NULL,NULL,NULL),(93,'国家新闻出版广电总局 - 行业信息 - 下基层捕捉新闻 从深处推进改革','下基层捕捉新闻 从深处推进改革 ——写在南阳报业传媒集团挂牌运营之际','2015年10月28日','韩为卿 冯兴阁  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 定篇幅，倡导“短、实、新”文风，压缩会议和领导活动报道，对关乎南阳发展的战略选题，不惜篇幅；定载体，哪种载体传播效果好，就用哪种载体；定属性、定价值、定位置、定责任……《南阳日报》不断提升报道质量，先后入选中国地市党报网站十强、全国自办发行先进单位、河南省“十佳报纸”…… </p> \n<p style=\"text-indent:2em;\"> 以南阳日报社为基础组建的南阳报业传媒集团日前正式挂牌运营。南阳报业传媒集团党委书记、社长、董事长关玉国对记者说，南阳日报社将从此迈入改革发展的新阶段，争取在办好报纸、抓好经营、搞好改革、谋好融合上取得更大突破。 </p> \n<p style=\"text-indent:2em;\"> <strong> 抓活鱼，唱响主旋律 </strong> </p> \n<p style=\"text-indent:2em;\"> <span style=\"font-family:楷体, 楷体_gb2312, simkai;\"> 一项采访活动派出15人，历时39天，行程6000多公里，既阐释中央政策也传播地方信息 </span> </p> \n<p style=\"text-indent:2em;\"> 弘扬主旋律，传递正能量需要践行开放办报理念，不囿于一省、一市，走出去交流、走出去合作。为此，南阳日报社既出台政策又拨付专款。如在“南水北调中线行”采风活动中，15人采风团历时39天，途经三省两市，行程6000多公里，既阐释了南水北调的重大意义，又传播了南阳的文化、经济、民情等。该系列报道被评为2010年度中国新闻奖三等奖。 </p> \n<p style=\"text-indent:2em;\"> 近年来，南阳日报人沿着全国农运会举办轨迹，从南阳走进前六届举办地；沿着生态经济崛起带，从南阳走到苏鲁赣；沿着直通西南发展轴，从南阳走到济郑渝；沿着务工创业人的足迹，从南阳走向全国……开设了《南阳群英谱》专栏，派出记者分赴全国各地进行采访报道。开放办报丰富了报纸的内容、扩大了报纸影响力。 </p> \n<p style=\"text-indent:2em;\"> 为了让新闻更丰富鲜活，南阳日报社充分发挥通讯员的力量。每年一度持续培训通讯员达1.4万余人次，成立新闻摄影学会，开设通讯员专版和专栏。此举深化了“走转改”活动，把镜头更多地对准基层，增强了报道的吸引力、影响力、感染力和亲和力。 </p> \n<p style=\"text-indent:2em;\"> 基层出新闻，创新出活力。从采写好新闻的角度出发，南阳日报社成立重点报道组、评论组和走基层特别报道组，在重大报道、重要评论和“走转改”中发挥了不可替代的作用。为了让好新闻更好看，南阳日报社明确六项要求：标题醒目、言论简明、图片大气、篇幅精短、栏目清晰、版式美观。在这些举措推动下，南阳日报社先后被评为第七届中国品牌媒体百强、地市党报影响力十强等。 </p> \n<p style=\"text-indent:2em;\"> <strong> 求创新，四轮驱动运营 </strong> </p> \n<p style=\"text-indent:2em;\"> <span style=\"font-family:楷体, 楷体_gb2312, simkai;\"> 推动报业经营从平面媒体向户外媒体扩张，2014年发行、广告、实体经济营收过亿 </span> </p> \n<p style=\"text-indent:2em;\"> 2014年，南阳日报社荣获中国报业经营管理奖，被评为第三届中国地市报经营创新十强。荣誉的获得来自于创新，更来自于实干。按照“多媒体运作、多元化经营”的思路，2013年，南阳日报社众新演艺公司举办亚洲巨星演唱会，带动南阳住宿、餐饮、旅游、交通业的消费增长达1.8亿元。 </p> \n<p style=\"text-indent:2em;\"> 在发展报业经营的基础上，兼顾新媒体扩张。南阳日报社投入1000万元在机场和高速公路入口等重要地段新建大型城市电视LED屏8块，实现了平面媒体向户外媒体扩张。2014年，南阳日报社通过整合，报社发行、广告、实体经济共实现经营收入1亿元，南阳网获中国最具影响力十大城市门户网站提名奖。2015年，户外传媒、物业、《躬耕》杂志、图书出版、旅游推介、金融运作等都有新扩张，多元化经营全面推进。此外，南阳日报社完成文新印务公司的组建和迁址工作，商品印刷2014年同比增长30%，实现印刷产值4000万元。 </p> \n<p style=\"text-indent:2em;\"> 面对报业广告寒冬的大环境，南阳日报社紧紧围绕重要节日、活动、行业，推动广告和项目、品牌、产业相结合，同时制定广告刊登规范流程、处罚办法、奖惩意见等。与此同时，南阳日报社提出一系列发行理念。2015年，《南阳日报》发行量稳定在9万份，《南阳晚报》发行量达8万份，《南都晨报》发行量突破3万份，三报发行首次突破20万份大关。《躬耕》杂志发行1万份，手机报6万份。南阳日报社连续荣获中国报协发行创新奖，多次被评为全国报纸自办发行工作先进集体。 </p> \n<p style=\"text-indent:2em;\"> <strong> 深改革，多元创新发展 </strong> </p> \n<p style=\"text-indent:2em;\"> <span style=\"font-family:楷体, 楷体_gb2312, simkai;\"> 多元创新性思路领跑报业发展，以印务公司试行股份制，以多元合作承办体育赛事 </span> </p> \n<p style=\"text-indent:2em;\"> 在数字化、融媒体时代，南阳日报社积极推进多元创新发展。为此，南阳日报社提出4种合作模式：一是资金投入，与河南日报报业集团合作成立大河印务股份有限公司，南阳日报社占35%的股份；承接了《河南日报》《大河报》的印刷发行任务。二是全员入股，报社全体员工筹资1000万元入股南阳文新印务公司，实现企业股份化，员工主人化。三是联合举办，在办学和培训业务拓展上，和河南大学新闻与传播学院共同开展在职在岗人员培训工作。四是专项合作，在体育赛事上，与南阳市体育局及河南男篮俱乐部合作，承办全国男子职业篮球联赛，实现广告收入和社会影响双丰收。 </p> \n<p style=\"text-indent:2em;\"> 做好了制度框架，南阳日报社细化目标管理制度，制定了“两定一联”（定年度目标，定成本管理，联系奖惩）的管理办法。2014年，南阳日报社和南阳晚报社广告发行超经营目标；南阳网在全省省辖市流量和排名位居前列。 </p> \n<p style=\"text-indent:2em;\"> 关玉国说，改革的成绩都是发展的基石，组建南阳报业传媒集团后，将抓住宣传质量提升期、多元经营突破期、媒体融合机遇期、阵地建设黄金期、队伍素质适应期、报业体制改革期的机遇，实现六大融合，即新闻资源大融合、经营活动大融合、发行队伍大融合、广告业务大融合、内部管理大融合、人力资源大融合，为做大做强报业提供强有力的保障。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：韩为卿 冯兴阁 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月28日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 下基层捕捉新闻 从深处推进改革','2015-12-16 17:37:46','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/267275.shtml',NULL,NULL,NULL,NULL,NULL),(94,'国家新闻出版广电总局 - 行业信息 - 华东教育社年会热议出版转型升级','华东教育社年会热议出版转型升级','2015年10月30日','左志红  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 10月27日-29日，第36届华东地区教育出版社年会在江西南昌举行。参加本届年会的人民教育出版社、华东地区11家教育出版社以及华中地区部分教育出版社的社长、总编辑，就“互联网+”与教育出版转型升级、新常态下的教育出版发展、教育出版社的集团化发展战略研究等话题展开热议。 <br> </p> \n<p style=\"text-indent:2em;\"> 曾在江西工作23年的原新闻出版总署副署长桂晓风出席了年会，他对教育出版社提出了3点希望：一是教育出版社应该在新一轮文化大发展过程中有更高追求，二是教育出版社要更好地为全面教育服务，三是教育出版社应该在全媒体出版和阅读、中国出版走向世界方面作出更大贡献。 </p> \n<p style=\"text-indent:2em;\"> 据悉，今年是此次年会承办方江西教育出版社成立30周年，中国新闻出版传媒集团有限公司总经理李忠代表集团向该社赠送了山水画《桃李春风青山绿水》表示祝贺。 </p> \n<p style=\"text-indent:2em;\"> 此外，本届年会还评选出华东地区优秀教育图书奖，《文化遗产在江西》（江西教育出版社）、《中国花鼓灯》（安徽教育出版社）、《不一样的数学故事》（山东教育出版社）等72种图书获奖。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：左志红 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月30日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 华东教育社年会热议出版转型升级','2015-12-16 17:37:47','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/267459.shtml',NULL,NULL,NULL,NULL,NULL),(95,'国家新闻出版广电总局 - 行业信息 - 中国出版代表团参加贝尔格莱德书展','中国出版代表团参加贝尔格莱德书展','2015年11月3日','','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 第60届贝尔格莱德国际书展10月25日至11月1日在塞尔维亚首都贝尔格莱德展览中心举办。由人民出版社、世纪出版集团、中国大百科全书出版社、中国青年出版社等十几家出版机构组成的中国出版代表团参展。 <br> </p> \n<p style=\"text-indent:2em;\"> 中国出版代表团展出的600余册图书受到读者欢迎。其中，英、法、德、俄、阿文版的《习近平谈治国理政》尤其引人关注，不断有读者翻阅与咨询。据代表团团长、人民出版社总编辑辛广伟介绍，该书目前的总发行量已经超过520余万册，其中外文版发行了31万册，创下了同类图书的多项纪录。此外，人民出版社的“图解当代中国丛书”、《中国经济新常态》、《“一带一路”：机遇与挑战》，北京师范大学出版社的《中国美术史》等反映当代中国政治、经济、文化的图书也受到普遍欢迎。代表团还积极与参展商特别是当地出版社，洽谈版权贸易与相关合作。 </p> \n<p style=\"text-indent:2em;\"> 书展上，中国代表团与塞尔维亚当地的孔子学院联合在中国展台举办了书法演示、中国结制作、中国主题电影放映等活动。 </p> \n<p style=\"text-indent:2em;\"> 塞尔维亚方面非常重视中国代表团的参加，本届书展主席佐兰专门到中国展位参观，并向代表团赠送了他撰写的著作。 </p> \n<p style=\"text-indent:2em;\"> 本届贝尔格莱德书展的主宾国是俄罗斯，来自20多个国家的近500家图书出版机构参展。 </p> \n<p> <br> </p> \n<p> 来源：中国新闻出版广电报 &nbsp;发布时间：2015年11月3日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 中国出版代表团参加贝尔格莱德书展','2015-12-16 17:37:48','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/267702.shtml',NULL,NULL,NULL,NULL,NULL),(96,'国家新闻出版广电总局 - 行业信息 - 《中国近代思想家文库》研讨会召开','《中国近代思想家文库》研讨会召开','2015年11月4日','范燕莹  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 由中国人民大学出版社和光明日报社评论部、光明读书会联合举办的《中国近代思想家文库》学术研讨会近日在京召开。与会嘉宾围绕《中国近代思想家文库》新书的出版，就“中国近代思想的现代化历程”这一话题展开讨论。全国人大教科文卫委员会主任委员、《中国近代思想家文库》编纂委员会主任柳斌杰出席研讨会并发言。 <br> </p> \n<p style=\"text-indent:2em;\"> 《中国近代思想家文库》是国家出版基金资助项目，由一大批海内外杰出的中国近现代史研究专家参与编纂。《文库》编纂历时5年，共100卷。入选的思想家，主要是活跃在1840年至1949年之间的思想人物。 </p> \n<p style=\"text-indent:2em;\"> 会上，柳斌杰和中国人民大学党委书记靳诺分别发言。他们认为，中国近代思想存在着无比的多样性和复杂性，各种思想交融、碰撞，呈现出绚丽多彩的姿态。虽然时隔久远，但它们并不会因此而失去价值。研究中国近代思想的现代化历程，对我们仍有参考借鉴的意义。 </p> \n<p style=\"text-indent:2em;\"> 与会学者还就“中国近代思想的现代化历程”这一话题展开热烈讨论。《文库》编纂委员会主编、国家清史编纂委员会主任戴逸介绍，百卷《中国近代思想家文库》是国内首部全面展现中国近代思想发展过程的鸿篇巨制。中国近代思想发展的这些成果不仅推动了中国的发展进步，也可以为现代中国的智库建设提供参考。中国社会科学院学部委员耿云志认为，中国近代思想家思考的问题主要在于中国如何面对世界以及如何最大限度地释放民族的创造力，《文库》集文献性和研究性于一体，具有重要的学术价值与现实意义。 </p> \n<p> <br> </p> \n<p> 作者：范燕莹 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年11月4日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 《中国近代思想家文库》研讨会召开','2015-12-16 17:37:49','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/267792.shtml',NULL,NULL,NULL,NULL,NULL),(97,'国家新闻出版广电总局 - 行业信息 - 294件作品获第二十五届中国新闻奖','294件作品获第二十五届中国新闻奖','2015年11月3日','赵新乐  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 11月2日，中华全国新闻工作者协会主办的第二十五届中国新闻奖评选结果揭晓。来自全国报社、通讯社、电台、电视台和新闻网站的294件作品获中国新闻奖，其中特别奖4件，一等奖47件（含10个新闻名专栏），二等奖91件，三等奖152件。 <br> </p> \n<p style=\"text-indent:2em;\"> 据悉，《解放军报》、新华社的通讯《生命线在强军兴军伟大征程中闪耀——习近平主席、中央军委领导和推进新形势下军队政治工作纪实》，《人民日报》的文字评论《标注共产党人的精神坐标》，新华社的文字通讯《更高举起改革开放伟大旗帜——写在邓小平同志诞辰110周年之际》，中央电视台的电视系列《新春走基层·家风是什么》获中国新闻奖特别奖。 </p> \n<p style=\"text-indent:2em;\"> 《江西日报》的文字消息《项目审批“长征”698天 泰豪动漫变“动慢”》、中央人民广播电台的广播专题《巡航钓鱼岛亲历》、内蒙古广播电视台的电视访谈《住在涵洞为讨薪》、北方网的网络访谈《“当代鲁班”的中国梦》等作品获中国新闻奖一等奖，《中国纪检监察报》的《方圆谈》、《新华日报》的《漫说快评》、西藏人民广播电台的《驻村夜话》、新华网的《数据新闻》等获新闻名专栏。 </p> \n<p> <br> </p> \n<p> 作者：赵新乐 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年11月3日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 294件作品获第二十五届中国新闻奖','2015-12-16 17:37:49','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/267703.shtml',NULL,NULL,NULL,NULL,NULL),(98,'国家新闻出版广电总局 - 行业信息 - 国产操作系统推动正版化进程','国产操作系统推动正版化进程 首届“一铭杯”中国Linux软件大赛开赛','2015年10月29日','邹韧  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 几乎人人都有电脑、手机，要使用这些硬件都离不开操作系统。但大多数人熟悉并使用的都是国外的操作系统，真正属于我国自己原创的操作系统少之又少，更别说普及应用了。近日，由国内著名操作系统软件厂商一铭软件主办的“第一届‘一铭杯’中国Linux软件大赛”在北京正式开赛。大赛的目的是为了寻找优秀的Linux软件应用项目和Linux软件开发团队，完善我国国产操作系统软件的产业链和生态环境，从而推动软件正版化国产化采购和信息化进程。 </p> \n<p style=\"text-indent:2em;\"> 据了解，这是近年来国内首届专门针对Linux的软件大赛。在大赛组委会召开的新闻发布会上，业界专家表示，面对Win8换代和XP停止服务的机会，国产Linux真的要来了。显然，在新型的市场环境和需求背景下，国产Linux不再仅仅满足于以往的政府采购市场，而是向往更广阔的发展空间。 </p> \n<p style=\"text-indent:2em;\"> <strong> 让中国人能用上国产操作系统 </strong> </p> \n<p style=\"text-indent:2em;\"> 在移动互联网、云计算、物联网、大数据、智慧城市等城市信息技术和应用新浪潮的今天，除了政府采购之外，国产软件看到更多的是党政军部门到金融、能源、电信、交通等关键行业，一直到普通消费者市场，甚至在新型市场，国产操作系统都有望成为市场核心。 </p> \n<p style=\"text-indent:2em;\"> 一铭软件CEO刘轩铭认为，国产操作系统要有“2C”思维，他希望未来中国的消费者都能用上国产操作系统。在他看来，国产操作系统的应用不只停留在桌面端、服务器端、嵌入式设备层面，更需要向移动互联网和物联网快速渗透。比如扩展到智能手机、平板电脑、可穿戴设备及其他移动终端。国产操作系统要有用户思维和大数据思维，数据不仅涉及信息安全，更是一切发展的基础和动力源，未来更是个性化和定制化的时代，操作系统会更接近于服务。在“互联网+”的大环境下，产业链上各个厂商之间要建立公平、开放、透明、协作的关系，国产操作系统需要百花齐放、百家争鸣。 </p> \n<p style=\"text-indent:2em;\"> <strong> 国产软件进入创新发展2.0时代 </strong> </p> \n<p style=\"text-indent:2em;\"> 在此前国产操作系统发展的多年里，其创新往往局限于局部的产品创新，而非包括技术、产品、人才、市场、服务和发展模式的整体创新，市场销售普遍将更多精力放在政府采购层面。如今，时代不同了，刘轩铭认为，如果说此前是单个国产操作系统厂商与包括芯片、服务器、杀毒软件、浏览器在内的产品适配和数据接口开放，那么全产业链的合作和生态优化则是把国产软件的创新发展推进到了2.0时代。所谓创新发展2.0，在纵向上就是把上下游的产业链打通，包括产品适配、服务和品牌，横向上则需要在重点行业、新兴行业、新兴市场上有所布局。 </p> \n<p style=\"text-indent:2em;\"> 众所周知，微软操作系统经过20多年的发展，有大批的硬件厂商、软件服务商围绕操作系统为其量身定制相关产品，形成了良性的生态圈。那么操作系统作为国产软件的基础，同时也是各种应用软件的底层平台，具备整合自主可控产业链上其他各环节的技术优势、产品优势和服务优势。对此，在第一届“一铭杯”中国Linux软件大赛的新闻发布会上，许多业界专家都表示，完善产业链、优化软件产业发展的生态环境绝非朝夕之事，国产软件厂商要想追赶国际厂商并与之形成抗衡局面，只能是以空间换时间，在较短的时间内换取更多更大的进步。 </p> \n<p style=\"text-indent:2em;\"> <strong> 大赛为国产软件创新搭台铺路 </strong> </p> \n<p style=\"text-indent:2em;\"> 刘轩铭提出，尽管操作系统软件作为软件产业的核心和基础，具有重要的核心产业地位，但是产业链的建设和生态环境的优化，不能以某家企业或某个产品为局限，业界需要有一个领军企业挑起大梁，在市场商用领域起到带头作用。国产软件生态链不是一两个厂商就能支撑起来的，而是需要软件厂商、软件开发者、硬件厂商、第三方评测机构，乃至用户的共同努力，构建起一个覆盖配件、终端、应用服务等诸多环节的完整的生态圈，实现整体突破。 </p> \n<p style=\"text-indent:2em;\"> 此次举办首届“一铭杯”中国Linux软件大赛，就是要围绕基于国产Linux的研发、应用和创新，搭建一个资源汇聚和创新的平台，并在此基础上，推动各个国产软硬件厂商之间的合作，共同推动软件产业和应用的有序发展。据悉，中国Linux软件大赛今后将定期举办，逐步发展成为一个培育创新技术、产品适配、孵化项目、汇聚资源、推广应用、人才的平台，借此完善国产软件产业链，优化生态发展环境。未来，大赛将会承载更多第三方功能、责任和使命。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：邹韧 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月29日 </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 国产操作系统推动正版化进程','2015-12-16 17:37:49','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/267382.shtml',NULL,NULL,NULL,NULL,NULL),(99,'国家新闻出版广电总局 - 行业信息 - 江苏：“你选书，我买单”活动常态化','江苏：“你选书，我买单”活动常态化','2015年11月5日','李明远  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> “自首届金陵图书馆读者节10月15日开幕以来，读者通过‘你选书，我买单’荐购活动在南京市新华书店新街口店借阅新书已达720册。”近日，江苏南京市新华书店社会团体服务中心经理郭伟在接受《中国新闻出版广电报》记者采访时说。 <br> </p> \n<p style=\"text-indent:2em;\"> 凤凰出版传媒集团发行部副主任秦俊俊说：“‘你选书，我买单’活动改变了以前新书上市两周甚至更长时间读者才能借阅的情况。它颠覆了传统图书馆采、藏、借的服务流程，让持有图书馆借阅证的读者能够在第一时间借到想看的新书。” </p> \n<p style=\"text-indent:2em;\"> <strong> 书店图书馆共建系统 惠民活动常态化 </strong> </p> \n<p style=\"text-indent:2em;\"> 秦俊俊说，为了有效推动“你选书，我买单”活动，书店和图书馆都付出了一定的人力物力，比如图书馆工作人员到书店指导店员操作，店员承担贴磁条、编目等工作。 </p> \n<p style=\"text-indent:2em;\"> 今年世界读书日期间，这项读者在书店借阅新书到图书馆归还的服务，率先在江苏苏州、镇江开展。半年来，图书馆采购借阅服务实现了由单向式“我送你接”向交互式“供需对接”转变，促进了读者、书店和图书馆三赢。 </p> \n<p style=\"text-indent:2em;\"> 为长期开展“你选书，我买单”荐购活动，书店专门打造了一套与图书馆对接的“书店-图书馆采购系统”。该系统可让图书馆掌握凤凰传媒集团能够采购到的新书情况，书店店员、图书馆管理员可通过扫描图书条形码，获取图书馆馆藏复本量、新书品种等信息，筛除不能参加活动的图书。随着系统不断成熟，活动流程已经非常清晰。读者在书店借阅新书时，可通过检验图书是否在可选范围内、保留名额、核验读者身份信息等几个非常简单的步骤完成。 </p> \n<p style=\"text-indent:2em;\"> <strong> 参与主体将不断扩展 活动形式更多元 </strong> </p> \n<p style=\"text-indent:2em;\"> “你选书，我买单”活动让读者选书的主动性增强，拓宽了图书馆采购渠道和书店的销售渠道。但如果想让活动辐射范围更广、更加便民，需要参与主体不断扩展，活动形式不断创新。 </p> \n<p style=\"text-indent:2em;\"> 在苏州，活动首期开放的书店包括苏州书城、苏州凤凰书城、苏州图书馆天香书屋以及两家初见书房。“苏州读者持市民卡或苏州市图书馆借阅证均可到书店选书。书店店员有了更多接触读者、与图书馆沟通的机会，这促进了本就承担社会公共文化服务功能的书店提升服务水平。”苏州市新华书店市场总监沈菊芃说。 </p> \n<p style=\"text-indent:2em;\"> 截至目前，由苏州读者荐购的图书已达2.3万册。苏州图书馆副馆长许晓霞告诉记者，除了到书店借阅，苏州的读者还可以通过手机应用“书香苏州”检索、荐购图书。苏州市图书馆可通过物流配送，将图书免费投递到读者所居住的社区。随着投递点的扩展，“你选书，我买单”活动覆盖面将更广。 </p> \n<p style=\"text-indent:2em;\"> 镇江读者如今可以到镇江市图书馆、江苏大学图书馆借阅图书。镇江市新华书店总经理贺卫东告诉记者，为鼓励读者发掘受大众喜爱的新书，荐购活动实行积分制。一名读者所借阅的图书若同时被其他几名读者借阅，则会获得相应积分。达到一定积分后，读者在书店同时借阅的图书册数可有所增加。 </p> \n<p style=\"text-indent:2em;\"> <strong> 未来有望全省联网推广 需要资金持续支持 </strong> </p> \n<p style=\"text-indent:2em;\"> “从系统运营情况看，活动已经比较成熟。只是仍需要通过新媒体手段、线下活动等让这项服务得到更多读者关注、参与。”沈菊芃告诉记者。10月11日、13日和20日，“你选书，我买单”活动相继走进苏州佳能公司、纽威阀门公司和平江中学，把荐购活动带到读者工作学习的场所，这样的便民服务获得了读者欢迎。沈菊芃说，将继续把图书带到企业、学校和社区等，让荐购活动与书香城市建设相互促进。 </p> \n<p style=\"text-indent:2em;\"> 由于图书馆的定位与读者群不同，各地各家图书馆在举办荐购活动时，图书品种、价格、复本量等方面各有规定。未来持续推进荐购活动，稳定、适度的购书资金不可或缺。“我们设定30天为一个周期，对活动情况持续观察。如果活动在两三年时间内形成良性循环，图书馆要制订计划，每年拿出一定比例的购书资金，专门用于结算读者荐购图书。”金陵图书馆采编部主任孙振强说。 </p> \n<p style=\"text-indent:2em;\"> 记者在采访中了解到，南京市新华书店正与江苏省图书馆洽谈，在南京扩展推广荐购活动。明年镇江也有望将本地具备一定规模的4家图书馆都纳入活动范畴。“随着‘你选书，我买单’荐购活动在苏州、镇江、南京的成功举办，凤凰出版传媒集团将继续在全省其他地区推广经验。”秦俊俊说。随着条件的成熟，未来江苏省的读者有望实现全省联网、一体借阅。 </p> \n<p> <br> </p> \n<p> 作者：李明远 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年11月5日 </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 江苏：“你选书，我买单”活动常态化','2015-12-16 17:37:49','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/267960.shtml',NULL,NULL,NULL,NULL,NULL),(100,'国家新闻出版广电总局 - 行业信息 - 持中知变 变中谋转','省级晚报、都市报老总谈报业整装突围—— 持中知变 变中谋转','2015年10月29日','范燕莹  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> “过去的辉煌变成了今天的包袱。”“牛市已过，熊市来临。”……这些话是一些晚报、都市报老总用来形容报业如今所面临的传媒“生态”。走过“黄金20年”，随着互联网的迅猛发展，晚报、都市报如何再续辉煌？ </p> \n<p style=\"text-indent:2em;\"> 第二十届全国省级晚报（都市报）总编辑年会高峰论坛近日在河南登封举办，从午后茶叙到秉烛夜谈，来自全国28家省级晚报（都市报）的社长、总编辑、资深媒体人，围绕“持中知变”的主题，共商晚报、都市报的整装突围之路，探讨变中取胜之道。 </p> \n<p style=\"text-indent:2em;\"> <strong> 内容为根本 </strong> </p> \n<p style=\"text-indent:2em;\"> “当年我还是一个青春勃发的小伙子，现在两鬓斑白了。心境不一样，上次都市报还是蓬勃向上，野蛮生长的时候，当时老总们在一起谈的都是钱，弯腰在地上都可以捡到钱的时代，现在是到处找钱。”2000年，《大河报》组织一批都市报的同仁到河南采风，现任《三湘都市报》总编辑的张云梦参与其中，而今故地重游，谈到都市报的处境让他感慨万千。 </p> \n<p style=\"text-indent:2em;\"> 面对当前报业面临的发展危机，一些老总认为，虽然形势不容乐观，但前途未必黑暗。若用一句典型的河南方言来形容，那就是纸媒还“中”。 </p> \n<p style=\"text-indent:2em;\"> 谈到坚守，安徽《新安晚报》总编辑丁传光说：“现在无非在报言报，没有什么好的办法，就是要坚持，有危机才要‘持’，既然要‘持’，就不能慌张。我们没有到山穷水尽的地步，也没有到非要把我们灭掉的地步。”他认为，20多年来，晚报、都市报锻造了全国新闻领域最过硬、最专业的新闻团队，只要团队不倒、人心不散，总有希望。 </p> \n<p style=\"text-indent:2em;\"> 在《中国记者》总编辑文璐看来，晚报、都市报是最能创新的队伍，是一支最有活力、最有执行力、最能做实事的队伍，最能开创新局面。“无论形态如何变化，内容应该永远是媒体的立身之本，在这种前提下坚守才能够开创新的前景。” </p> \n<p style=\"text-indent:2em;\"> <strong> 服务为纽带 </strong> </p> \n<p style=\"text-indent:2em;\"> “知变、谋变、善变，变则通，通则久。”《楚天都市报》总编辑陈剑文表示，本次年会主题特别贴切，“变”就是当下晚报、都市报面临的考验，传统媒体正主动拥抱变革，在未来的新环境中谋求新发展。 </p> \n<p style=\"text-indent:2em;\"> 《辽沈晚报》总编辑徐晓民谈道，今年该报在转型思路上确实有很大的变化，按照辽宁日报报业集团确定的“三小一大”原则，采取编采小、发行小、行政小、经营大的发展模式。 </p> \n<p style=\"text-indent:2em;\"> 河南日报报业集团编委、副总编辑董林认为，在过去“媒体+服务”模式中，晚报、都市报的市场化程度低，只会卖广告、卖报纸，但未来要转变为“服务+媒体”，要把服务放在重要位置，要用服务增强用户黏度，创造商业价值。 </p> \n<p style=\"text-indent:2em;\"> <strong> 品牌为抓手 </strong> </p> \n<p style=\"text-indent:2em;\"> 近几年，依靠自身品牌影响力，晚报、都市报在非报产业领域进行探索。不少老总认为，都市报想要生存，就要开源节流，而发展非报产业就是在“开源”。 </p> \n<p style=\"text-indent:2em;\"> “每个省级晚报、都市报都有一定的品牌价值，在转型过程中要利用好纸媒的这种价值。”宁夏《新消息报》总编辑张九阳认为，靠晚报、都市报多年积累的品牌效应，可以延伸很多实体产业。 </p> \n<p style=\"text-indent:2em;\"> “都市报深耕一个城市，把自身所在的城市挖透，从这个角度做非报产业的经营。”《兰州晨报》总编辑周丹波介绍，该报通过地铁修建，利用铁路开通等机会，拓展户外广告；另外，在新媒体尝试电商，利用平台优势，与金融机构、企业对接，共同经营一些项目。 </p> \n<p style=\"text-indent:2em;\"> 《海峡都市报》闽南版总编辑林华表示，目前，该报除了做房地产布局外，还有一个亮点，就是做了都市生活供应商，开创了95060生活服务平台，通过公众服务公司提供各种服务。但林华也坦言，根据对多地的考察，虽然很多媒体的非报产业取得一些成效，但是大多还没找到可复制的模式。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：范燕莹 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月29日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 持中知变 变中谋转','2015-12-16 17:37:50','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/267381.shtml',NULL,NULL,NULL,NULL,NULL),(101,'国家新闻出版广电总局 - 行业信息 - 第四届韬奋出版人才高端论坛在京举行','第四届韬奋出版人才高端论坛在京举行 业界热议培养“互联网+”时代出版人才','2015年11月6日','左志红 洪玉华  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 11月5日，时值邹韬奋同志诞辰120周年纪念日，第四届韬奋出版人才高端论坛在京举行。全国出版界近200位代表相聚在一起，畅谈如何继续弘扬和传承韬奋精神，培养“互联网+”时代的出版人才。 </p> \n<p style=\"text-indent:2em;\"> 作为纪念邹韬奋同志诞辰120周年系列纪念活动的重要组成部分，本届论坛内容格外丰富厚重：《韬奋全集（增补本）》《邹韬奋年谱长编》分别由上海人民出版社和上海交通大学出版社首发，第四届韬奋出版人才高端论坛征文评选活动颁奖，以“互联网+”时代的出版人才为主题的论坛举办。 </p> \n<p style=\"text-indent:2em;\"> 国家新闻出版广电总局人事司巡视员李潞在致辞中说，韬奋同志的爱国、奉献、创新和敬业精神，深深影响了几代人。在“互联网+”背景下，弘扬韬奋精神，培养新型出版人才，要求我们不断探索人才培养规律，拓宽人才培养渠道，通过产学研和行政部门共同努力，为出版业转型升级提供更多智力支持。 </p> \n<p style=\"text-indent:2em;\"> 在主题论坛上，韬奋基金会理事长聂震宁作了题为《“互联网+”时代的出版人才思维》的主旨报告。他说，自己刚刚出版的《韬奋精神六讲》中，将韬奋精神归纳为“为大众、爱祖国、敢斗争、善经营、懂管理、真敬业”。他认为，弘扬和继承韬奋精神，就要做好“互联网+”时代的人才培养和引进工作，高度重视生成内容专业人才、知识经济管理人才、信息营销复合人才、客户服务管理人才、既懂互联网又通传统出版的复合型领军人才。 </p> \n<p style=\"text-indent:2em;\"> 围绕“互联网+”背景下如何打造新型出版人才队伍、媒体融合时代出版人才的需求与应对等议题，中国出版集团公司副总裁刘伯根、中国教育出版传媒股份有限公司总经理于春迟、中国科技出版传媒集团有限公司董事长柳建尧、人民出版社社长黄书元、中国大学出版社协会理事长宗俊峰、山东出版集团有限公司董事长张志华等10余位代表先后发言，其中既有理论上的探索，也有对实践的总结。大家认为，唯有对新生业态积极学习并迅速适应，既懂出版专业又懂网络传播的出版人才，才能在“互联网+”时代展露身手，成为行业翘楚。 </p> \n<p style=\"text-indent:2em;\"> 上海人民出版社总编辑王为松介绍了《韬奋全集（增补本）》出版情况，增补本对20年前的老版本进行了校订和补充，以完善邹韬奋的思想、学术脉络，全面展现韬奋精神。上海交通大学出版社总编辑张天蔚在介绍《邹韬奋年谱长编》一书的出版情况时说，邹韬奋是上海交通大学杰出校友，明年是上海交大成立120周年，出版该书既是对邹韬奋的纪念，也是文化和精神的回归。 </p> \n<p style=\"text-indent:2em;\"> 本届论坛征文活动颁奖典礼同期举办，长征出版社姜兴华等60名征文作者和凤凰出版传媒集团等5家组织单位受到表彰。此次评选收到19个省（区、市）的181篇征文，稿件数量为历届之最。获奖征文内容涉及传统出版人才研究、数字出版人才研究、出版专业教育研究、案例研究等多个方面，具有很强的现实意义。 </p> \n<p style=\"text-indent:2em;\"> 本届论坛由韬奋基金会主办，中国新闻出版研究院、中国新闻出版广电报社联办，百道网协办。中国新闻出版传媒集团董事长、中国新闻出版广电报社社长兼总编辑马国仓主持新书发布会和颁奖典礼，中国新闻出版研究院院长魏玉山主持主题论坛。 </p> \n<p> <br> </p> \n<p> 作者：左志红 洪玉华 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年11月6日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 第四届韬奋出版人才高端论坛在京举行','2015-12-16 17:37:51','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/268109.shtml',NULL,NULL,NULL,NULL,NULL),(102,'国家新闻出版广电总局 - 行业信息 - 第22届中国国际广告节西安举行','第22届中国国际广告节西安举行','2015年10月28日','李雪昆  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 10月23日-26日，以“丝路·新语”为主题的第22届中国国际广告节在陕西西安举行。传媒人、企业负责人、广告策划人及高校的专家学者等共同探讨广告行业的现在和未来，营造了一场创意碰撞、知识交流、科技展示的广告盛会。 <br> </p> \n<p style=\"text-indent:2em;\"> 本届广告节在内容和形式上都较往届有所创新和突破，凸显出三大元素。一是广告元素，充分展示广告行业的新技术、新材料、新创意，宣传广告发展的新成就；二是陕西元素，本届广告节特设陕西馆，主要展示陕西本土广告媒体、广告企业、广告标识、驰著名商标以及本土文化活动，让全国同行领略到陕西广告业的独特魅力；三是丝路元素，作为丝绸之路的新起点，通过和丝路国家的广泛交流，使广告成为构建丝绸之路经济带的排头兵。 </p> \n<p style=\"text-indent:2em;\"> 此外，本届广告节还设置了广告高峰论坛、专业奖项颁奖、获奖作品展示、媒介设备展览等丰富多彩的内容，并举行了为广告业提供金融服务的战略合作签约仪式，实现广告业与金融业融合发展。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：李雪昆 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月28日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 第22届中国国际广告节西安举行','2015-12-16 17:37:51','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/267274.shtml',NULL,NULL,NULL,NULL,NULL),(103,'国家新闻出版广电总局 - 行业信息 - 《习近平谈治国理政》越南文版在河内出版发行','《习近平谈治国理政》越南文版在河内出版发行','2015年11月6日','','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 《习近平谈治国理政》越南文版11月4日在越南河内出版发行。《习近平谈治国理政》越南文版由中国外文出版社与越南国家政治出版社合作翻译出版。 <br> </p> \n<p style=\"text-indent:2em;\"> 《习近平谈治国理政》由国务院新闻办公室会同中共中央文献研究室、中国外文局编辑，由外文出版社以中、英、法、俄、阿、西、葡、德、日等多语种于2014年9月向全球发行。目前，该书已经发行到100多个国家和地区，发行量达520多万册。 </p> \n<p style=\"text-indent:2em;\"> 《习近平谈治国理政》越南文版是继韩文版之后第二个中方与外国出版机构合作翻译出版的版本，越方组织了国内顶尖翻译出版团队参与翻译出版工作。 </p> \n<p> <br> </p> \n<p> 来源：中国新闻出版广电报 &nbsp;发布时间：2015年11月6日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 《习近平谈治国理政》越南文版在河内出版发行','2015-12-16 17:37:51','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/268110.shtml',NULL,NULL,NULL,NULL,NULL),(104,'国家新闻出版广电总局 - 行业信息 - 《今晚报》：13年创办50个海外版','《今晚报》：13年创办50个海外版','2015年11月3日','樊国安 刘梦琦  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 10月15日，由《今晚报》与匈牙利主流华文媒体《新导报》合作的《今晚报·匈牙利英文版》在匈牙利首都布达佩斯创刊发行。这是《今晚报》创办的第50个海外版，也是该报创刊的第23个外文版。 <br> </p> \n<p style=\"text-indent:2em;\"> 《今晚报》从2002年3月创办第一个海外版开始，13年间50个海外版遍布全球29个国家及地区的31个城市，其中，外文版使用英、法、西、俄、德、日、韩7种语言，是目前中国拥有海外版最多、涉外语种最多的区域性媒体，每周对外发行的总数达到了300万份，有300多万人可以从中了解中国、了解天津，该报海外版成为传播中国声音、讲述天津故事的国际性媒体平台。业内人士认为，《今晚报》13年成功创办50个海外版的基本经验就是坚持走出去的发展战略，通过与海外媒体合作的方式，建立起传播中华文化、讲述中国故事的渠道。 </p> \n<p style=\"text-indent:2em;\"> 海外华文主流媒体分布在世界各大中心城市，拥有熟悉当地读者的办报经验，是当地华人和社会认可的新闻媒体。同时，这些媒体也愿意与《今晚报》合作，在当地创办《今晚报》的海外版，让更多的华人和当地读者了解改革开放的中国。《今晚报》海外版的创办经验和做法，得到了中央外宣办的重视和肯定，也吸引了更多海外华文媒体积极踊跃与《今晚报》合作创办海外版。 </p> \n<p style=\"text-indent:2em;\"> <strong> 向全语种扩展 </strong> </p> \n<p style=\"text-indent:2em;\"> 截止到2011年年底，《今晚报》已经创办了22个海外版，这些海外版为报道和宣传中国以及天津都发挥了积极作用，但是这22个海外版都是中文版，读者基本为海外华人以及能读懂中文的当地人群，其影响力和辐射力受到一定局限。 </p> \n<p style=\"text-indent:2em;\"> 对此，《今晚报》编委会认为，若想实现真正有效的国际传播，必须创办英文版或者当地母语版。在中国和平崛起的大背景下，海外华文媒体也在考虑通过外文版适应当地人群需求。因此，《今晚报》决定调整海外传播思路，向创办面向国外主流人群的英文版或者当地母语版的海外版转型。 </p> \n<p style=\"text-indent:2em;\"> 从2011年年末创办第一个外文版《今晚报·澳洲英文版》到2012年年底，在一年时间里，今晚报社相继创办了5个外文版，其中，3个英文版，包括澳洲版、美国版和非洲版，一个西班牙文版，一个日文版，这些外文版在这些国家和主流人群中产生了很好的反响。到今年10月15日，《今晚报》已经在全球五大洲的5个国家创办了7个语种的23个外文版。这些外文版有三个特点：一是合作对象均为当地有影响的华文媒体；二是这些海外华文媒体都有当地语种的新闻版，在当地主流人群中有一定影响力；三是他们都与今晚报社有良好的合作关系，具有扩大合作领域的基础。 </p> \n<p style=\"text-indent:2em;\"> 目前，《今晚报》编辑出版的海外版由于参考了国内权威外文报纸和国外权威报纸，使内容和版面风格更贴近国外读者的口味。让国外读者从这些报道中，了解真实的中国、真实的天津，获得了国外读者的广泛认可。同时，《今晚报》海外版在当地主流社会的影响力也日益凸显。 </p> \n<p style=\"text-indent:2em;\"> <strong> 突破合作模式 </strong> </p> \n<p style=\"text-indent:2em;\"> 《今晚报》在快速发展海外版的同时也在积极寻求与海外华文媒体合作模式的新突破，向产业化领域进军。 </p> \n<p style=\"text-indent:2em;\"> 尼日利亚最大的华文报纸《西非统一商报》与《今晚报》合作创办了《今晚报·西非版》之后，该报又与西非五国的华文媒体合作，将报纸发行到了西非五国；2013年年初，双方签订合作创办《今晚报·西非英文版》的协议，并且很快将两个版面的英文版发行到多个西非国家。去年6月间，《西非统一商报》董事长胡介国来到今晚报社进行工作访问，他希望利用今晚报社在印刷方面的优势，合作建一座现代化的印刷厂。一个月之后，双方签订了建立印务中心的合作协议。 </p> \n<p style=\"text-indent:2em;\"> 中央外宣办认为，此次合作项目是一个重大突破，是《今晚报》多年来以海外版作为主要载体，实施中央走出去战略方针的一次大胆尝试，可以将这一项目作成“非中文化传媒产业园区”，而将合作建立印务中心列为其一期工程。天津市政府也正积极将其列为2015年天津市文化产业走出去重点项目之一。 </p> \n<p style=\"text-indent:2em;\"> 根据具体合作协议，《今晚报》与《西非统一商报》合作建设的印务中心作为“非中文化传媒产业园区”项目的一期项目，将主要承担《西非统一商报》《中国日报·西非周刊》每周近10万份的印刷业务；与周边（西非地区）国家的华文媒体开展印务合作；承担当地其他重要印刷业的需求，并以此为基地，开展报纸发行、广告业务、中国境内其他获准在西非地区发行并印刷的业务。同时，开展新媒体合作、物流业合作等业务。这个“非中文化传媒产业园区”也将有望成为《今晚报》以西非华媒为主阵地，向非洲发展的新的增长点，并为该报扩大与其他地区华媒合作提供了有益的借鉴。 </p> \n<p style=\"text-indent:2em;\"> <strong> 扩大合作领域 </strong> </p> \n<p style=\"text-indent:2em;\"> 《今晚报》编委会认为，如何深化与海外华文媒体的合作，创新合作机制，在巩固中求发展，在发展中让合作者得到实质性的利益，已经成为一个突出的课题。在今年8月17日《今晚报》举办的“海外华文媒体峰会”上，《今晚报》与16家海外华文媒体负责人共同签署了《今晚海外传媒合作项目备忘协议书》，协议书中包括了人才引进、广告、经贸、文化、旅游等100多个项目的合作内容。除了《今晚报》与海外华文媒体的多项合作交流外，此次峰会还为国内企业搭建了商务合作的桥梁。峰会邀请天津市在服务贸易方面的优秀企业与各位海外华文传媒代表面对面，通过企业的自我介绍、代表提问与企业答疑等环节，充分向海外推介天津市的优秀企业，以期能借助海外华文媒体所在国拥有的传媒以及人脉优势，促进双方的合作，帮助天津企业走出去。 </p> \n<p style=\"text-indent:2em;\"> 天津市商务委副主任于强表示：“这次峰会中与海外华文媒体代表面对面交谈是非常接地气的一项活动。海外华文媒体带来的信息与之前我们所掌握的信息有一定区别，面对面交流不仅使我们及时更新了手中的信息，同时，还能根据对方提出的建议做出积极调整，更好地适应国际市场需求，向世界推介天津的服务贸易与优秀企业。” </p> \n<p style=\"text-indent:2em;\"> 今晚报社社长、总编辑刘凤山对记者表示，《今晚报》能够成为我国向海外发展最快的报纸，是因为一直把走出去作为重要的发展战略，《今晚报》创办海外版，不仅是向世界展示中国主流媒体的力量、发出中国主流媒体的声音，而且也适应了在全媒体时代中国主流媒体参与世界媒体竞争的大趋势。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：樊国安 刘梦琦 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年11月3日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 《今晚报》：13年创办50个海外版','2015-12-16 17:37:51','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/267700.shtml',NULL,NULL,NULL,NULL,NULL),(105,'国家新闻出版广电总局 - 行业信息 - 探寻印企可持续发展之道','探寻印企可持续发展之道','2015年10月28日','中国印刷技术协会“中印调查”专题组  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 在中国经济新常态的背景下，中国印刷业进入全面转型升级的关键期，追求可持续的健康增长已成为大多数印刷企业的发展目标，这也是企业成长进化的高级阶段。回顾历史，我国的许多企业在创建初期大多经历了企业规模、资金、市场快速扩张的初级阶段，但在进入成长期特别是成熟期之后，企业管理明显滞后，加之越来越快的市场风云变幻，企业的发展开始停滞甚至下滑。对于大多数印刷企业来说，如何实现可持续发展是一个全新的课题与挑战。 <br> </p> \n<p style=\"text-indent:2em;\"> 国家印刷示范企业是我国印刷行业中的佼佼者，他们理念先进、技术领先、管理科学，在市场竞争中一直保持优势，更在可持续发展之路上进行了富有成效的探索。本次针对2012年-2014年已获得国家印刷示范企业称号的80家企业发出调查问卷，收回有效问卷73份，旨在从全新的视角反映我国先进印刷企业的成就与特色，为印刷业可持续发展之路的探索提供借鉴与经验。 </p> \n<p style=\"text-indent:2em;\"> <strong> 科技创新获广泛关注 </strong> </p> \n<p style=\"text-indent:2em;\"> 如何在竞争激烈的市场环境中脱颖而出，拉开与竞争者的距离，不断提升企业创新能力，打造创意型企业是关键。只有创新是无法复制的，创新能力是企业核心竞争力的重要体现，也是保障企业可持续发展的不竭动力。 </p> \n<p style=\"text-indent:2em;\"> 在本次参与调查的73家企业中，获得各级科技创新奖励的企业累计数量达到48家，其中获得国家级奖励的企业数量最多，达到21家，在一定程度上反映出现有的示范企业群体已集聚了我国印刷业中创新力最强的企业。同时累计有44家企业曾在科技创新项目中得到国家各级有关部门的政策或资金支持，即半数企业在科技创新方面符合国家的产业创新发展方向。但是也有31家企业在创新的前沿领域没有取得战略性成果，还停留在传统的发展模式中，其成绩的取得可持续性较低。 </p> \n<p style=\"text-indent:2em;\"> 在企业设立科研机构方面，共计46家示范企业设立了专门的研发机构，占比达到受访企业总数的63%，大多数印刷示范企业在科技创新方面已经建立了相对完善的体系；从机构能力水平分布来看，省级研发机构占主体，占比约40%，其次为地市级机构，占比约22%。说明印刷示范企业科研机构的总体建设水平稳定偏上，而设立国家级机构的企业仅有1家，高水平的科技研发机构比较缺乏，企业在科技创新能力上仍有待突破。 </p> \n<p style=\"text-indent:2em;\"> 在科技创新资源投入方面，有70家企业有所作为，企业普遍比较关心与重视科技创新。但从投入的经费来看，75%以上的企业投入不足总销售额的5%，总体投入水平偏低。在研发人员配置上，63家受访企业都配备了一定比例的专业研发人员，占比达到86%，其中研发人员配备数量比重达到5%以上的企业占多数，接近总量的一半，在创新人才的储备上有了一定的积累。 </p> \n<p style=\"text-indent:2em;\"> <strong> 战略规划着眼未来竞争 </strong> </p> \n<p style=\"text-indent:2em;\"> 制定发展规划的前提是明确企业的发展状况，对于企业的长期健康发展而言，重视发展战略的企业往往更具大局观与前瞻性。统计显示，97%的示范企业都为自身的发展制定了战略规划，已经建立高层管理人员年薪制度和职业经理人制度的企业数量也占大多数，59%的企业实行了高层管理人员年薪制，73%的企业用职业经理人管理企业。 </p> \n<p style=\"text-indent:2em;\"> 从调查数据可以看出，国家印刷示范企业在ISO9001质量管理体系认证与ISO14001环境管理体系认证的通过率最高，还有50%以上的企业通过了职业健康安全管理体系认证，说明示范企业普遍重视质量管理与环境管理。 </p> \n<p style=\"text-indent:2em;\"> 在信息化管理情况方面，从仓库管理模式的调查数据来看，93%以上的示范企业已经完全实现机械化作业，在仓库管理的信息化运用上也达到了80%。ERP系统在示范企业的普及率也很高，企业大多对系统进行了较为深入的开发建设，基础性的信息化管理技术已经深入运行到企业的各个管理环节中。 </p> \n<p style=\"text-indent:2em;\"> 中国印刷业的结构调整与转型升级要求印刷企业彻底改变以往粗放型的高投入、低产出、高能耗、低效率的管理模式，从过去的经验型管理、手工管理转向现代化的科学管理、制度化管理以及信息化管理，全面提升管理服务水平。作为我国印刷业的代表性企业群体，示范企业理应在管理系统、制度建设等方面具有领先性。 </p> \n<p style=\"text-indent:2em;\"> <strong> 多元化布局呈明显趋势 </strong> </p> \n<p style=\"text-indent:2em;\"> 从调查数据来看，示范企业基本上覆盖了所有的印刷门类，而且除了个别专业的包装印刷企业之外，多数示范企业经营同时涉及多个印刷领域，其中，包装印刷所占比例是最高的。另外，约53%的示范企业在印刷业之外开辟了新的市场，多元化发展也是一个明显趋势。 </p> \n<p style=\"text-indent:2em;\"> 在业务区域方面，占主导地位的是国内外两个市场同时覆盖，占比达58%；纯粹的外向型企业和区域型企业最少，分别只占7%左右。在销售收入方面，年销售收入在3亿元以上的企业达61家，约84%，这说明示范企业大多经济规模偏上，同时基本上囊括了业内的特大型印刷企业，从体量上来说，示范企业群体集中了主要的规模企业。 </p> \n<p style=\"text-indent:2em;\"> 赢利调查项数据显示，87.7%的示范企业是赢利的，11%持平，1家企业亏损。由此看来，示范企业大多保持了连续赢利的健康发展态势，总体经济状况良好。从企业所获奖项来看，不少示范企业获得过多次国际和国家级印刷大奖，占比近88%的示范企业在印刷产品质量上具有较强的市场优势。 </p> \n<p style=\"text-indent:2em;\"> 总体上看，90%以上的示范企业都达到了国内领先水平，在硬件配套上有明显的优势，具有规模优势的企业占主体。 </p> \n<p style=\"text-indent:2em;\"> <strong> 绿色印刷成印企担当 </strong> </p> \n<p style=\"text-indent:2em;\"> 统计数据显示，73家参与调查的示范企业中，获得绿色印刷认证的有52家，从事绿色印刷业务的企业有67家，绿色印刷业务量达到30%以上的企业占比超过60%，示范企业在绿色印刷方面的普及率很高，包括非获证企业也开辟了绿色业务。此外，有近80%的示范企业将绿色印刷的宣传教育纳入到日常工作体系之中，在实现绿色印刷转型的进程中开始走向稳定与成熟。在推广绿色印刷方面表现出很强的主动性的企业达到53家，占73%，基本上成为这一群体的主流。 </p> \n<p style=\"text-indent:2em;\"> 从统计数据来看，企业在环保方面的总投入普遍不高，总投入达到3%以上的企业仅占27%左右，但每年投入递增的速度相对较快，增长率在5%以上的企业达到45家，占比60%以上，而完全没有投入与没有增长的企业只占极少数。这说明示范企业在环境保护方面已经形成了长效的发展机制，有相对稳定的投入与增长，也有一定的计划性。 </p> \n<p style=\"text-indent:2em;\"> 绿色印刷将是传统印刷企业的主要生产经营方式。可持续发展的环保要求影响着印刷企业未来的决策，未来只有实现绿色印刷，走资源节约型、环境友好型发展方式，印刷企业才能获得持续发展的机会。 </p> \n<p style=\"text-indent:2em;\"> <strong> 着力构筑人才高地 </strong> </p> \n<p style=\"text-indent:2em;\"> 从统计情况来看，本科以上学历员工达到20%以上的企业数量仅占33%，另有44%左右的企业的高学历员工也只达到10%以上，从总体的员工构成来看，高学历员工在示范企业内还只占少数。另有数据表明，约58%的企业中高级技工占比仅保证在5%以上，占比达到20%以上的企业仅占25%左右，总体来看，中高级技工的数量同样偏低。 </p> \n<p style=\"text-indent:2em;\"> 但是，企业骨干员工调查数据显示，示范企业的人才流失率普遍很低，这对保持企业的正常运营非常重要，同时也说明示范企业在留住人才方面做得不错，员工的认同感很高，最重要的人才力量基本上保持在稳定状态，并且长期坚持引进关键人才，重视人才的储备工作。 </p> \n<p style=\"text-indent:2em;\"> 受访的73家示范企业在员工培训与教育上都有一定的组织保障，而且既有专门机构又有培育计划的企业占绝大多数，达到86%以上。说明示范企业普遍重视企业的内部人才培育工作，而且建立了长效的培育机制。总体来看，基本上反映了印刷行业在人力成本上的普遍现实，人力成本的持续上升可能成为一种常态。 </p> \n<p style=\"text-indent:2em;\"> <strong> 共同愿景引领新航向 </strong> </p> \n<p style=\"text-indent:2em;\"> 调查结果表明，受访的73家示范企业都有企业文化建设的基本意识，而且74%的企业有非常明确的企业愿景与目标。而且多数示范企业已经有意识地将愿景目标传递给了员工，意在获得员工的认同感，并使企业目标成为所有员工共同的方向与使命。从统计数据来看，近90%的企业领导层非常重视文化建设工作，有系统的理论体系以及执行层面的举措，由此可见，示范企业在文化建设方面具有较强的推动力。 </p> \n<p style=\"text-indent:2em;\"> 媒体是企业与外界沟通并树立企业形象的重要渠道，对媒体的关注程度直接表明了企业与社会深入交流的态度。统计数据表明重视公共关系的示范企业占多数，基本属于主流。70家受访企业均与媒体保持着一定的联系，85%左右的企业很重视在公共关系上的维护。这说明示范企业普遍重视社会形象的宣传与正面形象的树立，大多愿意主动履行或承担企业的社会责任。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：中国印刷技术协会“中印调查”专题组 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月28日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 探寻印企可持续发展之道','2015-12-16 17:37:53','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/267276.shtml',NULL,NULL,NULL,NULL,NULL),(106,'国家新闻出版广电总局 - 行业信息 - 12家单位成立国家版权交易中心联盟','12家单位成立国家版权交易中心联盟','2015年10月29日','赖名芳  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 10月28日，12家经国家版权局批准的国家版权交易中心（国家版权贸易基地、国际版权交易中心）宣布成立“国家版权交易中心联盟”，旨在加强版权保护和运营，发挥各自特点，整合优势资源，互惠互利、资源共享、合作共赢，共同推动版权产业发展。 <br> </p> \n<p style=\"text-indent:2em;\"> 该联盟包括中国人民大学国家版权贸易基地、北京国际版权交易中心、北京东方雍和国际版权交易中心、华中国家版权交易中心、台儿庄国家版权贸易基地、青岛国际版权交易中心、国家海峡版权交易中心、广州市越秀区国家版权贸易基地、西部国家版权交易中心、国家版权贸易基地（上海）、成都国际版权交易中心、横琴国际知识产权交易中心等12家单位。 </p> \n<p style=\"text-indent:2em;\"> 该联盟发布的《倡议书》中提出，将共同遵守国家法律法规，以提高运营和保护水平、推动版权产业发展为宗旨，全面开展版权交易、服务与合作；强化自律，反对不正当竞争，加强联盟成员间合作交流，集聚版权资源和渠道，促进互利共赢，联手打造全国性、国际化版权交易服务体系；整合版权优势资源，定期交流管理及运营经验，加强资源和信息共享，注重版权人才培养，努力实现联盟间信息、人才等资源互联互通，促进共同发展；加强版权资产评估，探索建立版权价值评估体系，拓宽版权投融资渠道，共同搭建版权产业投融资服务平台；深化互信互利，求同存异，面向未来，立足发展，友好化解分歧，维护各自合法权益。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：赖名芳 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月29日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 12家单位成立国家版权交易中心联盟','2015-12-16 17:37:53','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/267384.shtml',NULL,NULL,NULL,NULL,NULL),(107,'国家新闻出版广电总局 - 行业信息 - 《大数据领导干部读本》在京首发','《大数据领导干部读本》在京首发','2015年10月28日','雷萌  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> “大数据和国家治理现代化”研讨会暨《大数据领导干部读本》新书发布会近日在京举行。 <br> </p> \n<p style=\"text-indent:2em;\"> 《大数据领导干部读本》是中国行政体制改革研究会承担的国家社科基金特别委托项目“大数据治国战略研究”的重要阶段性成果，由人民出版社出版发行。研究和写作团队由国家行政学院、中国科学院、中国工程院等单位专家组成。与会人员表示，了解大数据相关知识和理论，主动在工作中运用大数据，是时代赋予各级领导干部的历史使命。本书有利于各级领导干部在实践中更好地认识大数据、把握大数据、运用大数据，从而提高工作能力和水平。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：雷萌 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年10月28日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 《大数据领导干部读本》在京首发','2015-12-16 17:37:53','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/267273.shtml',NULL,NULL,NULL,NULL,NULL),(108,'国家新闻出版广电总局 - 行业信息 - 四川日报集团与阿里联合成立“封面传媒”','四川日报集团与阿里联合成立“封面传媒”','2015年11月3日','','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 10月28日，四川日报集团与阿里巴巴集团正式宣布成立“封面传媒”，携手打造一个强调“个性化定制”的新型主流媒体。 <br> </p> \n<p style=\"text-indent:2em;\"> 作为一个移动媒体平台，“封面”将以新闻客户端为主打，以The cover.cn网站为基础，涵盖微博、微信、视频、数据、论坛、智库等，逐步推出多个垂直细分领域的产品矩阵。目前，四川日报集团与阿里巴巴集团已联手组建全新的封面传媒有限责任公司，就“封面”传媒进行运营。 </p> \n<p style=\"text-indent:2em;\"> “封面”是四川日报集团与阿里巴巴集团“优势+”的新体现，是四川日报集团发展、改革、创新、创业“四位一体”融合发展的新成果，是中国第一张都市报——华西都市报融合转型的新载体。 </p> \n<p> <br> </p> \n<p> 来源：中国新闻出版广电报 &nbsp;发布时间：2015年11月3日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 四川日报集团与阿里联合成立“封面传媒”','2015-12-16 17:37:53','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/267701.shtml',NULL,NULL,NULL,NULL,NULL),(109,'国家新闻出版广电总局 - 行业信息 - 中宣部理论局 中组部干部教育局向党员干部推荐  第十一批学习书目','中宣部理论局 中组部干部教育局向党员干部推荐 第十一批学习书目','2015年11月4日','','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 中宣部理论局、中组部干部教育局11月2日向党员干部推荐第十一批学习书目。 <br> </p> \n<p style=\"text-indent:2em;\"> 第十一批学习书目包括7种图书：《习近平关于全面依法治国论述摘编》、《中国抗日战争史简明读本》、《马克思恩格斯列宁哲学论述摘编》（党员干部读本）、《“一带一路”：机遇与挑战》、《抗日战争》（1-3卷）、《谢觉哉家书》、《工业4.0大革命》。 </p> \n<p style=\"text-indent:2em;\"> 《习近平关于全面依法治国论述摘编》由中央文献研究室编写，中央文献出版社2015年4月出版。该书是党员干部全面把握依法治国指导思想、总目标、基本原则、总体要求，深刻理解全面依法治国重大意义的重要辅助材料。 </p> \n<p> <br> </p> \n<p> 来源：中国新闻出版广电报 &nbsp;发布时间：2015年11月4日 </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 中宣部理论局 中组部干部教育局向党员干部推荐 第十一批学习书目','2015-12-16 17:37:54','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/267794.shtml',NULL,NULL,NULL,NULL,NULL),(110,'国家新闻出版广电总局 - 行业信息 - 第二届中国音乐产业高端论坛把脉产业发展','第二届中国音乐产业高端论坛把脉产业发展 “互联网+”正开启行业新格局','2015年11月10日','孙海悦  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 11月6日，第二届中国音乐产业高端论坛在京举办，政府部门、行业协会、业界人士等国内外近200人参会，共同商讨中国音乐产业的现状与未来，为音乐产业的发展献计献策。论坛期间发布了《2015中国音乐产业发展报告》。《报告》显示，2014年中国音乐产业市场总规模约为2851.5亿元，较2013年增长4.73%，总体处于过渡转型、稳步上升期，音乐产业开启“互联网+”时代下的发展新格局。 </p> \n<p style=\"text-indent:2em;\"> <strong> 产业发展步入转型轨道 </strong> </p> \n<p style=\"text-indent:2em;\"> 在国家新闻出版广电总局指导、中国音像与数字出版协会音乐产业促进会支持下，《报告》由中国传媒大学音乐与录音艺术学院项目组完成。《报告》认为，在深化改革、结构调整的背景下，2014年中国音乐产业进一步走向成熟，核心层、关联层、拓展层产业结构逐步优化，商业模式和业务形态不断创新，政策环境和版权保护进一步完善，产业发展整体走入集约化、内涵式、可持续发展的转型轨道。 </p> \n<p style=\"text-indent:2em;\"> 《报告》以音乐作品原创为核心，将音乐产业结构分为核心层、关联层、拓展层3个层次。其中，核心层主要包括音乐演出、音乐图书、数字音乐、实体唱片、音乐版权5个部分；关联层指音乐教育培训、乐器及专业音响行业；拓展层则包括音乐评奖与排行榜，广播电视音乐，影视剧、游戏、动漫音乐，卡拉OK市场，国家音乐产业基地和音乐对外交流在内的6个方面。 </p> \n<p style=\"text-indent:2em;\"> 《报告》显示，从核心层音乐行业市场结构来看，数字音乐产业独占鳌头，约占核心层规模比重的75.5%。数字音乐市场规模整体呈稳步上升趋势。2014年，中国数字音乐的市场规模达491.2亿元，比上年增长11.5%。其中PC端音乐市场规模为51.2亿元，同比增长17.4%；移动端音乐市场规模为33.8亿元，同比增长10.9%；电信音乐增值业务为406.2亿元，同比增长10.8%。网络音乐用户规模达4.78亿元，同比增长5.5%，网络音乐企业数量激增至1034家，增幅49%。由于在线演艺商业模式的发展，未来PC端音乐市场规模有望继续快速增长，电信音乐增值业务与移动音乐APP的结合也会更加紧密。 </p> \n<p style=\"text-indent:2em;\"> <strong> 版权保护规范化迎来新机遇 </strong> </p> \n<p style=\"text-indent:2em;\"> 会议期间，由中国音像与数字出版协会音乐产业促进会发起的“尊重版权、弘扬优秀原创、传递音乐正能量”——《2015中国音乐宣言》签名活动，以及国家音乐产业发展人才培养基地项目同时启动。来自英国威斯敏斯特大学的音乐产业负责人Kienda教授，中国音像与数字出版协会副理事长臧彦彬，中国音乐著作权协会副总干事刘平，中央音乐学院音乐学系副主任和云峰，摩登天空首席执行官沈黎晖等30余位业界专家分别就音乐产业的创新与创投、音乐版权与产业业态发展等问题展开深入讨论。 </p> \n<p style=\"text-indent:2em;\"> 大家普遍认为，互联网改变了大众消费音乐的观念和方式。2014年，中国内地实体唱片产值规模约为6.15亿元，较2013年下降约5.4%，黑胶唱片的销售量却逆势增加。实体唱片的商业价值更多体现在“粉丝”经济上，其音乐传播载体的角色从之前的大众传播者向小众收藏品功能转变。与此相反的是，数字音乐的消费用户数量得到进一步增长。2014年，数字音乐用户规模约为4.78亿人，较2013年增长5.5%，其中移动互联网音乐用户规模约为3.66亿人，增长率为25.9%。“付费直播模式”成为2014年行业中备受关注的全新商业模式，自此，视频流媒体也加入了抢占演出市场份额的阵营。 </p> \n<p style=\"text-indent:2em;\"> 数字时代版权保护规范化迎来新机遇，则是与会者达成的另一个共识。2014年，中国音乐著作权协会的版权许可收益达1.37亿元，比上年增长22.36%，其中网络新媒体收入呈现明显增长趋势，增长达203.94%，各项政策的出台以及各大数字企业在版权争夺上纷纷投入巨资，为音乐平台付费模式的建立和版权保护规范化带来新的机遇。 </p> \n<p style=\"text-indent:2em;\"> 所有数据表明，数字化、移动化、交互式的文化消费模式已经倒逼音乐产品或服务商作出根本改变，数字音乐产业将逐步与音乐演出产业、实体唱片、版权经纪、音乐图书出版等原创内容行业发生关联，加速各类音乐产品或服务实现“原创—展示—体验—产业化”发展，极大地打破了原有产业价值链，实现上中下游全产业链平台化经营。 </p> \n<p> <br> </p> \n<p> 作者：孙海悦 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年11月10日 </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 第二届中国音乐产业高端论坛把脉产业发展','2015-12-16 17:37:55','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/268276.shtml',NULL,NULL,NULL,NULL,NULL),(111,'国家新闻出版广电总局 - 行业信息 - 24个文创项目签约近80亿元','24个文创项目签约近80亿元','2015年11月2日','王坤宁 李婧璇  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 10月30日，第十届北京文博会“项目签约仪式”举行，现场集中签约24个文创项目，签约额共计79.22亿元。 <br> </p> \n<p style=\"text-indent:2em;\"> 据介绍，其中，由8家单位联合开发的京津冀“燕国达人”文化旅游项目将京津冀三地博物馆、历史文化遗迹、旅游景区、旅游配套服务单位等整合成京津冀历史文化旅游线路。由乌鲁木齐阿拜之路文化传播有限公司与哈萨克斯坦“赛人（CAK）”文化传媒有限公司合作的丝绸之路动漫项目，对促进中哈两国文化交流具有重要意义。 </p> \n<p> <br> </p> \n<p> 作者：王坤宁 李婧璇 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年11月2日 </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 24个文创项目签约近80亿元','2015-12-16 17:37:57','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/267600.shtml',NULL,NULL,NULL,NULL,NULL),(112,'国家新闻出版广电总局 - 行业信息 - 网络音乐迎全面正版化拐点','网络音乐迎全面正版化拐点 “大限”之前，大批未授权歌曲紧急下架','2015年11月5日','方圆  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 互联网音乐服务商最近很忙——忙于赶在“大限”之前下架未授权歌曲。10月31日、11月1日，酷狗音乐、酷我音乐、QQ音乐、虾米音乐、天天动听、网易云音乐等音乐平台纷纷通过不同方式发出声明，称将全部未授权作品已经下线。此举或意味着网络音乐行业将迎来全面正版化的拐点。 </p> \n<p style=\"text-indent:2em;\"> 11月2日，《中国新闻出版广电报》记者尝试登录各音乐平台网页版和手机端，发现大部分平台已经把未授权专辑内容标灰，无法点击。QQ音乐平台上的少量歌曲在搜索时会显示“唱片公司没有把这首歌给我们，玩命争取中”。酷狗音乐和酷我音乐采取的方式更为干脆——直接撤掉未授权专辑。网易云音乐平台不久前刚刚与QQ音乐达成转授权合作，虽然独家版权歌曲有限，但可以收听到来自QQ音乐的大量转授权音乐，例如《我是歌手第三季》中的歌曲。上述平台相关负责人均表示，大部分音乐都有授权，此次版权清理对平台影响不大。 </p> \n<p style=\"text-indent:2em;\"> 作为阿里音乐旗下网站之一，虾米音乐平台受到的冲击较大，包括周杰伦、蔡依林、林俊杰、范玮琪等在内的一系列大牌歌手的未授权歌曲都已经被下线。阿里旗下另一成员——天天动听，11月1日下午也将侵权歌曲陆续下架变灰。 </p> \n<p style=\"text-indent:2em;\"> 自今年7月国家版权局发布《关于责令网络音乐服务商停止未经转授权传播音乐作品的通知》后，16家服务商在通知要求的7月31日之前紧急下线了未经授权的220多万首音乐作品。此后，各家音乐平台之间达成协定：将10月31日作为版权清查、侵权作品下线的最后限期，在此限期前各大平台必须全面清理自家曲库，不能以任何形式进行音乐侵权。 </p> \n<p style=\"text-indent:2em;\"> 酷狗音乐11月1日在官方微博发布版权声明，一方面表态积极响应国家版权局的全面正版化要求，“已成功全面清理未经授权的歌曲”，另一方面申明自身目前拥有国内最大的词曲版权库，已获得包括索尼唱片、华纳唱片、环球唱片、韩国CJ唱片等国际唱片公司的歌曲授权，以及海蝶唱片、孔雀唱片、丰华唱片等国内知名音乐公司的歌曲授权。同时，通过和腾讯公司合作，获得杰威尔音乐、福茂唱片、英皇唱片、韩国YG唱片等的音乐转授权。酷狗音乐相关负责人表示，目前合作的音乐版权方超过60家，累计拥有近2000万首的正版曲库授权，计划未来在正版化音乐曲库上每年将持续投入过亿元。 </p> \n<p style=\"text-indent:2em;\"> 行业内较早推进正版化的QQ音乐，也于11月1日通过官方微信发布版权声明。QQ音乐除了对“梳理曲库内版权不明歌曲导致部分歌曲无法试听”作出解释外，还表示目前已与全球200多家唱片公司达成版权合作，积累正版曲目达1500万首，与20余家版权方，包括华纳、索尼、YG、LOEN等海内外优秀唱片公司达成独家版权战略合作，并全面代理唱片公司在中国内地的版权管理、运营维权、转授权代理工作。 </p> \n<p style=\"text-indent:2em;\"> 对于版权清理后各个音乐平台之间的合作与监督，QQ音乐表示以开放诚恳的态度，愿意与更多音乐平台进行版权转授权合作，帮助音乐作品拓展更多优质渠道，为广大用户提供更丰富便捷的音乐体验，同时也对未经授权非法使用QQ音乐版权曲库的音乐平台，保留采取法律手段的权利。 </p> \n<p style=\"text-indent:2em;\"> 今年以来，在国家版权局的积极推动下，中国网络音乐行业秩序逐渐好转。一些音乐平台自发组成中国网络正版音乐促进联盟，着力推动行业自律，维护版权和相关权益人的合法权益。中国音乐著作权协会副总干事刘平就此表示，在《著作权法》等法律法规支撑下，在有关政府部门的坚定支持下，网络音乐正版化取得了健康、长足的发展。相信联盟成员会继续推动互联网业界和社会公众明辨是非，把著作权保护进一步落到实处。 </p> \n<p> <br> </p> \n<p> 作者：方圆 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年11月5日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 网络音乐迎全面正版化拐点','2015-12-16 17:37:57','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/267964.shtml',NULL,NULL,NULL,NULL,NULL),(113,'国家新闻出版广电总局 - 行业信息 - 中国科技出版社推出“中国好设计”丛书','中国科技出版社推出“中国好设计”丛书','2015年11月10日','李婧璇  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 11月5日，由中国科学技术出版社出版的“中国好设计”丛书在于杭州举办的“国际工程科技发展战略高端论坛”上首发。 <br> </p> \n<p style=\"text-indent:2em;\"> 作为中国工程院重大咨询项目“创新设计发展战略研究”成果之一的“中国好设计”丛书，此次推出的第一系列共计10册，包括“中国好设计案例研究”系列以及《创新设计发展战略综合研究》《中国创新设计路线图》《日本设计创新的历史和展望》等。据介绍，“中国好设计”丛书通过具有“创新设计”趋势和特征的典型案例，展示创新设计在产品创意创造、工艺技术创新、管理服务创新以及经营业态创新等方面的价值实现，以期推进大众创业、万众创新。 </p> \n<p> <br> </p> \n<p> 作者：李婧璇 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年11月10日 </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 中国科技出版社推出“中国好设计”丛书','2015-12-16 17:37:58','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/268277.shtml',NULL,NULL,NULL,NULL,NULL),(114,'国家新闻出版广电总局 - 行业信息 - 《韬奋精神六讲》在京举行发布会','《韬奋精神六讲》在京举行发布会','2015年11月4日','孙海悦  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 在邹韬奋同志诞生120周年之际，解读韬奋精神的读本《韬奋精神六讲》由生活·读书·新知三联书店和人民出版社联合出版。11月2日，该书发布会在京举行。 <br> </p> \n<p style=\"text-indent:2em;\"> 邹韬奋是我国现代进步新闻出版业的杰出代表，是生活·读书·新知三联书店的主要创始人之一，也是3位列入首批著名抗日英烈名录的新闻出版界人士之一，韬奋精神已经成为我国新闻出版事业的一面旗帜。在《韬奋精神六讲》中，作者韬奋基金会理事长聂震宁梳理归纳了“为大众”“爱祖国”“敢斗争”“善经营”“懂管理”“真敬业”的韬奋精神丰富内涵，勾勒出邹韬奋不懈奋斗的一生。 </p> \n<p style=\"text-indent:2em;\"> 邹韬奋孙女、韬奋基金会副理事长邹小桦，中国出版协会常务副理事长兼秘书长刘建国，中国编辑学会会长郝振省，中国书刊发行业协会理事长艾立民，中国出版传媒股份有限公司副总经理李岩，三联书店总经理路英勇、总编辑翟德芳，人民出版社社长黄书元，以及中宣部出版局有关负责人出席会议。 </p> \n<p> <br> </p> \n<p> 作者：孙海悦 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年11月4日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 《韬奋精神六讲》在京举行发布会','2015-12-16 17:37:58','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/267793.shtml',NULL,NULL,NULL,NULL,NULL),(115,'国家新闻出版广电总局 - 行业信息 - 文轩网“双11”单日销售额破亿','文轩网“双11”单日销售额破亿','2015年11月13日','李婧璇 范燕莹  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 在刚刚落幕的“双11”购物狂欢节中，图书的销售也迎来了井喷的增长。 <br> </p> \n<p style=\"text-indent:2em;\"> 据悉，网络购书平台文轩网捷报不断，取得了单日访客过百万，销售300万册图书，销售额过亿元的佳绩。文轩网与供应商紧密合作，特别推出独家经营的定制品种；通过对历史数据和行业数据的大数据分析，针对性备足货源；根据客户的浏览习惯和购物行为，文轩网精心设计了“职场白领”“读懂股市”等百余个推荐榜单，图书购买一键搞定；成都、北京、无锡三大库房提前做好物流作业准备，确保货物快速、准确地送到客户手中。 </p> \n<p style=\"text-indent:2em;\"> 记者从京东图书音像事业部获悉，从11月11日0点至24点，京东图书共销售传统出版物860多万册，移动端订单量占比首次突破75%。今年“双11”期间图书单品销量前5位为《摆渡人》《故宫日历（2016年）》《岛上书店》《戒了吧！拖延症：写给年轻人的拖延心理学》《信谊绘本世界精选图画书：猜猜我有多爱你（最新版）》，其中2016版《故宫日历》异军突起，销售量超过5万册，同期销量比2015版增长5倍。 </p> \n<p> <br> </p> \n<p> 作者：李婧璇 范燕莹 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年11月13日 </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 文轩网“双11”单日销售额破亿','2015-12-16 17:37:58','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/268691.shtml',NULL,NULL,NULL,NULL,NULL),(116,'国家新闻出版广电总局 - 行业信息 - 奔小康需要更多“书香干部”','奔小康需要更多“书香干部”','2015年11月11日','刘克梅  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 十八届五中全会吹响了全面建成小康社会决胜阶段的号角。全面小康，离不开文化的小康，我们开展全民阅读活动的任务更加迫切。此刻，需要更多的“书香干部”，好读乐读，引领潮流。近日，中宣部理论局、中组部干部教育局向党员干部推荐了第十一批学习书目，这正是春苗逢喜雨，饥渴遇佳肴。 <br> </p> \n<p> 　　“书香干部”多一些，可以提高政府履职尽责的素质和能力，加速国家治理体系和治理能力现代化进程。当下的各项事业发展，都日益迈向了高精尖、专业化、国际化层面，每一个领导干部都应当在工作之余，通过读书进行专业化补课。这样才能成为真正的学者型干部、专家型干部，克服本领恐慌、本领落后的问题。 </p> \n<p> 　　“书香干部”多一些，还可以促进社会道德水平、文明素养的提高。俗话说，“要想火车快，全靠车头带”。每一位领导干部要善于通过读书学习，不断净化心灵，纯洁信仰，不断增强为人民服务的公仆意识，老实做人，踏实干事，清白为官，敬畏权力，成为大众眼中崇高的道德风景，以此促进本地道德生态的美化和优化。 </p> \n<p> 　　对于新闻出版广播影视行业来说，“书香干部”多一些，更可以助力本地区的阅读风气早日形成，提升本地区的阅读氛围。领导干部走出庸俗化的迎来往送、觥筹交错，把更多时间投入到读书学习中去，使读书成为一种生活态度和精神习惯，整个社会的书香味儿就会逐渐浓起来。 </p> \n<p> <br> </p> \n<p> 作者：刘克梅 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年11月11日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 奔小康需要更多“书香干部”','2015-12-16 17:38:01','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/268450.shtml',NULL,NULL,NULL,NULL,NULL),(117,'国家新闻出版广电总局 - 行业信息 - 《2015年深圳阅读指数报告》发布','《2015年深圳阅读指数报告》发布 深圳居民日均读书超过1小时','2015年11月10日','范燕莹  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 继2014年深圳发布全国首份阅读指数报告后，近日，《2015年深圳阅读指数报告》在深圳发布。《报告》显示，今年深圳阅读指数为124.83，比2014年深圳阅读指数提高了19.03，增长率为17.99%。 </p> \n<p style=\"text-indent:2em;\"> 《报告》表明，城市阅读基础建设部分统计值增长显著，说明在过去一年，深圳在促进市民阅读的公共文化服务及设施上的投入力度加大。其中，阅读资金保障显著提高，财政性资金投入从605万元增加到1120万元，社会资金投入从1045.17万元增加至1515.9万元；实体书店、书吧的数量从95个增加到154个，实体书店的图书销售金额从37357万元增长到43852万元。各种阅读活动场次数量显著增加，今年指标值从2014年的9.70攀升为20.89。2014年全年深圳共举办各类阅读活动10066场次，比2013年翻了三番。 </p> \n<p style=\"text-indent:2em;\"> 针对市民的阅读行为与认知调查显示，今年深圳居民日均读书（包括纸质图书和电子图书）62.53分钟，比2014年增加了32.81分钟；日均读报25.38分钟，比2014年增加了8.7分钟；日均阅读期刊16.46分钟，比2014年增加了4.23分钟。此外，今年深圳居民家庭藏书量明显增多，有藏书的家庭增加到88.20%。在有藏书的家庭中，平均藏书量为75.53本，较之2014年增加11.86本。今年深圳居民年均阅读纸质图书的数量为6.61本，高于2014年全国人均阅读4.56本的阅读量；人均年阅读电子图书10.42本，远远超过3.22本的全国平均值。 </p> \n<p style=\"text-indent:2em;\"> 11月5日上午，深圳书城中心城亲子阅读中心正式揭牌。第16届深圳读书月期间，这里将举行20余场丰富多彩的亲子阅读活动。 </p> \n<p style=\"text-indent:2em;\"> 深圳书城在深圳读书月的第二个15年再出发，力图打造“中国最大、最具影响力的亲子阅读中心”。未来3-5年，深圳书城中心城将推出更多亲子阅读活动，并继续打造现有亲子阅读品牌活动。同时，为深圳的亲子家庭定制亲子阅读卡，让父母和孩子享受更多的购书优惠，畅享快乐亲子阅读时光。 </p> \n<p style=\"text-indent:2em;\"> 亲子阅读中心创建工作图文展、亲子阅读中心出版物主题展同期举行。 </p> \n<p> <br> </p> \n<p> 作者：范燕莹 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年11月10日 </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 《2015年深圳阅读指数报告》发布','2015-12-16 17:38:07','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/268278.shtml',NULL,NULL,NULL,NULL,NULL),(118,'国家新闻出版广电总局 - 行业信息 - 首都版权产业联盟动漫和游戏委员会成立','首都版权产业联盟动漫和游戏委员会成立','2015年11月13日','路艳霞  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 首都版权产业联盟动漫和游戏委员会11月10日成立，这是北京市首家以版权服务为主体的动漫和游戏行业组织。 <br> </p> \n<p style=\"text-indent:2em;\"> 2014年北京市动漫游戏产业总产值约372亿元，相比2013年增长69%，占全国动漫游戏产业总产值的近三分之一。该委员会主任王英表示，将通过各种措施和活动，改善目前动漫游戏产业的环境，形成良性版权保护和产业交易平台。 </p> \n<p> <br> </p> \n<p> 作者：路艳霞 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年11月13日 </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 首都版权产业联盟动漫和游戏委员会成立','2015-12-16 17:38:09','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/268694.shtml',NULL,NULL,NULL,NULL,NULL),(119,'国家新闻出版广电总局 - 行业信息 - 内蒙古第十届草原读书月启动','内蒙古第十届草原读书月启动','2015年11月11日','李国生  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 由内蒙古日报社、内蒙古自治区网信办等单位联合举办的“伊利·书香伴着乳香飘”第十届草原读书月启动暨内蒙古图书馆少儿馆开馆仪式，近日在内蒙古图书馆举行。活动以图书漂流大悦读、萌娃讲故事、名人荐书、国学大讲堂、诗歌朗诵会等10个项目组成。 <br> </p> \n<p> 　　内蒙古图书大厦受内蒙古图书馆之邀作为协办单位参加活动，并邀请内蒙古满族青年作家刘芳为活动助阵。此次活动将名人、作家讲堂搬进图书馆，进一步加深了图书大厦与内蒙古图书馆的合作，拓展了合作空间。 </p> \n<p> <br> </p> \n<p> 作者：李国生 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年11月11日 </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 内蒙古第十届草原读书月启动','2015-12-16 17:38:23','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/268448.shtml',NULL,NULL,NULL,NULL,NULL),(120,'国家新闻出版广电总局 - 行业信息 - 亚马逊首开实体书店引震动 当当3年内计划开1000家书店','亚马逊首开实体书店引震动 当当3年内计划开1000家书店','2015年11月11日','刘蓓蓓  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 全球最大网络电商亚马逊的首家实体书店“Amazon Books”，11月3日开业后，在业界引起很大震动。记者11月4日第一时间联系到中国图书电商当当网助理总裁张巍，他表示，当当也在执行开实体书店计划，预计3年内开到1000家。 <br> </p> \n<p> 　　资料显示，亚马逊书店陈列的图书是从其网上书城模式中所搜集到的数据入手，挑选西雅图读者最喜爱的图书。在细节地方也有与传统书店不同之处，比如每本书下方都有张评价卡，卡上标明网络书店的读者评价与分数，供顾客参考选择最合适的书籍。价格方面，亚马逊也表明实体店面与网络商店的书籍价格相同，省去顾客比价的麻烦。 </p> \n<p> 　　张巍表示，当当理解电商开实体书店的目的是丰富顾客体验，但图书电商巨头因规模带来的成本优势，可以支撑线上线下同价，还有大数据和评论等带来的增值服务，势必对实体书店形成进一步挤压。至于挤压有多严重，要看开店的规模。 </p> \n<p> 　　张巍还透露，当当已在执行开实体书店的计划。书店形式涵盖MALL店、超市书店、县城书店等多个类型，计划在3年内总计开到1000家左右。据了解，当当的第一家MALL店将于12月在湖南长沙开业，书店面积约为1200平方米，线上线下同价。张巍还提到，数月前当当首席执行官李国庆已与国内数家实体连锁书店的创办人沟通过实体书店线上线下同价，并提出给予毛利保护，但曲高和寡。 </p> \n<p> <br> </p> \n<p> 作者：刘蓓蓓 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年11月11日 </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 亚马逊首开实体书店引震动 当当3年内计划开1000家书店','2015-12-16 17:38:29','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/268447.shtml',NULL,NULL,NULL,NULL,NULL),(121,'国家新闻出版广电总局 - 行业信息 - 文轩网落地新西兰','文轩网落地新西兰','2015年11月11日','李婧璇  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 由中国外文局、新华文轩出版传媒股份有限公司、新西兰玛克威出版社、新西兰华文书店等单位主办的第七届新西兰读书文化节暨新西兰文轩网上线仪式近日在新西兰最大的城市奥克兰举行。活动上，新华文轩旗下的文轩国际文化传播有限公司及文轩网与新西兰华文书店联合推出了“新西兰文轩网”。 <br> </p> \n<p> 　　新西兰文轩网在新西兰的落地实现了域名本地化、服务器本地化、支付本地化、送货本地化，订单通过国际快递直接送货到读者手中，完全实现了本地化服务。新西兰文轩网的落地解决了新西兰读者在购买中文图书时的选择少、运输慢、时间久的问题，可以让海外华人和正在学习中文的外国朋友快速直接地了解和购买中国的最新图书。 </p> \n<p> <br> </p> \n<p> 作者：李婧璇 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年11月11日 </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 文轩网落地新西兰','2015-12-16 17:39:32','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/268446.shtml',NULL,NULL,NULL,NULL,NULL),(122,'国家新闻出版广电总局 - 行业信息 - 北京华语联合出版有限责任公司正式运营','北京华语联合出版有限责任公司正式运营','2015年11月10日','王坤宁  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 记者日前从北京市新闻出版广电局获悉，第一家获得对外专项出版权、由北京市新闻出版广电局主管的北京华语联合出版有限责任公司已于近日完成出版单位设立手续并已正式运营。 <br> </p> \n<p style=\"text-indent:2em;\"> 据悉，北京华语联合出版有限责任公司在完成了出版单位设立手续的同时也启动该新公司业务前期的准备工作，与著作权人达成协议，授予华语联合出版公司成立后出版外文版图书，筹备组并委托专业翻译人员对新公司拟出版作品进行英文的翻译校改。 </p> \n<p style=\"text-indent:2em;\"> 据了解，该公司拟在今年年底推出《文化苦旅》《古建筑之美》等系列图书，并通过英格拉姆面向中国境外全渠道发行。同时，北京华语联合出版有限责任公司将全面启动版权输出代理业务，目标是打造全国首家中国版权输出基地，充分利用美国版权资源丰富的优势，通过数百家一级版权代理公司和他们控制的全球二级版代体系，将中国版权源源不断地推荐给全球数十万家出版机构。 </p> \n<p> <br> </p> \n<p> 作者：王坤宁 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年11月10日 </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 北京华语联合出版有限责任公司正式运营','2015-12-16 17:39:34','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/268275.shtml',NULL,NULL,NULL,NULL,NULL),(123,'国家新闻出版广电总局 - 行业信息 - 全国出版社青年编校大赛26日举行','全国出版社青年编校大赛26日举行','2015年11月11日','章红雨  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 记者日前从中国出版协会获悉，由中国出版协会与韬奋基金会联合举办的第五届韬奋杯全国出版社青年编校大赛将于11月26日在京举行，27日上午将召开颁奖大会。主办方介绍，大赛已被纳入国家新闻出版广电总局人才培养计划，凡是进京参加决赛并取得优秀成绩的选手，颁发总局统一印制的结业证书。 <br> </p> \n<p> 　　据了解，大赛采用笔试形式，设团体赛和个人赛两个比赛项目。团体赛和个人赛各设一等奖2个、二等奖6个、三等奖12个、个人优秀奖30个；校对人员个人奖设一等奖2个、二等奖4个、三等奖6个、优秀奖20个。另设优秀组织奖10个。 </p> \n<p> 　　主办方表示，团体赛以代表队为单位参赛，每支代表队由3人组成，3人中须有1名专职校对人员，没有专职校对人员的单位不能参加团体赛。个人赛主要是分别计算编辑、校对人员的个人成绩，两者试题不同，分别答题。每省（区、市）可选派1-3个代表队参赛。因没有专职校对人员不能组队参加团体赛的省（区、市），最多可派5人参加个人赛。在京出版单位，每单位可选派1个代表队参加团体赛。如不能组队，可选派1-2人参加个人赛。大赛试题内容侧重于编辑加工和校对工作中应知应会的知识及解决实际问题的能力。试题以选择、判断、纠谬、改错为主。 </p> \n<p> <br> </p> \n<p> 作者：章红雨 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年11月11日 </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 全国出版社青年编校大赛26日举行','2015-12-16 17:39:34','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/268449.shtml',NULL,NULL,NULL,NULL,NULL),(124,'国家新闻出版广电总局 - 行业信息 - 《孙中山全集》16卷本问世','《孙中山全集》16卷本问世','2015年11月13日','雷萌  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 11月12日，由人民出版社主办的《孙中山全集》新书首发式在北京中山堂举行。本全集被列为“十二五”国家重点图书出版规划项目，并获得2012年度国家出版基金资助。 <br> </p> \n<p style=\"text-indent:2em;\"> 全集由长期从事中国近代史和孙中山、宋庆龄研究的尚明轩担纲主编，由中国社会科学院近代史研究所、北京大学、清华大学等单位的8位近代史研究专家任编委，历时六载艰苦努力，终于以16卷本的规模成书。据尚明轩介绍，全集在前人研究成果的基础上，采用“百衲本”的方式，比较参证，加工整理，同时吸收近些年来中外各界人士最新研究成果，并增加了外文著述和题词遗墨两大类，共收集整理稿件11500余篇、1010.6万字。 </p> \n<p style=\"text-indent:2em;\"> 中国宋庆龄基金会副主席唐闻生、国家新闻出版广电总局出版管理司司长周慧琳、国家出版基金规划管理办公室主任陈亚明、人民出版社社长黄书元等分别发言。 </p> \n<p style=\"text-indent:2em;\"> 早在1956年，人民出版社就出版过新中国成立后的第一部《孙中山选集》，曾多次重印，受到学界广泛好评。此次《孙中山全集》的出版，是人民社长期出版孙中山著作的又一个里程碑。 </p> \n<p> <br> </p> \n<p> 作者：雷萌 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年11月13日 </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 《孙中山全集》16卷本问世','2015-12-16 17:39:34','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/268693.shtml',NULL,NULL,NULL,NULL,NULL),(125,'国家新闻出版广电总局 - 行业信息 - 《中国和哈萨克斯坦的故事》中、俄文版首发','《中国和哈萨克斯坦的故事》中、俄文版首发','2015年11月13日','孙海悦  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 11月10日，“我们和你们”系列丛书之一《中国和哈萨克斯坦的故事》中、俄文版正式发行。该书由五洲传播出版社、外交笔会联合编辑出版。 <br> </p> \n<p style=\"text-indent:2em;\"> 《中国和哈萨克斯坦的故事》由外交官周晓沛担任主编，13位中方作者、9位哈方作者分别撰写了各自经历的中哈友好交往故事。哈萨克斯坦议会上院议长卡·托卡耶夫在为该书所作序言中，概括总结了24年来中哈友好关系的发展历程，并对中哈互利合作的美好前景进行了展望。 </p> \n<p style=\"text-indent:2em;\"> 五洲传播出版社于2014年启动“我们和你们”系列丛书，目前已出版以色列、俄罗斯、巴基斯坦、哈萨克斯坦4个分册。 </p> \n<p> <br> </p> \n<p> 作者：孙海悦 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年11月13日 </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 《中国和哈萨克斯坦的故事》中、俄文版首发','2015-12-16 17:39:35','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/268692.shtml',NULL,NULL,NULL,NULL,NULL),(126,'国家新闻出版广电总局 - 行业信息 - 吉林出版集团输出版权36项','吉林出版集团输出版权36项','2015年11月16日','刘蓓蓓  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 在2015中国上海国际童书展上，吉林出版集团携400余种精品少儿图书参展，达成版权输出合同共36项，与外方洽谈取得了初步版权输出意向34项。 <br> </p> \n<p style=\"text-indent:2em;\"> 记者在现场看到，吉林出版集团的展区布置颇有童趣，展位两侧的墙面上挂以水果色的方形书架，用红橙黄绿青蓝紫七色分别代表集团旗下的7家参展单位，设计风格富有创意，突出亲和力，将吉林出版集团的优秀图书呈现在读者面前。 </p> \n<p style=\"text-indent:2em;\"> 鼓励创作，扶持新作，推动中国原创科普图书的发展，是吉林出版集团始终坚持发展的重要方向。本次集团参展的少儿科普类图书异彩纷呈，涵盖了少儿科普的各个分类。无论是优势的传统纸质图书，还是音像结合的有声书、多材质的玩具书以及3D立体派，都体现出了各自生动新颖、寓教于乐的个性化、专业化特点。儿童文学也是吉林出版集团少儿图书发力的重头，此次集团带来了“黑鹤自然之歌”系列、“动物小说大王沈石溪系列典藏”、“孙幼军童话精选集”等知名儿童文学作家的经典作品。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：刘蓓蓓 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年11月16日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 吉林出版集团输出版权36项','2015-12-16 17:39:35','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/268763.shtml',NULL,NULL,NULL,NULL,NULL),(127,'国家新闻出版广电总局 - 行业信息 - 2015年河北惠民书市接待读者近百万','2015年河北惠民书市接待读者近百万','2015年11月17日','刘蓓蓓  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 历时5天的2015年河北惠民书市11月10日落幕。记者从河北省新华书店集团公司获悉，此次书市共接待读者近百万人次，销售出版物103.7万册、1605万元，书市期间整体让利680万元。 <br> </p> \n<p style=\"text-indent:2em;\"> 本届惠民书市汇聚了全国500余家出版社的20万种图书进行优惠展销，并通过发放优惠卡、代金券，开展购书抽奖、买书赠礼等多种方式，让读者获得更多实惠。书市现场还举办了上百场文化讲座、新书发布会以及听说读写大赛颁奖典礼、为盲童读书、阅读摄影展等系列文化活动。 </p> \n<p style=\"text-indent:2em;\"> 本次活动实现了文化惠民全省联动，除石家庄主会场外，组委会还在唐山市、邢台市设立主分会场，在河北省11个设区市以及定州市、辛集市新华书店开辟专区，144家市（县、区）新华书店设立专柜专架作为活动分会场。据了解，本届惠民书市期间，石家庄主会场客流25万人次，销售49万册、782万元，其他分会场客流达到70多万人次，销售57.4万册、823万元。 </p> \n<p style=\"text-indent:2em;\"> 书市期间，河北各地新华书店还积极开展以图书进机关、进社区、进乡村、进校园、进企业、进军营、进家庭为主要内容的“七进”活动。邯郸县、成安县等新华书店还成立了“赶集上会，服务农村”小分队，开展送书进社区、进庙会活动。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：刘蓓蓓 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年11月17日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 2015年河北惠民书市接待读者近百万','2015-12-16 17:39:35','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/268839.shtml',NULL,NULL,NULL,NULL,NULL),(128,'国家新闻出版广电总局 - 行业信息 - 中少总社倡议丝路少儿出版合作','中少总社倡议丝路少儿出版合作','2015年11月16日','金鑫  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 11月14日，中国少年儿童新闻出版总社启动“丝路书香 国际少儿出版多边合作框架”机制，旨在推动中国少儿出版与“一带一路”沿线的东南亚、南亚、西亚、中东国家乃至欧洲、非洲同行开展合作。 <br> </p> \n<p style=\"text-indent:2em;\"> 为进一步发挥少儿出版在走出去方面的排头兵作用，今年8月，中少总社开始着手筹划创设“丝路书香 国际少儿出版多边合作框架”。倡议得到了来自有关国家出版机构的积极反馈。本届童书展上，来自马来西亚、泰国、土耳其、德国、斯洛文尼亚等国的出版商就此项合作进行了首次研讨。 </p> \n<p style=\"text-indent:2em;\"> 该框架今后将吸纳中国和各相关国家守信用、有实力、有特色、坚持出版高品质儿童图书的出版社加入。同时，也将吸收版协等机构以及作家、插画家、书评家、记者等参加，并在主要的国际书展期间设立专门的联合展台及专业研讨会、合作洽谈会，不定期开展国际文化交流活动。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：金鑫 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年11月16日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 中少总社倡议丝路少儿出版合作','2015-12-16 17:39:35','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/268762.shtml',NULL,NULL,NULL,NULL,NULL),(129,'国家新闻出版广电总局 - 行业信息 - 《社会主义核心价值观优秀文学读本》出版','《社会主义核心价值观优秀文学读本》出版','2015年11月13日','杜羽  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 《社会主义核心价值观优秀文学读本》出版座谈会11月10日在京举行。该书由北京市西城区组织编写，收录了梁衡、彭程、李春雷等300余位作家的400多篇诗文作品，旨在通过生动的文学作品诠释社会主义核心价值观。 <br> </p> \n<p style=\"text-indent:2em;\"> 《读本》共六卷150万字，诗歌卷《温暖心河》、散文卷《爱在爱中》、儿童文学卷《快乐城堡》、报告文学卷《金城本色》、小说卷《最美的脸》、小小说卷《那人那事》分别由峭岩、李培禹、刘丙钧、林凯、赵李红、张宁担任主编。 </p> \n<p> <br> </p> \n<p> 作者：杜羽 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年11月13日 </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 《社会主义核心价值观优秀文学读本》出版','2015-12-16 17:39:35','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/268695.shtml',NULL,NULL,NULL,NULL,NULL),(130,'国家新闻出版广电总局 - 行业信息 - 中央财政安排7.31亿元支持文化企业发展','中央财政安排7.31亿元支持文化企业发展','2015年11月20日','涂桂林  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 日前，中央财政下达2015年国有资本经营预算资金7.31亿元支持67家财政部代表国务院履行出资人职责的中央文化企业实施96个项目。 <br> </p> \n<p style=\"text-indent:2em;\"> 这次资金重点支持三个方向：一是支持中央文化企业按照优势互补、资源组合的原则，合并组建新企业或集团公司，作为兼并主体通过购买、直接入股等方式取得其他文化企业所有权或控股权；二是支持中央文化企业集聚跨部门、跨地区、跨所有制的数字内容资源实施行业及数字运营平台建设，开展具有典型示范效应的网络传播与运营服务平台；三是支持具有竞争优势、品牌优势和经营管理能力的中央文化企业与国外有实力的文化机构进行项目合作，建设文化产品国际营销网络，推动文化产品和服务出口，开拓国际市场。 </p> \n<p style=\"text-indent:2em;\"> 据了解，2011年-2015年，中央财政已累计安排国有资本经营预算资金37.91亿元，推动中央文化企业做大做强。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：涂桂林 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年11月20日 </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 中央财政安排7.31亿元支持文化企业发展','2015-12-16 17:39:37','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/269084.shtml',NULL,NULL,NULL,NULL,NULL),(131,'国家新闻出版广电总局 - 行业信息 - “出版大讲堂”分析二战以来出版业','“出版大讲堂”分析二战以来出版业','2015年11月23日','范占英  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 11月19日，北京印刷学院综合楼会议厅内，200余名师生被中国出版协会常务副理事长、北京印刷学院特聘教授邬书林题为“二战以来世界文化产业的发展”的讲座所吸引。此次讲座是“出版大讲堂”第13期活动。 <br> </p> \n<p style=\"text-indent:2em;\"> 邬书林的讲座分为三部分：世界文化产业的现状和二战以来发展的回顾；我国文化产业发展面临的态势；准确把握文化的功能，促进经济发展和社会进步。邬书林认为，西方国家在二战以后文化产业的大发展，与其基础理论方面的研究和学术专著的积累是分不开的。而文化与产业资本、金融资本的结合，是推动文化产业形成和快速发展的关键因素。政府和社会的规划与政策引导，是推动文化发展的不可或缺的重要条件。综合运用文化的力量、政治的力量来发挥文化软实力的作用，是综合国力竞争的有效方法。在谈到准确把握文化的功能，促进经济发展和社会进步时，邬书林认为，在发挥好文化的政治功能、经济功能、文学艺术功能、对外交流功能，提升我国文化产业的核心竞争力，丰富人民精神文化生活，扩大中华文化的影响力的同时，要认真总结国内外文化发展经验，按文化规律、经济规律办事，更好地推进文化建设发展。 </p> \n<p style=\"text-indent:2em;\"> 据介绍，“出版大讲堂”系列讲座活动，旨在荟萃学界、业界精英，就新闻出版理论前沿、热点问题开展学术交流，促进思想碰撞与融合。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：范占英 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年11月23日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - “出版大讲堂”分析二战以来出版业','2015-12-16 17:39:37','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/269147.shtml',NULL,NULL,NULL,NULL,NULL),(132,'国家新闻出版广电总局 - 行业信息 - “互联网+”与新媒体法规研讨会关注版权保护','“互联网+”与新媒体法规研讨会关注版权保护','2015年11月23日','汤广花 刘佳宇 刘菲凡  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 全国“互联网+”与新媒体的法律规制研讨会近日在湖北武汉召开。来自高校和媒体的50多名专家学者就网络转载的版权保护、个人隐私权保护等问题展开深入研讨。 <br> </p> \n<p style=\"text-indent:2em;\"> 与会专家在研讨中提出，虽然新闻传播渠道、传统媒体纸媒地位和新闻文本发生了巨大变化，但受众对精准、权威、深度信息的依赖性愈来愈强烈。传媒业的规制是为了让人们更喜欢、更接近、更享受新媒体，也是为了让新媒体充满活力，适应新技术的发展。因此，在数据经济繁荣的背景下，互联网企业应利用市场优势，积极作为，但在此过程中应严格遵守传播秩序、版权规范。与此同时，传统媒体和新兴媒体应加强合作，共同推动信息传播，服务社会发展。 </p> \n<p style=\"text-indent:2em;\"> 会议由中南财经政法大学与检察日报社联合举办。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：汤广花 刘佳宇 刘菲凡 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年11月23日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - “互联网+”与新媒体法规研讨会关注版权保护','2015-12-16 17:39:37','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/269146.shtml',NULL,NULL,NULL,NULL,NULL),(133,'国家新闻出版广电总局 - 行业信息 - 中华出版促进会第三次理事会召开','中华出版促进会第三次理事会召开','2015年11月17日','王坤宁  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 11月14日，中华出版促进会第三次理事会全体会议在京召开。全国人大教科文卫委员会主任委员、中华出版促进会名誉理事长柳斌杰出席会议并讲话。 <br> </p> \n<p style=\"text-indent:2em;\"> 柳斌杰在讲话中说，2015年，中华出版促进会积极开展对外文化交流与合作，努力推动中华文化走出去，服务“一带一路”等重大战略实施，各项工作卓有成效。他强调，中华出版促进会的宗旨就是要以出版交流为平台，推动中华文化走出去。要把握中国视野和世界思维相结合、内容和渠道并重、发挥好政府与民间合力三项原则，推动中国出版走出去。同时，坚持依法办会，抓紧健全相关工作机构，与会员单位保持紧密联系，加强对会员单位的服务。 </p> \n<p style=\"text-indent:2em;\"> 柳斌杰强调，出版走出去首先出版要强。中华出版促进会就是要促进出版强国的建设和中华文化传播的影响力。推动中华文化走出去，要创新走出去的方式，用新概念、新态度、新表达传播中国好故事。同时，积极发展新技术，构建国际传播新平台。 </p> \n<p style=\"text-indent:2em;\"> 中华出版促进会理事长苏士澍代表理事会领导班子作工作报告。他表示，2016年，促进会将加快推进体制机制创新，推进创新发展、协调发展、绿色发展、开放发展、共享发展，努力加强国际传播能力建设，进一步推动中国出版走出去。 </p> \n<p style=\"text-indent:2em;\"> 会议透露，中华出版促进会拟成立媒体融合与创新专业委员会。会议还邀请了有关方面专家就新闻出版业的发展趋势与政策以及出版走出去等问题进行了交流和探讨。 </p> \n<p style=\"text-indent:2em;\"> 中宣部、国家新闻出版广电总局有关部门负责同志参加会议。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：王坤宁 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年11月17日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 中华出版促进会第三次理事会召开','2015-12-16 17:39:37','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/268840.shtml',NULL,NULL,NULL,NULL,NULL),(134,'国家新闻出版广电总局 - 行业信息 - 2015年《动漫蓝皮书》显示：“动漫+互联网+相关产业”将改变动漫产业格局','2015年《动漫蓝皮书》显示：“动漫+互联网+相关产业”将改变动漫产业格局','2015年11月18日','孙海悦  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 由北京电影学院牵头联合国内外众多专家学者和业界人士共同编撰的《动漫蓝皮书：中国动漫产业发展报告（2015）》近日由社会科学文献出版社出版。蓝皮书提出，中国动漫产业正在进入以互联网为核心，跨形态、跨媒介、跨行业融合发展的新时代，“动漫+互联网+相关产业”的融合发展必将使动漫产业的未来格局发生根本性变化。 <br> </p> \n<p style=\"text-indent:2em;\"> 蓝皮书认为，动漫产业是一个高速发展的新兴文化创意产业，2020年中国动漫产业有望在现有规模的基础上翻一番，产值规模突破2000亿元，真正跻身世界动漫强国行列。 </p> \n<p style=\"text-indent:2em;\"> 对于动漫企业如何在当前产业转型升级的战略机遇期找准产业定位、细分市场以谋求长远发展，蓝皮书建议，一是要持续深化“动漫+”战略，强化动漫与文学、影视、游戏等“泛娱乐”板块的互动融合，推进动漫产业与制造业、服务业等相关产业的跨界融合，形成跨行业融合发展的大动漫产业体系；二是要大力推进互联网化，建立“内容+平台+应用+终端”的生态体系；三是要加强资源整合，形成环环相扣的独特商业模式；四是要以人为本，通过大数据深入挖掘用户需求，利用粉丝经济提高品牌溢价和商业价值；五是要以内容为王，不断提升创意内容和品牌形象的创新力与生命力；六是要放眼全球，加大动漫品牌和衍生品开发的国际合作，打造面向全球的明星IP和重点产品。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：孙海悦 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年11月18日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 2015年《动漫蓝皮书》显示：“动漫+互联网+相关产业”将改变动漫产业格局','2015-12-16 17:39:37','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/268944.shtml',NULL,NULL,NULL,NULL,NULL),(135,'国家新闻出版广电总局 - 行业信息 - 《胡耀邦文选》出版','《胡耀邦文选》出版','2015年11月20日','','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 为纪念胡耀邦同志诞辰100周年，由中共中央文献编辑委员会编辑的《胡耀邦文选》已由人民出版社出版，即日起在全国发行。 <br> </p> \n<p style=\"text-indent:2em;\"> 这部文选，收入了胡耀邦同志1952年5月至1986年10月这段时间内的重要著作77篇，约49万字，包括文章、讲话、报告、谈话、批示、书信、题词等，相当一部分是第一次公开发表。这些著作集中反映了胡耀邦同志为推动社会主义革命和建设、为推动改革开放和社会主义现代化、为推动中国特色社会主义事业作出的贡献和提出的重要思想观点，集中反映了胡耀邦同志信念坚定、心系人民的高尚品格，实事求是、勇于开拓的探索精神，公道正派、清正廉洁的优良作风。 </p> \n<p style=\"text-indent:2em;\"> 《胡耀邦文选》的出版发行，对于广大干部群众深入了解胡耀邦同志的思想业绩和革命精神，深入学习新中国成立后特别是改革开放新时期党的路线方针政策，深刻理解中国特色社会主义的理论和实践历程，为实现中华民族伟大复兴的中国梦努力奋斗，具有重要意义。同时，该书也为党史、国史研究提供了重要的文献资料。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 来源：新华网 &nbsp;发布时间：2015年11月20日 </p> \n<p> </p>',NULL,'','新华网  ','国家新闻出版广电总局 - 行业信息 - 《胡耀邦文选》出版','2015-12-16 17:39:37','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/269085.shtml',NULL,NULL,NULL,NULL,NULL),(136,'国家新闻出版广电总局 - 行业信息 - 2016北京图书订货会明年1月7日启幕','2016北京图书订货会明年1月7日启幕','2015年11月11日','章红雨  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 一年一度的北京图书订货会将于2016年1月7日至9日在中国国际展览中心（老馆）举办。这是中国出版协会、中国书刊发行业协会11月10日联合在京举办的2016北京图书订货会首场新闻发布会上透露的。 <br> </p> \n<p> 　　据介绍，本届订货会主要亮点有：邀请业界权威人士参加高层论坛，就党的十八届五中全会提出的发展新理念和新闻出版业“十三五”改革发展新路向作深入探讨；邀请主管部门有关负责人阐释“双效结合是产业发展生命线和发展线”；邀请国家出版基金规划管理办公室负责人介绍国家出版基金项目规划与管理前瞻；邀请在出版走出去方面有突出成绩的集团负责人介绍“一带一路”发展思路；邀请领军企业负责人介绍传统出版与新兴出版融合走向。 </p> \n<p> 　　此外，本届订货会还将举办大家读好书、大屏幕精品图书展播等多个读书创意活动，开展“十佳文化活动”“十佳展台设计”“十佳新闻报道”“出版新技术应用”等评选活动。 </p> \n<p> 　　主办方表示，在国家“十三五”规划开局之年，北京图书订货会将运用新媒体科技手段，围绕创新、协调、绿色、开放、共享的主旨，突出主体多元化、交流广泛化、服务全面化、形式多样化，推动全民阅读进一步发展。 </p> \n<p> <br> </p> \n<p> 作者：章红雨 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年11月11日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 2016北京图书订货会明年1月7日启幕','2015-12-16 17:39:37','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/268451.shtml',NULL,NULL,NULL,NULL,NULL),(137,'国家新闻出版广电总局 - 行业信息 - 《赵朴初全集》出版启动暨编纂研讨会在京召开','《赵朴初全集》出版启动暨编纂研讨会在京召开','2015年11月18日','涂桂林  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 11月14日，由时代出版传媒有限公司、安徽教育出版社主办的《赵朴初全集》出版启动暨编纂研讨会在京召开。《赵朴初全集》由全国人大常务委员会原副委员长、民进中央原主席、著名学者许嘉璐主持编撰。 <br> </p> \n<p style=\"text-indent:2em;\"> 《赵朴初全集》分10卷约300万字，收录赵朴初讲话、论文、评论、序跋、散文、书信、诗、词、曲、联、题签、题名、题词等著述及书法作品，并附赵朴初年谱，是关于赵朴初最完备、最权威的著作资料整理成果，拟于2017年赵朴初诞辰110周年之际出版。 </p> \n<p style=\"text-indent:2em;\"> 许嘉璐在研讨会上指出，赵朴初的一生，是革命的一生，奉献的一生，朴实而真情的一生，他几十年如一日投入到中国人民的解放和建设事业，投入到中国佛教“走出去”的工作当中。赵朴初是卓越的佛教领袖、杰出的书法家、著名的社会活动家与伟大的爱国主义者，他在中国的传统文化研究方面也有很深的造诣。在中国的文化即将迎来高潮的时候，编辑出版《赵朴初全集》适逢其时，对社会能起到感召和引领的作用。 </p> \n<p style=\"text-indent:2em;\"> 研讨会上，与会者充分肯定了《赵朴初全集》的史料价值、研究价值和鉴赏价值，认为出版《赵朴初全集》对保存、研究和弘扬中华文化有着重要意义，并从不同视角、不同层面为出版好《赵朴初全集》献计献策。 </p> \n<p style=\"text-indent:2em;\"> 相关机构领导、学者及赵朴初亲属参加了活动。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：涂桂林 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年11月18日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 《赵朴初全集》出版启动暨编纂研讨会在京召开','2015-12-16 17:39:37','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/268947.shtml',NULL,NULL,NULL,NULL,NULL),(138,'国家新闻出版广电总局 - 行业信息 - 燕赵大地阅知行','燕赵大地阅知行 ——感受河北全民阅读的力量','2015年11月20日','杜一娜 范占英  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 最是书香能致远，燕赵大地阅知行。当全民阅读的推广以进农村、进社区、进校园、进军营、进企业、进机关、进家庭的“七进”方式在河北省全面推进后，该省的全民阅读活动呈现出这样的特点：品牌活动引领阅读风尚，“书香系列”评选营造尚文爱读氛围，农家书屋管理与使用探索因地制宜新思路。 </p> \n<p style=\"text-indent:2em;\"> 11月6日-7日，中国全民阅读媒体联盟走进燕赵大地，采访河北省第三届惠民阅读周暨惠民书市，走进社区书屋、农家书屋、书香家庭，在冬日初雪中感受河北省全民阅读的浓厚氛围。 </p> \n<p style=\"text-indent:2em;\"> <strong> 享受惠民阅读周乐趣 </strong> </p> \n<p style=\"text-indent:2em;\"> 阅读推广，惠民先行。在中国全民阅读媒体联盟举行“书香中国万里行·河北站”活动的第一天，恰逢河北省第三届惠民阅读周暨惠民书市开幕。河北出版传媒集团董事长杜金卿开玩笑地对大家说：“今天，双降。天气霜降，图书价降。” </p> \n<p style=\"text-indent:2em;\"> 旨在让利读者的惠民书市，因优惠的价格每年吸引着几十万读者前来淘书，今年的惠民书市更是在图书展销规模和让利上超过上两届。通过政府补贴、企业让利，惠民书市让河北广大读者真正享受到了文化惠民的好处。5万多种畅销书及新书七五折，8万多种常销书五折，5000多种冀版图书全部不高于四折，1万余种电子音像出版物、文化产品、数码产品、非遗产品等低于市场价格优惠销售。 </p> \n<p style=\"text-indent:2em;\"> 据统计，经过前两届惠民书市的品牌树立和人气积累，第三届惠民书市创出新高：5天时间里，惠民书市主会场举办了100多场阅读文化活动，全省共接待读者近百万人次，销售出版物103.7万册，为读者让利680万元。 </p> \n<p style=\"text-indent:2em;\"> 而精彩的阅读文化活动，也为第三届惠民阅读周暨惠民书市增添了活力，为初雪的燕赵大地燃起了暖暖的阅读篝火，开启了市民5天寻好书、读好书的盛宴。 </p> \n<p style=\"text-indent:2em;\"> 《寻找平山团》作者签售会、《太行山上》签售会、《红色记忆》图书推介会、《雪域长歌》作家读者见面会等红色主题图书的读书活动，勾起了曾是抗日老区读者的深深回忆；“勇敢者联盟”之大侦探签售会、《精灵梦叶罗丽》图书签售会、《小强来了》读者见面会等激发着现场小读者阅读的激情。与阅读相关的各种文艺活动，德孝音乐故事会、京津冀曲艺汇演、《爸爸在哪儿》精选话剧展演、第四届相约读书日朗诵会等，在惠民阅读周内，每天都吸引着不同的读者走进会场，欣赏文化，享受阅读。 </p> \n<p style=\"text-indent:2em;\"> <strong> 阅读推广深入人心 </strong> </p> \n<p style=\"text-indent:2em;\"> 读书滋养心灵，阅读改变生活。当全民阅读走进人心时，成为习惯时，一个人、一个村、一个镇、一个县都会随着阅读而潜移默化地发生改变。在河北，全民阅读的推广以“七进”方式正在覆盖燕赵大地的每一个角落。 </p> \n<p style=\"text-indent:2em;\"> 从2008年河北省启动书香家庭评选开始，燕赵大地掀起争当“书香先进”的激烈角逐。今年5月，河北省新闻出版广电局联合河北省委宣传部、河北省全民阅读办公室共同启动了“书香系列”的评选活动，让这场“书香先进”的角逐更加白热化。 </p> \n<p style=\"text-indent:2em;\"> “家庭是社会的细胞，乡村、社区是社会基本的组成部分，挖掘和树立家庭、乡村、社区读书榜样，让每个家庭、乡村社区、社会大众时时处处感受到阅读的存在，更有利于广泛深入地辐射全民阅读理念，对推动全社会共同读书氛围的形成具有重要意义。”河北省新闻出版广电局局长李晓明在11月6日做客“红沙发”系列访谈时表示。 </p> \n<p style=\"text-indent:2em;\"> 深入推动“七进”工作，以“书香系列”评选为重点，河北省在全民阅读推广上打造了自己独有的品牌活动——河北省青少年“阅·知·行”读书活动，倡导孩子们诵经典学国学。“我为盲童读经典”活动关爱特殊孩子、传递听读乐趣，为偏远中小学校捐助优秀出版物活动送书香、播智慧等，这些活动有力地推动了当地培育尊重知识、热爱阅读、追求创新、奋发向上的社会风尚的形成。 </p> \n<p style=\"text-indent:2em;\"> “今后，我们还要将评选活动逐步扩展到书香机关、书香校园、书香军营、书香企业、书香区县、书香城市等各个领域，在全省各村镇县市、各行各业形成争创‘书香集体’、争当‘书香单位’的全民阅读社会氛围，为‘书香河北’建设打下坚实的基础。”李晓明向记者们介绍说：“目前，正在着手‘24小时书吧’试点建设，确保提供方便、对口味、有吸引力的阅读服务。” </p> \n<p style=\"text-indent:2em;\"> <strong> 让书香永驻农家 </strong> </p> \n<p style=\"text-indent:2em;\"> 走进燕赵大地，听取农家书屋故事，听一听农家人的读书需求。河北省衡水市深州市穆村乡火头村的书屋管理员李坤峰对记者们说：“我们村是果树生产专业村，所以我们最希望看到瓜果种植的书。”衡水市桃城区邓家庄大葛村的农家书屋管理员赵永祥则提出，因为村南种植葡萄，村北种植蘑菇，所以更需要这两方面的科技图书。在衡水市桃城区郑家河沿镇北沼村，书屋管理员李秀峰说，村民们更喜欢看人物传记，孩子们爱看《森林报》和《赛尔号》。 </p> \n<p style=\"text-indent:2em;\"> 农忙时种树，农闲时补脑。不是不爱读书，只是爱读有用的书。这些心声，让记者们印象深刻，随着全民阅读推广的深入，随着农家书屋管理到位，农家人也开始越来越爱看书。 </p> \n<p style=\"text-indent:2em;\"> “每个农家书屋原则上可供借阅的实用图书不少于1500册，报刊不少于30种，电子音像制品不少于100种（张），具备条件的地区，可增加一定比例的网络图书、网络报纸、网络期刊等出版物。”河北省局公共服务处处长丁欣向记者们介绍了河北省农家书屋建设的基本标准。通过这次随访，丁欣也检查了这3家农家书屋管理和使用的情况，全部合格。 </p> \n<p style=\"text-indent:2em;\"> 据了解，自2009年起，河北省新闻出版主管单位首创并开展的“情暖故土书香农家”帮扶联系活动，通过在全省范围内招募志愿者，实行一对一或一对多、多对一的帮扶方式，为全国农家书屋的管理、使用探索出了一条新路，如今已见成效。河北省在对农家书屋的管理和使用上正在向深层拓展，希望让农家书屋的管理员更加安心，让农家书屋的新书更多，让书香永驻农家。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：杜一娜 范占英 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年11月20日 </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 燕赵大地阅知行','2015-12-16 17:39:37','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/269083.shtml',NULL,NULL,NULL,NULL,NULL),(139,'国家新闻出版广电总局 - 行业信息 - 深圳将建非遗走出去出版交流中心','深圳将建非遗走出去出版交流中心','2015年11月25日  ','','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em\"> 深圳出版发行集团、海天出版社近日在京举办“非遗·中国年画经典”系列新书发布会并讨论非遗大数据暨非遗走出去出版交流中心建设事宜。据了解，深圳出版发行集团、海天出版社正在着手打造非遗大数据暨非遗走出去出版交流中心，建设中国非遗大型网络平台。 <br> 　　“非遗·中国年画经典”系列图书共10本，根据题材分为《门神年画》《财神年画》《神仙年画》《戏出年画》《风俗年画》等。该书强调“用实物资料说话”，收录作者沈泓收藏的近3000幅年画，堪称中国目前最全的年画百科全书，每本书附送一张16开宣纸印制的经典年画。 <br> 　　正在着手打造的非遗大数据暨非遗走出去出版交流中心将以非遗大数据为主要内容，重点向海外推广中国非遗的出版物以及中国非遗的相关文化产品。它将推出非遗在线出版功能，以中、英、法、日等语种介绍非遗出版物、非遗产品、非遗传承人等。 </p> \n<p> &nbsp; </p> \n<p> 作者:王坤宁&nbsp;&nbsp;&nbsp;来源:中国新闻出版广电报&nbsp;&nbsp;&nbsp;发布时间：2015年11月25日 </p> \n<p> &nbsp; </p> \n<p> </p>',NULL,'','','国家新闻出版广电总局 - 行业信息 - 深圳将建非遗走出去出版交流中心','2015-12-16 17:39:37','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/269282.shtml',NULL,NULL,NULL,NULL,NULL),(140,'国家新闻出版广电总局 - 行业信息 - 上海童书展打造东方博洛尼亚','上海童书展打造东方博洛尼亚','2015年11月16日','金鑫  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 11月13日至15日，2015中国上海国际童书展如约而至。自筹备之初，中国上海国际童书展就以意大利博洛尼亚童书展为目标，并且认真学习博洛尼亚童书展的成熟经验，希望吸引世界各地的作者和作品来到这里，为童书发展贡献力量。流连在本届童书展上，记者发现，一些变化正在悄然发生。 <br> </p> \n<p style=\"text-indent:2em;\"> <strong> 产业链上角色总动员 </strong> </p> \n<p style=\"text-indent:2em;\"> 本届童书展可谓产业链上各种角色到得最齐的一次。作家、译者、插画家、出版人自不必说，技术服务商、网络平台、印刷企业、阅读推广组织等，也纷纷以不同形式参与到童书展和同期的各项活动中。东软云观信息技术有限公司、腾讯网络平台都是以大面积、特装展位参展，并发出与传统出版合作的讯息；“魔法童书会”、蒲蒲兰等阅读推广机构从线上走到线下，举办的亲子阅读推广论坛别具一格；柯达公司亚太区市场总监带来的深入解读数码印刷技术如何开拓儿童图书新蓝海的主题演讲，上海新华印刷厂隆重发布《儿童时代》采用水墨柔版印刷，带给儿童健康阅读的理念。 </p> \n<p style=\"text-indent:2em;\"> 产业链上下游间的交流互动更为频繁。今年的专业论坛，在短短两天之中竟然举办了18场之多，而首届童书展同期只有4场专业论坛。专业观众在线预登记开展前已过万，比上年增长20%。励展博览集团项目总监汪韧春在接受记者采访时表示，搭建交流沟通平台，把专业知识、技能分享给大家是设计所有专业论坛的初衷。 </p> \n<p style=\"text-indent:2em;\"> 汪韧春还告诉记者，常常会听到出版社吐槽说，想找到风格合适的插画师太难了。于是，今年不仅举办了“插画师沙龙”“全球插画师论坛”，还有了金风车国际青年插画家大赛的想法，旨在发现新人新作，鼓励儿童作品原创，为全球插画作品展示、青年插画师交流搭建了优质平台。大赛一经推出便受到了国际关注，陆续收到来自16个国家和地区的252名插画家的千余幅投稿作品。这个奖项今后会成为童书展上的重要活动，将持续举办。 </p> \n<p style=\"text-indent:2em;\"> <strong> 经营国内国际两个市场 </strong> </p> \n<p style=\"text-indent:2em;\"> 本届童书展上，中国童书版权合作、版权输出明显增多。少年儿童出版社《十万个为什么》与美国《国家地理学习》杂志战略合作，不仅会出版一套全新的多媒体科普杂志，还将整合教育力，为中国中小学生科学素养的提升注入新活力。 </p> \n<p style=\"text-indent:2em;\"> 日本倍乐生公司引进了中国中福会出版社的《中华奇趣故事》。倍乐生商贸（中国）有限公司董事长松平隆接受采访时表示，一直以来，日本的“乐智小天地”被中国引进，受到中国家庭孩子的喜爱。现在反过来了，选择中福会出版社出版的非常棒的这套绘本，翻译成日文，要让日本的孩子也能体会到中国文化的美好。“这只是一个开始，接下来这个工作还会持续下去。”松平隆说。记者还获悉，阿拉伯联合参展商购买了海豚出版社的70多个版权。 </p> \n<p style=\"text-indent:2em;\"> 中国少年儿童新闻出版总社社长李学谦认为，10年前，中国少儿出版在国际化方面以引进借鉴为主，现在中国少儿出版的国际视野正在形成。从一个选题到图书的出版，如果没有考虑到版权的输出，哪怕在国内销售再好也是失败。他告诉记者，“十三五”期间，中少总社必须完成“从单一经营国内市场为主，到经营国内国际两个市场”的转变。 </p> \n<p style=\"text-indent:2em;\"> <strong> 从“中国加工”到“中国制造” </strong> </p> \n<p style=\"text-indent:2em;\"> 有数据显示，中国引进版图书在少儿图书市场唱主角的状况从2014年开始发生变化。2015年1-6月，根据开卷公司提供的最新资料，排名在前100种的少儿畅销书中，有79种是由中国作家创作的。据国家新闻出版广电总局的汇总分析，2014年少儿图书选题中，引进版图书只有4969种，仅占8.8%。本土原创图书成为少儿出版的主流，说明中国少儿出版已经完成了由“中国加工”到“中国制造”的转变，进入到发展的新阶段。 </p> \n<p style=\"text-indent:2em;\"> 新蕾出版社总编辑马玉秀告诉记者，从第二届童书展开始，新蕾社展台就开始了特装，尽管现在版贸量不是很大，但只有在中国本土才会有这样一个向外商展示的机会，事实上也会助力后来的引进输出合作。 </p> \n<p style=\"text-indent:2em;\"> 李学谦说，打造东方的博洛尼亚是中国童书出版人共同的心愿，诚然，还有很多标准衡量，要走的路也还很长，但是，在中国童书走向国际化的过程中，专业平台打造正当时。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：金鑫 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年11月16日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 上海童书展打造东方博洛尼亚','2015-12-16 17:39:37','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/268764.shtml',NULL,NULL,NULL,NULL,NULL),(141,'国家新闻出版广电总局 - 行业信息 - 我国游戏市场今年销售收入达1407亿元','我国游戏市场今年销售收入达1407亿元','2015年12月16日','尹琨  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 2015年，我国游戏市场实际销售收入达1407亿元，同比增长22.9%。国家新闻出版广电总局今年批准出版的游戏约750款，“简政放权”促发展成为游戏出版行政监管的核心。 <br> </p> \n<p style=\"text-indent:2em;\"> 这是记者从12月15日发布的《2015年中国游戏产业报告》中获悉的。 </p> \n<p style=\"text-indent:2em;\"> 中国音像与数字出版协会游戏出版工作委员会联合专业机构发布的该《报告》，从客户端游戏、网页游戏、移动游戏、单机游戏、电视游戏等细分领域，对游戏研发、出版、运营等情况进行具体分析，并高度关注“电子竞技”“影游联动”等游戏产业新热点、新业务。 </p> \n<p style=\"text-indent:2em;\"> <strong> 新政策带来新机遇 </strong> </p> \n<p style=\"text-indent:2em;\"> 2015年，一系列法律法规与政策为游戏产业“保驾护航”。今年9月施行的新广告法对游戏推广作出了明确规定：不得在针对未成年人的大众传播媒介上发布不利于未成年人身心健康的网络游戏广告。《报告》指出，新广告法明确了游戏广告、宣传推广的边界，对游戏产业健康发展具有重要的推动作用。 </p> \n<p style=\"text-indent:2em;\"> “互联网+”“大众创业、万众创新”等国家战略为游戏产业提供了良好的宏观发展环境。《报告》认为，在“互联网+”战略引导下，可穿戴智能设备、VR（虚拟现实）设备将为新兴游戏类型发展提供硬件基础。游戏产业借助“大众创业、万众创新”的战略背景，催生了一批优质的研发型企业，诞生了一批精品游戏，也为年轻人提供了多种多样的就业机会。 </p> \n<p style=\"text-indent:2em;\"> 此外，“一带一路”战略的实施给游戏海外出口带来新的机遇。《少年三国志》《太极熊猫》等拥有中国文化内涵、具有跨国文化元素的精品游戏逐渐得到海外市场认可。《报告》显示，2015年，中国自主研发的网络游戏海外出口实际销售收入达53.1亿美元，同比增长达72.4%。 </p> \n<p style=\"text-indent:2em;\"> <strong> 新技术打开新市场 </strong> </p> \n<p style=\"text-indent:2em;\"> 《报告》指出，2015年，宽带提速、4G网络为游戏产业发展提供了良好的网络支持。智能电视、智能盒子的快速普及，为电视游戏带来新的契机。可穿戴智能设备、VR设备、现实加强设备等相关硬件的发展，为下一阶段游戏产业发展提供了新的方向。 </p> \n<p style=\"text-indent:2em;\"> 2015年是游戏视频直播元年，一系列衔接用户、赛事、产品的游戏直播平台构建了游戏电子竞技生态圈。无论是客户端游戏，还是移动游戏，都呈现出明显的竞技化趋势。《报告》指出，游戏研发技术的迭代催生了一批MOBA（多人在线战术竞技）类游戏的诞生，这些游戏也成为移动电子竞技市场的有力支撑。 </p> \n<p style=\"text-indent:2em;\"> 随着基础技术与硬件的快速发展，《报告》同时预测，移动电子竞技、Html5游戏、电视游戏等新兴细分游戏市场将成为产业增长点。 </p> \n<p style=\"text-indent:2em;\"> <strong> 新布局促进新发展 </strong> </p> \n<p style=\"text-indent:2em;\"> 《报告》显示，2015年，客户端游戏市场实际销售收入达到611.6亿元，发展保持平稳；网页游戏市场实际销售收入达到219.6亿元，呈现“高投入高收入”的状态；移动游戏市场实际销售收入达到514.6亿元，从单纯获取流量向深入挖掘用户价值转变。值得关注的是，小众游戏表现出色，游戏企业通过这些游戏掌握用户偏好，锻炼新人，对提升企业形象，改善中国游戏生态起到了积极作用。 </p> \n<p style=\"text-indent:2em;\"> 游戏资本市场今年持续活跃：一方面，上市帮助游戏企业借助资本市场的力量获得发展；另一方面，上市的游戏企业能够通过收购等手段进行资源整合，进入发展壮大快车道。《报告》显示，我国上市游戏企业已达171家，市值接近5万亿元。 </p> \n<p style=\"text-indent:2em;\"> 在“影游联动”的探索上，《西游记之大圣归来》《花千骨》《琅琊榜》等影视、文学作品为游戏产业提供了新鲜血液。《报告》认为，游戏产业与其他文创领域互动增多，跨媒体整合加速，并通过联动的方式，探索出一条全新的围绕娱乐产业进行的产业链。在这条产业链上，任何一种类型的作品都能转化为其他类型的作品，从而发挥出IP（知识产权）的最大价值。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：尹琨 &nbsp;来源：中国新闻出版广电报 &nbsp; 发布时间：2015年12月16日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报   ','国家新闻出版广电总局 - 行业信息 - 我国游戏市场今年销售收入达1407亿元','2015-12-16 17:39:46','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/270574.shtml',NULL,NULL,NULL,NULL,NULL),(142,'国家新闻出版广电总局 - 行业信息 - 第二届国际数字出版大会举行','第二届国际数字出版大会举行','2015年11月20日','王坤宁 李婧璇  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 11月18日-19日，以“年轻的移动阅读群体与出版业的变革”为主题的第二届国际数字出版大会在京举行。来自中国、美国、德国等国家的数字出版人，就全球出版传媒业面临的移动出版新变革新机遇进行了深入探讨。 <br> </p> \n<p style=\"text-indent:2em;\"> 在主论坛上，德国法兰克福国际书展主席尤根·博斯介绍了欧美数字出版的现状。他说，移动出版改变了传统出版的产品、营销、服务模式，包括移动出版在内的数字出版已成为国际书展的重中之重。美国世哲（SAGE）出版社亚太区总裁保罗·伊凡斯谈道，无论电子书还是纸质书，都要以内容质量为前提。德国维肯林克（Wolkenlenker）公司创始人兼总裁塞巴斯蒂安·维诺从知识产权开发的角度，展示了跨媒介融合移动出版的成功案例。同方知网数字出版集团移动出版总监刘艳军则阐述了我国移动出版面临的机遇与挑战。与会者还从产品、技术、赢利模式等方面，分享了各自在数字出版业务上的探索和经验。 </p> \n<p style=\"text-indent:2em;\"> 为期两天的会议还举办了8场分会活动。30余家国内少儿、科技、教育出版单位，以及数字出版企业相关负责人就专业出版在科学技术上的发展潜力与挑战、移动阅读对大众出版的影响、国际数字出版市场和中外合作的潜力等议题与各国出版人展开交流。 </p> \n<p style=\"text-indent:2em;\"> 会议由中国知网主办，中国新闻出版研究院希普思文化咨询公司承办。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：王坤宁 李婧璇 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年11月20日 </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 第二届国际数字出版大会举行','2015-12-16 17:39:46','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/269082.shtml',NULL,NULL,NULL,NULL,NULL),(143,'国家新闻出版广电总局 - 行业信息 - 今年全国报业用纸量下降18%','今年全国报业用纸量下降18%','2015年12月10日','李雪昆  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 12月9日，由中国报业协会主办的2015中国报业物资供应年会在重庆召开。记者了解到，从中国报协最近对全国93家用纸量大的报社2014年、2015年用纸量的调查数据来看，今年比去年用纸量增加的有18家，比例为19.4%；减少的有67家，比例为72.0%；持平的有8家，比例为8.6%。综合各因素进行推算，2015年全国报业用纸量约为221万吨，相比2014年的270万吨减少49万吨，约下降18%。 <br> </p> \n<p style=\"text-indent:2em;\"> 据中国报业协会统计，今年国内新闻纸总产量约为235万吨，比去年260万吨的产量下降25万吨，下降幅度为9.6%。在出口方面，由于出口价格低，今年的出口量较少，预计今年新闻纸的总出口量约为2万吨。进口纸方面，预计今年全年进口量在10万吨左右，进口来源国主要是韩国，约占进口量的70%，其余的依次为比利时、瑞典、加拿大、美国。 </p> \n<p style=\"text-indent:2em;\"> 记者从区域市场分析数据中看到，全国六大区域中的报社用纸量与去年相比都在下降。从报纸类型分析看，93家报社中，今年比去年用纸量增加的18家单位中有10家是中央级报社和2家省级党报及6家地市级党报。持平的8家单位中有5家中央级报社、1家省级党报和2家地市报。 </p> \n<p style=\"text-indent:2em;\"> 中国报协秘书长胡怀福指出，根据各项数据统计汇总，今年新闻纸总供应量约为245万吨，需求总量约为221万吨，供大于求。他分析说，用纸量增加或持平的报社主要是得益于行政力量的推动。其他主要报业集团用纸量下降幅度较大，基本是在20%-40%之间，主要原因还是各报业集团所拥有的都市报等市场化的报纸广告、发行下降幅度较大，因此用纸量也有较大幅度下降，而集团内部的党报用纸量基本不受影响，有的还略有增加。但由于都市报的新闻纸使用量在报业集团里所占比重很大，从而带动报业集团整体用纸量的下降。 </p> \n<p style=\"text-indent:2em;\"> 谈及对2016年新闻纸市场的预测，胡怀福表示，明年我国新闻纸市场变化虽然还有很多不确定因素，但是市场需求不断减少将成为常态。而明年大部分新闻纸生产厂家将继续保持正常生产，但有部分新闻纸厂迫于经营压力过大，还有可能关停并转，新闻纸产量也将进一步降低。因此，从2016年起新闻纸供需形势将逐渐趋于平衡，有利于缓解新闻纸“严重供大于求”的态势。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：李雪昆 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年12月10日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 今年全国报业用纸量下降18%','2015-12-16 17:39:46','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/270196.shtml',NULL,NULL,NULL,NULL,NULL),(144,'国家新闻出版广电总局 - 行业信息 - 中国图书评论学会发布10月“中国好书”','中国图书评论学会发布10月“中国好书”','2015年12月10日','杨雅莲  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 中国图书评论学会日前发布10月“中国好书”，共有8本图书入选。 <br> </p> \n<p style=\"text-indent:2em;\"> 上榜图书分别为：《孔学古微》（华东师范大学出版社）、《施光南传》（江苏人民出版社）、《敦煌石窟艺术简史》（中国青年出版社）、《连环画里的时代记忆》（人民出版社）、《呦呦有蒿：屠呦呦与青蒿素》（中国科学技术出版社）、《癌症·真相：医生也在读》（清华大学出版社）、《对话孩子：我在加拿大做心理咨询与治疗》（上海社会科学院出版社）、《弯弯的辛夷花》（天天出版社）。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：杨雅莲 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年12月10日 </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 中国图书评论学会发布10月“中国好书”','2015-12-16 17:39:46','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/270195.shtml',NULL,NULL,NULL,NULL,NULL),(145,'国家新闻出版广电总局 - 行业信息 - 完美世界“笑傲江湖”私服外挂案宣判 3名被告分别被判处  有期徒刑','完美世界“笑傲江湖”私服外挂案宣判 3名被告分别被判处 有期徒刑','2015年12月9日','邹韧  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> 北京市海淀区人民法院近日以侵犯著作权罪分别判处何某某、厉某、罗某3人有期徒刑2年至3年不等，缓期执行，并处罚金10万元至16万元不等。 <br> </p> \n<p style=\"text-indent:2em;\"> 2013年11月16日，北京市文化市场行政执法总队接到完美世界（北京）网络技术有限公司的举报，反映该公司享有著作权的“笑傲江湖OL”动漫游戏软件被盗，发现有人私自架设服务器，并在该私服内安装、运行该公司拥有著作权的“笑傲江湖OL”游戏软件，并通过提供注册、充值等服务进行牟利。接报后，北京市文化市场行政执法总队联合海淀区公安分局网安大队组成专案组联合办案。 </p> \n<p style=\"text-indent:2em;\"> 由于此案点多、面广、线长，因此侦破难度非常大。经查，不法团伙利用互联网在江苏省、四川省、浙江省等多个省市，从事私服网络游戏违法经营活动，以谋取经济利益。涉案犯罪团伙主要有3人：江苏省南京市的厉某，浙江省温州市的何某某，四川省的罗某。专案组发现犯罪嫌疑人具有一定的反侦查意识，域名注册、服务器架设在美国、中国香港特区，给执法人员取证造成了困难，属于有意逃避打击的行为。经调查发现，该案团伙成员分居浙江、江苏、四川三地，利用网络组成了涉案团伙，他们在不到一年的时间内，经营面涉及诸多城市，规模之大、发展速度之快令人吃惊。 </p> \n<p style=\"text-indent:2em;\"> 在当地公安部门的配合下，3名犯罪嫌疑人分别被抓获或自首。案发后，何某某、厉某各自的家属分别代为向完美公司赔偿人民币10万元和20万元，罗某自首后，其家属代为赔偿人民币20万元。对此完美公司出具了书面意见对3人表示谅解。 </p> \n<p style=\"text-indent:2em;\"> 2015年11月2日，海淀区人民法院依照《中华人民共和国刑法》以侵犯著作权罪判处何某某有期徒刑三年，缓刑四年，判处罚金16万元；判处厉某有期徒刑三年，缓刑四年，判处罚金16万元；判处罗某有期徒刑两年，缓刑三年，判处罚金10万元。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：邹韧 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年12月9日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 完美世界“笑傲江湖”私服外挂案宣判 3名被告分别被判处 有期徒刑','2015-12-16 17:39:46','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/270129.shtml',NULL,NULL,NULL,NULL,NULL),(146,'国家新闻出版广电总局 - 行业信息 - 印刷业：“创新”“绿色”成主旋律','印刷业：“创新”“绿色”成主旋律','2015年12月8日','李明远  ','首页 > 新闻资讯 > 行业信息','<p> </p> \n<p style=\"text-indent:2em;\"> “十二五”时期，是我国印刷业发展不平凡的5年。5年间，印刷业稳中求进，总产值于2013年首次突破1万亿元，整体规模升至全球第二位。2014年实现总产值10857.5亿元，是2010年总产值的1.4倍。 <br> </p> \n<p style=\"text-indent:2em;\"> 5年间，印刷人在推进行业转型升级方面义无反顾。他们的行动力从绿色印刷方面踏出的坚实步伐便可看出——从2010年绿色印刷工程启动到2014年秋季全部中小学教科书实现绿色印刷，仅仅用了4年时间。伴随我国经济发展进入新常态，印刷业正面临着增速放缓的现状，逐渐进入深度调整、融合创新的发展时期。 </p> \n<p style=\"text-indent:2em;\"> <strong> 提质增效成果显现 示范企业释放榜样力量 </strong> </p> \n<p style=\"text-indent:2em;\"> “从2012年开始，我国印刷业的增速由两位数降为一位数，并连续两年下降，但由于企业数量减少、劣质企业出局，平均到每个企业的单位产值呈上升趋势，劳动生产率也逐年提高。”国家新闻出版广电总局印刷发行司司长王岩镔对记者说，我国印刷业增速换挡、提质增效已经显现成果。 </p> \n<p style=\"text-indent:2em;\"> “十二五”期间，我国印刷业产业结构持续优化。2014年，中部地区印刷业规模已经达到珠三角地区的85%，发展增量占全国的25%。印刷业产业结构基本形成东部地区稳定发展，中西部地区承接产业梯度转移，由量变转为质变的态势。与此同时，我国印刷行业产业集中度正在不断提高。年产值5000万元以上的印刷企业从2010年的2147家增长到2014年的3125家，占全国印刷企业总数的3%，但其产值总量占全国的一半以上。优质资源不断向骨干企业聚集，加速淘汰了一批落后产能的小型企业。 </p> \n<p style=\"text-indent:2em;\"> 在依法开展行业监管、维护市场公平竞争秩序的同时，总局积极发挥政府对产业的宏观引导作用。2012年-2015年的4年间，总局共认定95家国家印刷示范企业。据统计，2014年，全国80余家印刷示范企业销售收入同比增长8%，高于同期全国平均水平，这显示出企业的集约化发展取得成效。王岩镔认为，这些示范企业在文化创意、科技创新、绿色环保、跨界融合等方面都走在了全行业的前列，充分发挥出引导和示范作用。 </p> \n<p style=\"text-indent:2em;\"> <strong> 环保意识深入人心 全产业链共推绿色发展 </strong> </p> \n<p style=\"text-indent:2em;\"> 绿色发展是党中央为“十三五”时期制定的“五大发展理念”之一，而我国的印刷业在“十二五”就践行了这一理念。 </p> \n<p style=\"text-indent:2em;\"> 早在2011年10月，原新闻出版总署和环境保护部共同发布《关于实施绿色印刷的公告》时，我国还没有一件严格意义上的绿色印刷产品，绿色印刷还是个有待普及的概念。经过4年的积极推进，如今，全国有925家企业获得绿色印刷认证，近200万名印刷从业者的工作环境得到改善。2015年秋季，12亿册中小学教科书全部实现了绿色印刷。绿色印刷产品还覆盖到大众图书、票据、包装等领域。 </p> \n<p style=\"text-indent:2em;\"> 随着绿色发展理念深入人心，标准体系逐步完善，检测能力不断提高，绿色印刷市场倒逼机制初步形成。“以前大家主要看重印刷的产量、质量，现在越来越深刻认识到绿色发展对于社会的重要性。”上海印刷（集团）有限公司总经理沈剑毅告诉记者，2011年上海印刷集团成功研制出水墨柔印工艺，在出版物上应用时，曾经历因环保工艺成本高于传统工艺而难以推行的情况。让沈剑毅感到欣慰的是，4年来，越来越多的出版单位主动选择这种更为环保的工艺。 </p> \n<p style=\"text-indent:2em;\"> 绿色印刷得到宣传推广，离不开各方给予印刷业发展的支持。2013年~2015年，中央文资办连续3年对绿色印刷给予了总额6亿元的资金扶持，北京、上海、福建每年也分别有3000万元、600万元和230万元对绿色印刷的奖励资金。 </p> \n<p style=\"text-indent:2em;\"> 为了让更多的人支持、理解绿色印刷，从2011年11月开始，我国还先后举办了5个绿色印刷宣传周活动。 </p> \n<p style=\"text-indent:2em;\"> 在政策引导下，不仅印刷企业实施绿色印刷的主动性明显增强，越来越多的纸张、版材、油墨企业也专注于研发环保原辅材料。据总局发布的《实施绿色印刷成果报告（2015年）》显示，目前，环保油墨使用量已经占到全国油墨总使用量的25%；在胶印领域，已经有30%的企业安装了粉尘收集装置。 </p> \n<p style=\"text-indent:2em;\"> <strong> 数字技术广泛运用 传统企业迎来“二次创业” </strong> </p> \n<p style=\"text-indent:2em;\"> 随着个性化印刷需求持续稳定上升，我国数字化技术在行业内的应用逐步深入、趋于广泛。截至2014年年底，国内数字印刷企业已扩张至2622家，较2012年增长了255%。“数字印刷适应了快速响应客户个性化需要的业务需求。近年来，数字印刷的应用主体从快印企业向传统印刷企业扩展。高速喷墨按需出版系统在国内得以应用。”中国印刷科学技术研究院院长、中国印刷技术协会数字印刷分会理事长陈彦分析说。 </p> \n<p style=\"text-indent:2em;\"> 对于一些先行的传统印刷企业来说，引入数字印刷技术并不仅仅是增加了一项服务，而是开启了“二次创业”。“面对未来，印刷企业要做颠覆者，就注定要和风险相伴，只有‘眼高’方能‘路宽’。”江苏凤凰新华印务有限公司总经理张在健表示，凤凰新华印务5年来重新创业，成功建立了国内首个大型全媒体云出版印刷平台，已从一个单一教材印刷的传统制造工厂转变为以数字创意为核心的新型国际化企业。 </p> \n<p style=\"text-indent:2em;\"> 雅昌、圣彩虹、虎彩、长荣健豪……一家家优秀企业投入数字印刷领域，形成独特的经营模式。陈彦认为，随着数字印刷技术的持续发展、商业形态更趋成熟和完善，数字印刷企业正与大数据、互联网融合，开创了新的市场机遇。 </p> \n<p style=\"text-indent:2em;\"> <strong> 争做专精特新企业 “互联网+印刷”成为新课题 </strong> </p> \n<p style=\"text-indent:2em;\"> 经济发展的新常态对于印刷业而言意味着挑战，但同时也带来了新机遇。一些坚持走专精特新道路的印刷企业仍然实现了高于宏观经济发展速度的增长。它们正不断强化自身在优势领域的研发能力。例如，在薄纸印刷和精装领域处于领先地位的南京爱德印刷有限公司2012年设立了技术研发中心，与客户充分沟通需求，运用新材料、新工艺，实现创意与灵感。在儿童益智图书印制方面占据优势的北京尚唐印刷包装有限公司打造了一支20余人的技术研发部，负责立体书、玩具书等益智图书的结构设计研发，以及新材料与新技术的引进和革新。 </p> \n<p style=\"text-indent:2em;\"> 培育新的经济增长点是传统印刷企业“十二五”期间的必选动作。报纸印刷产量在2013年开始逐年断崖式下滑，让湖北楚天传媒印务有限责任公司总经理金则云在2012年上任后不久就面临严峻形势。“针对市场变化情况，楚天印务顺应时代发展潮流，大力开展新技术、新工艺、新产品的研发应用，并结合自身实际确定转型方向和目标。”金则云表示，5年来，楚天印务自主研发数字印刷管理信息平台，推动印刷创意向服务型转变，开辟物流运输、仓储管理、厂房租赁等新业务，实现了多元发展。 </p> \n<p style=\"text-indent:2em;\"> 新一代信息技术，特别是“互联网+印刷”的深度融合，优化了资源配置，正在创造印刷业新的发展生态。飓风网、印猫网、盛通商印快线、阳光印网等电商平台的推出使线上线下加速融合。用互联网激活印刷跨界融合，已成为印刷企业正在探索的新课题。 </p> \n<p> <br> </p> \n<p style=\"text-indent:0em;\"> 作者：李明远 &nbsp;来源：中国新闻出版广电报 &nbsp;发布时间：2015年12月8日 </p> \n<p> <br> </p> \n<p> </p>',NULL,'','中国新闻出版广电报  ','国家新闻出版广电总局 - 行业信息 - 印刷业：“创新”“绿色”成主旋律','2015-12-16 17:39:46','','','','','','','','','','','','http://www.gapp.gov.cn/news/1658/270074.shtml',NULL,NULL,NULL,NULL,NULL);

/*Table structure for table `t_news_info_config` */

DROP TABLE IF EXISTS `t_news_info_config`;

CREATE TABLE `t_news_info_config` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT '主键id',
  `src_id` int(11) DEFAULT NULL COMMENT '来源Id',
  `name` varchar(20) DEFAULT NULL COMMENT '抓取名称',
  `prop` varchar(20) DEFAULT NULL COMMENT '属性',
  `selector` varchar(200) DEFAULT NULL COMMENT '抓取页面的选择器',
  `orSelector` varchar(200) DEFAULT NULL COMMENT '如果根据selector选择器没有抓取到的候选的selector',
  `is_resv_Html` varchar(2) DEFAULT NULL COMMENT '是否保留HTML标签',
  `subReg` varchar(50) DEFAULT NULL COMMENT '通过正则表达式从字符串中截取字符串',
  `xml_node` varchar(20) DEFAULT NULL COMMENT '如果是用xml生成的，在xml中的节点名',
  `resv1` varchar(50) DEFAULT NULL COMMENT '是否需要下载',
  `resv2` varchar(50) DEFAULT NULL COMMENT '保留字段2',
  `resv3` varchar(50) DEFAULT NULL COMMENT '保留字段3',
  `resv4` varchar(50) DEFAULT NULL COMMENT '保留字段4',
  PRIMARY KEY (`id`)
