/*Table structure for table `t_news_info_source` */

DROP TABLE IF EXISTS `t_news_info_source`;

CREATE TABLE `t_news_info_source` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT '主键',
  `site_url` varchar(100) NOT NULL DEFAULT '' COMMENT '抓取网站',
  `site_name` varchar(100) DEFAULT NULL COMMENT '抓取网站名',
  `site_type` varchar(20) DEFAULT NULL COMMENT '分类，政策类，公告类等',
  `startUrl` varchar(100) DEFAULT NULL COMMENT '开始页面地址',
  `label_id` int(11) DEFAULT NULL COMMENT '所属分类',
  `is_xml` varchar(2) DEFAULT NULL COMMENT '是否使用xml抓取',
  `xml_url` varchar(50) DEFAULT NULL COMMENT 'xml文件地址',
  `is_wrap_b` varchar(2) DEFAULT NULL COMMENT '是否在文本上面wrap一个b标签',
  `is_valid` varchar(2) DEFAULT NULL COMMENT '是否有效',
  `last_crawl_time` datetime DEFAULT NULL COMMENT '上次时间',
