/* Function  structure for function  `fn_t_info_content` */

/*!50003 DROP FUNCTION IF EXISTS `fn_t_info_content` */;
DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` FUNCTION `fn_t_info_content`(`v_content` TEXT) RETURNS text CHARSET utf8
BEGIN  
  DECLARE iStart, iEnd, iLength int;   
    WHILE Locate( '<', v_content ) > 0 And Locate( '>', v_content, Locate( '<', v_content )) > 0 DO   
      BEGIN  
        SET iStart = Locate( '<', v_content ), iEnd = Locate( '>', v_content, Locate('<', v_content ));   
        SET iLength = ( iEnd - iStart) + 1;   
        IF iLength > 0 THEN  
          BEGIN  
            SET v_content = Insert(v_content, iStart, iLength, '');   
          END;   
        END IF;   
      END;   
    END WHILE;   
    RETURN v_content;   
END */$$
DELIMITER ;

/* Procedure structure for procedure `sp_child` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_child` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_child`(IN `v_pid` INT)
BEGIN   
      DECLARE done INT DEFAULT 0;   
      DECLARE b INT;   
      DECLARE cur1 CURSOR FOR SELECT id FROM t_menu  WHERE p_id=v_pid;   
      DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = 1;   
      SET max_sp_recursion_depth=3;   
      
		
      INSERT INTO t_tmp(rootid) 
		VALUES (v_pid);   
       
      OPEN cur1;   
       
      FETCH cur1 INTO b;   
      WHILE done=0 DO   
               CALL sp_child(b);   
              FETCH cur1 INTO b;   
      END WHILE;   
       
      CLOSE cur1;   
END */$$
DELIMITER ;

/* Procedure structure for procedure `sp_label` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_label` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_label`(IN `v_pid` INT)
BEGIN   
      DECLARE done INT DEFAULT 0;   
      DECLARE b INT;   
      DECLARE cur1 CURSOR FOR SELECT id FROM t_label  WHERE p_id=v_pid;   
      DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = 1;   
      SET max_sp_recursion_depth=3;   
      
		
      INSERT INTO t_tmp_label(rootid) 
		VALUES (v_pid);   
       
      OPEN cur1;   
       
      FETCH cur1 INTO b;   
      WHILE done=0 DO   
               CALL sp_label(b);   
              FETCH cur1 INTO b;   
      END WHILE;   
       
      CLOSE cur1;   
END */$$
DELIMITER ;

