mapper.select:
    select 

    from t_cust_info_enshrine_rel
    where 1 = 1

		and cust_id = #{cust_id,jdbcType=INTEGER}


		and  info_id = #{infoId,jdbcType=INTEGER}


        and is_valid = #{is_valid,jdbcType=INTEGER}

select e.cust_id,e.info_id as infoId,c.content content,c.title AS title, DATE_FORMAT(c.create_time,'%Y/%m/%d') AS DATE,DATE_FORMAT(c.create_time,'%H时') AS TIME,t.origin AS origin, (SELECT COUNT(1) FROM t_info_comment e WHERE e.info_id = c.info_id AND e.is_valid = 0) AS comment_count, (SELECT IF(#{infoId,jdbcType=INTEGER} IN(SELECT info_id FROM t_cust_info_enshrine_rel WHERE cust_id = #{cust_id,jdbcType=INTEGER} AND is_valid =0 ),1,0) )AS is_enshrine from t_cust_info_enshrine_rel e left join t_info t on t.info_id = e.info_id left join t_info_banner b on b.info_id = t.info_id LEFT JOIN t_info_content c ON c.info_id = t.info_id where 1=1
and e.cust_id = #{cust_id,jdbcType=INTEGER}
and e.info_id = #{infoId,jdbcType=INTEGER}
GROUP BY e.info_id

select e.cust_id,e.info_id,IF(b.url IS NULL ,'http://42.96.165.121:8086/upload/20151210/20151210192709_banner_679834.jpg',b.url ) AS bannerUrl,c.title AS title, date_format(c.create_time,'%Y年%m月%d日') as date,date_format(c.create_time,'%H:%i') as time from t_cust_info_enshrine_rel e left join t_info t on t.info_id = e.info_id left join t_info_banner b on b.info_id = t.info_id AND b.is_valid='0' AND (b.is_header IS NULL AND b.is_show IS NULL) left join t_info_content c on c.info_id = t.info_id where 1=1 and e.is_valid = 0
and e.cust_id = #{cust_id,jdbcType=INTEGER}
GROUP BY e.info_id order by e.id desc
limit #{startRow}
,${pageSize}

mapper.update:
  		update t_cust_info_enshrine_rel 

		  is_valid=2

		where cust_id = #{cust_id,jdbcType=INTEGER}
              and info_id = #{infoId,jdbcType=INTEGER}

update.foreach:
update t_cust_info_enshrine_rel 

  is_valid=2

where cust_id = ${item.cust_id} 
and info_id = ${item.info_id}

mapper.update:
update t_cust_info_enshrine_rel 

  is_valid=2

where cust_id = ${cust_id} 
  and info_id in 

     ${item}
